INSERT INTO `himnos` (`id_himno`, `pagina_0`, `pagina_1`, `pagina_2`, `pagina_3`, `pagina_4`, `pagina_5`, `pagina_6`, `pagina_7`, `pagina_8`, `pagina_9`, `pagina_10`, `pagina_11`, `pagina_12`, `num`) VALUES
(1, '1 \r\nA MI SALVADOR    ', '1\r\nQuiero cantar a mi Señor,\r\nPor su afecto y su perdón.\r\nPues su sangre, Él, derramó,\r\nPor salvarme del error', 'CORO\r\nA mi Salvador: ¡Sea la gloria!\r\nLa honra y el honor,\r\nPorque Él dio su vida.\r\nY a los cielos, ¡Victorioso! ascendió.', '2\r\nDe perecer me rescató,\r\nPues mi ruego, Él, atendió.\r\n¿Olvidar tan sublime amor?\r\nNo podría, ¡Nunca! ¡No!', 'CORO\r\nA mi Salvador: ¡Sea la gloria!\r\nLa honra y el honor,\r\nPorque Él dio su vida.\r\nY a los cielos, ¡Victorioso! ascendió', '3\r\nY así, con gran humildad,\r\nTodo mi ser, se rinde a Él.\r\nAlabanzas entono hoy.\r\n¡En su amor me gozaré!', 'CORO(final)\r\nA mi Salvador: ¡Sea la gloria!\r\nLa honra y el honor,\r\nPorque Él dio su vida.\r\nY a los cielos, ¡Victorioso! ascendió.', '', '', '', '', '', '', 6),
(2, '2\r\nACLAMAD A DIOS ', '1\r\n¡Aclamad a Dios con alegría!\r\n¡Cantad la gloria de su Nombre!\r\n¡Dadle gloria con alabanza!\r\n¡Cuán asombrosas son sus obras!', 'CORO\r\n// ¡Venid y cantad por sus hechos!\r\n¡Volvió el mar en tierra seca!\r\n¡Alaben cielo y tierra a Dios!\r\nCosas admirables, Él, ¡Ha hecho! // ', '2\r\n¡Aclamad a Dios con voz ferviente!\r\nSu pueblo entero le alabe.\r\n¡Con fiel corazón, hoy, cantadle!\r\n¡Rendidle gloria a su Nombre!', 'CORO(final)\r\n// ¡Venid y cantad por sus hechos!\r\n¡Volvió el mar en tierra seca!\r\n¡Alaben cielo y tierra a Dios!\r\nCosas admirables, Él, ¡Ha hecho! //', '', '', '', '', '', '', '', '', 4),
(3, '3\r\nALABAD', '1\r\n¡Alabad a nuestro Padre!\r\n¡Toda gloria sea a Él!\r\nPor su amor inescrutable.\r\n¡Alabemos siempre a Él!', 'CORO\r\nToda gloria, en su honor, \r\nEs elevada a nuestro Dios,\r\nDesde ahora y por siempre. \r\n¡Sea así!', 'CORO\r\n// Hoy unamos nuestras voces\r\n¡Y exaltemos al Señor!\r\nPor los siglos de los siglos. \r\n¡Sea así! //', '2\r\nAlabar todos unidos,\r\n¡Cuán hermoso ha de ser!\r\nEn presencia del bendito,\r\nY su rostro conocer.', 'CORO\r\nToda gloria, en su honor, \r\nEs elevada a nuestro Dios,\r\nDesde ahora y por siempre. \r\n¡Sea así!', 'CORO(final)\r\n// Hoy unamos nuestras voces\r\n¡Y exaltemos al Señor!\r\nPor los siglos de los siglos. \r\n¡Sea así! //', '', '', '', '', '', '', 6),
(4, '4\r\nALABARLE, ADORARLE', '1\r\n¡Alabado sea el Padre!\r\n¡A Él, gloria y honor!\r\nCada uno alabarle\r\nY adorarle con fervor.', 'CORO\r\n¡Alabarle, adorarle! Pueblos todos al Creador.\r\n¡Tributarle, ensalzarle! Porque a su Hijo dio.', '2\r\nEn el día de reposo,\r\nQue nos dio nuestro Creador,\r\nAlabanzas a su Nombre,\r\nSe le den de corazón.', 'CORO\r\n¡Alabarle, adorarle! Pueblos todos al Creador.\r\n¡Tributarle, ensalzarle! Porque a su Hijo dio.', '3\r\nCon gran gozo, entonarle\r\nAlabanzas de loor,\r\nPor su amor inescrutable.\r\n¡Digno es de adoración!', 'CORO\r\n¡Alabarle, adorarle! Pueblos todos al Creador.\r\n¡Tributarle, ensalzarle! Porque a su Hijo dio.', '4\r\nAlabado sea el Padre,\r\nQue ofrece salvación.\r\n¡Rico es en bendiciones!\r\n¡Honra y gloria a nuestro Dios!', 'CORO(final)\r\n¡Alabarle, adorarle! Pueblos todos al Creador.\r\n¡Tributarle, ensalzarle! Porque a su Hijo dio.', '', '', '', '', 8),
(5, '5\r\n¡ALABEMOS A NUESTRO DIOS!', '1\r\nTodos juntos a una voz,\r\n¡Alabemos a nuestro Dios!\r\n¡Demos gloria! ¡Demos loor!\r\nAl Eterno y perdonador.\r\nOfrendemos con devoción,\r\nComo un agradable olor.\r\n¡Gratitud con el corazón!\r\n¡Hoy cantemos al Creador!', 'CORO\r\n¡Alabemos a nuestro Dios!\r\n¡Demos honra y adoración!\r\nSu misericordia y bondad,\r\n¡Nunca! ¡Nunca se agotarán!\r\nPorque justo y bueno es Él;\r\nPor los siglos ha sido fiel.\r\n¡Exaltemos su majestad!\r\nAl Altísimo, ¡Alabad!', '2\r\nCon sus manos, Él levantó\r\nLas montañas en esplendor;\r\nRíos, mares, nubes también,\r\nFueron hechos con su poder.\r\nEs eterno, quien nos formó;\r\nPara siempre es su amor.\r\nAdoremos con gozo hoy,\r\nAl autor de la creación.', 'CORO(final)\r\n¡Alabemos a nuestro Dios!\r\n¡Demos honra y adoración!\r\nSu misericordia y bondad,\r\n¡Nunca! ¡Nunca se agotarán!\r\nPorque justo y bueno es Él;\r\nPor los siglos ha sido fiel.\r\n¡Exaltemos su majestad!\r\nAl Altísimo, ¡Alabad!', '', '', '', '', '', '', '', '', 4),
(6, '6\r\nBATALLÓN DE CRISTO', '1\r\nBatallón de Cristo: firmes hay que estar.\r\n¡Niños preparados! ¡Vamos a cantar!\r\nEn la Iglesia, nuestras voces subirán,\r\nCon gran alegría, al Padre celestial.', 'CORO\r\nQuiero adorarte, quiero a Ti cantar.\r\nPorque Tú me cuidas en todo lugar.\r\nY mi corazón te quiero entregar,\r\nEn esta alabanza que recibirás.', '2\r\nNotas con el piano y con el violín,\r\nLas de la guitarra y las del flautín,\r\nSeguirán mi canto que, con mucho amor,\r\nSalen de mis labios para Ti, mi Dios.', 'CORO\r\nQuiero adorarte, quiero a Ti cantar.\r\nPorque Tú me cuidas en todo lugar.\r\nY mi corazón te quiero entregar,\r\nEn esta alabanza que recibirás.', '3\r\nUn favor te pido mi Señor, mi Dios:\r\nBendice a mis padres, dales bienestar.\r\nEllos encaminan mis pasos a Ti,\r\nPara que por siempre, Tú vivas en mí.', 'CORO 2(final)\r\n// Quiero alabarte, quiero a Ti cantar,\r\nEn todo momento y en todo lugar.\r\nEn el batallón de Cristo quiero estar,\r\n¡En sus filas siempre quiero militar! //', '', '', '', '', '', '', 6),
(7, '7\r\nBENDICE ALMA MÍA AL SEÑOR', '1\r\n¡Bendice alma mía, al Señor!\r\nY no olvides sus beneficios.\r\n¡Da gracias por la vida! ¡Qué grande bendición!\r\nEl fruto del trabajo y la salud.', 'CORO\r\nBendice ¡Oh, alma mía!\r\nA Dios por sus grandes bondades.\r\nPor su misericordia\r\nY su infinito amor.', '2\r\n¡Bendice alma mía, a tu Creador!\r\n¡Bendice al que te hizo y formó!\r\n¡Da gracias al Eterno! por su hermosa creación.\r\n¡Da gloria y loores al Señor!', 'CORO\r\nBendice ¡Oh, alma mía!\r\nA Dios por sus grandes bondades.\r\nPor su misericordia\r\nY su infinito amor.', '3\r\n¡Bendice alma mía, al Señor!\r\n¡En sus manos tiene la vida!\r\n¡Da gracias por los dones que ha derramado en ti!\r\n¡Bendice alma mía, a tu Hacedor!', 'CORO(final)\r\nBendice ¡Oh, alma mía!\r\nA Dios por sus grandes bondades.\r\nPor su misericordia\r\nY su infinito amor.', '', '', '', '', '', '', 6),
(8, '8\r\nBENDITO SALVADOR', '1\r\nGracias, bendito Señor,\r\nPor el amor que me das.\r\n//Canto ante Ti con fervor. //\r\n¡Acepta mi loor!', 'CORO\r\nAnhelo estar cerca de Ti,\r\nPara alabar tú Nombre.\r\nDándote gloria y honor,\r\nMi bendito Salvador...,\r\n¡Bendito Salvador!', '2\r\nMi Cristo, mi Redentor,\r\nSé que me oyes clamar.\r\n//Me brindas todo tu amor. //\r\n¡Aleluya, Señor!', 'CORO\r\nAnhelo estar cerca de Ti,\r\nPara alabar tú Nombre.\r\nDándote gloria y honor,\r\nMi bendito Salvador...,\r\n¡Bendito Salvador!', '3\r\nUn canto lleno de amor,\r\n¡Oh Cristo! te vengo a dar;\r\n//Lleno de gloria y loor//\r\n¡Bendito Salvador!', 'CORO(final)\r\nAnhelo estar cerca de Ti,\r\nPara alabar tú Nombre.\r\nDándote gloria y honor,\r\nMi bendito Salvador...,\r\n¡Bendito Salvador!', '', '', '', '', '', '', 6),
(9, '9\r\nCANTA A DIOS, CANTO A DIOS', '1\r\nA Dios demos alabanza con fervor;\r\nCánticos..., porque en su gracia nos creó.\r\nPara alabanza y su servicio nos formó;\r\nObra útil de sus manos. ¡Gloria a Dios!', 'CORO\r\n¡Canta a Dios! ¡Canta a Dios!\r\n¡Honra y gloria demos hoy!\r\n¡Canto a Dios! ¡Sí! ¡Canto a Dios!\r\n¡Alabemos al Señor!', '2\r\nAnunciemos, con voz firme, que el Señor,\r\nDigno es de toda honra y loor.\r\nY que, en la tierra, mares, cielos por doquier,\r\nSe engrandezca el Nombre del Eterno Dios.', 'CORO\r\n¡Canta a Dios! ¡Canta a Dios!\r\n¡Honra y gloria demos hoy!\r\n¡Canto a Dios! ¡Sí! ¡Canto a Dios!\r\n¡Alabemos al Señor!', '3\r\nCon palabra y obra la Iglesia de Dios,\r\nEl gran Nombre del Creador ensalzará.\r\nLa gloria es suya por los siglos y hasta el fin,\r\nPor su amor tan grande y su fidelidad.', 'CORO(final)\r\n¡Canta a Dios! ¡Canta a Dios!\r\n¡Honra y gloria demos hoy!\r\n¡Canto a Dios! ¡Sí! ¡Canto a Dios!\r\n¡Alabemos al Señor!', '', '', '', '', '', '', 6),
(10, '10\r\nCANTAD A DIOS', '1\r\n¡Cantad a Dios cántico nuevo!\r\n¡Toda la tierra cante al Señor!\r\n¡Cantad a Dios, su Nombre es santo!\r\n¡Y anunciad su Salvación!', '2\r\nProclamen las naciones todas,\r\n¡La gloria del gran Dios de bondad!\r\n¡Anúnciense! entre los pueblos,\r\n¡Las maravillas de Jehová!', '3\r\n¡Grande es Jehová! Su alabanza,\r\nToda criatura debe ensalzar;\r\n¡Honra y tributo en la tierra!,\r\nAl Rey eterno ofrendad.', '4(final)\r\nTraed ofrenda a sus “atrios”,\r\nPues de Dios es la honra y poder.\r\n¡Alégrense cielos y tierra!\r\n¡Alaba a Dios! ¡Gózate en Él!', '', '', '', '', '', '', '', '', 4),
(11, '11\r\nCANTARÉ A MI DIOS', '1\r\n¡Cantaré, a mi Dios! con sinceridad,\r\nPorque Él me colma de paz.\r\nCantaré su amor, su inmensa bondad;\r\n¡A Él glorias por la eternidad!', 'CORO\r\n// ¡Gloria! // ¡Sea a Dios!\r\nCon todo el corazón, hoy, ofrendaré:\r\n¡Alabanza digna de Él!', '2\r\n¡Cantaré, a mi Dios! con gran devoción,\r\nPor las bendiciones que da.\r\n¡Gratitud hay en mí! ¡Le quiero alabar!\r\nSu Nombre, en su templo, ensalzar.', 'CORO\r\n// ¡Gloria! // ¡Sea a Dios!\r\nCon todo el corazón, hoy, ofrendaré:\r\n¡Alabanza digna de Él!', '3\r\n¡Cantaré, a mi Dios! y me gozaré.\r\nSus magnificencias diré.\r\nQue toda la creación le rinda loor,\r\n¡Al Dios de los siglos! ¡Amén!', 'CORO(final)\r\n// ¡Gloria! // ¡Sea a Dios!\r\nCon todo el corazón, hoy, ofrendaré:\r\n¡Alabanza digna de Él!', '', '', '', '', '', '', 6),
(12, '12\r\nCANTO CON GOZO', '1\r\nCanto con gozo, a mi Señor;\r\nCanto con gozo, a nuestro Dios.\r\nA Él, mis labios alabarán.\r\n¡Al Rey del cielo, la gloría doy!', 'CORO\r\n¡Canten los astros a una voz!\r\n¡Cante el Pueblo de Dios!\r\nTodos unidos en adoración,\r\nRindamos la honra al Señor.', '2\r\nCanto con gozo, a mi Señor;\r\nCon alegría y devoción.\r\nCielos y tierra le alabarán,\r\nReconociendo su gran bondad.', 'CORO\r\n¡Canten los astros a una voz!\r\n¡Cante el Pueblo de Dios!\r\nTodos unidos en adoración,\r\nRindamos la honra al Señor.', '3\r\nCanto con gozo, a mi Señor;\r\n¡Cántico nuevo del corazón!\r\nTodos sus santos le alabarán,\r\nLlenos de espíritu y verdad.', 'CORO(final)\r\n¡Canten los astros a una voz!\r\n¡Cante el Pueblo de Dios!\r\nTodos unidos en adoración,\r\nRindamos la honra al Señor.', '', '', '', '', '', '', 6),
(13, '13\r\nCLAMARÉ, ALABARÉ, ADORARÉ', '1\r\nAl que creó todas las cosas:\r\n¡Clamaré!\r\nAl que dio luz a mi vida:\r\n¡Alabaré!\r\nAl que es conmigo siempre:\r\n¡Adoraré!', 'CORO\r\n// ¡Clamaré!, ¡Alabaré!, ¡Adoraré! //\r\n// ¡Al que siempre es fiel! //', '2\r\nAl que me dio nueva vida:\r\n¡Clamaré!\r\nAl que me amó primero:\r\n¡Alabaré!\r\nAl que envió a su Hijo amado:\r\n¡Adoraré!', 'CORO\r\n// ¡Clamaré!, ¡Alabaré!, ¡Adoraré! //\r\n// ¡Al que siempre es fiel! //', '3\r\nAl que escucha y responde:\r\n¡Clamaré!\r\nAl que cubre mi sendero:\r\n¡Alabaré!\r\nA mi Dios, toda mi vida:\r\n¡Adoraré!', 'CORO(final)\r\n// ¡Clamaré!, ¡Alabaré!, ¡Adoraré! //\r\n// ¡Al que siempre es fiel! //', '', '', '', '', '', '', 6),
(14, '14\r\n¿CÓMO PODRÉ DIOS MÍO?', '1\r\n¿Cómo podré, Dios mío,\r\nTu amor disimular?\r\nSi siempre en mi camino, Señor,\r\nSe muestra más y más.', 'CORO\r\n¿Cómo podré olvidarme\r\nDe tu infinito amor?\r\nSi todo lo que veo Señor,\r\nEs obra tuya, ¡Oh Dios!', '2\r\nEl sol y las estrellas,\r\nLa luna y la mar,\r\nSon las más bellas cosas, Señor,\r\nQue puedo yo contemplar.', 'CORO\r\n¿Cómo podré olvidarme\r\nDe tu infinito amor?\r\nSi todo lo que veo Señor,\r\nEs obra tuya, ¡Oh Dios!', '3\r\nLa amistad contigo,\r\nDelicia es para mí,\r\nPues eres el amigo mejor,\r\nCon quién, hoy vivo feliz.', 'CORO(final)\r\n¿Cómo podré olvidarme\r\nDe tu infinito amor?\r\nSi todo lo que veo Señor,\r\nEs obra tuya, ¡Oh Dios!', '', '', '', '', '', '', 6),
(15, '15\r\nCRISTO BENDITO, TE ALABARÉ', '1\r\nEn primavera, en el verano, ¡Te alabaré!\r\nCada momento de mi existencia, ¡Te adoraré!\r\nEn paz sublime o en la angustia que pueda haber.\r\nYa sea otoño o sea invierno, ¡Te alabaré!', 'CORO\r\n¡Te alabaré!\r\n¡Tu Santo Nombre adoraré!\r\nMientras yo viva, Cristo bendito,\r\n¡Te alabaré!', '2\r\nPor la mañana y en la noche, ¡Te alabaré!\r\nCada minuto, en cada suspiro, ¡Te ensalzaré!\r\nY si no hay luna y el sol no brilla, mi corazón\r\nCante, a tu gloria, con alegría y devoción.', 'CORO\r\n¡Te alabaré!\r\n¡Tu Santo Nombre adoraré!\r\nMientras yo viva, Cristo bendito,\r\n¡Te alabaré!', '3\r\nPor el sendero que me has trazado, ¡Caminaré!\r\nQue, a cada paso, mi pensamiento, en Ti esté.\r\n¡Ese es mi anhelo!; servirte siempre, hasta que al fin\r\nDe mi jornada, descanse y tu voz pueda oír.', 'CORO(final)\r\n¡Te alabaré!\r\n¡Tu Santo Nombre adoraré!\r\nMientras yo viva, Cristo bendito,\r\n¡Te alabaré!', '', '', '', '', '', '', 6),
(16, '16\r\nDIA DE AGRADECIMIENTO', '1\r\nHoy es día de reposo,\r\nMuy feliz me acercaré.\r\nY en su templo, respetuoso,\r\nMi holocausto entregaré.', 'CORO\r\nDía de agradecimiento\r\n¡Este sábado será!\r\nCon fervor al Dios eterno,\r\nLe venimos a adorar.', '2\r\nVoy con fe a su Presencia\r\nOfreciendo lo mejor;\r\nY abundancia de su gracia\r\nMe dará nuestro Creador.', 'CORO\r\nDía de agradecimiento\r\n¡Este sábado será!\r\nCon fervor al Dios eterno,\r\nLe venimos a adorar.', '3\r\nTodos con gran alegría,\r\nRendiremos todo honor.\r\nTodo aquel que en Él confía,\r\nCantará con devoción.', 'CORO(final)\r\nDía de agradecimiento\r\n¡Este sábado será!\r\nCon fervor al Dios eterno,\r\nLe venimos a adorar.', '', '', '', '', '', '', 6),
(17, '17\r\nDIOS', '1\r\nDios, que estás en los cielos;\r\nDios, Creador de la tierra;\r\n¡Recibe la honra y la gloria!\r\nPor siglos, ¡Tu Nombre bendito!\r\nRecibe de ángeles, miles,\r\nHosanas, ¡Bendito Señor!', '2\r\nDios, tu Iglesia te alaba,\r\nPorque das tu gracia infinita.\r\nReunidos, como un solo hombre,\r\nCantamos tus obras perfectas.\r\nQue llegue a Ti, nuestra alabanza;\r\nAl trono de tu Majestad.', '3(final)\r\nDios, en Ti esperaremos,\r\nPorque Tú darás el consuelo,\r\n¡Y tus mandamientos guardamos!\r\nEn tu Ley nos deleitaremos.\r\nDios nuestro, te damos las gracias,\r\n// Y te rendimos todo honor. //', '', '', '', '', '', '', '', '', '', 3),
(18, '18\r\nDIOS DE BONDAD', '1\r\n¡Pueblos todos alabad a Dios!\r\nEntonad canción con amor.\r\nSobre nosotros ha puesto su mano.\r\n¡A Él, sea la honra y gloria!', 'CORO\r\n// ¡Oh, gran Señor!\r\n¡Oh, gran Creador!\r\nEres Dios de bondad. //', '2\r\nDe tu paz, enriquecidos hoy;\r\nEngrandecemos tu favor.\r\nTú nos respondes en todo momento.\r\nEres, de adoración, digno.', 'CORO\r\n// ¡Oh, gran Señor!\r\n¡Oh, gran Creador!\r\nEres Dios de bondad. //', '3\r\nBendiciones nos das, sin par.\r\nNos das fuerza para vencer\r\nY en cada instante que somos tentados,\r\nTu espíritu nos dirige.', 'CORO(final)\r\n// ¡Oh, gran Señor!\r\n¡Oh, gran Creador!\r\nEres Dios de bondad. //', '', '', '', '', '', '', 6),
(19, '19\r\nDIOS ES TODO', '1\r\nSi Dios mostró su amor para ti;\r\nSi Dios te salvó de lo vil;\r\nSi Dios cuidó tu vida aquí:\r\nDigno es de nuestro servir.', 'CORO\r\n// Para Dios vivir, para Dios morir;\r\nEs todo en ti y en mí. //', '2\r\nSi Cristo mostró el camino a seguir;\r\nSi Cristo veló, fiel, por ti;\r\nSi Cristo entregó su vida al morir:\r\nDigno es de nuestro servir.', 'CORO\r\n// Para Dios vivir, para Dios morir;\r\nEs todo en ti y en mí. //', '3\r\nSi Dios prometió un reino aquí;\r\nSi Dios prometió el vivir;\r\nSi Dios prometió, jamás ya sufrir:\r\nDigno es de nuestro servir.', 'CORO(final)\r\n// Para Dios vivir, para Dios morir;\r\nEs todo en ti y en mí. //', '', '', '', '', '', '', 6),
(20, '20\r\nELEVA HOY TU CANTO A DIOS', '1\r\n//Eleva hoy tu canto a Dios//\r\n¡Con júbilo, con gran fervor!,\r\nEnsalza el Nombre de nuestro Creador.', 'CORO\r\nQue los cielos cuenten su magnificencia;\r\nLas estrellas, todo su esplendor;\r\nY la tierra muestre, su grandeza eterna,\r\nEn cada cosa que el Señor formó.', '2\r\n// Eleva hoy tu voz a Dios, //\r\nCon gratitud de corazón;\r\nQue digno de alabanza es el Señor.', 'CORO\r\nQue los cielos cuenten su magnificencia;\r\nLas estrellas, todo su esplendor;\r\nY la tierra muestre, su grandeza eterna,\r\nEn cada cosa que el Señor formó.', '3\r\n// Eleva hoy tu ofrenda a Dios, //\r\nCon gran amor y devoción;\r\nQue escudo nuestro es y salvación.', 'CORO\r\nQue los cielos cuenten su magnificencia;\r\nLas estrellas, todo su esplendor;\r\nY la tierra muestre, su grandeza eterna,\r\nEn cada cosa que el Señor formó.', '4\r\n//Eleva hoy tu adoración//\r\nConfiésale su grande amor,\r\nPues da la vida, fuerza y bendición.', '2o. CORO\r\nQue los cielos cuenten su magnificencia;\r\nLas estrellas, todo su esplendor;\r\nY la tierra muestre, su grandeza eterna,\r\nEn cada cosa que el Señor formó.', '2o. CORO(final)\r\n¡Libera hoy tu corazón! \r\n//Eleva hoy tu canto a Dios//', '', '', '', 9),
(21, '21\r\nEN ESPÍRITU Y VERDAD', '1\r\n¡Ha llegado la hora! ¡Hoy es el momento!\r\nDe dar al Padre honra, con todo sentimiento.', 'CORO\r\nEn espíritu y verdad,\r\nEl cristiano verdadero, a su Dios adorará.\r\n¡Sólo a Él le servirá!', '2\r\nFieles adoradores, creyentes sinceros.\r\nPor Jesús redimidos, limpiados por su sangre.', 'CORO\r\nEn espíritu y verdad,\r\nEl cristiano verdadero, a su Dios adorará.\r\n¡Sólo a Él le servirá!', '3\r\n¡Dios te llama hermano! ¡Debes ser valiente!\r\n¡Hoy entrega tu vida! ¡Sé honesto y obediente!', 'CORO(final)\r\nEn espíritu y verdad,\r\nEl cristiano verdadero, a su Dios adorará.\r\n¡Sólo a Él le servirá!', '', '', '', '', '', '', 6),
(22, '22\r\nGLORIA A NUESTRO DIOS', '1\r\n¡Gloria a nuestro Dios!, cantemos hoy;\r\nPorque nos permite gozar de su amor\r\nY así, nos mantiene en paz, sin temor.\r\n¡Alabanza al Rey con gran loor!', 'CORO\r\nNuestro cántico recibe, Dios.\r\nHolocausto es del corazón,\r\nQue ofrecemos hoy con gran amor.\r\nDanos, ¡Oh Señor! tu bendición.', '2\r\nQue, con tu Presencia, este lugar,\r\nInundado siempre esté de fervor.\r\nAlabanza hoy te venimos a dar.\r\nNos gozamos, Dios, con tu favor.', 'CORO\r\nNuestro cántico recibe, Dios.\r\nHolocausto es del corazón,\r\nQue ofrecemos hoy con gran amor.\r\nDanos, ¡Oh Señor! tu bendición.', '3\r\n¡Con gran devoción, con dulce voz!,\r\nLa Iglesia de Dios enaltece tu amor.\r\nObra de tus manos, Señor, somos hoy;\r\nCreados para tu alabanza, Dios.', 'CORO(final)\r\nNuestro cántico recibe, Dios.\r\nHolocausto es del corazón,\r\nQue ofrecemos hoy con gran amor.\r\nDanos, ¡Oh Señor! tu bendición.', '', '', '', '', '', '', 6),
(23, '23\r\nGLORIA SEA AL CREADOR', '1\r\n¡Gloria sea al Creador!\r\n¡Su grandeza ensalzad!\r\nFue tan grande su amor\r\nQue, a su Hijo, entregó.', 'CORO\r\n¡Gloria sea a Jesús!\r\nQue su sangre vertió,\r\nPara dar salvación\r\nAl más vil pecador.', '2\r\nNuevo canto elevad,\r\nAl gran Dios de verdad;\r\nDadle en santidad,\r\nAlabanza eternal.', 'CORO\r\n¡Gloria sea a Jesús!\r\nQue su sangre vertió,\r\nPara dar salvación\r\nAl más vil pecador.', '3\r\n¡Gloria al Hijo de Dios!\r\n¡Que muy pronto vendrá!\r\nA reinar con amor,\r\nPara siempre jamás.', 'CORO FINAL\r\n¡Gloria sea a Jesús!\r\nQue su sangre vertió,\r\nPara dar salvación\r\nAl más vil pecador.\r\n¡Gloria sea a Jesús!\r\nQue su sangre vertió.', '', '', '', '', '', '', 6),
(24, '24\r\nGRACIAS POR AMARME ASÍ', '1\r\nGracias por amarme así,\r\nGracias por quererme así,\r\nCanto siempre para Ti. Eres mi esperanza,\r\nToda mi confianza, ¡Gracias por amarme así!', 'CORO\r\n// ¡Oh Jesús! ¡Oh Jesús!\r\nDigno de alabanza eres Tú //', '2\r\nGracias por tenerme aquí,\r\nCerca, muy cerca de Ti.\r\nEres todo para mí; mi vida te entrego...\r\nVe que soy sincero. ¡Gracias por amarme así!', 'CORO(final)\r\n// ¡Oh Jesús! ¡Oh Jesús!\r\nDigno de alabanza eres Tú //', '', '', '', '', '', '', '', '', 4),
(25, '25\r\nGRACIAS POR TU AMOR', '1\r\nTu amor, ¡Oh Dios!, es inmenso.\r\nEs profundo como el mar;\r\nEs tan alto como los cielos.\r\nNada hay que se le puede comparar.', 'CORO\r\nGracias por tu amor, tu infinito amor.\r\nGrande bendición y sublime don.\r\nGracias ¡Oh, Señor! Te alabamos,\r\nPorque diste, sin medida, tu amor.', '2\r\nEn tu creación lo has plasmado.\r\nEstá en una flor o en el sol.\r\nLas montañas dan testimonio.\r\nNo hay algo, en que no esté tu amor.', 'CORO\r\nGracias por tu amor, tu infinito amor.\r\nGrande bendición y sublime don.\r\nGracias ¡Oh, Señor! Te alabamos,\r\nPorque diste, sin medida, tu amor.', '3\r\nY así, Señor, con gran gozo,\r\nTe ensalzamos, ¡Oh gran Creador!\r\nLleguen hasta Ti nuestros cantos.\r\nDamos gracias con sincero corazón.', 'CORO(final)\r\nGracias por tu amor, tu infinito amor.\r\nGrande bendición y sublime don.\r\nGracias ¡Oh, Señor! Te alabamos,\r\nPorque diste, sin medida, tu amor.', '', '', '', '', '', '', 6),
(26, '26\r\nGRACIAS SEÑOR POR ESTE DÍA', '1\r\nEstoy aquí para hacer tu voluntad.\r\nUna oración, una alabanza vengo a dar.\r\nCon gozo y alegría te canto, mi Señor,\r\nAcepta de mi boca el cantar.', 'CORO\r\nGracias Señor, por este día,\r\nTan lleno de dicha y amor.\r\nRecibe mi alabanza, en señal de gratitud;\r\nUn canto que brotó del corazón.', '2\r\nGracias mi Dios por la oportunidad,\r\nDe deleitarme en tu Palabra, una vez más.\r\nCon gozo y alegría, te alabo mi Señor;\r\nConfieso yo, que todo, Tú, me das.', 'CORO(final)\r\nGracias Señor, por este día,\r\nTan lleno de dicha y amor.\r\nRecibe mi alabanza, en señal de gratitud;\r\nUn canto que brotó del corazón.', '', '', '', '', '', '', '', '', 4),
(27, '27\r\nGRACIAS TE DOY', '1\r\n¡Oh, mi Señor!, gracias te doy\r\nPor concederme la bendición,\r\nDe estar aquí, de reposar,\r\nPorque aún te puedo cantar.\r\nMejor momento no habrá,\r\nDe agradecer lo que me das.', '2\r\nGracias te doy porque aún,\r\nAnte tus ojos, valioso soy.\r\nTu luz, en mí, me motivo\r\nA presentarte ofrendas, mi Dios.\r\nCuando te abrí mi corazón,\r\nSentí solaz... felicidad.', '(Final)\r\nPor eso, hoy te alabo Creador;\r\nTu gloria quiero ensalzar.\r\nGracias Señor, por tu bondad;\r\nTus maravillas voy a contar.\r\nMejor momento no habrá\r\nDe agradecer lo que me das.', '', '', '', '', '', '', '', '', '', 3),
(28, '28\r\nGRAN DIOS DE AMOR ERES TÚ', '1\r\nDios, ¡Altísimo y sublime!\r\nDios, ¡El misericordioso!\r\nDios, ¡Eterno y bondadoso!\r\n¡Gran Dios de amor, eres Tú!', 'CORO\r\n//Reconocemos tus maravillas\r\nY la grandeza de tus obras.\r\nReconocemos que el universo\r\nEs resultado de tu creación//', '2\r\nTú, habitas en los cielos;\r\n¡Grande en misericordia!\r\nEres justo y verdadero.\r\n¡Gran Dios de amor, eres Tú!', 'CORO\r\n//Reconocemos tus maravillas\r\nY la grandeza de tus obras.\r\nReconocemos que el universo\r\nEs resultado de tu creación//', '3\r\nDios, ¡El Todopoderoso!\r\nDios, ¡Clemente y amoroso!\r\nDios, ¡Dador de bendiciones!\r\n¡Gran Dios de amor, eres Tú!', 'CORO(final)\r\n//Reconocemos tus maravillas\r\nY la grandeza de tus obras.\r\nReconocemos que el universo\r\nEs resultado de tu creación//', '', '', '', '', '', '', 6),
(29, '29\r\nGRANDE ES JEHOVÁ', '1\r\nGrande es Jehová y digno de alabanza.\r\n¡Cantad a Dios!, fortaleza nuestra.\r\nMi corazón está dispuesto, ¡Oh Dios!,\r\nA entonar canción de amor.', 'CORO\r\nY contaré todas tus maravillas.\r\nY alabaré tu santo Nombre.\r\nMe gozaré en tu magna gloria,\r\n¡Oh Jehová!', '2\r\n¡Oh Dios! que mi alabanza no calle.\r\nTe alabaré, porque Tú eres grande\r\nY hacedor de maravillas, mil.\r\nMe colmarás de bendición.', 'CORO\r\nY contaré todas tus maravillas.\r\nY alabaré tu santo Nombre.\r\nMe gozaré en tu magna gloria,\r\n¡Oh Jehová!', '3\r\nAlabaré tu Nombre, ¡Oh mi Dios!\r\nTe exaltaré con voz reverente:\r\nAl Creador que me hizo y me formó.\r\nTe alabaré con devoción.', 'CORO(final)\r\nY contaré todas tus maravillas.\r\nY alabaré tu santo Nombre.\r\nMe gozaré en tu magna gloria,\r\n¡Oh Jehová!', '', '', '', '', '', '', 6),
(30, '30\r\nGRANDE ES NUESTRO DIOS', '1\r\nPadre nuestro, ¡Señor Omnipotente!\r\nTu Iglesia te alaba. ¡No hay otro, sólo Tú!\r\n¡Oh, Dios nuestro! Recibe este canto\r\nQue hoy, ¡Te ofrecemos con todo el corazón!', 'CORO\r\nGrande eres, Dios,\r\nY digno de ser alabado;\r\nPorque enviaste a tu Hijo amado,\r\nPara salvarnos del pecado.', '2\r\nPadre nuestro; ¡Las gracias hoy te damos!,\r\nPor tus misericordias, ¡Que infinitas son!\r\nQue tu mano, nos dé la fortaleza;\r\nTu espíritu nos guíe, por siempre a la verdad.', 'CORO FINAL\r\n//Grande eres Dios\r\nY digno de ser alabado;\r\nPorque enviaste a tu Hijo amado,\r\nPara salvarnos del pecado//', '', '', '', '', '', '', '', '', 4),
(31, '31\r\nGRATAS BENDICIONES', '1\r\nA Ti, Padre eterno, sea este canto.\r\nQue olor fragante, ¡Llegue hasta tu trono!\r\n¡Cuántas maravillas haces con nosotros!\r\nY así con gozo, tu Nombre ensalzamos.', 'CORO\r\nGratas bendiciones\r\nDe Ti recibimos;\r\nY en cualquier momento: ¡Tu amor divino!\r\nLlenas por completo nuestros corazones.', '2\r\nCuando contemplamos tu poder divino,\r\nTe rendimos gloria, ¡Oh Señor bendito!\r\nRecibe amoroso nuestra gratitud,\r\nPor tu amor tan grande y tu gran virtud.', 'CORO(final)\r\nGratas bendiciones\r\nDe Ti recibimos;\r\nY en cualquier momento: ¡Tu amor divino!\r\nLlenas por completo nuestros corazones.', '', '', '', '', '', '', '', '', 4),
(32, '32\r\nGRATITUD', '1\r\nSeñor, estoy agradecido\r\nPorque me haz escogido;\r\nPorque me haz escuchado;\r\nPorque me haz perdonado.\r\nPor eso:', 'CORO\r\n//Te canto, hoy, con todo mi corazón,\r\nCon gratitud y amor;\r\n¡Por darme la salvación!\r\n¡Por darme de tu amor! //', '2\r\nEn tu camino andaré;\r\nTus pasos, fiel seguiré.\r\n¡Hasta el final llegaré!\r\n¡Tu mano me sostendrá!\r\nPor eso:', 'CORO\r\n//Te canto, hoy, con todo mi corazón,\r\nCon gratitud y amor;\r\n¡Por darme la salvación!\r\n¡Por darme de tu amor! //', '3\r\nSé, que Tú guías mi camino;\r\nPor fe, en Ti, he creído.\r\nSi viene a mí el enemigo,\r\n¡Siempre estarás a mi lado!\r\nPor eso:', 'CORO(final)\r\n//Te canto, hoy, con todo mi corazón,\r\nCon gratitud y amor;\r\n¡Por darme la salvación!\r\n¡Por darme de tu amor! //', '', '', '', '', '', '', 6),
(33, '33\r\nHAY UN DIOS', '1\r\nHay un Dios que sabe lo que siento.\r\nHay un Dios que escucha lo que pienso.\r\nHay un Dios que oye cuando clamo.\r\nHay un Dios que acude a mi llamado.', 'CORO\r\nEse Dios... ¡Es el Dios poderoso!\r\nQue formó los cielos y la tierra.\r\nY hoy, le entono mi alabanza,\r\nA mi Dios, por su infinita gracia.', '2\r\nHay un Dios que me ama sin medida.\r\nHay un Dios que cuida de mi vida.\r\nHay un Dios que vela mientras duermo.\r\nHay un Dios que siempre está atento.', 'CORO\r\nEse Dios... ¡Es el Dios poderoso!\r\nQue formó los cielos y la tierra.\r\nY hoy, le entono mi alabanza,\r\nA mi Dios, por su infinita gracia.', '3\r\nHay un Dios que siempre me da abrigo.\r\nHay un Dios que siempre va conmigo.\r\nHay un Dios que ya salvó mi vida,\r\nHay un Dios que me da alegría.', 'CORO\r\nEse Dios... ¡Es el Dios poderoso!\r\nQue formó los cielos y la tierra.\r\nY hoy, le entono mi alabanza,\r\nA mi Dios, por su infinita gracia.', '4\r\nVen y dile todo lo que sientes.\r\nReconoce que sólo Él te entiende.\r\nPon en Él, tu fe y tu confianza\r\nY obtendrás la vida en recompensa .', 'CORO(final)\r\nEse Dios... ¡Es el Dios poderoso!\r\nQue formó los cielos y la tierra.\r\nY hoy, le entono mi alabanza,\r\nA mi Dios, por su infinita gracia.', '', '', '', '', 8),
(34, '34\r\nHOY TE AMO SEÑOR', '1\r\nHoy te amo Señor,\r\nPorque siempre, Tú me has dado amor.\r\nHoy al fin comprendo,\r\nTu poder, en tu obra, y la verdad de tu corazón.', 'CORO 1\r\nHoy te siento tan cerca...\r\nMe amparaste en tu gran perdón.\r\nTeniéndote a mi lado,\r\n¡Cuán feliz me hace tu amor!', '2\r\nHoy mi vida te doy,\r\nElevando mi feliz gratitud,\r\nPorque al fin comprendo\r\nTu poder, en tu obra, y la verdad de tu corazón.', 'CORO 2\r\n¡Oh Señor Dios! yo quiero\r\nVivir en tu inmenso amor.\r\nNo puedo ser sin Ti;\r\nYa no quiero estar sin Ti.', '3(1)\r\nHoy te amo Señor,\r\nPorque siempre, Tú me has dado amor.\r\nHoy al fin comprendo\r\nTu poder, en tu obra, y la verdad de tu corazón.', 'CORO 1\r\nHoy te siento tan cerca.\r\nMe amparaste en tu gran perdón.\r\nTeniéndote a mi lado,\r\n¡Cuán feliz me hace tu amor!', 'CORO 2(final)\r\n¡Oh Señor Dios! yo quiero\r\nVivir en tu inmenso amor.\r\nNo puedo ser sin Ti,\r\nya no quiero estar sin Ti', '', '', '', '', '', 7),
(35, '35\r\nHUMILDE OFRENDA', '1\r\nA Ti, Padre santo,\r\n¡Es mi alabanza!\r\nPues yo ¡Te amo tanto!\r\nRecibe hoy mi canto.', 'CORO\r\nUna humilde ofrenda,\r\nDe corazón, ¡Doy!\r\n¡Sea bella fragancia!\r\nA Ti mi Señor.', '2\r\nMis labios te alaben,\r\n¡Oh Dios! para siempre;\r\n¡Bendigan al nombrarte!\r\n¡Tu gloria, siempre cuenten!', 'CORO\r\nUna humilde ofrenda,\r\nDe corazón, ¡Doy!\r\n¡Sea bella fragancia!\r\nA Ti mi Señor.', '3\r\nA Ti, Padre eterno,\r\nDoy toda la honra;\r\nRecibe, mi presente,\r\nQue ofrezco ahora.', 'CORO\r\nUna humilde ofrenda,\r\nDe corazón, ¡Doy!\r\n¡Sea bella fragancia!\r\nA Ti mi Señor.', '4\r\nUn ferviente canto,\r\nMi ser, hoy, entona.\r\nMi ofrenda te entrego,\r\n¡Recíbela, oh Dios!', 'CORO(final)\r\nUna humilde ofrenda,\r\nDe corazón, ¡Doy!\r\n¡Sea bella fragancia!\r\nA Ti mi Señor.', '', '', '', '', 8),
(36, '36\r\nJEHOVÁ', '1\r\nJehová, Tú que estás en los cielos:\r\n¡Danos de tu amor!\r\nJehová, Tú que estás en tu trono:\r\n¡Danos la paz!', 'CORO\r\n//Porque sin Ti, Dios, nada somos.\r\nSólo en Ti, hallamos la paz. //', '2\r\nJehová, te alabamos con gozo\r\nY todo el amor.\r\nAnte Ti, ofrecemos un canto:\r\n¡Acéptalo!', 'CORO\r\n//Porque sin Ti, Dios, nada somos.\r\nSólo en Ti, hallamos la paz. //', '3\r\nLa razón eres Tú, Dios bendito,\r\nDel don de existir;\r\nPorque en Ti, nuestra vida se esconde.\r\nRecíbenos.', 'CORO(final)\r\n//Porque sin Ti, Dios, nada somos.\r\nSólo en Ti, hallamos la paz. //', '', '', '', '', '', '', 6),
(37, '37\r\nLA CREACIÓN', '1\r\nEn el primer día de la creación,\r\nSeparó, nuestro Dios, a la luz, de las tinieblas.\r\nDijo Dios: “Sea la luz” y vio Dios que era buena.\r\nÉl habló y fue así. Fue la tarde y la mañana.', '2\r\nEl día segundo de la creación,\r\nApartó, nuestro Dios, a las aguas, de las aguas.\r\nY así fue la expansión, a la cual llamó los cielos.\r\nÉl habló y fue así. Fue la tarde y la mañana.', '3\r\nEn el tercer día de la creación,\r\nSeparó, nuestro Dios, a la tierra de los mares.\r\nY produzca también hierba y-árboles con fruto.\r\nDios habló y fue así. Fue la tarde y la mañana.', '4\r\nEn el cuarto día de la creación,\r\nLas lumbreras, creó Dios; que iluminen a la tierra.\r\nPara el día, el sol, y la luna en la noche.\r\nDios habló y fue así. Fue la tarde y la mañana.', '5\r\nEn el quinto día de la creación,\r\nDijo Dios: “Que las aguas produzcan seres vivos”;\r\n“Aves en la expansión” y los monstruos de los mares.\r\nDios habló y fue así. Fue la tarde y la mañana.', '6\r\nEn el sexto día de la creación,\r\nDijo Dios: “Que la tierra produzca seres vivos\".\r\nToda bestia creó; y al hombre a su imagen.\r\nÉl habló y fue así. Fue la tarde y la mañana.', '7(final)\r\nEl séptimo día de la creación,\r\nNuestro Dios terminó, con los cielos y la tierra.\r\nY este día, el Señor, reposó de toda su obra.\r\nY lo santificó; día santo por los siglos.', '', '', '', '', '', 7),
(38, '38\r\nLA GRATITUD DEL CORAZÓN', '1\r\nAcercamos, ¡Oh Señor!\r\nNuestro espíritu.\r\nBusca el alma tu salud;\r\nEscucha nuestra oración.', 'CORO\r\n¡A Ti brindamos todo honor!\r\n¡Dad gloria a nuestro Hacedor!\r\nLa gratitud del corazón\r\nRecibe como un grato olor.', '2\r\nTu Iglesia clama hoy\r\nCon ardiente amor.\r\nTe ensalzamos, Dios Creador,\r\nAlzando en coro nuestra voz.', 'CORO\r\n¡A Ti brindamos todo honor!\r\n¡Dad gloria a nuestro Hacedor!\r\nLa gratitud del corazón\r\nRecibe como un grato olor.', '3\r\nPor tus beneficios, Dios,\r\nQue incontables son,\r\nTe loamos con fervor,\r\nCantando al mundo tu amor.', 'CORO\r\n¡A Ti brindamos todo honor!\r\n¡Dad gloria a nuestro Hacedor!\r\nLa gratitud del corazón\r\nRecibe como un grato olor.', '4\r\n¡Gloria!, ¡Gloria y majestad!\r\nAl Rey celestial;\r\nPor los siglos reinará;\r\nSu reino eternal traerá.', 'CORO(final)\r\n¡A Ti brindamos todo honor!\r\n¡Dad gloria a nuestro Hacedor!\r\nLa gratitud del corazón\r\nRecibe como un grato olor.', '', '', '', '', 8),
(39, '39\r\nLEVANTAD LA VOZ', '1\r\n¡Canta loores Iglesia de Dios!,\r\nCon este himno; ¡De corazón!\r\n¡Júbilo y gozo con gran fervor!\r\n¡Demos la honra a Dios el Creador!', 'CORO\r\n¡Levantad la voz!\r\n¡Levantad la voz!\r\n¡Canten al Señor!\r\n¡Siempre con amor!', '2\r\nMi pensamiento elevo hacia Ti,\r\nPara agradarte en el culto a Ti;\r\n¡Porque eres digno de todo honor!\r\nPorque me diste mi salvación.', 'CORO\r\n¡Levantad la voz!\r\n¡Levantad la voz!\r\n¡Canten al Señor!\r\n¡Siempre con amor!', '3\r\nQue tu Presencia esté en mi ser,\r\nPorque no quiero desfallecer.\r\n¡Quiero alabarte!, quiero servir;\r\n¡Toda mi vida entrego a Ti!', 'CORO(final)\r\n¡Levantad la voz!\r\n¡Levantad la voz!\r\n¡Canten al Señor!\r\n¡Siempre con amor!', '', '', '', '', '', '', 6),
(40, '40\r\nMI CANTAR A DIOS', '1\r\n¡Grande es tu creación! ¡Grande es tu bondad!\r\n¡Es incomparable tu amor!\r\nFuente de la vida y de la verdad.\r\nY los cielos son tu mansión.', 'CORO\r\nPadre celestial: ¡Infinito Dios!\r\n¡Te alabo! ¡Te doy loor!\r\nDesde lo profundo del corazón,\r\nQuiero darte, hoy, mi cantar.', '2\r\nA tu Hijo enviaste para morir,\r\nCon tu plan de la salvación.\r\nSufrió lo más vil y subió hasta Ti,\r\n¡Otorgándonos redención!', 'CORO\r\nPadre celestial: ¡Infinito Dios!\r\n¡Te alabo! ¡Te doy loor!\r\nDesde lo profundo del corazón,\r\nQuiero darte, hoy, mi cantar.', '3\r\n¡La expansión anuncia tu inmensidad!\r\n¡Qué insignificante soy yo!...\r\nMás me diste una oportunidad,\r\nA una vida nueva de amor.', 'CORO(final)\r\nPadre celestial: ¡Infinito Dios!\r\n¡Te alabo! ¡Te doy loor!\r\nDesde lo profundo del corazón,\r\nQuiero darte, hoy, mi cantar.', '', '', '', '', '', '', 6),
(41, '41\r\nMI JESÚS', '1\r\nMi Jesús, mi Salvador,\r\nPor el mundo se entregó.\r\nMi Jesús, consolador,\r\nCon su paz, Él me llenó.', 'CORO\r\n¡A Ti mi Jesús! Un canto de amor.\r\n¡A Ti mi Jesús! Elevo mi voz.\r\nSeñor amoroso. Señor bondadoso.\r\nHijo de Dios. Mi intercesor.', '2\r\nMi Jesús, en cruz murió.\r\nMis pecados, perdonó.\r\nMi Jesús, resucitó:\r\n¡De la muerte es vencedor!', 'CORO\r\n¡A Ti mi Jesús! Un canto de amor.\r\n¡A Ti mi Jesús! Elevo mi voz.\r\nSeñor amoroso. Señor bondadoso.\r\nHijo de Dios. Mi intercesor.', '3\r\nMi Jesús, mi buen Pastor:\r\nDame paz en la aflicción.\r\nMi Jesús, mi Redentor:\r\nQuiero un día oír tu voz.', 'CORO(final)\r\n¡A Ti mi Jesús! Un canto de amor.\r\n¡A Ti mi Jesús! Elevo mi voz.\r\nSeñor amoroso. Señor bondadoso.\r\nHijo de Dios. Mi intercesor.', '', '', '', '', '', '', 6),
(42, '42\r\nNO HAY DIOS TAN GRANDE COMO TÚ', '1\r\n//Bendito Dios, hoy te damos honra y loor //\r\nEres Tú, la verdad,\r\nQue nos da protección.\r\n', '1\r\nAlabado seas por siempre:\r\n¡Digno eres de toda adoración!\r\nNo hay Dios tan grande como Tú;\r\nNada como tu gran plenitud.\r\nNo hay Dios tan grande como Tú,\r\nPorque Tú eres verdad.', '2\r\n//Sublime Dios, hoy te damos gloria y honor //\r\nEres Tú el amor,\r\nQue nos da compasión.', '2\r\nTu bendito Nombre, ¡Por siempre!\r\nPor amarnos y darnos bendición.\r\nNo hay Dios tan grande como Tú,\r\nQue entregara a su Hijo en la cruz.\r\nNo hay Dios tan grande como Tú,\r\nPorque Tú eres amor.', '3\r\n//Divino Dios, hoy te damos nuestro corazón //\r\nEres tú el Creador,\r\nDel plan de salvación.', '3(final)\r\nTe pedimos seas por siempre\r\nCon nosotros, eterno Padre Dios.\r\n//No hay Dios tan grande como Tú,\r\nQue nos diera su gracia en Jesús.\r\nNo hay Dios tan grande como Tú,\r\nPorque Tú eres perdón.//', '', '', '', '', '', '', 6),
(43, '43\r\nNUESTRA CONFIANZA ES DIOS', '1\r\nHoy canto a Ti, Jehová.\r\nElevo mi voz con fervor.\r\nQue el gozo de tu pueblo esté\r\nAnte Ti, tierno Padre Dios.', '2\r\nPor tus maravillas, Señor,\r\nTus hijos te rinden honor.\r\nPorque eres bueno y perdonador:\r\n¡Sea a Ti nuestra devoción!', 'CORO\r\nA nuestra fortaleza:\r\n¡Gloria y loor!\r\nY cánticos de júbilo\r\nPara nuestro Hacedor.', 'CORO\r\nCantamos a Ti, Jehová;\r\nTu diestra nos guíe hasta el fin.\r\nAunque al acecho el mal esté,\r\n¡Nuestra confianza está en Dios!', '3\r\nPor tus maravillas, Señor, \r\nTus hijos te rinden honor. \r\nPorque eres bueno y perdonador: \r\n¡Sea a Ti nuestra devoción!', 'Coro 2 \r\nA nuestra fortaleza: \r\n¡Gloria y loor! \r\nY cánticos de júbilo \r\nPara nuestro Hacedor.', 'Coro 2(final) \r\nCantamos a Ti Jehová; \r\nTu diestra nos guíe hasta el fin. \r\nAunque al acecho el mal esté, \r\n¡Nuestra confianza está en Dios! \r\n¡Nuestra seguridad es Dios¡ ', '', '', '', '', '', 7),
(44, '44\r\n¡OH, GRAN SEÑOR!', '1\r\nPara Ti no hay nada imposible, Señor;\r\nAl momento que Tú quieras, Tú lo puedes hacer.\r\nEres dueño de mi vida y de todo lo que soy.\r\n¡Te doy gloria!, ¡Aleluya! Amén.', 'CORO\r\n¡Oh, gran Señor!, torrente de amor,\r\nFuente de vida; colmas Tú mi corazón;\r\nSatisfaces mi alma y quitas mi dolor;\r\nQuitas lo terrible y lo tornas en amor.', '2\r\nTu misericordia es infinita Señor;\r\nNo se compara con nada, porque Tú eres amor.\r\nLa grandeza de tus obras nunca puede terminar.\r\nTodo lo imposible, lo haces real.', 'CORO(final)\r\n¡Oh, gran Señor!, torrente de amor,\r\nFuente de vida; colmas Tú mi corazón;\r\nSatisfaces mi alma y quitas mi dolor;\r\nQuitas lo terrible y lo tornas en amor.', '', '', '', '', '', '', '', '', 7),
(45, '45\r\nPADRE ETERNO DIOS', '1\r\nTe amo Padre Dios,\r\nPor Cristo mi Redentor;\r\nEscucha, hoy, mi voz,\r\nPadre, eterno Dios.', 'CORO\r\n//Examina Tú mi corazón.//\r\nOfrenda que te ofrezco, yo:\r\nTe amo eterno Dios.', '2\r\nRecibe eterno Dios,\r\nMi amor. ¡Te rindo loor!\r\nTe amo, Dios de amor.\r\n“Te alaba el corazón”.', 'CORO\r\n//Examina Tú mi corazón.//\r\nOfrenda que te ofrezco, yo:\r\nTe amo eterno Dios.', '3\r\nIncomparable amor:\r\nMe amaste a mí, un pecador;\r\nMe enviaste redención:\r\nA Cristo el Salvador.', 'CORO(final)\r\n//Examina Tú mi corazón.//\r\nOfrenda que te ofrezco, yo:\r\nTe amo eterno Dios.', '', '', '', '', '', '', 6),
(46, '46\r\nPARA TI LA ALABANZA', '1\r\nNo con los labios, mas de corazón,\r\n¡Será la alabanza! ¡Será la alabanza!\r\n¡A Ti, oh mi Dios!', 'CORO\r\n¡Digno eres de adoración!\r\nLa alabanza ¡Es sólo a Ti!', '2\r\nNo por la fuerza, mas de voluntad,\r\n¡Será la alabanza!, ¡Será la alabanza!\r\n¡A Ti, mi Señor!', 'CORO\r\n¡Digno eres de adoración!\r\nLa alabanza ¡Es sólo a Ti!', '3\r\nNo por rutina, mas por gratitud,\r\n¡Será la alabanza! ¡Será la alabanza!\r\n¡A Ti, mi Señor!', 'CORO\r\n¡Digno eres de adoración!\r\nLa alabanza ¡Es sólo a Ti!', '4\r\nNo por contienda, mas con humildad,\r\n¡Será la alabanza! ¡Será la alabanza!\r\n¡A Ti, mi Señor!', 'CORO(final)\r\n¡Digno eres de adoración!\r\nLa alabanza ¡Es sólo a Ti!', '', '', '', '', 8),
(47, '47\r\nPRECIOSO SALVADOR', '1\r\nTú, precioso Salvador,\r\nEres mi consolación.\r\nMe salvaste del error;\r\nMe otorgaste tu perdón.', 'CORO\r\nQue tu mano no me deje,\r\nEn los días de aflicción.\r\nQue tu diestra me sostenga,\r\n¡Oh, precioso Salvador!', '2\r\nPor tu amor que tierno das,\r\nSólo a Ti alabaré.\r\nPor tu gracia y tu bondad,\r\nYo te quiero adorar.', 'CORO\r\nQue tu mano no me deje,\r\nEn los días de aflicción.\r\nQue tu diestra me sostenga,\r\n¡Oh, precioso Salvador!', '3\r\n¡Oh bendito Salvador!\r\n¡A tu Nombre todo honor!\r\nNo me dejes, Tú, caer.\r\nGuárdame del tentador.', 'CORO(final)\r\nQue tu mano no me deje,\r\nEn los días de aflicción.\r\nQue tu diestra me sostenga,\r\n¡Oh, precioso Salvador!', '', '', '', '', '', '', 6),
(48, '48\r\n¡QUÉ GRAN COMPASIÓN!', '1\r\nGrande es tu amor, Señor. ¡Qué gran compasión!\r\nTal fue tu amor, ¡Oh, sí!, que has dado a tu Hijo Jesús.\r\nCondenado a sufrir destino cruel:\r\nCon la sangre que Él vertió, la deuda quitó.', 'CORO\r\nTe alabamos ¡Oh Señor!,\r\nPor tu grande compasión.\r\nDe tus hijos, gratitud,\r\nRecibe hoy.', '2\r\nCuando pensaba que iba a perecer;\r\nQue no tenían perdón mis faltas, mi pecado vil.\r\nTu voz me consoló y en mí surgió\r\nLa esperanza de alcanzar de Ti, salvación.', 'CORO\r\nTe alabamos ¡Oh Señor!,\r\nPor tu grande compasión.\r\nDe tus hijos, gratitud,\r\nRecibe hoy.', '3\r\nPor la muerte de Aquel, que la cruz sufrió,\r\nEs que gozo al fin, de una nueva vida en Ti.\r\nHoy te vengo a decir de corazón:\r\n¡Gracias!, por tan grande amor, que dio redención.', 'CORO(final)\r\nTe alabamos ¡Oh Señor!,\r\nPor tu grande compasión.\r\nDe tus hijos, gratitud,\r\nRecibe hoy.', '', '', '', '', '', '', 6),
(49, '49\r\nREY Y SOBERANO', '1\r\nTiembla a su Presencia toda la tierra,\r\nHuyen los collados si oyen su voz.\r\nObra de sus manos, el firmamento,\r\nÉl a cada estrella nombre le dio.', 'CORO\r\n¡Gloria en las alturas, sea a nuestro Padre!\r\nToda su simiente démosle honor.\r\nPorque para siempre vive su Nombre,\r\n¡Rey y soberano de la creación!', '2\r\n¡Él formó las nubes por su gran gloria!;\r\n¡Limitó los mares con gran poder!\r\nÉl trazó la senda que sigue el trueno;\r\nNada hay comparable a su parecer.', 'CORO\r\n¡Gloria en las alturas, sea a nuestro Padre!\r\nToda su simiente démosle honor.\r\nPorque para siempre vive su Nombre,\r\n¡Rey y soberano de la creación!', '3\r\nSerafines cantan ante su trono:\r\n¡Santo, Santo, Santo, es el Señor!\r\nSe ciñó de luz y de fortaleza;\r\nManantial de vida es su corazón.', 'CORO(final)\r\n¡Gloria en las alturas, sea a nuestro Padre!\r\nToda su simiente démosle honor.\r\nPorque para siempre vive su Nombre,\r\n¡Rey y soberano de la creación!', '', '', '', '', '', '', 6),
(50, '50\r\nTE AGRADEZCO SEÑOR', '1\r\nBendito Señor, grande es tu fidelidad;\r\n¡Oh, mi Dios! ¿Quién soy yo que me das tu perdón?\r\nDe toda maldad, mi alma lograste salvar;\r\nPor tan gran redención, te alabo Señor.', 'CORO\r\n¡Gloria a Ti, mi Señor!\r\nSea a tu Nombre el honor.\r\nDedico a Ti mi empeño y mi voluntad;\r\nTe agradezco Señor tu amor, tu bondad.', '2\r\nEn Ti esperé, Ti, en silencio aguardé;\r\nMi clamor, hacia Ti elevé con fervor.\r\nCon gran compasión, Tú quitaste mi aflicción;\r\nHoy me rindo a Ti, tuyo soy, mi Señor.', 'CORO\r\n¡Gloria a Ti, mi Señor!\r\nSea a tu Nombre el honor.\r\nDedico a Ti mi empeño y mi voluntad;\r\nTe agradezco Señor tu amor, tu bondad.', '3\r\nSi hoy canto a Ti, y loor a tu Nombre doy,\r\nEs porque, en verdad, agradezco tu amor.\r\nAnhelo, a Ti fiel, por siempre seguirte; hasta el fin;\r\nAlabar y ensalzar tu Nombre ¡Oh Señor!', 'CORO(final)\r\n¡Gloria a Ti, mi Señor!\r\nSea a tu Nombre el honor.\r\nDedico a Ti mi empeño y mi voluntad;\r\nTe agradezco Señor tu amor, tu bondad.', '', '', '', '', '', '', 6),
(51, '51\r\nTE EXALTAMOS', '1\r\nAnte Ti ¡Oh, Dios! te venimos a cantar,\r\nCon gran devoción, alabanzas, por todo lo que nos das.\r\nCon gozo y alegría, ¡A Ti la exaltación!\r\nPorque tus maravillas y obras no tienen final.', 'CORO\r\n¡Te exaltamos, oh Señor!\r\n¡Te exaltamos, oh gran Dios!\r\nPor tu gracia y tus proezas.\r\n¡Digno Tú de adoración!\r\nNuestras voces cantarán,\r\nTu grandeza y tu bondad.\r\n¡A tu Nombre sea la gloria!\r\nTuya por la eternidad.', '2\r\nGracias, ¡Oh, Señor! por tan grande bendición.\r\nCuánta dicha es que en tus atrios nos gocemos, ¡Gran Creador!\r\nCon gozo y alegría: ¡Te damos el loor!\r\nRecibe de tu Iglesia, toda la adoración.', 'CORO(final)\r\n¡Te exaltamos, oh Señor!\r\n¡Te exaltamos, oh gran Dios!\r\nPor tu gracia y tus proezas.\r\n¡Digno Tú de adoración!\r\nNuestras voces cantarán,\r\nTu grandeza y tu bondad.\r\n¡A tu Nombre sea la gloria!\r\nTuya por la eternidad.', '', '', '', '', '', '', '', '', 4),
(52, '52\r\nTÚ ERES DIOS', '1\r\n¿Quién eres Tú, \r\nque formaste los cielos?\r\n¿Quién eres Tú, \r\nque hiciste los mares?\r\n¿Quién eres Tú, \r\nque ordenaste los tiempos?', '1\r\n¡Eres el que eres!\r\nY de siglo en siglo, eres.\r\n¡Tú eres Dios!\r\n', '1 \r\n¡Dios eterno y ninguno como Tú!\r\nEres antes que tu gran creación.\r\n¡Eres sin principio!\r\nY de siglo en siglo, eres.\r\n¡Tú eres Dios!', '2 \r\n¿Quién eres Tú, \r\nque formaste la tierra? \r\n¿Quién eres Tú, \r\nque hiciste los montes? \r\n¿Quién eres Tú, \r\nque del polvo nos creaste?', '2\r\n¡Eres el que eres!\r\nY de siglo en siglo, eres.\r\n¡Tú eres Dios!', '2(final) \r\n//¡Dios eterno y ninguno como Tú! \r\nHoy te damos fiel adoración. \r\n¡Eres sin principio! \r\nY de siglo en siglo, eres. \r\n¡Tú eres Dios! //', '', '', '', '', '', '', 6),
(53, '53\r\nUNA ALABANZA', '1\r\nUna alabanza a mi Señor,\r\nDe gratitud y adoración;\r\nPor tanto, amor,\r\nPor tanta felicidad.', 'CORO\r\nDispuesto está el corazón a darte honor.\r\nQue grato perfume, ¡Llegue a Ti!\r\nAnte tu altar, con devoción,\r\n¡Te damos loor!\r\nTus siervos te alaban, Señor.', '2\r\nUn canto nuevo se entona, hoy,\r\nA mi Señor, de corazón;\r\nRecibe ¡Oh Dios!\r\nLa ofrenda, en santidad.', 'CORO(final)\r\nDispuesto está el corazón a darte honor.\r\nQue grato perfume, ¡Llegue a Ti!\r\nAnte tu altar, con devoción,\r\n¡Te damos loor!\r\nTus siervos te alaban, Señor.', '', '', '', '', '', '', '', '', 4),
(54, '54\r\nYO ADORO', '1\r\nTe adoro buen Jesús.\r\nA tu Nombre alabaré.\r\nEn tus brazos viviré,\r\nPara siempre, en luz.', 'CORO\r\nVida eterna tengo yo,\r\nPorque Cristo me salvó.\r\nDesde ahora cantaré:\r\n¡Alabanza a nuestro Rey!', '2\r\nTe adoro buen Jesús.\r\nA tu Nombre doy loor,\r\nPor tu muerte en la cruz;\r\nAgradezco tu amor.', 'CORO(final)\r\nVida eterna tengo yo,\r\nPorque Cristo me salvó.\r\nDesde ahora cantaré:\r\n¡Alabanza a nuestro Rey!', '', '', '', '', '', '', '', '', 4),
(55, '55\r\nANGUSTIADO Y AFLIGIDO', '1\r\nTe alabamos buen Jesús,\r\nPor amarnos... ¡Salvador!;\r\nY morir en cruenta cruz,\r\nDejándonos así tu amor.', 'CORO\r\n“Angustiado y afligido, Él, no abrió su boca”.\r\n“Fue llevado al matadero”; “Como oveja enmudeció”.\r\n“Queja alguna no emitió”. Humillado fue el Señor...\r\nTodo por amor, sufrió..., y así nos dio la redención.', '2\r\nTú llevaste con amor,\r\nNuestras cargas y dolor.\r\nEn angustia y aflicción,\r\nSeñor, nos diste salvación.', 'CORO\r\n“Angustiado y afligido, Él, no abrió su boca”.\r\n“Fue llevado al matadero”; “Como oveja enmudeció”.\r\n“Queja alguna no emitió”. Humillado fue el Señor...\r\nTodo por amor, sufrió..., y así nos dio la redención.', '3\r\nSangre de la expiación\r\nDerramaste, ¡Oh buen Señor!\r\nPor el mundo pecador.\r\n¡Recibe gloria y honor!', 'CORO(final)\r\n“Angustiado y afligido, Él, no abrió su boca”.\r\n“Fue llevado al matadero”; “Como oveja enmudeció”.\r\n“Queja alguna no emitió”. Humillado fue el Señor...\r\nTodo por amor, sufrió..., y así nos dio la redención.', '', '', '', '', '', '', 6),
(56, '56\r\nCORONA DE DOLOR', '1\r\nTus pasos fueron rumbo a la cruz...\r\nHacia una cruz de cruel dolor.\r\n¡Cruento dolor! Señor Jesús...,\r\nY así Tú mueres, por mi error.', 'CORO:\r\nCorona de dolor, llevaste Tú,\r\nSeñor Jesús; divino amor...\r\nTu sangre redimió al hombre vil:\r\n¿Cómo pagarte, por precioso don?', '2\r\nTus pies clavados, fueron por mí,\r\nEn cruenta cruz, mi fiel Señor.\r\nCordero santo, fuiste allí:\r\nNisán catorce, día de expiación.', 'CORO:\r\nCorona de dolor, llevaste Tú,\r\nSeñor Jesús; divino amor...\r\nTu sangre redimió al hombre vil:\r\n¿Cómo pagarte, por precioso don?', '3\r\nCruel agonía, te haría exclamar:\r\n\"El desamparo es mi habitación”;\r\n“Mi Dios, perdona su gran maldad,\r\nPues doy mi sangre, en expiación”.', 'CORO:(final)\r\nCorona de dolor, llevaste Tú,\r\nSeñor Jesús; divino amor...\r\nTu sangre redimió al hombre vil:\r\n¿Cómo pagarte, por precioso don?', '', '', '', '', '', '', 6),
(57, '57\r\nDÁDIVA DIVINA', '1\r\n¡Jamás se podrá pagar por este amor!\r\nNo hay en el mundo alguien cual mi Jesús.\r\n¡Oh, cuán cruel y grande fue aquel dolor!,\r\nEn el cruento sufrimiento... ¡En esa cruz!', 'CORO\r\nSublime compasión... ¡Dádiva divina!\r\nNuestro Padre, en su amor,\r\nEntregó a su Hijo por el mundo pecador.\r\n...¡Nos ha dado vida!', '2\r\nJesús, el Hijo de Dios, se humilló\r\nY Él, cual cordero, su boca no abrió.\r\nFue su pena la que le hizo exclamar:\r\n“Mi Dios, hágase en mí, tu voluntad”.', 'CORO(final)\r\nSublime compasión... ¡Dádiva divina!\r\nNuestro Padre, en su amor,\r\nEntregó a su Hijo por el mundo pecador.\r\n...¡Nos ha dado vida!', '', '', '', '', '', '', '', '', 4),
(58, '58\r\nDIVINA LUZ', '1\r\nMiré una luz, a lo largo está de mi camino,\r\nQue alumbraba con un cierto brillo,\r\nDisipando así, toda la oscuridad.', '1\r\nMiré una luz revelando todo entendimiento,\r\nHizo florecer este “palo seco”.\r\nEsa luz es Jesús.', 'CORO 1\r\nJesús, Tú que esos montes alumbraste,\r\nY con tu verdad todo cambiaste.\r\nDame de tu luz, ¡Oh, Jesús!', 'CORO 1\r\n¡Mi luz! que a este mundo, tarde no llegaste.\r\nCon tu gran amor nos abrazaste.\r\n¡Oh divina luz! llévame a la eternidad.', '2\r\nHay una luz irradiando con fulgor divino,\r\nDándome la luz del evangelio,\r\nPara que mi ser adquiera claridad.', '2\r\nHay una luz disipando dudas del pensamiento,\r\nDando a entender lo que viene presto.\r\nEsa luz es Jesús.', 'CORO 2\r\nJesús Tu que esos montes alumbraste\r\nY con tu verdad todo cambiaste.\r\nDame de tu luz, ¡Oh Jesús!', 'CORO 2\r\n¡Mi luz! que a este mundo, tarde no llegaste.\r\nCon tu gran amor nos abrazaste.\r\n¡Oh divina luz! ¡Bella luz!', 'CORO 2\r\n¡Oh Jesús! Tú que esos montes alumbraste\r\nY con tu verdad todo cambiaste.\r\nDame de tu luz, ¡Oh Jesús!', 'CORO 2(final)\r\n¡Oh mi luz! que a este mundo, \r\ntarde no llegaste. \r\nCon tu gran amor nos abrazaste. \r\n¡Oh divina luz! \r\n//Llévame a la eternidad.//', '', '', 10);
INSERT INTO `himnos` (`id_himno`, `pagina_0`, `pagina_1`, `pagina_2`, `pagina_3`, `pagina_4`, `pagina_5`, `pagina_6`, `pagina_7`, `pagina_8`, `pagina_9`, `pagina_10`, `pagina_11`, `pagina_12`, `num`) VALUES
(59, '59\r\nEL CORDERO INMOLADO', '1\r\nCuando el pecado entró al mundo,\r\nLa consecuencia fue atroz.\r\nEl hombre, entonces, se encontró\r\nSin Dios, sin fe; lejos de ÉI.', 'CORO\r\nPero el Cordero fue inmolado,\r\nPara brindarnos la redención.\r\nNo le importó dejar su gloria;\r\nFiel y obediente, la cruz sufrió.', '2\r\nVino a este mundo en humildad.\r\nLas buenas nuevas, proclamó.\r\n¡Luz y esperanza al mundo dio!\r\nMisericordia, amor, perdón.', 'CORO\r\nPero el Cordero fue inmolado,\r\nPara brindarnos la redención.\r\nNo le importó dejar su gloria;\r\nFiel y obediente, la cruz sufrió.', '3\r\nManifestó su amor divino.\r\nNuestros dolores, padeció.\r\nNos libertó, su sacrificio\r\nY por su llaga, nos curó.', 'CORO\r\nPero el Cordero fue inmolado,\r\nPara brindarnos la redención.\r\nNo le importó dejar su gloria;\r\nFiel y obediente, la cruz sufrió.', '4\r\nExclamó al Padre en agonía:\r\n“Consumado es\" y expiró.\r\nCon gran poder venció la muerte.\r\n¡Vive, por siempre, el Señor!', 'CORO(final)\r\nPero el Cordero fue inmolado,\r\nPara brindarnos la redención.\r\nNo le importó dejar su gloria;\r\nFiel y obediente, la cruz sufrió.', '', '', '', '', 8),
(60, '60\r\nEL SACRIFICIO SIN IGUAL', '1\r\n¡Preciosas manos! mi Señor,\r\n¡Heridas fueron sin piedad!\r\nQue siempre abriste con amor,\r\nEn busca de quién perdonar.', '1\r\nLlevaron tus cansados pies,\r\nMensaje de la salvación;\r\nBendito andar, de fin tan cruel:\r\nCon clavos, lleno de dolor.', '2\r\nMolido y sin compasión...\r\n¡Sufriste tanto mi Jesús!\r\nY por cumplir con tu misión,\r\nDestino amargo, fue la cruz.', '2\r\nMirando al cielo, se elevó\r\nTu pensamiento, en oración.\r\nDivino rostro que clamó:\r\n“Perdón” ..., por nuestra rebelión.', '3\r\nTu cuerpo inerte se quedó...\r\nY en el madero expiró.\r\nEn un sepulcro descansó.\r\n¡Dios con poder te levantó!', '3\r\nEl sacrificio, sin igual,\r\nLlevaste en tu crucifixión.\r\nCordero fiel: ¿Cómo pagar?\r\nSublime precio de tu amor.', '4\r\nY por tu sangre estoy aquí.\r\nSeñor, tu gracia me alcanzó.\r\nTú eres pan de vida, en mí.\r\nTe ofrezco, hoy, mi adoración', '4(final)\r\nCuando Tú vuelvas, Rey de paz,\r\nYa no será con aflicción.\r\n¡Vendrás con gloria y majestad!\r\nPara reinar con bendición.', '', '', '', '', 8),
(61, '61\r\nEN LA BIBLIA LO LEO', '1\r\nTe agradezco Señor por venirme a salvar;\r\nPor venir a entregar tu existir en la cruz.\r\n//En la Biblia lo leo: Que eres Tú el Cordero,\r\nQue con sangre pagó; dándome redención//.', '2\r\nCon tu sangre, Jesús, se alivió mi dolor.\r\nY en tu amor, mi Señor, ¡Ya no existe el temor!\r\n//En la Biblia lo leo: Que eres, Tú, mi esperanza;\r\nEl Divino Pastor, ¡Que mi herida sanó! //.', '3(final)\r\nY es por Ti, que cambió mi vivir, Salvador.\r\n¡Me has mostrado la senda de la salvación!\r\n//En la Biblia lo leo: Que eres, Tú, el Camino\r\nY a mi Dios llevarás, mi sincera oración//.', '', '', '', '', '', '', '', '', '', 3),
(62, '62\r\nJESUCRISTO ENVIADO DEL PADRE', '1\r\nJesucristo es nuestro Salvador;\r\nEs el Hijo del eterno Dios.\r\nJesucristo es nuestro gran Señor.\r\n¡Honra sea a nuestro Rey!', 'CORO\r\nPor el Padre, enviado, descendió del cielo;\r\nTrajo la palabra de nuestro Creador:\r\nDando testimonio de amor y verdad.\r\n¡Nadie llega al Padre, si no es por Él!', '2\r\nJesucristo es Príncipe de paz;\r\nÉl es nuestro “pan espiritual”.\r\nEs la vida y la resurrección.\r\nFuente de agua viva, es Él.', 'CORO\r\nPor el Padre, enviado, descendió del cielo;\r\nTrajo la palabra de nuestro Creador:\r\nDando testimonio de amor y verdad.\r\n¡Nadie llega al Padre, si no es por Él!', '3\r\nJesucristo, nuestro buen Pastor;\r\nEl camino a la salvación.\r\nEs promesa que Dios ofreció.\r\nEsperanza eternal.', 'CORO\r\nPor el Padre, enviado, descendió del cielo;\r\nTrajo la palabra de nuestro Creador:\r\nDando testimonio de amor y verdad.\r\n¡Nadie llega al Padre, si no es por Él!', '4\r\nAbogado e Intercesor,\r\nA la diestra de Dios, ascendió.\r\n¡Jesucristo viene a reinar!\r\nSu Iglesia espera ya.', 'CORO(final)\r\nPor el Padre, enviado, descendió del cielo;\r\nTrajo la palabra de nuestro Creador:\r\nDando testimonio de amor y verdad.\r\n¡Nadie llega al Padre, si no es por Él!', '', '', '', '', 8),
(63, '63\r\nNO FUE EN VANO EL SACRIFICIO', '1\r\nVarón de dolores, fue\r\n¡Despreciado y desechado!\r\nÉl llevó nuestros pecados\r\nEn la cruz. ¡Allí murió!', 'CORO\r\n// ¡Oh Señor, gracias te damos!\r\nPor tu amor y tu bondad.\r\nNo fue en vano el sacrificio...\r\n¡Nos viniste a rescatar! //', '2\r\nÉl su boca no abrió;\r\n¡Fue angustiado y afligido!\r\nComo oveja fue llevado...\r\nCon su muerte, ¡Él pagó!', 'CORO\r\n// ¡Oh Señor, gracias te damos!\r\nPor tu amor y tu bondad.\r\nNo fue en vano el sacrificio...\r\n¡Nos viniste a rescatar! //', '3\r\nEn Gólgota, Él, sufrió...\r\n¡Fue acusado y sentenciado!\r\nCristo, por nuestros pecados,\r\nInmolado fue, en la cruz.', 'CORO(final)\r\n// ¡Oh Señor, gracias te damos!\r\nPor tu amor y tu bondad.\r\nNo fue en vano el sacrificio...\r\n¡Nos viniste a rescatar! //', '', '', '', '', '', '', 6),
(64, '64\r\nNO MERECÍA TANTO DOLOR', '1\r\nMi Señor pereció, por ti..., por mí...\r\nMi Jesús padeció, en cruz tan vil.', 'CORO\r\nÉl no merecía tanto dolor,\r\nPorque a nadie, mal causó, el Señor.\r\nÉl no merecía tanto dolor...\r\n¡Sólo Él! me dio su eterno amor.', '2\r\nMi Señor me otorgó la Salvación.\r\nMi Jesús, al morir, su amor me dio.', 'CORO\r\nÉl no merecía tanto dolor,\r\nPorque a nadie, mal causó, el Señor.\r\nÉl no merecía tanto dolor...\r\n¡Sólo Él! me dio su eterno amor.', '3\r\nCon dolor, Él me dio, su gran amor.\r\nY por Él, vida eterna busco yo.', 'CORO(final)\r\nÉl no merecía tanto dolor,\r\nPorque a nadie, mal causó, el Señor.\r\nÉl no merecía tanto dolor...\r\n¡Sólo Él! me dio su eterno amor.', '', '', '', '', '', '', 6),
(65, '65\r\n¡OH, JESÚS! VEN A MÍ', '1\r\nYo te ruego Señor que vengas,\r\nNo me niegues, no, tu protección.\r\nY así viviré confiado,\r\nAl cuidado tuyo, mi Señor.', 'CORO\r\nVen a mí, te ruego, ven a mí,\r\nVen y mora en mi corazón.\r\nComo oveja herida, errante voy,\r\n¡Oh, Jesús! te pido, ven a mí.', '2\r\nGuíame a delicados pastos,\r\nY que en ellos pueda descansar.\r\nDe la fuente de amor divino,\r\nAgua viva pueda, yo, tomar.', 'CORO\r\nVen a mí, te ruego, ven a mí,\r\nVen y mora en mi corazón.\r\nComo oveja herida, errante voy,\r\n¡Oh, Jesús! te pido, ven a mí.', '3\r\n¡Oh, Señor! tú eres compasivo,\r\nAnte el Padre, aboga hoy por mí.\r\n¿Dime a quién debo ir, Maestro?\r\nVida y salvación, están en Ti.', 'CORO(final)\r\nVen a mí, te ruego, ven a mí,\r\nVen y mora en mi corazón.\r\nComo oveja herida, errante voy,\r\n¡Oh, Jesús! te pido, ven a mí.', '', '', '', '', '', '', 6),
(66, '66\r\nDIA TAN GRANDE', '1\r\nCuenta la gloria de su amor,\r\nCuenta la historia de su creación:\r\nObra divina, de sus dedos fue.', '1\r\nLos mares cuentan su verdad;\r\nLos cielos cuentan su bondad;\r\nLa tierra cuenta: “hombre, polvo es”.', 'CORO\r\nDía tan grande es, ¡A su alabanza!\r\nDía sublime es hoy, ¡Es su reposo!\r\nHonras y glorias son para Dios;\r\nDueño de todo.', '2\r\nLa Biblia narra de un ayer,\r\nDe que en seis días trabajó Él.\r\nObra perfecta, de sus manos fue.', '2\r\nHabla de un día muy especial,\r\nDonde se oyó bello cantar:\r\n¡A su alabanza! ¡Día santo es!', 'CORO\r\nDía tan grande es, ¡A su alabanza!\r\nDía sublime es hoy, ¡Es su reposo!\r\nHonras y glorias son para Dios;\r\nDueño de todo.', '3\r\nUna ordenanza hizo Dios,\r\nPara vivir en santidad:\r\n¡Guardar reposo! Singular señal.', '3\r\nSu orden es, fue y será;\r\nPor las edades durará.\r\nSanto reposo. Sábado eternal.', 'CORO(final)\r\nDía tan grande es, ¡A su alabanza!\r\nDía sublime es hoy, ¡Es su reposo!\r\nHonras y glorias son para Dios;\r\nDueño de todo.', '', '', '', 9),
(67, '67\r\nDIEZ HERMOSOS DECRETOS', '1\r\nSus mandamientos son vida\r\nPara el que ama su Ley.\r\nSi tu alma está redimida,\r\nObedece hoy a tu Rey.', 'CORO\r\nÉl entregó sus mandamientos.\r\nEn lo alto del Sinaí,\r\nSon diez hermosos decretos,\r\n//Para ti y para mí//', '2\r\nHonrar a dioses ajenos,\r\nSu Nombre en vano tomar,\r\nSon actos que no son buenos;\r\nNo debe el hombre olvidar.', 'CORO\r\nÉl entregó sus mandamientos.\r\nEn lo alto del Sinaí,\r\nSon diez hermosos decretos,\r\n//Para ti y para mí//', '3\r\nSábado es su reposo;\r\nDía que Dios apartó.\r\nComo señal, que amoroso,\r\nDio al hombre que acepta a Dios.', 'CORO\r\nÉl entregó sus mandamientos.\r\nEn lo alto del Sinaí,\r\nSon diez hermosos decretos,\r\n//Para ti y para mí//', '4\r\nHonra a tu padre y tu madre:\r\nLargo vivir te dará;\r\nSi amas a Dios, nuestro Padre:\r\nNo matarás, ni hurtarás.', 'CORO\r\nÉl entregó sus mandamientos.\r\nEn lo alto del Sinaí,\r\nSon diez hermosos decretos,\r\n//Para ti y para mí//', '5\r\nEl adulterio es pecado,\r\nNo lo ve bien nuestro Dios.\r\nNo al testimonio que es falso.\r\nNi codiciar desde hoy.', 'CORO(final)\r\nÉl entregó sus mandamientos.\r\nEn lo alto del Sinaí,\r\nSon diez hermosos decretos,\r\n//Para ti y para mí//', '', '', 10),
(68, '68\r\nEL DÍA DE REPOSO', '1\r\nHoy es el día de reposo;\r\nNinguna labor en él se hará.\r\nEs el día apartado,\r\nPor Dios, desde la creación.', 'CORO\r\nEs un mandato dado a la Iglesia de Dios.\r\nEs para todo aquel que teme el Nombre del Creador.\r\nY de su trono, bendiciones se derramarán;\r\nPues el Señor, así, lo prometió.', '2\r\nSi retrajeres, éste día,\r\nTu pie, de hacer tu voluntad;\r\nLlamándolo santo día,\r\nEn Dios, te deleitarás.', 'CORO\r\nEs un mandato dado a la Iglesia de Dios.\r\nEs para todo aquel que teme el Nombre del Creador.\r\nY de su trono, bendiciones se derramarán;\r\nPues el Señor, así, lo prometió.', '3\r\nRecuerda ponerlo por obra.\r\n¡Mandato es de la Ley Dios!\r\nPor siempre has de guardarlo,\r\nPues esto agrada a Dios.', 'CORO(final)\r\nEs un mandato dado a la Iglesia de Dios.\r\nEs para todo aquel que teme el Nombre del Creador.\r\nY de su trono, bendiciones se derramarán;\r\nPues el Señor, así, lo prometió.', '', '', '', '', '', '', 6),
(69, '69\r\nEL DÍA DEL SEÑOR', '1\r\nNuestro Dios nos dejó, para reposar,\r\nUn día especial.\r\nTodo ser que ama y acepta a Dios,\r\nLo deberá guardar.', 'CORO\r\n¡Éste es el día del Señor!\r\n¡Día de bendición!\r\nDesde el principio lo apartó.\r\n¡Sábado para Dios!', '2\r\nHoy debemos cumplir lo que pide Dios:\r\n¡Obedecer su Ley!\r\nPues nos dice: “de él te has de acordar;\r\nSanto es para Ti”.', 'CORO\r\n¡Éste es el día del Señor!\r\n¡Día de bendición!\r\nDesde el principio lo apartó.\r\n¡Sábado para Dios!', '3\r\nTodos juntos, hermanos, con devoción\r\nAnte el eterno Dios;\r\n¡Ensalcemos! ¡Cantemos con gran fervor!\r\nAl Rey de la Creación.', 'CORO(final)\r\n¡Éste es el día del Señor!\r\n¡Día de bendición!\r\nDesde el principio lo apartó.\r\n¡Sábado para Dios!', '', '', '', '', '', '', 6),
(70, '70\r\nEL GUARDAR TU LEY', '1\r\nEs tu Palabra, ¡Oh mi Dios!\r\nMás dulce que la miel.\r\n//¡Cambia vidas! Trae el bien,\r\nEl guardar tu Ley//', '2\r\nTus mandamientos, mí Señor,\r\nYo quiero obedecer.\r\n//”Ropas limpias”, con tu Ley,\r\nYo conservaré.//', '3\r\nTu voluntad, ¡Oh Padre Dios!\r\nPerfecta siempre es.\r\n// ¡Dame ayuda por piedad!\r\n¡No quiero caer! //', '4(final)\r\nEn tu camino, ¡Oh gran Dios!\r\nQuiero, fiel, caminar.\r\n//Tus consejos me guiarán\r\nA la santidad//', '', '', '', '', '', '', '', '', 4),
(71, '71\r\nEL SÁBADO GLORIOSO ES', '1\r\nDejé, ya todo atrás:\r\n¡He dejado de pecar!\r\nPues yo, mi bien aquí encontré,\r\nCuando vengo, fiel, a reposar.', 'CORO\r\n¡Cuánta dicha gozaremos!\r\nCongregados en este lugar.\r\nTodos juntos cantaremos:\r\n¡Gloria a nuestro Padre celestial!', '2\r\n¡Qué gozo y qué paz! Siento yo, ya en mi ser...\r\nCuando en glorioso sábado,\r\nEstoy junto a tu bendita grey.', 'CORO\r\n¡Cuánta dicha gozaremos!\r\nCongregados en este lugar.\r\nTodos juntos cantaremos:\r\n¡Gloria a nuestro Padre celestial!', '3\r\n¡Glorioso, el sábado es!\r\n¡Ya no tardes pecador!\r\nPues te invita a descansar,\r\nNuestro sublime Salvador.', 'CORO(final)\r\n¡Cuánta dicha gozaremos!\r\nCongregados en este lugar.\r\nTodos juntos cantaremos:\r\n¡Gloria a nuestro Padre celestial!', '', '', '', '', '', '', 6),
(72, '72\r\nEN EL MONTE SINAÍ', '1\r\nEn el Monte Sinaí\r\nEscribiste diez mandatos,\r\nPara que tu pueblo fuese:\r\nPueblo sabio, fiel, santo y entendido.', 'CORO\r\n¡No seré rebelde!\r\n¡Mi Dios, tu voz escucharé!\r\nTu Ley será mi vida,\r\nPor siempre mi deleite;\r\nEs manantial de vida,\r\nY en él me saciaré.', '2\r\nTus preceptos santos son,\r\nQue iluminan nuestros ojos\r\nY nos dan sabiduría;\r\nNos enseñan, ¡La senda de la vida!', 'CORO(final)\r\n¡No seré rebelde!\r\n¡Mi Dios, tu voz escucharé!\r\nTu Ley será mi vida,\r\nPor siempre mi deleite;\r\nEs manantial de vida,\r\nY en él me saciaré.', '', '', '', '', '', '', '', '', 4),
(73, '73\r\nEN TU SÁBADO SEÑOR', '1\r\nHas apartado este día,\r\nEn que podamos reposar\r\nY así, con gozo y alegría,\r\nHoy te venimos a alabar.', 'CORO\r\nEn tu sábado, Señor,\r\nTe loamos con fervor.\r\nPara Ti, eterno Dios,\r\nSean la gloria y el honor.', 'CORO\r\nLectura habrá de tu Ley;\r\nLección se estudiará;\r\nY así reunida tu grey,\r\nSu canto te elevará.', '2\r\nLa acción de gracias que te damos,\r\nEs por lo mucho que nos das;\r\nY nuestros ruegos, Padre Santo,\r\nDesde tu trono escucharás.', 'CORO\r\nEn tu sábado, Señor,\r\nTe loamos con fervor.\r\nPara Ti, eterno Dios,\r\nSean la gloria y el honor.', 'CORO\r\nLectura habrá de tu Ley;\r\nLección se estudiará;\r\nY así reunida tu grey,\r\nSu canto te elevará.', '3\r\nHoy tu Iglesia está dispuesta\r\nPara cumplir tu voluntad.\r\nQue con respeto disfrutemos\r\nEl alimento espiritual.', 'CORO\r\nEn tu sábado, Señor,\r\nTe loamos con fervor.\r\nPara Ti, eterno Dios,\r\nSean la gloria y el honor.', 'CORO(final)\r\nLectura habrá de tu Ley;\r\nLección se estudiará;\r\nY así reunida tu grey,\r\nSu canto te elevará.', '', '', '', 9),
(74, '74\r\nESTOS SON DIEZ MANDAMIENTOS', '1\r\nHabló Dios estas palabras,\r\nEn el Monte Sinaí:\r\n“No tendrás dioses ajenos”,\r\n“Ni imagen te harás de mí”.', 'CORO\r\nEstos son diez mandamientos,\r\n¡Eternos como su Autor!;\r\nQue recomienda su Hijo,\r\n//Cristo, nuestro Redentor//', '2\r\n“El Nombre de Dios en vano,\r\nNunca lo mencionarás”;\r\nY descansará tu mano,\r\nEl sábado de Jehová.', 'CORO\r\nEstos son diez mandamientos,\r\n¡Eternos como su Autor!;\r\nQue recomienda su Hijo,\r\n//Cristo, nuestro Redentor//', '3\r\n“A tu padre y a tu madre,\r\nDebida honra les darás”;\r\n“No mates, ni adulteres”,\r\nTambién te prohíbe hurtar.', 'CORO\r\nEstos son diez mandamientos,\r\n¡Eternos como su Autor!;\r\nQue recomienda su Hijo,\r\n//Cristo, nuestro Redentor//', '4\r\nTestigo falso no es bueno,\r\nNi debes nunca tú ser;\r\n“No codiciarás lo ajeno”:\r\nNi casa, ni la mujer.', 'CORO(final)\r\nEstos son diez mandamientos,\r\n¡Eternos como su Autor!;\r\nQue recomienda su Hijo,\r\n//Cristo, nuestro Redentor//', '', '', '', '', 8),
(75, '75\r\nGUARDA LOS DIEZ MANDAMIENTOS', '1\r\nPara agradar a Dios\r\nGuarda los diez mandamientos\r\nPara vida obtener\r\nDebes cumplir con esto:', '1\r\nNo tendrás dioses ajenos,\r\nNi imagen te harás de Él.\r\nNo debes tomar en vano\r\nEl Nombre de nuestro Dios.', '2\r\nSi quieres bienestar\r\nGuarda los diez mandamientos,\r\nY corona ceñir\r\nCumple sus mandamientos.', '2\r\nReposa el séptimo día,\r\nA tus padres honraras.\r\nNo mates ni adulteres,\r\nTampoco debes hurtar', '3\r\nPara el reino alcanzar\r\nGuarda los diez mandamientos,\r\nEn campo o ciudad,\r\nAcuérdate de ellos.', '3(final)\r\nNo seas falso testigo,\r\nDebes buscar siempre el bien.\r\nNo codiciarás lo ajeno,\r\nNi casa ni la mujer.', '', '', '', '', '', '', 6),
(76, '76\r\nHOY ES EL DÍA', '1\r\nHoy es el día,\r\nEl día del Señor.\r\nEl sol se ha ocultado,\r\n¡Cantemos al Creador!', 'CORO\r\nTodos unidos\r\nCon mucha gratitud.\r\n¡Hoy es el día santo!\r\nFundado en la creación.', '2\r\nCon alabanzas\r\nNos hemos de acercar,\r\nHermosas melodías,\r\n¡Que al Padre subirán!', 'CORO\r\nTodos unidos\r\nCon mucha gratitud.\r\n¡Hoy es el día santo!\r\nFundado en la creación.', '3\r\nHoy el reposo,\r\n¡Ha comenzado ya!\r\nY Dios, que es bondadoso,\r\n¡Su gracia nos dará!', 'CORO(final)\r\nTodos unidos\r\nCon mucha gratitud.\r\n¡Hoy es el día santo!\r\nFundado en la creación.', '', '', '', '', '', '', 6),
(77, '77\r\nLOS MANDAMIENTOS', '1\r\nEscucha, Iglesia de Dios:\r\nPor obra, hay que poner\r\nLa Ley, que Él nos dio;\r\n¡Y escríbela en tu corazón!', 'CORO\r\nPues tesoros preciados son\r\nLos mandamientos del Señor.\r\nDeléitate, tú, en ellos\r\nY así, tendrás bendición.', '2\r\nSe fiel, Iglesia de Dios,\r\nAl mandamiento, en fe.\r\nTendrás galardón,\r\nGuardándolos en tu corazón.', 'CORO\r\nPues tesoros preciados son\r\nLos mandamientos del Señor.\r\nDeléitate, tú, en ellos\r\nY así, tendrás bendición.', '3\r\nEscucha, Iglesia de Dios:\r\nSabiduría dan;\r\nSon palabra fiel;\r\nY dulces, más, que la misma miel.', 'CORO(final)\r\nPues tesoros preciados son\r\nLos mandamientos del Señor.\r\nDeléitate, tú, en ellos\r\nY así, tendrás bendición.', '', '', '', '', '', '', 6),
(78, '78\r\nPERFECTA ES TU LEY', '1\r\n¡Oh Señor! Perfecta, es tu Ley;\r\nQue por los siglos durará.\r\nUn pacto santo con aquellos que te sirven.\r\nEn guardarla hay grande galardón.', '2\r\nEs tu Ley, tesoro, ¡Oh Señor!\r\nTesoro que da salvación;\r\nQue aquel que lo encuentra, se goza en tenerlo,\r\nY ya nunca verá su desnudez.', '3(final)\r\nDe tus mandamientos, ¡Oh Señor!\r\nNunca jamás me olvidaré;\r\nPorque con ellos, Tú me has vivificado.\r\nTuyo soy, ¡Guárdame de no caer!', '', '', '', '', '', '', '', '', '', 3),
(79, '79\r\nREPOSO', '1\r\nCon gozo este día,\r\nTu pueblo viene a Ti.\r\nCantando, en armonía,\r\nHosannas para Ti.', 'CORO\r\nAl soberano\r\nLa gloria elevad.\r\nPor sus proezas,\r\nA Él la honra tributad.', '2\r\nPor tu misericordia,\r\nNos congregamos hoy.\r\nReunida está tu Iglesia,\r\nRindiéndote loor.', 'CORO\r\nAl soberano\r\nLa gloria elevad.\r\nPor sus proezas,\r\nA Él la honra tributad.', '3\r\nReposo, este día\r\nTus hijos guardarán.\r\nEn sacrificio vivo\r\nEl alma, ofrecerán.', 'CORO(final)\r\nAl soberano\r\nLa gloria elevad.\r\nPor sus proezas,\r\nA Él la honra tributad.', '', '', '', '', '', '', 6),
(80, '80\r\nQUE GRANDE BENDICION', '1\r\nHoy en tu santuario estamos,\r\nCongregados por tu amor\r\nSon tus hijos que se acercan\r\nimplorando bendición.', 'CORO\r\n¡Qué grande bendición Señor!\r\nCompartes con tus hijos hoy,\r\nUniendo hoy sus vidas con tu tierno amor\r\n¡Qué grande bendición Señor!', '2\r\nJuntos en tu Nombre santo,\r\nUn hogar comenzarán.\r\nLa felicidad, encuentren\r\nDía a día sin cesar.', 'CORO\r\n¡Qué grande bendición Señor!\r\nCompartes con tus hijos hoy,\r\nUniendo hoy sus vidas con tu tierno amor\r\n¡Qué grande bendición Señor!', '3\r\nEn tu Iglesia se conserven\r\nComo ejemplo de tu amor,\r\nY en su vida siempre fieles\r\nSean tus siervos, ¡Oh Señor!', 'CORO(final)\r\n¡Qué grande bendición Señor!\r\nCompartes con tus hijos hoy,\r\nUniendo hoy sus vidas con tu tierno amor\r\n¡Qué grande bendición Señor!', '', '', '', '', '', '', 6),
(81, '81\r\nSÁBADO ES, GLORIOSO DÍA', '1\r\nHoy es sábado, ¡Es reposo!\r\nDemos gracias al Señor.\r\nÉste día es glorioso,\r\nApartado por nuestro Dios.', 'CORO\r\nOlvidemos hoy nuestras obras,\r\nReposemos en el Señor.\r\nNos ha dado, Él, seis días.\r\nY el sábado es de Dios.', '2\r\nAceptemos, hoy, con gozo\r\nEl día santo del Señor.\r\nA sus obras dio descanso,\r\nDía es, de alabanza a Dios.', 'CORO\r\nOlvidemos hoy nuestras obras,\r\nReposemos en el Señor.\r\nNos ha dado, Él, seis días.\r\nY el sábado es de Dios.', '3\r\nHoy es sábado, ¡Es reposo!;\r\nY su Iglesia cantará.\r\nAl Señor, Dios de los cielos,\r\nHonra y gloria, por su bondad.', 'CORO(final)\r\nOlvidemos hoy nuestras obras,\r\nReposemos en el Señor.\r\nNos ha dado, Él, seis días.\r\nY el sábado es de Dios.', '', '', '', '', '', '', 6),
(82, '82\r\nSÁBADO ES HOY', '1\r\nPor tu amor incomparable,\r\nTe venimos a adorar.\r\n¡Tu día es inmutable!;\r\nLo creaste en tu bondad.', 'CORO\r\n// A Ti, Señor, damos loor,\r\n¡Por tanto amor en tu creación!\r\nAl darle al hombre este descanso.\r\n¡Sábado es hoy!//', '2\r\nEsta señal será eterna,\r\nEntre el hombre y su Creador.\r\nSi en tu día reposamos,\r\nTú nos darás bendición.', 'CORO\r\n// A Ti, Señor, damos loor,\r\n¡Por tanto amor en tu creación!\r\nAl darle al hombre este descanso.\r\n¡Sábado es hoy!//', '3\r\nCon gozo y entendimiento,\r\nHoy te rendimos honor.\r\nNos dice tu mandamiento:\r\nQue este día hay que guardar', 'CORO\r\n// A Ti, Señor, damos loor,\r\n¡Por tanto amor en tu creación!\r\nAl darle al hombre este descanso.\r\n¡Sábado es hoy!//', '4\r\nTu Iglesia aquí te alaba,\r\nLlena de gran devoción,\r\nCon cantos y alabanzas,\r\nEn tu reposo, Señor.', 'CORO(final)\r\n// A Ti, Señor, damos loor,\r\n¡Por tanto amor en tu creación!\r\nAl darle al hombre este descanso.\r\n¡Sábado es hoy!//', '', '', '', '', 8),
(83, '83\r\nSÁBADO HAS DE REPOSAR', '1\r\nHoy venimos ¡Oh gran Dios!\r\nToda tu congregación,\r\nAlabando y exaltando,\r\nTu grandeza y tu amor.', 'Coro\r\nNuestra obra y trabajo\r\nEste día cesarán,\r\nPues Tú, Dios, lo has mandado:\r\n“Es tu sábado a guardar”.', '2\r\nCada uno llegará\r\nCon ofrenda a tu altar.\r\nDía grande, y ¡Cuánta dicha!\r\nA tu Nombre ensalzar.', 'Coro\r\nNuestra obra y trabajo\r\nEste día cesarán,\r\nPues Tú, Dios, lo has mandado:\r\n“Es tu sábado a guardar”.', '3\r\nDía es de bendición\r\nPorque Tú nos mandarás\r\nTu espíritu, ¡Que gozo!\r\nY en tus santos morará.', 'Coro(final)\r\nNuestra obra y trabajo\r\nEste día cesarán,\r\nPues Tú, Dios, lo has mandado:\r\n“Es tu sábado a guardar”.', '', '', '', '', '', '', 6),
(84, '84\r\nAL CAER LA TARDE', '1\r\nAl caer la tarde\r\nMi Rey dio su vida;\r\nLugar del ruin, tomó.\r\nNo hubo nadie que lo impidiese\r\nLugar de reo tomó.', 'CORO\r\nSublime don, Dios me dio;\r\n¡Incomparable amor!\r\n...Su gracia derramó...\r\n¡Jesús, por mí, murió!\r\nY gran dolor sufrió.', '2\r\nAl caer la tarde\r\n¡Mi Rey fue inmolado!\r\nCordero, manso, fue.\r\nObediente, fue hasta la muerte;\r\nCruelmente, sufrió la cruz.', 'CORO\r\nSublime don, Dios me dio;\r\n¡Incomparable amor!\r\n...Su gracia derramó...\r\n¡Jesús, por mí, murió!\r\nY gran dolor sufrió.', '3\r\nLlegada la noche,\r\nEn paz, cenaremos:\r\n¡Unidos por su amor!\r\n¡Vid Sagrada! que hoy beberemos\r\nY Pan, que da salvación.', 'CORO(final)\r\nSublime don, Dios me dio;\r\n¡Incomparable amor!\r\n...Su gracia derramó...\r\n¡Jesús, por mí, murió!\r\nY gran dolor sufrió.', '', '', '', '', '', '', 6),
(85, '85\r\nBAJARÉ, ME LAVARÉ', '1\r\nEs momento, he de bajar\r\nA las aguas que me han de lavar;\r\nY con Cristo podré reinar,\r\nAlabando al Padre celestial.', 'CORO\r\nBajaré, me lavaré;\r\nMis pecados, así borraré.\r\nBajaré, me lavaré.\r\nEn la lucha, ¡Yo sí venceré!', '2\r\nEn las aguas, renaceré.\r\nHoy mi vida, a Dios consagraré\r\nY en su Nombre, yo lucharé.\r\n¡La victoria con Él obtendré!', 'CORO\r\nBajaré, me lavaré;\r\nMis pecados, así borraré.\r\nBajaré, me lavaré.\r\nEn la lucha, ¡Yo sí venceré!', '3\r\nAlegría siento al bajar\r\nPorque un “nuevo hombre” hoy seré.\r\n¡Gloria sea a Cristo Jesús!\r\nEn su sangre, hoy limpio seré.', 'CORO(final)\r\nBajaré, me lavaré;\r\nMis pecados, así borraré.\r\nBajaré, me lavaré.\r\nEn la lucha, ¡Yo sí venceré!', '', '', '', '', '', '', 6),
(86, '86\r\nBENDICE ESTA UNIÓN', '1\r\nQue puedan con sabiduría y amor,\r\nSu hogar, en tu nombre, formar,\r\nY alcanzar miles de bendiciones, ¡Oh Dios!\r\n¡Esta unión, Tú bendigas con paz!', 'CORO\r\nY así, que vivan felices y confiados,\r\nPorque tu espíritu nunca ha de faltar.\r\nTu amor les colme en sus necesidades;\r\n¡Vivas por siempre Señor! En su corazón.', '2\r\nSu bello hogar siga creciendo en amor,\r\nEn fervor y temor de Ti ¡Oh Dios!\r\nY en la prueba podrán, de tu mano, tomar;\r\nY contigo, en luz caminar.', 'CORO\r\nY así, que vivan felices y confiados,\r\nPorque tu espíritu nunca ha de faltar.\r\nTu amor les colme en sus necesidades;\r\n¡Vivas por siempre Señor! En su corazón.', '3\r\nLos hijos, a su vida habrán de llegar;\r\nLes instruyan a amarte, Señor.\r\nY así, junto a Ti, siempre habrá bendición,\r\nPorque, Padre, Tú les guiarás.', 'CORO\r\nY así, que vivan felices y confiados,\r\nPorque tu espíritu nunca ha de faltar.\r\nTu amor les colme en sus necesidades;\r\n¡Vivas por siempre Señor! En su corazón.', '4\r\nAsí no habrá dolor que pueda empañar\r\nEl amor y paz que les darás.\r\nUna vida tendrán, llena de bendición,\r\nPorque, Tú, sostendrás esta unión.', 'CORO(final)\r\nY así, que vivan felices y confiados,\r\nPorque tu espíritu nunca ha de faltar.\r\nTu amor les colme en sus necesidades;\r\n¡Vivas por siempre Señor! En su corazón.', '', '', '', '', 8),
(87, '87\r\nCENA DEL SEÑOR', '1\r\nDios, has bendecido a tu Iglesia,\r\nDe una forma especial.\r\nGuiados de tu mano y unidos\r\nEn tu Nombre: ¡A celebrar!', 'CORO\r\nÉste hermoso día, la Iglesia de Dios\r\nCumple el mandamiento: cena del Señor.\r\nRecordamos que nuestro Señor Jesús,\r\nSu vida entregó en cruenta cruz.', '2\r\nHoy en armonía con tus hijos,\r\nÉsta cena se dará.\r\n¡El pan y el vino tomaremos!\r\nEn señal de redención.', 'CORO\r\nÉste hermoso día, la Iglesia de Dios\r\nCumple el mandamiento: cena del Señor.\r\nRecordamos que nuestro Señor Jesús,\r\nSu vida entregó en cruenta cruz.', '3\r\nUn día en el reino, con los santos,\r\nEl Señor se reunirá.\r\nHoy con ésta cena, anunciamos:\r\nQue Él, ¡Muy pronto volverá!', 'CORO(final)\r\nÉste hermoso día, la Iglesia de Dios\r\nCumple el mandamiento: cena del Señor.\r\nRecordamos que nuestro Señor Jesús,\r\nSu vida entregó en cruenta cruz.', '', '', '', '', '', '', 6),
(88, '88\r\nDEDICACIÓN', '1\r\nSeñor, Dios del cielo y de la tierra,\r\nPedimos tu bendición,\r\nPara este templo que hoy,\r\nDedicamos a Ti, Señor.', 'CORO\r\n/// ¡Bendito seas! ///\r\nSeñor, Dios poderoso.\r\nCuando tus hijos a Ti clamen,\r\nDesde los cielos oirás.\r\nSi te imploraren misericordia\r\nSus pecados perdonarás.', '2\r\nTus ojos estén sobre esta casa\r\nCuando acudamos a Ti.\r\nCasa de la cual dijiste:\r\n¡Estará mi Nombre allí!', 'CORO\r\n/// ¡Bendito seas! ///\r\nSeñor, Dios poderoso.\r\nCuando tus hijos a Ti clamen,\r\nDesde los cielos oirás.\r\nSi te imploraren misericordia\r\nSus pecados perdonarás.', '3\r\n¡Cantad al Señor porque es bueno\r\nQue por siempre existirá\r\nGrande es su misericordia,\r\nQue por siempre otorgará.', 'CORO(final)\r\n/// ¡Bendito seas! ///\r\nSeñor, Dios poderoso.\r\nCuando tus hijos a Ti clamen,\r\nDesde los cielos oirás.\r\nSi te imploraren misericordia\r\nSus pecados perdonarás.', '', '', '', '', '', '', 6),
(89, '89\r\nDISPUESTOS EN TU CENA', '1\r\nSacrificio es la palabra\r\nQue describe tu amor, Señor.\r\nGrande amor el que mostraste,\r\nOfreciendo tu existir, Jesús.', '1\r\nEn nuestro corazón\r\nSólo hay gratitud.\r\nAnunciamos tu muerte, Señor,\r\nY tu resurrección.', 'CORO\r\n¡Emblemas de unión!...\r\nTu cuerpo y tu sangre son.\r\nLa copa y el pan.\r\nEjemplo de amor,', 'CORO\r\nEs el lavar con humildad\r\nLos pies de los hermanos,\r\nEn bella comunión.\r\n¡Mandato que cumplimos hoy!\r\nDispuestos en tu cena, Señor.', '2\r\nDevoción es la respuesta\r\nQue ofrecemos hoy ante tu “altar”;\r\nDemostrando lo que sentimos;\r\nManteniéndonos fieles aquí.', '2\r\nNos viniste a salvar:\r\n¡Libres somos del mal!\r\nNoche de remembranza, hoy es...\r\n¡Y noche sin igual!', 'CORO\r\n¡Emblemas de unión!...\r\nTu cuerpo y tu sangre son.\r\nLa copa y el pan.\r\nEjemplo de amor,', 'CORO(final)\r\nEs el lavar con humildad\r\nLos pies de los hermanos,\r\nEn bella comunión.\r\n¡Mandato que cumplimos hoy!\r\nDispuestos en tu cena, Señor.', '', '', '', '', 8),
(90, '90\r\nEL AMOR GRANDE ES', '1\r\nUn sueño, por fin, se realiza...\r\nY ante Ti estamos, hoy, ¡Oh Señor!\r\nTestigos seremos del gozo,\r\nDe los que se han de jurar amor.', 'CORO\r\nEl amor grande es, todo lo sufre, todo lo cree.\r\n¡El amor nunca deja de ser!\r\nEl amor grande es, todo lo sufre, todo lo cree.\r\nEl amor de ellos, en Dios, grande es.', '2\r\nBendice, ¡Oh, Señor!, estas almas,\r\nQue a tu “altar” vienen con emoción.\r\nQue crezca sin fin la alegría\r\nComo pétalos de una flor.', 'CORO\r\nEl amor grande es, todo lo sufre, todo lo cree.\r\n¡El amor nunca deja de ser!\r\nEl amor grande es, todo lo sufre, todo lo cree.\r\nEl amor de ellos, en Dios, grande es.', '3\r\nQue guarden en sus corazones,\r\nTus preciados consejos, Señor.\r\n¡Y venzan las pruebas! que vengan,\r\nSiendo uno, en tu amor, ¡Oh, Gran Dios!', 'CORO\r\nEl amor grande es, todo lo sufre, todo lo cree.\r\n¡El amor nunca deja de ser!\r\nEl amor grande es, todo lo sufre, todo lo cree.\r\nEl amor de ellos, en Dios, grande es.', '4\r\nY unidos en sus peticiones,\r\nFortalezcan su hogar con virtud.\r\nRodeados de tus bendiciones,\r\nDe su vida alejen el mal.', 'CORO(final)\r\nEl amor grande es, todo lo sufre, todo lo cree.\r\n¡El amor nunca deja de ser!\r\nEl amor grande es, todo lo sufre, todo lo cree.\r\nEl amor de ellos, en Dios, grande es.', '', '', '', '', 8),
(91, '91\r\nEN ESTE RÍO CRISTALINO', '1\r\nEn este río cristalino,\r\nMis faltas, hoy, voy a lavar;\r\nPues he aceptado a mi Cristo,\r\nQue mi alma vino a rescatar.', 'CORO\r\n¡Grande es el amor de Dios!\r\nQue, a su Hijo, dio por mí.\r\n¡Oh manantial de vida eterna!\r\nMis faltas vengo a redimir.', '2\r\nA nueva vida me ha llamado\r\nJesús, con su inmenso amor.\r\n¡Un nuevo ser, hoy, ha nacido!\r\n¡Bendito manantial de amor!', 'CORO\r\n¡Grande es el amor de Dios!\r\nQue, a su Hijo, dio por mí.\r\n¡Oh manantial de vida eterna!\r\nMis faltas vengo a redimir.', '3\r\nCuando en las nubes, Él, regrese,\r\nCon “ropas limpias” quiero estar.\r\n¡En santidad ha de encontrarme!\r\n¡Con Cristo yo voy a reinar!', 'CORO(final)\r\n¡Grande es el amor de Dios!\r\nQue, a su Hijo, dio por mí.\r\n¡Oh manantial de vida eterna!\r\nMis faltas vengo a redimir.', '', '', '', '', '', '', 6),
(92, '92\r\nEN LAS AGUAS LÍMPIAME', '1\r\n¡Junto al río!... Voy a contemplar\r\nEsas aguas, ¡Hoy mi vida ha de cambiar!\r\nCon mi Cristo podré disfrutar,\r\nEn el Reino, junto al Padre celestial.', 'CORO\r\nEn el río, ¡Lávame!\r\nY al “viejo hombre”, ¡Sepultaré!\r\nEn las aguas, ¡Límpiame!\r\nSin pecado, por fin, seré.', '2\r\nMe bautizo, por fe en mi Dios;\r\nHago un pacto santo con mi Salvador.\r\n¡Hoy mi vida le entrego al Señor!\r\nVida eterna quiero con mi Redentor.', 'CORO\r\nEn el río, ¡Lávame!\r\nY al “viejo hombre”, ¡Sepultaré!\r\nEn las aguas, ¡Límpiame!\r\nSin pecado, por fin, seré.', '3\r\n¡Gozo siento! ¡Estoy muy feliz!\r\nEl pecado en mi carne ha de morir.\r\n¡Gloria a Cristo! Que por mí murió.\r\nÉl ha de nombrarme, al fin, vencedor.', 'CORO(final)\r\nEn el río, ¡Lávame!\r\nY al “viejo hombre”, ¡Sepultaré!\r\nEn las aguas, ¡Límpiame!\r\nSin pecado, por fin, seré.', '', '', '', '', '', '', 6),
(93, '93\r\nEN MEMORIA DE SU AMOR', '1\r\nBienvenidos, hoy hermanos,\r\nA la cena del Señor.\r\nTomemos el pan y el vino,\r\nEn memoria de su amor.', 'CORO\r\nHoy su muerte recordamos\r\nY que Él resucitó.\r\nDemos, hoy, tributo y honra,\r\n¡A Jesús el Redentor!', '2\r\nEl que de este pan comiere\r\nY de esta copa beba hoy;\r\nAnunciando esta su muerte\r\nHasta que venga el Señor', 'CORO\r\nHoy su muerte recordamos\r\nY que Él resucitó.\r\nDemos, hoy, tributo y honra,\r\n¡A Jesús el Redentor!', '3\r\nSi al hermano tú lavares\r\nLos pies, con gran humildad;\r\nEl ejemplo estás siguiendo,\r\nDel Cordero celestial.', 'CORO\r\nHoy su muerte recordamos\r\nY que Él resucitó.\r\nDemos, hoy, tributo y honra,\r\n¡A Jesús el Redentor!', '4\r\nLos emblemas, hoy tomemos;\r\nCon respeto y dignidad.\r\nEl que guarda su palabra,\r\n¡Vida eterna heredará!', 'CORO\r\nHoy su muerte recordamos\r\nY que Él resucitó.\r\nDemos, hoy, tributo y honra,\r\n¡A Jesús el Redentor!', '5\r\nEsta cena celebremos,\r\nEn paz y en comunión.\r\nAl Señor demos las gracias,\r\nPor su incomparable amor.', 'CORO(final)\r\nHoy su muerte recordamos\r\nY que Él resucitó.\r\nDemos, hoy, tributo y honra,\r\n¡A Jesús el Redentor!', '', '', 10),
(94, '94\r\nEN TU CENA', '1\r\nNuevamente reunidos, con amor,\r\nRecordamos tu muerte, hoy, Señor.\r\nFue tan grande tu pena y cruel dolor...\r\nY por ello, tenemos salvación.', 'CORO\r\nEn tu cena, ¡Oh, Redentor!\r\nLa Iglesia reunida...\r\nRemembranza haremos de tu inmolación.\r\n', 'CORO\r\nToda gloria y honor sean a tu Nombre,\r\nPor tu sacrificio. ¡Bendito Señor!', '2\r\nCon tu sangre nos has dado el perdón\r\nDe las faltas y del mal proceder.\r\nMuestra grande y sublime de amor.\r\nTe venimos, Señor, a agradecer.', 'CORO\r\nEn tu cena, ¡Oh, Redentor!\r\nLa Iglesia reunida...\r\nRemembranza haremos de tu inmolación.', 'CORO(final)\r\nToda gloria y honor sean a tu Nombre,\r\nPor tu sacrificio. ¡Bendito Señor!', '', '', '', '', '', '', 6),
(95, '95\r\nENVIA TU DULCE BENDICIÓN', '1\r\nEsperando Señor tu bendición,\r\nde rodillas, están ante tu altar,\r\nellos vienen a pedirte, ¡Oh Dios!\r\nTú ilumines la senda de su andar.', '(Coro)\r\n¡Abre tus ventanas, sublime Dios!\r\nY envía tu dulce bendición\r\n¡Abre tus ventanas, eterno Dios!\r\nY envía tu tierno amor.', '2\r\nHoy tu siervo imparte bendición,\r\nY exhorta que siempre se han de amar.\r\nTu Presencia guíe esta unión;\r\nNunca falte tu espíritu en su hogar.', '(Coro)\r\n¡Abre tus ventanas, sublime Dios!\r\nY envía tu dulce bendición\r\n¡Abre tus ventanas, eterno Dios!\r\nY envía tu tierno amor.', '3\r\nSi en las lides que enfrenten hay dolor,\r\nDe rodillas encuentren solución,\r\nTu palabra les sea de valor\r\nY Tú guíes sus pasos con amor.', '(Coro)\r\n¡Abre tus ventanas, sublime Dios!\r\nY envía tu dulce bendición\r\n¡Abre tus ventanas, eterno Dios!\r\nY envía tu tierno amor.', '4\r\nDe tu mano y tu Ley, en su vivir,\r\nSiempre fieles, entre ellos y hacia Ti,\r\nDe las pruebas que juntos tendrán,\r\nTu Presencia, bonanza les dará.', '(Coro)(final)\r\n¡Abre tus ventanas, sublime Dios!\r\nY envía tu dulce bendición\r\n¡Abre tus ventanas, eterno Dios!\r\nY envía tu tierno amor.', '', '', '', '', 8),
(96, '96\r\nESTE PACTO QUE HOY HARÁS', '1\r\n¡Oye hermano!, que hoy estás\r\nEn presencia de tu Dios,\r\nHoy tu vida entregarás,\r\nA servirle al Señor.', 'CORO\r\n//Este pacto, que hoy harás,\r\n¡El Señor lo sellará!\r\nY hoy tu nombre escribirá...\r\nPara que recibas vida. //', '2\r\nHoy tu vida cambiará;\r\nYa lo viejo pasará...\r\nHombre nuevo, tú, serás;\r\nA Él, sólo servirás.', 'CORO\r\n//Este pacto, que hoy harás,\r\n¡El Señor lo sellará!\r\nY hoy tu nombre escribirá...\r\nPara que recibas vida. //', '3\r\nTal vez, pruebas tú tendrás,\r\n¡Pero solo no estarás!\r\n¡El Señor te ayudará!\r\nSiempre a Él, debes orar.', 'CORO\r\n//Este pacto, que hoy harás,\r\n¡El Señor lo sellará!\r\nY hoy tu nombre escribirá...\r\nPara que recibas vida. //', '4\r\n¡Este mundo pasará!\r\nSu placer terminará.\r\nSi a Dios sirves sin dudar:\r\n¡Vida eterna, te dará!', 'CORO(final)\r\n//Este pacto, que hoy harás,\r\n¡El Señor lo sellará!\r\nY hoy tu nombre escribirá...\r\nPara que recibas vida. //', '', '', '', '', 8),
(97, '97\r\nHEREDAD SON LOS HIJOS', '1\r\nHe aquí, heredad de Jehová\r\nSon los hijos.\r\nHermosa creación que, en nuestras manos,\r\nÉl, ha depositado.', 'CORO\r\n¡Del Señor son bendición!\r\nInstrucción dales, siempre, en su camino.\r\nDel Señor heredarán,\r\nEl vivir en el reino eternal.', '2\r\nJoyas son y corona de bien,\r\nA sus padres.\r\nSi temen a su Dios, les cuidará por\r\nDoquiera que ellos anden.', 'CORO\r\n¡Del Señor son bendición!\r\nInstrucción dales, siempre, en su camino.\r\nDel Señor heredarán,\r\nEl vivir en el reino eternal.', '3\r\nAnte Dios, en su templo, hay que\r\nPresentarles;\r\nY el siervo del Señor implorará,\r\nPara que Él les guarde siempre.', 'CORO(final)\r\n¡Del Señor son bendición!\r\nInstrucción dales, siempre, en su camino.\r\nDel Señor heredarán,\r\nEl vivir en el reino eternal.', '', '', '', '', '', '', 6),
(98, '98\r\nLA CENA', '1\r\nSe acerca el momento... ¡Momento esperado!\r\nSer digno de Ti, quiero, ¡Oh mi Dios!', '1\r\nAnhelo yo la dicha de estar en tu Presencia\r\nCon sencillez de corazón.', '2\r\nYa casi el sol se oculta y junto a mis hermanos,\r\nEn tu santuario, te alabaré.', '2\r\nTu Iglesia ya reunida, recordará la noche,\r\nCuando tu Hijo padeció.', '3\r\nPor eso en su memoria, tomamos pan y vino;\r\nEmblemas que el Maestro nos dejó.', '3\r\nCumplido el mandamiento, en acto de humildad,\r\nMe postraré a lavar los pies.', '4\r\nEterno Padre Dios, ¡Bendito sea Tu Nombre!\r\nAcepta toda nuestra gratitud', '4(final)\r\nPor otorgarnos vida y realizar la cena;\r\nLa cena del Señor Jesús.', '', '', '', '', 8),
(99, '99\r\nLA CENA DEL SEÑOR', '1\r\nHoy, invitados a la cena del Señor.\r\nTodos aquí congregados; con gratitud y fervor.', 'CORO\r\nCon conciencia y sumisión, participemos del pan;\r\nDel Maestro es el cuerpo, camino de salvación.', 'CORO\r\nY la sangre que Él vertió, ¡En este vino, será!\r\nCumplamos el mandamiento. Vida eterna, franca está.', '2\r\nEl “pan de vida”, del cielo, a mí descendió;\r\nCurado fui por su herida... en la cruz por mí murió.', 'CORO\r\nCon conciencia y sumisión, participemos del pan;\r\nDel Maestro es el cuerpo, camino de salvación.', 'CORO\r\nY la sangre que Él vertió, ¡En este vino, será!\r\nCumplamos el mandamiento. Vida eterna, franca está.', '3\r\nEn comunión con Dios vivamos en fe.\r\nNuestra humildad demostremos, lavando a otros los pies.', 'CORO\r\nCon conciencia y sumisión, participemos del pan;\r\nDel Maestro es el cuerpo, camino de salvación.', 'CORO\r\nY la sangre que Él vertió, ¡En este vino, será!\r\nCumplamos el mandamiento. Vida eterna, franca está.', '4\r\nAmado Padre, conserva, así nuestra fe;\r\nY que tu espíritu santo, guíe nuestro proceder.', 'CORO\r\nCon conciencia y sumisión, participemos del pan;\r\nDel Maestro es el cuerpo, camino de salvación.', 'CORO(final)\r\nY la sangre que Él vertió, ¡En este vino, será!\r\nCumplamos el mandamiento. Vida eterna, franca está.', 12),
(100, '100 \r\nLA CENA DEL SEÑOR, LA CENA ESPIRITUAL', '1\r\n¡La cena del Señor!... El Padre preparó\r\nAl Hijo de su amor; ¡Del mundo es Redentor!', '1\r\nLos que escogió Jesús, a éstos llamó;\r\nY con los que cenó, también santificó.', 'CORO\r\n¡Allá en Jerusalén! Jesús instituyó\r\nLa cena, que con pan y vino compartió;', 'CORO\r\nEmblema espiritual de aquel Cordero es,\r\nQue Dios sacrificó, para poder limpiar\r\nAl mundo que amó y Cristo consumó.', '2\r\nSu cuerpo es el pan. El vino, sangre de Él;\r\nQue derramada fue, para purificar,', '2\r\nLas almas que, a su vez, el Padre celestial,\r\nDispuso en su bondad y quiso rescatar.', 'CORO\r\n¡Allá en Jerusalén! Jesús instituyó\r\nLa cena, que con pan y vino compartió;', 'CORO\r\nEmblema espiritual de aquel Cordero es,\r\nQue Dios sacrificó, para poder limpiar\r\nAl mundo que amó y Cristo consumó.', '3\r\n¡Ordena el Señor! la cena espiritual,\r\nA los que redimió, cada año, celebrar;', '3\r\nEn la memoria de Él, que vino a pagar\r\nLas culpas, en la cruz, por la humanidad.', 'CORO\r\n¡Allá en Jerusalén! Jesús instituyó\r\nLa cena, que con pan y vino compartió;', 'CORO(final)\r\nEmblema espiritual de aquel Cordero es,\r\nQue Dios sacrificó, para poder limpiar\r\nAl mundo que amó y Cristo consumó.', 12),
(101, '101\r\nLÁVAME, LÍMPIAME', '1\r\nSeñor que estás allá en las alturas;\r\nSeñor que desde allá me estás mirando;\r\nVengo a Ti, a pedirte perdón, de rodillas, Señor,\r\nPorque he pecado.', 'CORO\r\n¡Lávame! ¡Límpiame!\r\nPerdóname, ¡Oh mi Dios!\r\n¡Lávame! ¡Límpiame!\r\nPerdóname, porque Tú eres Amor.', '2\r\nSeñor, ¡No puedo más!... Tú me haces falta.\r\nSeñor: ¿Qué puedo hacer sin Tu Presencia?\r\nVengo a Ti, a pedirte Señor, sanes Tú, mi dolor,\r\nPor tu clemencia.', 'CORO\r\n¡Lávame! ¡Límpiame!\r\nPerdóname, ¡Oh mi Dios!\r\n¡Lávame! ¡Límpiame!\r\nPerdóname, porque Tú eres Amor.', '3\r\nDespués de tanto andar, te he encontrado;\r\nDespués de tanto andar en este mundo,\r\nVengo a Ti, a decirte Señor: tomes mi corazón,\r\nPorque es muy tuyo.', 'CORO(final)\r\n¡Lávame! ¡Límpiame!\r\nPerdóname, ¡Oh mi Dios!\r\n¡Lávame! ¡Límpiame!\r\nPerdóname, porque Tú eres Amor.', '', '', '', '', '', '', 6),
(102, '102\r\nLO QUE TÚ HAS DE UNIR', '1\r\n¡Oh, Señor! acudimos, reverentes, ante Ti;\r\nCon temor, bendito Padre, a implorar tu bendición.\r\nEntregamos lo mejor, para que podamos hoy,\r\nSin dudar llegar hasta tu altar.', 'Coro\r\nQue lo que tú has de unir, nadie pueda separar,\r\nFortalece estos lazos de amor.\r\nY que juntos hasta el fin, nos podamos conservar,\r\nDando gloria a tu Nombre celestial.', '2\r\n¡Oh, Señor! te pedimos bendición en nuestro hogar.\r\nQue pongamos todo empeño en nuestra vida espiritual.\r\nY los hijos que, en amor, nos llegues a conceder,\r\nLos formemos en tu voluntad.', 'Coro\r\nQue lo que tú has de unir, nadie pueda separar,\r\nFortalece estos lazos de amor.\r\nY que juntos hasta el fin, nos podamos conservar,\r\nDando gloria a tu Nombre celestial.', '3\r\n¡Oh, Señor! tu palabra ilumine nuestro andar,\r\nQue tu espíritu nos guíe en los problemas y el dolor.\r\nComprensión, fidelidad; nunca lleguen a faltar,\r\nY que juntos te demos loor.', 'Coro(final)\r\nQue lo que tú has de unir, nadie pueda separar,\r\nFortalece estos lazos de amor.\r\nY que juntos hasta el fin, nos podamos conservar,\r\nDando gloria a tu Nombre celestial.', '', '', '', '', '', '', 6),
(103, '103\r\nMATRIMONIO HONROSO', '1\r\nPareja idónea formó Dios en la creación.\r\nPara ser uno en Él,\r\nHan prometido llevar su vida en santidad.', 'Coro\r\nQue Dios los guarde y proteja,\r\nBendiga esta unión\r\nQue sólo la muerte, en ellos, haga separación.', '2\r\nEl matrimonio en Dios: ¡Honra ha de ser!\r\nSanto, sincero y fiel.\r\nSiempre será su deber dejar que Él guíe su hogar.', 'Coro\r\nQue Dios los guarde y proteja,\r\nBendiga esta unión\r\nQue sólo la muerte, en ellos, haga separación.', '3\r\nY en su camino el Señor les bendecirá,\r\nPues cardos pueden hallar,\r\nMás si unidos están Él los guiará hasta el final.', 'Coro(final)\r\nQue Dios los guarde y proteja,\r\nBendiga esta unión\r\nQue sólo la muerte, en ellos, haga separación.', '', '', '', '', '', '', 6),
(104, '104\r\nMI PETICIÓN', '1\r\nDelante de Ti,\r\nVenimos ¡Oh Señor!\r\nRogando escuches Tú,\r\nNuestra humilde petición.', 'CORO\r\nGuíanos por tus Sendas;\r\n¡Líbranos de peligros!\r\nQue juntos podamos llegar\r\nHasta el final.', '2\r\nTal vez tribulación,\r\nAngustia o tentación\r\nLastimen este amor...\r\nLucharemos por él.', 'CORO\r\nGuíanos por tus Sendas;\r\n¡Líbranos de peligros!\r\nQue juntos podamos llegar\r\nHasta el final.', '3\r\nPedimos con fe,\r\nTu bendición, ¡Oh Dios!\r\n¡Valor para vencer!\r\nLas pruebas y el dolor.', 'CORO(final)\r\nGuíanos por tus Sendas;\r\n¡Líbranos de peligros!\r\nQue juntos podamos llegar\r\nHasta el final.', '', '', '', '', '', '', 6),
(105, '105\r\nOBRA DE TUS MANOS', '1\r\nUn ser pequeño, viene ante Ti;\r\nUn alma limpia, fruto de amor.\r\nQue cual ventana, nos deja ver,\r\nLas maravillas de tu creación.', 'CORO\r\n¡Es obra de tus manos!\r\nComo precioso amanecer.\r\n¡Oh Dios! hoy te pedimos,\r\nCuides su vida con tu favor;\r\nColmes su vida de bendición.', '2\r\nTú lo formaste con tu poder;\r\nSoplo de vida, diste a su ser,\r\nCon alegría, cantamos hoy,\r\nTe damos gracias, Eterno Dios.', 'CORO\r\n¡Es obra de tus manos!\r\nComo precioso amanecer.\r\n¡Oh Dios! hoy te pedimos,\r\nCuides su vida con tu favor;\r\nColmes su vida de bendición.', '3\r\nHoy te pedimos, Padre de amor,\r\nTus manos toquen su corazón.\r\nQue siempre encuentre consejo en Ti.\r\nBajos tus alas: ¡Tu protección!', 'CORO(final)\r\n¡Es obra de tus manos!\r\nComo precioso amanecer.\r\n¡Oh Dios! hoy te pedimos,\r\nCuides su vida con tu favor;\r\nColmes su vida de bendición.', '', '', '', '', '', '', 6),
(106, '106\r\nPIENSA BIEN', '1\r\nPiensa bien lo que hoy vas a hacer,\r\nSi tu vida anhelas salvar.\r\nPues desde hoy tendrás que ser,\r\n¡Sólo siervo del Señor!\r\n¡Y soldado de mucho valor!', 'CORO\r\n¡Sí!, ¡Bautízate! Es lo mejor\r\nQue en la vida, tú puedes hacer.\r\nPero fiel tendrás que ser, en las “filas de Israel”;\r\nQue es el único pueblo de Dios.', '2\r\nPiensa bien lo que hoy vas a hacer...\r\n¡Que el bautismo no es para jugar!\r\nSi eres fiel, ¡Premio tendrás!\r\nCuando venga el Salvador;\r\nAllá en Sion, tú con Él, estarás.', 'CORO\r\n¡Sí!, ¡Bautízate! Es lo mejor\r\nQue en la vida, tú puedes hacer.\r\nPero fiel tendrás que ser, en las “filas de Israel”;\r\nQue es el único pueblo de Dios.', '3\r\nPiensa bien lo que hoy vas a hacer...\r\n¡Que harás pacto con el Señor!\r\nTe tendrás que despedir,\r\nDe este mundo de maldad\r\nY a Dios, entregarte, a servir.', 'CORO\r\n¡Sí!, ¡Bautízate! Es lo mejor\r\nQue en la vida, tú puedes hacer.\r\nPero fiel tendrás que ser, en las “filas de Israel”;\r\nQue es el único pueblo de Dios.', '4\r\nPiensa bien lo que hoy vas a hacer...\r\nSi te apartas, tendrás que llorar...\r\nLlora, ¡Sí!... ¡Haciendo bien! ¡Sufre! como Él sufrió;\r\nPues ejemplo, Cristo nos dejó.', 'CORO(final)\r\n¡Sí!, ¡Bautízate! Es lo mejor\r\nQue en la vida, tú puedes hacer.\r\nPero fiel tendrás que ser, en las “filas de Israel”;\r\nQue es el único pueblo de Dios.', '', '', '', '', 8),
(107, '107\r\nTE PEDIMOS', '1\r\nGuárdalos, ¡Oh, Dios! del mal.\r\nGuárdalos en santidad.\r\n¡Guíalos! Que nunca jamás\r\nEstén en soledad.', 'CORO\r\nLíbralos de todo pecado.\r\nDales hoy tu fortaleza,\r\nPara alejar al enemigo.\r\n¡Con tu poder caerá rendido!', 'CORO\r\nTú que eres perfecto, ¡Oh, mi Dios!\r\n¡Grande en misericordia!\r\nTe pedimos: sé con ellos;\r\nY así, nada podrá vencerlos.', '2\r\nGuarda siempre esta unión.\r\n¡Bríndales tu protección!\r\nQue de Ti, podrán recibir,\r\nLa guía de tu amor.', 'CORO\r\nLíbralos de todo pecado.\r\nDales hoy tu fortaleza,\r\nPara alejar al enemigo.\r\n¡Con tu poder caerá rendido!', 'CORO\r\nTú que eres perfecto, ¡Oh, mi Dios!\r\n¡Grande en misericordia!\r\nTe pedimos: sé con ellos;\r\nY así, nada podrá vencerlos.', '3\r\nBendice el fruto de amor.\r\nGuárdalos en su andar.\r\nSólo les podrá separar,\r\nLa muerte hasta el final.', 'CORO\r\nLíbralos de todo pecado.\r\nDales hoy tu fortaleza,\r\nPara alejar al enemigo.\r\n¡Con tu poder caerá rendido!', 'CORO(final)\r\nTú que eres perfecto, ¡Oh, mi Dios!\r\n¡Grande en misericordia!\r\nTe pedimos: sé con ellos;\r\nY así, nada podrá vencerlos.', '', '', '', 9),
(108, '108\r\nTU SACRIFICIO', '1\r\nCuando viniste a la tierra, Jesús,\r\nTú nos mostraste, el sendero de luz.\r\nPero tu pueblo no quiso escuchar\r\nY en cruenta cruz, tu existir, fuiste a dar.', 'CORO\r\nTe alabamos, ¡Bendito Señor!\r\nPor ese amor que nos dio salvación.\r\nAunque sufriste gran pena y dolor,\r\nTu sacrificio nos dio redención.', '2\r\nTú, ¡Fiel Cordero! Bendito... ¡De paz!\r\nQue el Padre, en su amor, entregó.\r\nTú que dejaste tu trono de luz,\r\nPor el martirio. ¡Mereces loor!', 'CORO\r\nTe alabamos, ¡Bendito Señor!\r\nPor ese amor que nos dio salvación.\r\nAunque sufriste gran pena y dolor,\r\nTu sacrificio nos dio redención.', '3\r\nEn esta noche, amoroso Señor,\r\nNos acercamos con toda humildad,\r\nPara tomar este vino y pan;\r\nY en comunión a tu nombre ensalzar.', 'CORO(final)\r\nTe alabamos, ¡Bendito Señor!\r\nPor ese amor que nos dio salvación.\r\nAunque sufriste gran pena y dolor,\r\nTu sacrificio nos dio redención.', '', '', '', '', '', '', 6),
(109, '109\r\nUN HOGAR EN EL SEÑOR', '1\r\nComo Cristo amo a la Iglesia, entregándole su ser,\r\nAsí debe el hombre amar a su mujer.\r\nComo vaso que es muy frágil y que es fácil de romper;\r\nCon ternura él cuidara de su esplender.', 'CORO\r\nYa nunca más serán dos, otra vez.\r\nSe unirá el uno al otro, para ser\r\nUna carne, sólo un cuerpo.\r\nQue en paciencia y con respeto\r\nEdifiquen siempre unidos su hogar.', '2\r\nComo Sara obedecía, sujetándose por fe,\r\nAsí debe honrar al hombre la mujer.\r\nSiendo ejemplo de respeto, santidad y honestidad;\r\nUna ayuda y compañera sin igual.', 'CORO\r\nYa nunca más serán dos, otra vez.\r\nSe unirá el uno al otro, para ser\r\nUna carne, sólo un cuerpo.\r\nQue en paciencia y con respeto\r\nEdifiquen siempre unidos su hogar.', '3\r\nDejará a su padre y madre, cada uno, para ser,\r\nUna mente en un mismo parecer.\r\nSiempre juntos en las pruebas, caminando con amor,\r\nEl sendero de una vida en el Señor.', 'CORO(final)\r\nYa nunca más serán dos, otra vez.\r\nSe unirá el uno al otro, para ser\r\nUna carne, sólo un cuerpo.\r\nQue en paciencia y con respeto\r\nEdifiquen siempre unidos su hogar.', '', '', '', '', '', '', 6),
(110, '110 \r\nUN PACTO CON DIOS', '1\r\nComo Israel fue bautizado,\r\nBajo la nube y en la mar;\r\nAhora Dios, te ha invitado:\r\n¡Hermano!, ¡Vente a bautizar!', 'CORO\r\nAcepta hoy, la invitación\r\nQue te hace nuestro Dios.\r\n¡Entrégale hoy tu corazón!\r\nY haz un pacto, ...entre los dos.', '2\r\n¡Limpia tu ser del vil pecado!\r\n¡Lávate en la fuente de amor!\r\nCristo su sangre ha derramado\r\nPara salvar al pecador.', 'CORO\r\nAcepta hoy, la invitación\r\nQue te hace nuestro Dios.\r\n¡Entrégale hoy tu corazón!\r\nY haz un pacto, ...entre los dos.', '3\r\nNo hagas vano el sacrificio\r\nDe Cristo, el gran Redentor.\r\nObtendrás como beneficio:\r\n¡Vida eterna con tu Dios!', 'CORO(final)\r\nAcepta hoy, la invitación\r\nQue te hace nuestro Dios.\r\n¡Entrégale hoy tu corazón!\r\nY haz un pacto, ...entre los dos.', '', '', '', '', '', '', 6);
INSERT INTO `himnos` (`id_himno`, `pagina_0`, `pagina_1`, `pagina_2`, `pagina_3`, `pagina_4`, `pagina_5`, `pagina_6`, `pagina_7`, `pagina_8`, `pagina_9`, `pagina_10`, `pagina_11`, `pagina_12`, `num`) VALUES
(111, '111\r\nUN VÍNCULO PERFECTO', '1\r\nCon tu mano has trazado en perfección,\r\nEl destino de dos almas, que en amor,\r\nHan de unir su corazón en este día;\r\nPara andar como uno solo, ¡En bendición!', 'CORO\r\n¡Una mente en un vínculo perfecto!\r\nSean las almas que hoy se allegan a tu “altar”.\r\nSuplicamos, que ante Ti, Señor bendito:\r\nLo que hoy juntas, nada pueda separar.', '2\r\nCon paciencia, con respeto afirmarán,\r\nEl sendero de una vida en santidad;\r\nProcurando, en un lecho sin mancilla,\r\nSer ejemplo para el mundo, en unidad.', 'CORO\r\n¡Una mente en un vínculo perfecto!\r\nSean las almas que hoy se allegan a tu “altar”.\r\nSuplicamos, que ante Ti, Señor bendito:\r\nLo que hoy juntas, nada pueda separar.', '3\r\nY con grato testimonio, en claridad,\r\nSembrarán en su simiente tu verdad;\r\nConduciendo, a cada cual, por tu camino\r\nDe justicia y rectitud, con caridad.', 'CORO\r\n¡Una mente en un vínculo perfecto!\r\nSean las almas que hoy se allegan a tu “altar”.\r\nSuplicamos, que ante Ti, Señor bendito:\r\nLo que hoy juntas, nada pueda separar.', '4\r\nSiendo firme el fundamento, en Ti, Señor,\r\nY tomados de tu mano en la aflicción,\r\nConstruirán un matrimonio, en Ti confiados.\r\n¡Que los guíe hasta el final tu bendición!', 'CORO(final)\r\n¡Una mente en un vínculo perfecto!\r\nSean las almas que hoy se allegan a tu “altar”.\r\nSuplicamos, que ante Ti, Señor bendito:\r\nLo que hoy juntas, nada pueda separar.', '', '', '', '', 8),
(112, '112\r\nUNO, SON', '1\r\nDios, bendice a los que hoy,\r\nJuntos vienen a tu altar.\r\nDales Tú, la dirección,\r\nAl formar su nuevo hogar.', 'CORO\r\n¡Oh, Señor!, ¡Bendice Tú!\r\nEl momento de esta unión.\r\nUno, son, y en Ti será\r\nBendecido su amor.', '2\r\nEn su senda, siempre Tú,\r\nConsejero y protector.\r\nEn tristezas y dolor,\r\nSé su fiel consolador.', 'CORO\r\n¡Oh, Señor!, ¡Bendice Tú!\r\nEl momento de esta unión.\r\nUno, son, y en Ti será\r\nBendecido su amor.', '3\r\nSiempre guarden la lealtad,\r\nA este voto de amor.\r\nDios divino, ayúdalos,\r\nGocen de tu bendición.', 'CORO\r\n¡Oh, Señor!, ¡Bendice Tú!\r\nEl momento de esta unión.\r\nUno, son, y en Ti será\r\nBendecido su amor.', '4\r\nY así juntos, hasta el fin,\r\nPermanecerán, Señor.\r\nDicha y paz, otórgales,\r\nCon el fruto de su unión.', 'CORO(final)\r\n¡Oh, Señor!, ¡Bendice Tú!\r\nEl momento de esta unión.\r\nUno, son, y en Ti será\r\nBendecido su amor.', '', '', '', '', 8),
(113, '113\r\nVENGAMOS A ÉL COMO NIÑOS', '1\r\nQuién no fuere como un niño\r\nNo podrá al reino entrar.\r\nSu pensar y obrar con limpieza,\r\nLes permita con Cristo estar.', 'CORO\r\nVengamos a Él como niños\r\nCon sencillez y humildad.\r\nPues de Dios, premio para ellos:\r\nSerá con Cristo reinar.', '2\r\nJoyas del Señor, son tesoro,\r\nLos que sin dudar dan perdón.\r\nQue desde hoy y en todos sus días,\r\nSu servicio, entreguen a Dios.', 'CORO(final)\r\nVengamos a Él como niños\r\nCon sencillez y humildad.\r\nPues de Dios, premio para ellos:\r\nSerá con Cristo reinar.', '', '', '', '', '', '', '', '', 4),
(114, '114\r\nY TODAS LAS VECES', '1\r\nVamos hermanos, éste es el día:\r\n¡Participemos de esta cena!\r\nCon humildad y un corazón limpio,\r\nNos acercaremos hoy.', 'CORO\r\nY todas las veces que comiereis este pan;\r\nY todas las veces que bebiereis de esta copa;\r\nLa muerte del Señor anunciáis,\r\nHasta que, Él, vuelva en gloria.', '2\r\nTierno llamando, el del Maestro:\r\nQue recordemos su sacrificio.\r\nPor nuestras culpas fue entregado;\r\nCon su sangre, Él, nos compró.', 'CORO\r\nY todas las veces que comiereis este pan;\r\nY todas las veces que bebiereis de esta copa;\r\nLa muerte del Señor anunciáis,\r\nHasta que, Él, vuelva en gloria.', '3\r\nComo el Señor nos ha perdonado,\r\nAsí nosotros también hagamos;\r\nSi acaso alguien nos ha ofendido,\r\n¡Perdonemos con amor!', 'CORO(final)\r\nY todas las veces que comiereis este pan;\r\nY todas las veces que bebiereis de esta copa;\r\nLa muerte del Señor anunciáis,\r\nHasta que, Él, vuelva en gloria.', '', '', '', '', '', '', 6),
(115, '115\r\nACEPTA A TU SEÑOR', '1\r\nEn esta vida\r\nSe necesita,\r\nValor y fe\r\nCon oración.', 'CORO\r\n// En esta vida nada eres,\r\nSi tú no aceptas al Señor.\r\n¡Ven hacia Él!, quizá otra vez\r\nNo habrá. //', '2\r\nCristo es el medio\r\nDe salvación.\r\n¡No te detengas!,\r\n¡Confía en Él!', 'CORO\r\n// En esta vida nada eres,\r\nSi tú no aceptas al Señor.\r\n¡Ven hacia Él!, quizá otra vez\r\nNo habrá. //', '3\r\nCuando a Él vinieres,\r\nDescansarás;\r\nSus brazos tiernos\r\nTe envolverán.', 'CORO(final)\r\n// En esta vida nada eres,\r\nSi tú no aceptas al Señor.\r\n¡Ven hacia Él!, quizá otra vez\r\nNo habrá. //', '', '', '', '', '', '', 6),
(116, '116 \r\nACUÉRDATE DE ÉL', '1\r\nCuando luches en la vida\r\nY que lejos estés de la salud.\r\nCuando llores por ayuda\r\nY que nadie la pueda ofrecer.', '1\r\nCuando ya no tengas gozo y te sientas triste,\r\n¡Acuérdate de Dios!\r\nBusca estar a su lado..., te extenderá su mano...,\r\nSe acordará de ti.', '2\r\nCuando toques a una puerta\r\nY que nadie te quiera escuchar.\r\nCuando andes en tinieblas\r\nY la luz no puedas encontrar.', '2\r\nCuando sientas que tu vida no tiene sentido,\r\n¡Acuérdate de Él!\r\nSiempre lleva en la mente que, en su pensamiento,\r\nSe acordará de ti.', 'Final\r\nCuando ya no tengas gozo y te sientas triste,\r\n¡Acuérdate de Él!\r\nBusca estar a su lado..., te extenderá su mano...,\r\nSe acordará de ti.', '', '', '', '', '', '', '', 5),
(117, '117\r\nCONFÍA EN ÉL', '1\r\nHay momentos de la vida\r\nQue no puedes comprender.\r\nLlegan solos, sin aviso,\r\nY te pueden sorprender.', 'CORO\r\nSalvación encontrarás\r\nSi te entregas, hoy, a Él.\r\nCon sus “plumas” te cubrirá,\r\nSolamente fía en Él.', '2\r\nSi te sientes solo y triste,\r\nY no puedes continuar.\r\nYa no tardes, ¡Ven a Cristo!\r\nÉl tu carga quitará.', 'CORO\r\nSalvación encontrarás\r\nSi te entregas, hoy, a Él.\r\nCon sus “plumas” te cubrirá,\r\nSolamente fía en Él.', '3\r\nNo desmayes por la prueba,\r\n¡Tú la puedes soportar!\r\nFiando siempre en Jesucristo,\r\nLa victoria obtendrás.', 'CORO(final)\r\nSalvación encontrarás\r\nSi te entregas, hoy, a Él.\r\nCon sus “plumas” te cubrirá,\r\nSolamente fía en Él.', '', '', '', '', '', '', 6),
(118, '118\r\nDAME HIJO MÍO TÚ CORAZÓN', '1\r\n¡Joven!, decide caminar con Dios.\r\nAún estás a tiempo de empezar,\r\nPorque Dios, te espera con amor.\r\n¡Decídete y no lo dudes más!', 'CORO\r\n“Dame, hijo mío, tu corazón\r\nY está atento a mi clamor”,\r\n“Porque en Mí tendrás perdón\r\nY encontrarás la paz y el amor”.\r\n“Dame, hijo mío, tu corazón”.', '2\r\nTu vida, hoy, acerca al Señor.\r\nY deja a Cristo, llenarte de amor.\r\nLibre del mundo, te quiere el Señor.\r\nEntrega a Dios, tu fuerza y tu valor.', 'CORO\r\n“Dame, hijo mío, tu corazón\r\nY está atento a mi clamor”,\r\n“Porque en Mí tendrás perdón\r\nY encontrarás la paz y el amor”.\r\n“Dame, hijo mío, tu corazón”.', '3\r\nTu corazón, te pide el Dios de amor;\r\nQuiere llenarlo de paz y bondad;\r\nDáselo a Cristo, con fe y humildad,\r\nY di: “Señor, te doy mi corazón”.', 'CORO(final)\r\n“Dame, hijo mío, tu corazón\r\nY está atento a mi clamor”,\r\n“Porque en Mí tendrás perdón\r\nY encontrarás la paz y el amor”.\r\n“Dame, hijo mío, tu corazón”.', '', '', '', '', '', '', 6),
(119, '119\r\nEL LLAMADO', '1\r\nEscucha hoy la Voz\r\nQue te llama con un singular amor.\r\n¡Ya no sufras más y sírvele de corazón!\r\nY su paz, a tu alma, dará tranquilidad.', 'CORO\r\n¡Acéptale! No dudes más...\r\n¡Su mano siempre te dará!\r\nAcepta su llamado, ¡Mañana tarde será!\r\n¡No te niegues la dicha a la felicidad!', '2\r\n¿Dolor hay en tu ser?\r\n¿Y pesares que no te dejan seguir?\r\nEl Señor te enviará, si quieres, sanidad.\r\n¡No desmayes! ¡No temas! ¡Su gracia te dará!', 'CORO\r\n¡Acéptale! No dudes más...\r\n¡Su mano siempre te dará!\r\nAcepta su llamado, ¡Mañana tarde será!\r\n¡No te niegues la dicha a la felicidad!', '3\r\n¡Él prometió y hará!\r\nSólo pide, guiado en humildad y fe.\r\nToda lágrima enjugará de quien le es fiel.\r\nCon su diestra y justicia, ¡Él te sustentará!', 'CORO(final)\r\n¡Acéptale! No dudes más...\r\n¡Su mano siempre te dará!\r\nAcepta su llamado, ¡Mañana tarde será!\r\n¡No te niegues la dicha a la felicidad!', '', '', '', '', '', '', 6),
(120, '120 \r\nEL PREGÓN DE SALVACIÓN', '1\r\nReflexiona pecador, con presteza decidido;\r\nYa no tarda el clamor: “El esposo ha venido”;\r\nY entre al reino, todo aquel\r\nQue haya sido redimido.', 'CORO\r\n¡El pregón de salvación se está escuchando!\r\n¡El pregón de salvación se está anunciando!\r\n¡El final de redención se está acercando!\r\n¡El mensaje del Señor, se está cerrando!', '2\r\nLa trompeta del final, sonará en muy poco tiempo:\r\nLos que “duerman” vivirán y los vivos se transforman;\r\nSu labor merece ya,\r\nLa corona de la vida.', 'CORO\r\n¡El pregón de salvación se está escuchando!\r\n¡El pregón de salvación se está anunciando!\r\n¡El final de redención se está acercando!\r\n¡El mensaje del Señor, se está cerrando!', '3\r\nYa no vivas en maldad y en las cosas temporales.\r\n¡Ven! Y dale a tu Señor, frutos solo espirituales.\r\nNo te obstines en seguir\r\nEl camino de los males.', 'CORO\r\n¡El pregón de salvación se está escuchando!\r\n¡El pregón de salvación se está anunciando!\r\n¡El final de redención se está acercando!\r\n¡El mensaje del Señor, se está cerrando!', '4\r\nYa se acerca el clarín, de los seres celestiales;\r\nLas naciones estarán en el sitio de los valles...,\r\nDonde el Juez sentenciará,\r\nJustamente a esos mortales.', 'CORO(final)\r\n¡El pregón de salvación se está escuchando!\r\n¡El pregón de salvación se está anunciando!\r\n¡El final de redención se está acercando!\r\n¡El mensaje del Señor, se está cerrando!', '', '', '', '', 8),
(121, '121 \r\nES TIEMPO DE BUSCAR A DIOS', '1\r\n“Escucha hijo mío mis palabras\r\nY guarda con amor, hoy, mis consejos”;\r\nAma al Señor Jehová y así tu alma salvarás.\r\nEs tiempo de buscar a Dios, ¡No dudes!', '2\r\nTe llenará de bienaventuranza\r\nY próspero serás, tú, en la vida.\r\nNo te apartes del Señor, ¡Sólo en Él hay salvación!\r\nEs tiempo de buscar a Dios, ¡No dudes!', '3\r\nTu vida no malgastes en placeres:\r\nSerá la perdición de tu camino;\r\nAntes, en la Ley de Dios, con amor meditarás.\r\nEs tiempo de buscar a Dios, ¡No dudes!', '4(final)\r\nEs tiempo de buscar a Dios, ¡Sin duda!\r\nY abrir tu corazón y ser sensible;\r\nDeja que Él more en ti; lucha trabajando fiel.\r\nEs tiempo de buscar a Dios. ¡No dudes!', '', '', '', '', '', '', '', '', 4),
(122, '122\r\nGRACIAS SEÑOR', '1\r\nSólo en Cristo se encuentra solaz.\r\n¡Ven! sí, a Él, y podrás descansar.\r\nEs su sangre una muestra de amor.\r\n¡Por dar vida, en la cruz, Él murió!', 'CORO\r\n¡Gracias Señor! ¡Gracias por tu amor!\r\n¡Recibe hoy Gloria y Honor!\r\nSólo en Ti se hallará redención\r\nY el pecador, goza ya tu perdón.', '2\r\n¡Ven a Cristo, oirá tu aflicción!\r\nYa no hay dolor bajo su protección.\r\nSi tú fías en Él, hallarás\r\nUn refugio de amor y de paz.', 'CORO\r\n¡Gracias Señor! ¡Gracias por tu amor!\r\n¡Recibe hoy Gloria y Honor!\r\nSólo en Ti se hallará redención\r\nY el pecador, goza ya tu perdón.', '3\r\nDeja a Cristo vivir, hoy, en ti,\r\nSi en vida eterna tú quieres vivir.\r\nCon su amor siempre te guardará\r\nY su reino podrás disfrutar.', 'CORO(final)\r\n¡Gracias Señor! ¡Gracias por tu amor!\r\n¡Recibe hoy Gloria y Honor!\r\nSólo en Ti se hallará redención\r\nY el pecador, goza ya tu perdón.', '', '', '', '', '', '', 6),
(123, '123\r\n¿HAS PENSADO ...?', '1\r\n¿Has pensado lo que puedes ganar\r\nSi tu vida anhelas cambiar?\r\n¿Sabes cuánto has de renovar,\r\nSi entregas tu vida al Señor?', '1\r\nHoy, Jesús quiere tu vida moldear.\r\nÉl te ofrece su gran protección;\r\nQuiere darte paz y amor,\r\nPara que tengas una vida mejor.', '2\r\n¿Has pensado en el efecto que habrá\r\nAl dejar el camino del mal?\r\n¿Cuán dispuesto estás a cambiar\r\nY dejar que, en ti, viva el Señor?', '2(final)\r\nNo dudes, ¡ven a Jesús! a sanar.\r\nTe ayudará, aliviará tu pesar;\r\nSolo en Él encontrarás paz,\r\nVen a sus brazos y descanso hallarás.', '', '', '', '', '', '', '', '', 4),
(124, '124\r\nHEME AQUÍ', '1\r\nHoy escuché, ¡Y muy feliz!\r\nVoz al llamar dentro de mí;\r\nEs mi alma y mi espíritu, que dicen: “¡Heme aquí!”\r\nQuieren llenarse más de', 'CORO\r\n|| ¡Heme aquí! ¡Heme aquí!\r\nTe digo, mi Señor;\r\nQuiero llenarme de tu amor;\r\nQuiero que seas mi Salvador ||', '2\r\nHay una lucha en mi interior,\r\nEntre tu amor y mi rencor;\r\nEnséñame a perdonar, también a olvidar;\r\nPara, a Ti, mi alma entregar.', 'CORO(final)\r\n|| ¡Heme aquí! ¡Heme aquí!\r\nTe digo, mi Señor;\r\nQuiero llenarme de tu amor;\r\nQuiero que seas mi Salvador ||', '', '', '', '', '', '', '', '', 4),
(125, '125\r\nLLAMA CRISTO', '1\r\n¡Tantas ovejas sin un pastor!\r\nPor el desierto errantes van;\r\nClaman y un día escucharán,\r\nLa voz del tierno y buen Pastor.', 'CORO\r\n¡Llama Cristo!\r\n¡Hoy a tu puerta está!\r\nNo tardes tanto, y abre ya,\r\nAtiende a su dulce voz.', '2\r\nA su rebaño, Él, guardará\r\nLejos del cruel engañador,\r\nY en el aprisco, con gran amor,\r\nA cada cual sustentará.', 'CORO\r\n¡Llama Cristo!\r\n¡Hoy a tu puerta está!\r\nNo tardes tanto, y abre ya,\r\nAtiende a su dulce voz.', '3\r\nA las perdidas, buscando está;\r\nCon gran amor las curará;\r\nSi están sedientas, las llevará\r\nA su divino manantial.', 'CORO(final)\r\n¡Llama Cristo!\r\n¡Hoy a tu puerta está!\r\nNo tardes tanto, y abre ya,\r\nAtiende a su dulce voz.', '', '', '', '', '', '', 6),
(126, '126\r\nLLAMA HOY', '1\r\nDe una o dos maneras, nos habla el Señor,\r\nQueriendo rescatarnos, del mundo a su redil;\r\nMas por dureza nuestra, evitamos el oír,\r\nEl tierno llamamiento del buen Pastor.', 'CORO\r\n¡Llama hoy! ¡Sí! ¡Llama hoy!\r\n¡No endurezcas ya más tu corazón!\r\n¡Sí, llama hoy! Hoy dile ya:\r\n“Yo dejo el mundo, por Ti Señor”.', '2\r\nSi en senda de amargura caminas y sin paz,\r\nNo dudes ni un momento, ¡Jesús contigo está!\r\nÉl quiere ser tu apoyo en esa lucha hostil,\r\n¡Que libras entre tu alma y el mundo vil!', 'CORO\r\n¡Llama hoy! ¡Sí! ¡Llama hoy!\r\n¡No endurezcas ya más tu corazón!\r\n¡Sí, llama hoy! Hoy dile ya:\r\n“Yo dejo el mundo, por Ti Señor”.', '3\r\nEscrito está en la Biblia, que luchas siempre habrá;\r\nEspíritu y carne batallan sin cesar.\r\nApóyate en Cristo, si en pruebas tú estás;\r\nDe todas, ¡Vencedor resultarás!', 'CORO(final)\r\n¡Llama hoy! ¡Sí! ¡Llama hoy!\r\n¡No endurezcas ya más tu corazón!\r\n¡Sí, llama hoy! Hoy dile ya:\r\n“Yo dejo el mundo, por Ti Señor”.', '', '', '', '', '', '', 6),
(127, '127\r\nLOS ÚLTIMOS TIEMPOS', '1\r\nSon los últimos tiempos,\r\nNo tardes hermano, busca a Dios.\r\nSi tú te arrepientes y le aceptas ya,\r\nEl Señor tu vida cambiará.', 'Coro\r\nSi Cristo hoy viniera, ¿Que le responderás?\r\n¿Del por qué hubo dudas en tu ser?\r\nEntrégate a Cristo y Él te salvará,\r\nCon Él vida eterna gozaras.', '2\r\nDeja el mundo, que el juicio\r\nDe Dios, sobre éste, llegará.\r\nEl tiempo se acaba, no lo pienses más,\r\nQue Jesús muy pronto volverá.', 'Coro\r\nSi Cristo hoy viniera, ¿Que le responderás?\r\n¿Del por qué hubo dudas en tu ser?\r\nEntrégate a Cristo y Él te salvará,\r\nCon Él vida eterna gozaras.', '3\r\nSon los últimos tiempos,\r\nY tú aun pensando si está bien\r\nQue dejes por Cristo tu vida anterior,\r\nLos placeres que son vanidad.', 'Coro(final)\r\nSi Cristo hoy viniera, ¿Que le responderás?\r\n¿Del por qué hubo dudas en tu ser?\r\nEntrégate a Cristo y Él te salvará,\r\nCon Él vida eterna gozaras.', '', '', '', '', '', '', 6),
(128, '128\r\nPOR TI Y POR MI', '1\r\nEn la cruz del Calvario,\r\nEl Señor murió\r\nPara darnos salud\r\nY también redención.', '2\r\nNo te ofrece una vida\r\nPasajera y fugaz;\r\nÉl te ofrece vivir\r\nToda la eternidad.', '3\r\n¡Sólo acéptalo hoy!\r\n¡Ya no lo dudes más!\r\nY así sentirás\r\nSu poder, en ti, obrar.', '4(final)\r\nEn la cruz, Él murió,\r\nPor ti y por mí.\r\nSi lo recibes hoy,\r\nGozarás de su amor.', '', '', '', '', '', '', '', '', 4),
(129, '129\r\nSI TE CONVIRTIERES', '1\r\nSi te convirtieres: sus brazos te abrirá.\r\n¡Él sólo quiere tu felicidad!\r\nDeja que Dios te llene de amor.\r\nHoy es el día ideal, ¡Quizá el mañana no llegará!', 'CORO\r\n¡Qué bello sería oír su voz!\r\nEscuchar tu nombre por nuestro Dios.\r\n¡Ven! y acepta a Cristo, para que sea así.\r\nSi a Él, no vienes tú, no habrá más nadie a quien puedas ir.', '2\r\nSi te convirtieres: su amor te guardará.\r\n¡Y deja a Cristo que te de su paz!\r\nTan solo Él te libertará.\r\nSi le recibes hoy, sigue el camino que Él mostró.', 'CORO\r\n¡Qué bello sería oír su voz!\r\nEscuchar tu nombre por nuestro Dios.\r\n¡Ven! y acepta a Cristo, para que sea así.\r\nSi a Él, no vienes tú, no habrá más nadie a quien puedas ir.', '3\r\nSi te convirtieres: un nuevo ser serás.\r\nEntrega a Cristo, hoy, tu voluntad.\r\nTu corazón, quiere Él, llenar.\r\n¡Escucha su llamar! ¡Deja que Dios te transforme hoy!', 'CORO(final)\r\n¡Qué bello sería oír su voz!\r\nEscuchar tu nombre por nuestro Dios.\r\n¡Ven! y acepta a Cristo, para que sea así.\r\nSi a Él, no vienes tú, no habrá más nadie a quien puedas ir.', '', '', '', '', '', '', 6),
(130, '130\r\nSUFRIÓ POR TI, SUFRIÓ POR MÍ', '1\r\n¡Cuánto sufrió el Señor Jesús!\r\n¡Cuánto dolor Él soportó!\r\n¡Cuánta tristeza en su mirar!\r\nCuando Pedro lo negó.', 'CORO\r\nSufrió por ti, sufrió por mí.\r\nSufrió por darnos salvación.\r\nNo rechacemos al Señor.\r\nValoremos su amor.', '2\r\nCuál reo llevado a prisión,\r\nMuchos azotes recibió,\r\nSin merecerlos. Soportó\r\nBurlas. Insultos escuchó.', 'CORO\r\nSufrió por ti, sufrió por mí.\r\nSufrió por darnos salvación.\r\nNo rechacemos al Señor.\r\nValoremos su amor.', '3\r\nEl sacrificio de Jesús,\r\nNo lo rechaces pecador.\r\n¡Acéptalo!, no dudes más.\r\nCon gran amor te llama, ya.', 'CORO(final)\r\nSufrió por ti, sufrió por mí.\r\nSufrió por darnos salvación.\r\nNo rechacemos al Señor.\r\nValoremos su amor.', '', '', '', '', '', '', 6),
(131, '131\r\nSU GRACIA Y SU PERDÓN', '1\r\nAntes de que el tiempo se acabe,\r\nAcepta a Cristo, el Salvador.\r\nDeja que su amor te envuelva.\r\n¡Entrégale tu corazón!', 'CORO\r\nNo tardes más y acércate a Cristo;\r\nPor ti, paciente, espera hoy.\r\nQuiere brindarte sus beneficios:\r\nGracia hallarás y su perdón.', '2\r\nOlvida lo que el mundo ofrece;\r\n¡Renuncia a la vanidad!\r\nSepulta hoy al “viejo hombre”.\r\nEn Cristo, un nuevo ser, serás.', 'CORO\r\nNo tardes más y acércate a Cristo;\r\nPor ti, paciente, espera hoy.\r\nQuiere brindarte sus beneficios:\r\nGracia hallarás y su perdón.', '3\r\nY cuando hayas confirmado\r\nQue Cristo es tu Salvador,\r\nAnuncia que eres redimido;\r\nQue, en Él, está la salvación.', 'CORO(final)\r\nNo tardes más y acércate a Cristo;\r\nPor ti, paciente, espera hoy.\r\nQuiere brindarte sus beneficios:\r\nGracia hallarás y su perdón.', '', '', '', '', '', '', 6),
(132, '132\r\nTOMA TU CRUZ Y SÍGUEME', '1\r\n“Toma tu cruz y sígueme”,\r\nTe dice tu Salvador.\r\nCierra los ojos y dile:\r\nA mi ven, ¡Oh Señor!', '(Coro)\r\nQuiero el “Pan de la vida”.\r\n¡Quiero vivir con Jesús!\r\nQuiero ser alma escogida.\r\nQuiero servirle y ser luz.', '2\r\n“Toma tu cruz y sígueme”,\r\n“Y toma agua de Mí;\r\n“Porque con ella redime,\r\nMi Padre, tu existir”.', '(Coro)\r\nQuiero el “Pan de la vida”.\r\n¡Quiero vivir con Jesús!\r\nQuiero ser alma escogida.\r\nQuiero servirle y ser luz.', '3\r\n“Toma tu cruz y sígueme”.\r\nY a todos alza tu voz.\r\nDales el pan con que limpie,\r\nEl hombre su error.', '(Coro)\r\nQuiero el “Pan de la vida”.\r\n¡Quiero vivir con Jesús!\r\nQuiero ser alma escogida.\r\nQuiero servirle y ser luz.', '4\r\n“Toma tu cruz y sígueme”,\r\n“Vida y camino Yo Soy”.\r\nQue el mundo por Mi recibe”,\r\n“De Dios veraz perdón”.', '(Coro)(final)\r\nQuiero el “Pan de la vida”.\r\n¡Quiero vivir con Jesús!\r\nQuiero ser alma escogida.\r\nQuiero servirle y ser luz.', '', '', '', '', 8),
(133, '133\r\nTÚ ME LLAMASTE', '1\r\nTú me llamaste, Yo mi nombre oí.\r\nTu mirar sentí, Tiernamente en mí.\r\nTú me dijiste: “Yo te ayudaré”,\r\n“Deja de sufrir”, “Vive para mí.”', '2\r\n“Yo te elegí antes de nacer”;\r\n“Mi hijo serás, Tu Dios yo seré”, \r\n“Quiero tu vida, y quiero tu paz”,\r\n“Te deseo colmar de felicidad”.', '3(final)\r\n“Déjame guiarte Y morar en ti”.\r\n“Transformar tu ser, Llenarlo de luz”.\r\n“No me deseches, No te apartes más”,\r\n“Ven y sírveme, Escucha mi voz”.', '', '', '', '', '', '', '', '', '', 3),
(134, '134 \r\nVEN A ÉL', '1\r\nEscucha hoy la voz de Dios.\r\n¡Escucha! ¡Ven a Él!\r\nTe llama hoy con tierno amor;\r\nNo le desprecies, ¡Ven!', 'CORO\r\n¡Oh ven! ¡Oh, oh, ven!\r\n¡La puerta abierta está!\r\n¡Oh ven! ¡Oh, oh ven!\r\nVida eternal tendrás.', '2\r\nAcepta, hoy, al Dios de amor;\r\n¡Serás feliz con Él!\r\nTu vida cambiará, desde hoy,\r\nSi vienes, tú, a Él.', 'CORO\r\n¡Oh ven! ¡Oh, oh, ven!\r\n¡La puerta abierta está!\r\n¡Oh ven! ¡Oh, oh ven!\r\nVida eternal tendrás.', '3\r\nNueva criatura, tú, serás.\r\n¡Ya no lo dudes más!\r\nSu redención te otorgará.\r\n¡Síguele en santidad!', 'CORO(final)\r\n¡Oh ven! ¡Oh, oh, ven!\r\n¡La puerta abierta está!\r\n¡Oh ven! ¡Oh, oh ven!\r\nVida eternal tendrás.', '', '', '', '', '', '', 6),
(135, '135\r\nA TI CLAMO', '1\r\nA Ti clamo ¡Oh, mi Dios!\r\nMi refugio eres Tú;\r\nMi esperanza y mi salvación.\r\nOye, atento, mi oración.', 'CORO\r\nEl clamor, Señor, de mi corazón,\r\nDeposito ante tu “altar”.\r\nHumillado estoy ¡Tenme compasión!\r\nPon en mí, tu voluntad.', '2\r\nA Ti elevo mi oración;\r\n¡Sé que, Tú, escucharás!\r\nPorque eres un Dios de amor.\r\n¡De tu gracia me darás!', 'CORO\r\nEl clamor, Señor, de mi corazón,\r\nDeposito ante tu “altar”.\r\nHumillado estoy ¡Tenme compasión!\r\nPon en mí, tu voluntad.', '3\r\nA Ti quiero expresar\r\nMi sincera gratitud.\r\nPor lo que, Señor, Tú a mi me das,\r\n¡Hoy te ensalzo, Dios de luz!\r\n', 'CORO(final)\r\nEl clamor, Señor, de mi corazón,\r\nDeposito ante tu “altar”.\r\nHumillado estoy ¡Tenme compasión!\r\nPon en mí, tu voluntad.', '', '', '', '', '', '', 6),
(136, '136\r\nANTE TI', '1\r\nCuando miro los cielos\r\nY contemplo, Señor, tu creación,\r\nMe pregunto: ¿Quién soy? ¡Oh Dios!\r\n¿Por qué de mí tienes compasión?', 'CORO\r\nY por eso es que estoy ante Ti,\r\nImplorando tu perdón.\r\nAnte Ti, vengo a agradecer\r\nTu compasión.', '2\r\nTen piedad. Sé propicio.\r\nA tus “atrios” hoy vengo a rogar:\r\nQue en tu gracia pueda encontrar,\r\nLa paz que sólo Tú puedes dar.', 'CORO\r\nY por eso es que estoy ante Ti,\r\nImplorando tu perdón.\r\nAnte Ti, vengo a agradecer\r\nTu compasión.', '3\r\nSé muy bien que mis obras\r\nNo son rectas, divino Señor.\r\nGuíame a la salvación,\r\nPorque Tú eres perfecto amor.', 'CORO(final)\r\nY por eso es que estoy ante Ti,\r\nImplorando tu perdón.\r\nAnte Ti, vengo a agradecer\r\nTu compasión.', '', '', '', '', '', '', 6),
(137, '137\r\nDIOS MÍO VENGO A TI', '1\r\nDios mío, una vez más\r\nVengo a renovar\r\nMis votos ante Ti;\r\n¡Lléname de paz!', '1\r\nLléname de paz sublime...\r\nPon en mí tu faz\r\nY haz que el corazón encuentre,\r\nEl dulce solaz.', '2\r\nDios mío, vengo a Ti;\r\nBusco tu perdón.\r\n¿Tu gracia encontraré,\r\nTierno Padre Dios?', '2\r\nHallar gracia ante tus ojos,\r\nEs mi petición.\r\nQuita el mal que anida y daña,\r\nA mi corazón.', '3\r\nDios mío, una vez más,\r\nClamo tu perdón\r\nY busco tu bondad\r\nPara mi dolor.', '3(final)\r\nEl dolor que abate el alma,\r\nEs mi aflicción.\r\n¡Ven y dame el presto auxilio!\r\nPor tu Hijo, ¡Oh, Dios!', '', '', '', '', '', '', 6),
(138, '138\r\nDULCES MOMENTOS DISFRUTO', '1\r\nDulces momentos disfruto en paz,\r\nEn unión, en oración...\r\n¡A solas con Dios!\r\nToda angustia, de mí, se irá.\r\nMi fe, en Él, puesta está.\r\n¡Sé bien que escuchará!', '2\r\nEn los problemas me ayudará.\r\n¡Me dará la solución!\r\n¡No me dejará!\r\nLo que me falte, Él, suplirá\r\nY conforme a su bondad,\r\nMe proveerá mi Dios.', '3(final)\r\nDulces momentos disfruto en paz.\r\nA su “altar” me acercaré.\r\n¡Le agradeceré!\r\nEstos momentos de gran solaz\r\nCon paciencia buscaré. En Dios los hallaré.', '', '', '', '', '', '', '', '', '', 3),
(139, '139\r\nEL MUNDO NO ES PARA MÍ', '1\r\n¡Oh, mi Señor!, elevo a Ti\r\nMi oración, que es mi sentir.\r\nIgnora lo malo que hay\r\nEn mí y me impide, a Ti, llegar.', 'CORO\r\nEl mundo, ¡No es para mí!\r\nY no deseo, a él regresar.\r\nGuía mi mente y caminar,\r\nY que, en mí, reine bienestar.', '2\r\nPerdido anduve, ¡Oh, Señor!,\r\nPero tu gracia me alcanzó.\r\nCerca de Ti, deseo estar.\r\nEn Ti, mis cargas, descansar.', 'CORO\r\nEl mundo, ¡No es para mí!\r\nY no deseo, a él regresar.\r\nGuía mi mente y caminar,\r\nY que, en mí, reine bienestar.', '3\r\nAhora que, de Ti, encontré\r\nLa paz que tanto, yo, busqué;\r\nConfiado voy, pues sé que Tú,\r\n¡Me guardarás de no caer!', 'CORO(final)\r\nEl mundo, ¡No es para mí!\r\nY no deseo, a él regresar.\r\nGuía mi mente y caminar,\r\nY que, en mí, reine bienestar.', '', '', '', '', '', '', 6),
(140, '140\r\nFORTALECE MI ESPÍRITU', '1\r\nEn momentos de temor,\r\nNecesito de Ti, Señor.\r\nEn momentos de dolor:\r\n¡Da sostén con tu amor!', 'CORO\r\nPorque sin Ti, eterno Dios,\r\nEs vacío mi existir... ¡Sin fe!\r\n¡Fortalece mi espíritu y mi ser!\r\n¡Ayúdame Señor y venceré!', '2\r\nEn angustia y soledad,\r\nTu Presencia me ayudará.\r\n¡Guíame con tu bondad!\r\nMi alma... paz encontrará.', 'CORO\r\nPorque sin Ti, eterno Dios,\r\nEs vacío mi existir... ¡Sin fe!\r\n¡Fortalece mi espíritu y mi ser!\r\n¡Ayúdame Señor y venceré!', '3\r\nEn momentos de aflicción,\r\nOyes, Tú, mi clamor... mi voz;\r\nAtiende a mi oración.\r\n¡Oh, excelso Dios de amor!', 'CORO FINAL\r\nPorque sin Ti, eterno Dios,\r\nEs vacío mi existir... ¡Sin fe!\r\n¡Fortalece mi espíritu y mi ser!\r\n//¡Ayúdame Señor y venceré!//.', '', '', '', '', '', '', 6),
(141, '141 \r\nHALLAR GRACIA', '1\r\nCuando se encuentra el alma\r\nEn angustia, ¡Clamo a Dios!\r\nSé que Él da la fortaleza,\r\nAl cansado corazón.', 'CORO\r\nHallar gracia, ante tus ojos,\r\nEs mi humilde petición;\r\nQue mi vida halle gracia,\r\nAnte Ti, Señor mi Dios.', '2\r\nEn las penas y las pruebas,\r\nMe humillo ante Ti;\r\nPorque el mal hastía mi alma,\r\n¡Dios, apiádate de mí!', 'CORO\r\nHallar gracia, ante tus ojos,\r\nEs mi humilde petición;\r\nQue mi vida halle gracia,\r\nAnte Ti, Señor mi Dios.', '3\r\n¡Oh Jesús, Pastor Divino!\r\nLleva al Padre mi oración.\r\nSálvame del mar profundo,\r\nPorque perezco, Señor.', 'CORO(final)\r\nHallar gracia, ante tus ojos,\r\nEs mi humilde petición;\r\nQue mi vida halle gracia,\r\nAnte Ti, Señor mi Dios.', '', '', '', '', '', '', 6),
(142, '142\r\nHOY VENGO A TI', '1\r\nHoy vengo a Ti, mi Dios, implorando perdón...\r\nArrepentido estoy, ¡De todo corazón!...\r\n¡Es grande mi maldad!', '1\r\n...La puedes, Tú, borrar....\r\nTu bondad puede hacer,\r\nAl pecador volver.', 'CORO\r\nSi quieres, mi buen Dios,\r\nMi vida limpiar, hoy.\r\nYa no puedo esperar\r\n¡Ayúdame a cambiar!', '2\r\nYa no quiero vivir, si estás lejos de mí.\r\n¡Ayúdame!, mi Dios, a corregir mi error.\r\nTe doy mi vida, ¡Sí!', '2\r\nMe rindo a Ti, Señor,\r\nTe pertenezco, Dios;\r\nPerdona al pecador.', 'CORO(final)\r\nSi quieres, mi buen Dios,\r\nMi vida limpiar, hoy.\r\nYa no puedo esperar\r\n¡Ayúdame a cambiar!', '', '', '', '', '', '', 6),
(143, '143\r\nHUMILDE VENGO A TI', '1\r\nHoy mi Señor, vengo ante Ti.\r\nHoy mi Señor, te canto a Ti.\r\nY me presento con humildad...\r\nCon un corazón, ya limpio del mal.', 'CORO\r\nMi oración elevo y alzo mi voz;\r\nSé que no desprecias mi corazón,\r\nCon sinceridad y lealtad a Ti,\r\nHumilde y lleno de tu amor;\r\nEs lo que ofrezco en mi vivir.', '2\r\nPerdóname, soy pecador.\r\nVengo a tus pies, ¡Oh gran Señor!\r\nLimpia mi vida, con tu bondad\r\nY en nuevo camino pueda andar.', 'CORO\r\nMi oración elevo y alzo mi voz;\r\nSé que no desprecias mi corazón,\r\nCon sinceridad y lealtad a Ti,\r\nHumilde y lleno de tu amor;\r\nEs lo que ofrezco en mi vivir.', '3\r\n¡Ven mi Jesús! te ruego hoy.\r\n¡Regresa ya! mi Salvador,\r\nEs lo que anhelo; contigo estar:\r\nFeliz en tu reino, siempre morar.', 'CORO(final)\r\nMi oración elevo y alzo mi voz;\r\nSé que no desprecias mi corazón,\r\nCon sinceridad y lealtad a Ti,\r\nHumilde y lleno de tu amor;\r\nEs lo que ofrezco en mi vivir.', '', '', '', '', '', '', 6),
(144, '144\r\nLLEGADA LA ORACIÓN', '1\r\nHa llegado el momento\r\nDe elevar nuestra oración;\r\nDando gracias por la vida\r\nY tu grande bendición.', 'CORO\r\n¡Oh Señor!, hoy te pedimos\r\nQue escuches nuestra voz;\r\nNuestras faltas sean disueltas,\r\n¡Santo Dios! perdonador.', '2\r\nCada uno de tus hijos,\r\nEn tu “altar” se postrará,\r\nDe rodillas, humillado...\r\n¡A tu Nombre clamará!', 'CORO\r\n¡Oh Señor!, hoy te pedimos\r\nQue escuches nuestra voz;\r\nNuestras faltas sean disueltas,\r\n¡Santo Dios! perdonador.', '3\r\nPruebas miles y aflicciones,\r\nNos rodean sin piedad...\r\nPero, Tú, eres nuestro amparo.\r\n¡Nuestras cargas quitarás!', 'CORO\r\n¡Oh Señor!, hoy te pedimos\r\nQue escuches nuestra voz;\r\nNuestras faltas sean disueltas,\r\n¡Santo Dios! perdonador.', '4\r\nAcercamos nuestras almas,\r\n¡Oh Creador!, Fuente de Amor.\r\nQuitarás nuestras angustias\r\nCon tu amor, Eterno Dios.', 'CORO(final)\r\n¡Oh Señor!, hoy te pedimos\r\nQue escuches nuestra voz;\r\nNuestras faltas sean disueltas,\r\n¡Santo Dios! perdonador.', '', '', '', '', 8),
(145, '145\r\nMI ALFARERO ERES TÚ', '1\r\nAnte Ti estoy oh Dios\r\nSuplicando tu perdón\r\nQuiero darte mi loor\r\nAnte Ti postrado estoy\r\nHumillando el corazón\r\nY pidiendo tu amor.', 'CORO\r\nHas en mi tu voluntad,\r\nHas de mi un nuevo ser,\r\nMi alfarero eres Tú,\r\nY el barro yo soy.', '2\r\nA tus pies estoy Señor,\r\nImplorando compasión.\r\nQuiero darte lo mejor,\r\nToma ya mi corazón,\r\nY enséñale mí Dios,\r\nDe tu ser el amor.', 'CORO\r\nHas en mi tu voluntad,\r\nHas de mi un nuevo ser,\r\nMi alfarero eres Tú,\r\nY el barro yo soy.', '3\r\nHumillado en oración,\r\nQuiero yo vivir tu amor.\r\nNo te alejes por favor,\r\nGuíame en santidad,\r\nY así poder mirar,\r\nTu fiel rostro brillar.', 'CORO(final)\r\nHas en mi tu voluntad,\r\nHas de mi un nuevo ser,\r\nMi alfarero eres Tú,\r\nY el barro yo soy.', '', '', '', '', '', '', 6),
(146, '146\r\nNO TARDES MÁS', '1\r\nMe acerco a tu altar, humillado ante Ti, Señor...\r\n¡Cansado de andar!... lleno de dolor y ésta soledad...\r\nSé que sólo en Ti, me refugiaré con seguridad.', '1\r\n¡Sé mi Roca fiel! Por piedad, Señor, dame tu amor.\r\nPon tu mano en mí, ¡Haz que sienta, yo, tu poder!\r\n¡Ansioso estoy!', 'CORO\r\n¡Ven Señor Jesús!, derrama en mi ser, tu espíritu.\r\nYo espero en Ti, ¡No tardes más!\r\nLimpia de mi ser, toda iniquidad: ¡Quiero ver tu faz!...\r\nYo te esperaré. ¡No dudaré! ¡Ven Señor!', '2\r\n¡Cuánta ausencia hay! de esa Santidad que enseñaste Tú.\r\nPor eso Señor, ¡Me esforzaré! ¡Sé que me usarás!\r\nLa humanidad necesita, quien muestre tu paz;', '2\r\nYo seré un canal que lleve verdad por tu espíritu.\r\n¡Guíame Señor! ¡Muéstrame hacia dónde iré!\r\n¡No me negaré!', 'CORO(final)\r\n//¡Ven Señor Jesús!, derrama en mi ser, tu espíritu.\r\nYo espero en Ti, ¡No tardes más!\r\nLimpia de mi ser, toda iniquidad: ¡Quiero ver tu faz!...\r\nYo te esperaré. ¡No dudaré! ¡Ven Señor!//', '', '', '', '', '', '', 6),
(147, '147\r\nNUEVO SER', '1\r\n¡Renueva mi ser!\r\n¡Transforma mi corazón!\r\n¡Cambia mis pensamientos! ¡Oh Señor!', 'CORO\r\n¡Porque quiero cambiar!\r\n¡Quiero dejar el mal!\r\nSer en Ti, un nuevo ser.\r\n¡Porque quiero cambiar!\r\nQuiero servirte fiel\r\nY hacer tu voluntad.', '2\r\nCansado estoy...\r\n¡Mis cargas quiero dejar!\r\nSé que solo en Ti, puedo descansar.', 'CORO\r\n¡Porque quiero cambiar!\r\n¡Quiero dejar el mal!\r\nSer en Ti, un nuevo ser.\r\n¡Porque quiero cambiar!\r\nQuiero servirte fiel\r\nY hacer tu voluntad.', '3\r\nCon tu gran poder,\r\nUn nuevo hombre seré.\r\nSolo a Ti, ¡Mi alabanza ofreceré!', 'CORO(final)\r\n¡Porque quiero cambiar!\r\n¡Quiero dejar el mal!\r\nSer en Ti, un nuevo ser.\r\n¡Porque quiero cambiar!\r\nQuiero servirte fiel\r\nY hacer tu voluntad.', '', '', '', '', '', '', 6),
(148, '148\r\n¡OH, SEÑOR!', '1\r\n¡Oh Señor!, que moras en el cielo.\r\n¡Oh mi Dios!, ¡Escucha mi oración!\r\nAngustiado, delante de Ti, vengo;\r\nTraigo a Ti, mi humilde corazón.', 'CORO\r\nToma mi corazón, ¡Oh, mi Dios bendito!\r\nLímpialo con tu amor. ¡Oh Dios hazlo tuyo!\r\nNada soy sin Ti, lo reconozco.\r\n¡Oh Señor!\r\nLimpia mi corazón.', '2\r\n¡Oh Señor, bendito sea tu Nombre!\r\n¡Digno eres de adoración!\r\nTen de mi compasión..., misericordia...\r\n¡Oh Señor, te doy mi corazón!', 'CORO\r\nToma mi corazón, ¡Oh, mi Dios bendito!\r\nLímpialo con tu amor. ¡Oh Dios hazlo tuyo!\r\nNada soy sin Ti, lo reconozco.\r\n¡Oh Señor!\r\nLimpia mi corazón.', '3\r\n¡Oh Señor!, es tuya mi existencia.\r\n...Vengo a Ti, pidiéndote perdón.\r\nDe rodillas ante Ti me acerco\r\n¡Oh Señor! no veas mi rebelión.', 'CORO(final)\r\nToma mi corazón, ¡Oh, mi Dios bendito!\r\nLímpialo con tu amor. ¡Oh Dios hazlo tuyo!\r\nNada soy sin Ti, lo reconozco.\r\n¡Oh Señor!\r\nLimpia mi corazón.', '', '', '', '', '', '', 6),
(149, '149\r\nPADRE, A TI CLAMO', '1\r\nPadre: ¿Qué te puedo, decir?\r\nConoces mi andar y sé que quizá no soy,\r\nLo que esperas de mí, Señor...\r\nPero sé, que estás cerca de mí.', '2\r\nPadre, ¡Apiádate de mí!\r\nNo me dejes sentir que soy,\r\nUn bocado tibio, que un día desecharás;\r\nHazme un nuevo ser. ¡Quiero cambiar!', '3\r\nPadre, rodeado estoy del mal...\r\nQue nubla mi visión, sin ser capaz de ocultar\r\nTu Presencia bendita;\r\nQue da a mi ser, ¡Mucho valor!', '4(final)\r\nPadre, no quisiera morir,\r\nSin saber que mi vida está en tus manos, Dios,\r\nY que nada me faltará.\r\nTú estarás en mí, hasta el final.', '', '', '', '', '', '', '', '', 4),
(150, '150\r\nPERDÓN TE PIDO', '1\r\nMi corazón cansado está;\r\nMi pensamiento turbio es,\r\nEn mi presencia Dios no está,\r\nPorque en mi vida hago mal.', 'CORO\r\nRogando a Ti, Señor, mi Dios:\r\nQue limpies toda mi maldad.\r\nSufro porque sé que hice mal,\r\nAsí no quiero continuar.', '2\r\nPerdón te pido, ¡Oh Señor!\r\nPorque mi vida mal usé.\r\nLa gracia que me dio tu amor\r\nCon rebelión, la malgasté.', 'CORO\r\nRogando a Ti, Señor, mi Dios:\r\nQue limpies toda mi maldad.\r\nSufro porque sé que hice mal,\r\nAsí no quiero continuar.', '3\r\nMe humillo a Ti con mi dolor,\r\nPara que vivas en mi ser,\r\nPara que así, con tu amor,\r\nDe aquí, por siempre, serte fiel.', 'CORO(final)\r\nRogando a Ti, Señor, mi Dios:\r\nQue limpies toda mi maldad.\r\nSufro porque sé que hice mal,\r\nAsí no quiero continuar.', '', '', '', '', '', '', 6),
(151, '151\r\nPIDIÉNDOTE EN ORACIÓN', '1\r\nSeñor, con toda humildad,\r\nNos presentamos ante Ti.\r\nTe imploramos, gran Señor,\r\nEscuches nuestra voz.', 'CORO\r\nPidiéndote en oración,\r\nTu oído escuche el clamor.\r\nPor tu misericordia, ¡Oh Dios!\r\nNos des tu bendición.', '2\r\nHoy que tu Iglesia unida está,\r\nTrayendo ofrendas a tu “altar”,\r\nNo dejes de escuchar, Señor,\r\nNuestra necesidad.', 'CORO\r\nPidiéndote en oración,\r\nTu oído escuche el clamor.\r\nPor tu misericordia, ¡Oh Dios!\r\nNos des tu bendición.', '3\r\nDios de los cielos, Tú darás\r\nRespuesta a toda petición.\r\nRecibe nuestra gratitud,\r\nEterno Dios de amor.', 'CORO(final)\r\nPidiéndote en oración,\r\nTu oído escuche el clamor.\r\nPor tu misericordia, ¡Oh Dios!\r\nNos des tu bendición.', '', '', '', '', '', '', 6),
(152, '152\r\nPOSTRADO ANTE TI', '1\r\nSeñor mi Dios, postrado ante Ti,\r\nTe digo hoy, que yo te olvidé.\r\nMi vida es oscura, cual noche, sin luz.', '1\r\nEres bondad, justicia y amor.\r\nCon fe mi Dios, al fin te encontré,\r\nMás necio fui y en negro pecado viví.', '2\r\nCerca de Ti yo quiero vivir.\r\nTu santa Ley, siempre obedecer.\r\nTu gracia en mí podrá mi alma fortalecer.', '2(final)\r\nTe pido hoy, me escuches Señor:\r\nQuiero de Ti, tener tu perdón.\r\nPodré vivir si no quitas tu espíritu, de mí.', '', '', '', '', '', '', '', '', 4),
(153, '153\r\nPOSTRADO DE RODILLAS', '1\r\nAnte Ti, postrado de rodillas,\r\nPido hoy, clemencia ¡Oh mi Dios!\r\nSé que no he seguido lo recto;\r\nPor piedad, escúchame.', 'Coro\r\n¡Oh, Señor! mi vida está en tus manos,\r\nNada a Ti se puede esconder.\r\nReconozco que he fallado;\r\nPor tu amor, perdóname.', '2\r\nMi alma en Ti, confía plenamente,\r\nY en tu amor, tu gracia extenderás.\r\nQuiero hoy despojarme y ser libre,\r\nDe maldad y vanidad.', 'Coro\r\n¡Oh, Señor! mi vida está en tus manos,\r\nNada a Ti se puede esconder.\r\nReconozco que he fallado;\r\nPor tu amor, perdóname.', '3\r\nY conforme a tu misericordia,\r\nHalle gracia ante Ti, Señor.\r\nQue atrás queden mis rebeliones;\r\nLimpia Tú, mi corazón.', 'Coro(final)\r\n¡Oh, Señor! mi vida está en tus manos,\r\nNada a Ti se puede esconder.\r\nReconozco que he fallado;\r\nPor tu amor, perdóname.', '', '', '', '', '', '', 6),
(154, '154\r\nQUIERO IR EN POS DE TI', '1\r\nMe acerco sólo a Ti, ¡Oh Dios!\r\nYo de tu mano quiero ir...\r\nSi mi vida está en aflicción,\r\nTú me confortas con amor.', 'CORO\r\nQuiero ir en pos de Ti...\r\n¡Alcanzar la salvación!\r\nY más cerca estar de Ti,\r\nMi mano toma, ¡Oh Señor!', '2\r\nMe humillo ante Ti, Señor,\r\nPorque conozco tu poder.\r\nSólo Tú disipas mi dolor\r\nY me reprendes con amor.', 'CORO\r\nQuiero ir en pos de Ti...\r\n¡Alcanzar la salvación!\r\nY más cerca estar de Ti,\r\nMi mano toma, ¡Oh Señor!', '3\r\nMe presento ante Ti, Señor,\r\nCon gran fervor en oración.\r\nTu misericordia pido hoy...\r\nDe mí, ten grande compasión.', 'CORO(final)\r\nQuiero ir en pos de Ti...\r\n¡Alcanzar la salvación!\r\nY más cerca estar de Ti,\r\nMi mano toma, ¡Oh Señor!', '', '', '', '', '', '', 6),
(155, '155\r\nROCA DE SALVACIÓN', '1\r\nSeñor, Tú eres mi esperanza,\r\nPues no hay alguien como Tú.\r\nHoy postrado, en tu “altar”,\r\nRuego compasión.', 'CORO\r\n¡Líbrame! ¡Rescátame!\r\nSé propicio a mí.\r\n¡Sálvame! ¡Escúchame!\r\nMi roca de salud.', '2\r\nSeñor, Tú eres justo y santo.\r\nAmoroso das perdón.\r\nLímpiame de mi maldad.\r\n¡Dame bendición!', 'CORO\r\n¡Líbrame! ¡Rescátame!\r\nSé propicio a mí.\r\n¡Sálvame! ¡Escúchame!\r\nMi roca de salud.', '3\r\nSeñor, Tú eres pronto auxilio.\r\nNadie puede consolar,\r\nNi mostrar tu gran piedad.\r\nEn Ti está la paz.', 'CORO(final)\r\n¡Líbrame! ¡Rescátame!\r\nSé propicio a mí.\r\n¡Sálvame! ¡Escúchame!\r\nMi roca de salud.', '', '', '', '', '', '', 6),
(156, '156\r\nSE ACERCA EL MOMENTO', '1\r\nSe acerca el momento de la oración.\r\nAcércate presto al sublime don.\r\nAl Creador eleva, con fe y devoción,\r\nLo que tu alma esconde: toda tu ansiedad.\r\n¡Dios escuchará!', '2\r\nEl dulce momento de la oración,\r\nQue el Señor ha dado, trae bendición.\r\nEs el sacrificio de la humillación,\r\nEl que el Padre espera, del fiel servidor.\r\n¡A Dios el honor!', '3(final)\r\nPadre, a Ti clamamos, sé clemente ¡Oh Dios!\r\nEn el Nombre Santo, del buen Salvador.\r\nTu misericordia nos alcanzará.\r\n“Alma, aún espera, el Señor hará”.\r\n¡Dios responderá!', '', '', '', '', '', '', '', '', '', 3),
(157, '157 \r\nSÉ MI COLIRIO', '1\r\nEstoy ante Ti, Dios...\r\nLimpio quiero ser, Señor.\r\nMi camino sucio está...', '1\r\n¡Límpiame y ten de mí piedad!\r\nMe olvidé de andar por tu senda, que es verdad.\r\nCreí no necesitar tu Presencia, y me aparté.', 'CORO\r\nMis ojos están cansados, de siempre mirar al mal...\r\nA Ti vengo de hinojos y pido, hoy, tu perdón.\r\nSé mi Colirio ¡Limpia todo mi ser!\r\nEn Ti confío, ¡Deseo estar junto a Ti!', '2\r\nLo inmenso de tu amor,\r\nNo entendía mi razón.\r\nSe nubló mi corazón... mi enemigo, era yo, Señor.', '2\r\n¿He de justificar mi manera de actuar?\r\nMe alejaba más y más.\r\nDios, ¡Me hiciste recapacitar!', 'CORO\r\nMis ojos están cansados, de siempre mirar al mal...\r\nA Ti vengo de hinojos y pido, hoy, tu perdón.\r\nSé mi Colirio ¡Limpia todo mi ser!\r\nEn Ti confío, ¡Deseo estar junto a Ti!', '3\r\n¡He vuelto, hoy, a Ti!\r\n¿Mi pecado quitarás?\r\n¡Cómo desearía escuchar!:', '3\r\n“Vete en paz, no vuelvas a pecar”.\r\nHe comprendido al fin tu secreto... ¡El amar!\r\nNo me alejaré de Ti. ¡Sin Ti, nunca quiero ya vivir!', 'CORO(final)\r\nMis ojos están cansados, de siempre mirar al mal...\r\nA Ti vengo de hinojos y pido, hoy, tu perdón.\r\nSé mi Colirio ¡Limpia todo mi ser!\r\nEn Ti confío, ¡Deseo estar junto a Ti!', '', '', '', 9),
(158, '158\r\nTEN GRAN PIEDAD', '1\r\n¡De la maldad quiero huir!\r\n¡No quiero! ¡No! faltarte más.\r\n¡Cansado estoy de ésta prisión!\r\nDame tu paz; ten compasión.', 'CORO\r\nTen gran piedad de mí, ¡Oh Dios!\r\nBorra el mal que hice ayer.\r\nCrea en mí, un corazón\r\nLimpio y sincero; a Ti, fiel.', '2\r\nMi carne y espíritu están,\r\nSiempre luchando, en mi interior.\r\nNo me abandones, ¡No, Señor!\r\nHaz que, yo, en Ti, pueda vencer.', 'CORO\r\nTen gran piedad de mí, ¡Oh Dios!\r\nBorra el mal que hice ayer.\r\nCrea en mí, un corazón\r\nLimpio y sincero; a Ti, fiel.', '3\r\nOye mi ruego, ¡Oh Señor!\r\nClamo a Ti con devoción.\r\nSi no es a Ti, ¿A quién iré?\r\nPor tu amor, perdóname.', 'CORO(final)\r\nTen gran piedad de mí, ¡Oh Dios!\r\nBorra el mal que hice ayer.\r\nCrea en mí, un corazón\r\nLimpio y sincero; a Ti, fiel.', '', '', '', '', '', '', 6),
(159, '159\r\nTEN MISERICORDIA DE MÍ', '1\r\nSI la tristeza invade mi ser,\r\nMe acerco a mi Salvador.\r\nLlenará mi alma de gozo y paz;\r\nConsuelo en Cristo podré hallar.', 'CORO\r\nTen misericordia de mí, Señor.\r\nTen misericordia de mí.\r\nPorque en Ti ha confiado mi alma,\r\nY en las “sombras de tus alas”, me acogerás.', '2\r\nDuras batallas contiende mi ser;\r\nNo temeré y con fe, diré:\r\n“AI sólo Dios vivo y fuerte, ¡Iré!\r\nY Él, desde el cielo, me escuchará”.', 'CORO\r\nTen misericordia de mí, Señor.\r\nTen misericordia de mí.\r\nPorque en Ti ha confiado mi alma,\r\nY en las “sombras de tus alas”, me acogerás.', '3\r\nSatán ataca, queriéndome herir...\r\n¡Estoy en las manos de Dios!\r\nY así de nada me preocuparé;\r\nÉl me resguarda de todo mal.', 'CORO(final)\r\nTen misericordia de mí, Señor.\r\nTen misericordia de mí.\r\nPorque en Ti ha confiado mi alma,\r\nY en las “sombras de tus alas”, me acogerás.', '', '', '', '', '', '', 6),
(160, '160 \r\nTE PIDO MISERICORDIA', '1\r\n¡Desperté desconsolado!...\r\nNo encontré la senda recta de tu amor.\r\n¡Mi alma está desamparada!...\r\nMe pregunto: ¿Cómo es que llegué hasta aquí?', 'CORO\r\nTen piedad de mí, ¡Te ruego!\r\nTal vez no signifique nada, ya para Ti...\r\n¡Mírame!, estoy postrado...\r\nYo te pido, tan sólo tu misericordia.', '2\r\nEl saber que estoy perdido,\r\nAniquila en mí, el deseo de vivir...\r\n¡Mi dolor es tan profundo!...\r\nHumillado de rodillas, ¡Vengo a Ti!', 'CORO\r\nTen piedad de mí, ¡Te ruego!\r\nTal vez no signifique nada, ya para Ti...\r\n¡Mírame!, estoy postrado...\r\nYo te pido, tan sólo tu misericordia.', '3\r\nMe atreví a suplicarte,\r\nPorque sé, que existe un puente hasta Ti;\r\n¡Ese puente es Jesucristo!\r\nEn Jesús, ¡Bendito Nombre! Vengo a Ti.', 'CORO(final)\r\nTen piedad de mí, ¡Te ruego!\r\nTal vez no signifique nada, ya para Ti...\r\n¡Mírame!, estoy postrado...\r\nYo te pido, tan sólo tu misericordia.', '', '', '', '', '', '', 6),
(161, '161\r\nVENGO A TI', '1\r\nVengo a Ti, ¡Oh Señor!,\r\nA pedirte el perdón,\r\nPorque mi alma está\r\nEn prisión de maldad.', 'CORO\r\nOye Tú mi oración,\r\nCompasivo Señor.\r\nClamo ante tu altar:\r\nQuita mi iniquidad.', '2\r\nRuego a Ti, Señor Dios,\r\nQue no veas mi error.\r\nQuiero ser siervo fiel,\r\n¡No volver a caer!', 'CORO\r\nOye Tú mi oración,\r\nCompasivo Señor.\r\nClamo ante tu altar:\r\nQuita mi iniquidad.', '3\r\nSé mi consolación\r\nEn la tribulación.\r\nNo quiero perecer.\r\nLíbrame con poder.', 'CORO\r\nOye Tú mi oración,\r\nCompasivo Señor.\r\nClamo ante tu altar:\r\nQuita mi iniquidad.', '4\r\nEres mi Redentor,\r\nTraigo a Ti mi dolor.\r\nNadie pudo sanar\r\nDe mi ser la maldad.', 'CORO(final)\r\nOye Tú mi oración,\r\nCompasivo Señor.\r\nClamo ante tu altar:\r\nQuita mi iniquidad.', '', '', '', '', 8),
(162, '162 \r\nA LA PUERTA', 'CORO\r\n//Si Cristo viniera hoy aquí,\r\nDime: ¿Tú qué harías? //', '1\r\n¿Querrías volver el tiempo atrás?...\r\n¡Sería ya muy tarde para arrepentirse!\r\nPues nunca quisiste escuchar\r\nLo que podía salvarte.', 'CORO\r\n//Si Cristo viniera hoy aquí,\r\nDime: ¿Tú qué harías? //', '2\r\nTe dice: “Estoy Yo, a la puerta,\r\nAbre tu corazón, en el quiero morar”.\r\n“Si abres, Yo cenaré contigo,\r\nTe colmaré de gracia”.', 'CORO\r\n//Si Cristo viniera hoy aquí,\r\nDime: ¿Tú qué harías? //', '3\r\nHermano, ¡Ya no lo pienses más!\r\nEntrégale a Cristo, tu ser, tus pensamientos.\r\nHoy tienes grande oportunidad:\r\n¡Ser libre del pecado!', 'CORO(final)\r\n//Si Cristo viniera hoy aquí,\r\nDime: ¿Tú qué harías? //', '', '', '', '', '', 7),
(163, '163\r\nAMA A TU PRÓJIMO COMO A TI MISMO', '1\r\nEn este mundo, donde hace falta amor;\r\nDonde es común, el encontrar sólo rencor...\r\nPorque sabemos que el amor\r\nEs una orden que Él nos dio,\r\nDe amarnos todos, hasta que Él\r\nVuelva otra vez.', 'CORO\r\nPorque el amar, es mucho más que tolerar;\r\nEs el saber que debes amar no solo a tu amigo.\r\nTienes que amar, porque Jesús así lo dijo:\r\n“A tu enemigo amarás como a ti mismo”.', '2\r\nEs más sencillo a un amigo darle amor;\r\nEs más difícil con aquel que da rencor.\r\nBendecirás y amarás\r\nA todo aquel que te hace mal;\r\nEs el ejemplo que Él dejó\r\nCuando murió.', 'CORO(final)\r\nPorque el amar, es mucho más que tolerar;\r\nEs el saber que debes amar no solo a tu amigo.\r\nTienes que amar, porque Jesús así lo dijo:\r\n“A tu enemigo amarás como a ti mismo”.', '', '', '', '', '', '', '', '', 4),
(164, '164\r\nBIENAVENTURADO', '1\r\nSerás bienaventurado\r\nCuando su senda camines;\r\nSi eres fuerte en la prueba,\r\nSi escuchas su voz de amor.', '1\r\nEn Él, irás tu confiado,\r\nSi hoy, como árbol te nutres\r\nDe sus corrientes de vida,\r\n¡Si lo amas de corazón!', 'CORO\r\nDios te dará la paciencia, ¡Él te dará fortaleza!\r\nNo es fácil este camino, pero no te dejará.\r\n¡Dios te dará la respuesta! ¡Debes tener esperanza!\r\nNo hay justo desamparado y Él, te prosperará.', '2\r\n¿Tú crees que no te responde?\r\nSu juicio tú no conoces.\r\nNo desanimes tú alma,\r\nJesús, más que tú sufrió...', '2\r\nÉl ve tus necesidades,\r\nConoce tus aflicciones...\r\nUn poco y llega la calma;\r\nSigue pidiendo al Señor...', 'CORO\r\nDios te dará la paciencia, ¡Él te dará fortaleza!\r\nNo es fácil este camino, pero no te dejará.\r\n¡Dios te dará la respuesta! ¡Debes tener esperanza!\r\nNo hay justo desamparado y Él, te prosperará.', '3\r\n¡Conoces de sus promesas!\r\n¡Debes de ellas convencerte!\r\n¡Ya no estás solo en el mundo!\r\nTe cubrirá con su amor.', '3\r\nSi estás en valle de sombras...,\r\nSi te amenaza la muerte...,\r\nNo temerás mal alguno,\r\n¡Porque Jehová es tu Pastor!', 'CORO(final)\r\nDios te dará la paciencia, ¡Él te dará fortaleza!\r\nNo es fácil este camino, pero no te dejará.\r\n¡Dios te dará la respuesta! ¡Debes tener esperanza!\r\nNo hay justo desamparado y Él, te prosperará.', '', '', '', 9),
(165, '165\r\n¡CLAMA A DIOS!', '1\r\nCuando las pruebas\r\nTe aflijan más y más,\r\nY sientas perdida la esperanza...\r\n¿No encuentras paz?\r\nSi en la vida te sientes desmayar,\r\nPor cardos y espinas que laceran tu voluntad:', 'CORO\r\n¡Clama a Dios! ¡Clama a Dios!\r\nY te guiará para vencer; confía sólo en Él.\r\n¡Clama a Dios! ¡Clama a Dios!\r\nÉl responderá tu petición. ¡Clama a Dios!', '2\r\nCuando las penas\r\nQuedaren muy atrás\r\nY tu alma rebose de quietud\r\nY gran solaz;\r\n¡Dirige presto, a Dios, tu corazón!:\r\nOfrece, amoroso, humillado,\r\nTu gratitud.', 'CORO(final)\r\n¡Clama a Dios! ¡Clama a Dios!\r\nY te guiará para vencer; confía sólo en Él.\r\n¡Clama a Dios! ¡Clama a Dios!\r\nÉl responderá tu petición. ¡Clama a Dios!', '', '', '', '', '', '', '', '', 4),
(166, '166\r\nCONGREGADOS EN UNO', '1\r\nUn Dios y Padre de todos,\r\nY Cristo, nuestro Mediador.\r\nQue todos, en Dios, seamos uno:\r\nEs la Voluntad del Señor.', 'CORO\r\n¡Congregados en uno!\r\nLa Iglesia, hoy, debe marchar.\r\nSirviéndole siempre unidos:\r\nA Cristo, ¡Velando! esperad.', '2\r\n¡Somos ministros de Cristo!\r\n¡En Él, la unidad conservad!\r\n¡Embajadores de su reino!\r\n¡La comunión siempre guardad!', 'CORO\r\n¡Congregados en uno!\r\nLa Iglesia, hoy, debe marchar.\r\nSirviéndole siempre unidos:\r\nA Cristo, ¡Velando! esperad.', '3\r\nEn obediencia a Cristo,\r\nSujetos debemos andar.\r\n¡Llegar al varón que es perfecto!\r\nLlevados por su Voluntad.', 'CORO\r\n¡Congregados en uno!\r\nLa Iglesia, hoy, debe marchar.\r\nSirviéndole siempre unidos:\r\nA Cristo, ¡Velando! esperad.', '4\r\nCon Cristo somos un Cuerpo;\r\n¡Su amor nos fortalecerá!\r\nY Dios nos ha dado promesa:\r\n“La vida eterna, heredar”.', 'CORO(final)\r\n¡Congregados en uno!\r\nLa Iglesia, hoy, debe marchar.\r\nSirviéndole siempre unidos:\r\nA Cristo, ¡Velando! esperad.', '', '', '', '', 8);
INSERT INTO `himnos` (`id_himno`, `pagina_0`, `pagina_1`, `pagina_2`, `pagina_3`, `pagina_4`, `pagina_5`, `pagina_6`, `pagina_7`, `pagina_8`, `pagina_9`, `pagina_10`, `pagina_11`, `pagina_12`, `num`) VALUES
(167, '167\r\nCRISTO ES ALFA Y OMEGA', '1\r\nA tu siervo Moisés le dijiste,\r\nQue tus hijos harían siempre así,\r\nCelebrando por pacto perpetuo,\r\nEl recuerdo del Getsemaní.', 'CORO\r\nCristo es alfa, omega; y principio;\r\nEs fin, es fin.\r\nPor sus sendas debemos de ir.\r\nEsperar la corona de gloria,\r\nQue los salvos han de recibir.', '2\r\nEl Pimpollo que ha aparecido,\r\nDe las doce tribus de Israel:\r\n¡Admirable! y gran consejero;\r\n¡El primado de Jerusalén!', 'CORO\r\nCristo es alfa, omega; y principio;\r\nEs fin, es fin.\r\nPor sus sendas debemos de ir.\r\nEsperar la corona de gloria,\r\nQue los salvos han de recibir.', '3\r\nMandamientos y leyes nos diste,\r\nDescendiste sobre el Sinaí.\r\nLes dijiste: ¡Yo soy el camino!\r\n¡Pocos son los que han de ir por aquí!', 'CORO\r\nCristo es alfa, omega; y principio;\r\nEs fin, es fin.\r\nPor sus sendas debemos de ir.\r\nEsperar la corona de gloria,\r\nQue los salvos han de recibir.', '4\r\nEn el árbol de la gran oliva,\r\nFue ingerida la Iglesia de Dios;\r\nY al que toca las siete trompetas,\r\nSi en verdad escuchamos su voz.', 'CORO\r\nCristo es alfa, omega; y principio;\r\nEs fin, es fin.\r\nPor sus sendas debemos de ir.\r\nEsperar la corona de gloria,\r\nQue los salvos han de recibir.', '5\r\nTe rogamos Señor infinito;\r\nTe pedimos, tus santos también:\r\n¡Que nos lleves con mano potente!\r\n¡A tu reino de Jerusalem!', 'CORO(final)\r\nCristo es alfa, omega; y principio;\r\nEs fin, es fin.\r\nPor sus sendas debemos de ir.\r\nEsperar la corona de gloria,\r\nQue los salvos han de recibir.', '', '', 10),
(168, '168\r\nDAME LA MANO HERMANO', '1\r\nDame la mano, hermano,\r\nY permanezca la paz.\r\nY entre nosotros, mi hermano,\r\nSiempre haya, amor fraternal.', '1\r\nDame la mano, hermano,\r\nY escucha con atención:\r\nEl que no ama a su hermano,\r\nAún no conoce al Señor.', 'CORO\r\nSolícitos y guardando\r\nLa unidad en la paz.\r\nCon humildad, mansedumbre,\r\nPaciencia y fe: ¡Soportad!', 'CORO\r\nUn mismo espíritu y cuerpo,\r\nSomos llamados a ser;\r\nEn una misma esperanza.\r\n¡Cumplamos ese deber!', '2\r\nToma mi mano, hermano,\r\n¡La fuerza está en la unidad!\r\nDejemos murmuraciones;\r\nCuidemos de nuestro hablar.', '2\r\nNo señalemos, hermano,\r\nPorque el Señor juzgará.\r\nDame la mano, hermano,\r\nY el Padre nos sostendrá.', 'CORO\r\nSolícitos y guardando\r\nLa unidad en la paz.\r\nCon humildad, mansedumbre,\r\nPaciencia y fe: ¡Soportad!', 'CORO(final)\r\nUn mismo espíritu y cuerpo,\r\nSomos llamados a ser;\r\nEn una misma esperanza.\r\n¡Cumplamos ese deber!', '', '', '', '', 8),
(169, '169\r\nEL ÁNGEL DE JEHOVÁ', '1\r\nSi te sientes sólo y triste,\r\nY no encuentras solución,\r\nYa no tengas más temor,\r\nAlguien vela tu andar.', 'CORO\r\n//El ángel de Jehová//\r\nAcampa alrededor de los que le temen.\r\n//El ángel de Jehová//\r\nProtege al que le teme y lo defiende.', '2\r\nSi vas a emprender un viaje\r\n¿Sin saber si has de llegar?\r\nSin temor debes estar,\r\nPues en Dios has de confiar.', 'CORO\r\n//El ángel de Jehová//\r\nAcampa alrededor de los que le temen.\r\n//El ángel de Jehová//\r\nProtege al que le teme y lo defiende.', '3\r\nSi caminos peligrosos\r\nTienes tú que atravesar,\r\nNo tendrás ningún temor,\r\nPues Dios te sabe cuidar.', 'CORO(final)\r\n//El ángel de Jehová//\r\nAcampa alrededor de los que le temen.\r\n//El ángel de Jehová//\r\nProtege al que le teme y lo defiende.', '', '', '', '', '', '', 6),
(170, '170\r\nEL GÉNESIS', '1\r\n¿Quién hizo el cielo?\r\n¿Y las estrellas?\r\n¿Los ríos y el sol?\r\n¿Quién hizo la tierra?\r\n“Contesta un querube”:\r\n¡La mano de Dios!', '1\r\nCallé y me narró de Adán;\r\nDel diluvio y Babel;\r\nDe Abraham y su hijo Isaac;\r\nY cesó mi dudar.', '2\r\n“La voz del querube”,\r\nDe mi alma arranca,\r\nMil cantos de amor;\r\nQue suben, a lo alto\r\nDel trono sublime,\r\nDel Padre, mi Dios!', '2(final)\r\n¿Quién es este fiel mensajero?\r\nEl Génesis, es.\r\nEl libro primero; la flor\r\n// Del Edén. Es del mundo el albor. //', '', '', '', '', '', '', '', '', 4),
(171, '171\r\nEL OLIVO DE DIOS', '1\r\nLa Santa Escritura, declara un olivo;\r\n¡Un árbol grandioso, de origen divino y raíces sin fin!;\r\nDe ramas frondosas; de hermoso follaje;\r\nPues se alimentaba de la rica savia que emana de Dios.', 'CORO\r\n¡Fueron señalados! Porque eran primicias\r\nDel glorioso pacto y depositarios del culto y la Ley,\r\nPero había unas ramas que fueron cortadas;\r\n¡“A lo suyo vino”! ¡No le recibieron como Hijo de Dios!', '2\r\nQue extraño misterio, ¡Dios hizo un injerto!\r\nCuando incorpora un olivo silvestre al olivo de amor.\r\nDe esas dos naciones: judíos y gentiles.\r\nSe ha formado un pueblo, por Dios bendecido: El pueblo de Dios.', 'CORO\r\n¡Fueron señalados! Porque eran primicias\r\nDel glorioso pacto y depositarios del culto y la Ley,\r\nPero había unas ramas que fueron cortadas;\r\n¡“A lo suyo vino”! ¡No le recibieron como Hijo de Dios!', '3\r\n¡Cuán incomprensibles! Señor, son tus obras,\r\nY maravillosos tus planes divinos de la salvación.\r\nAyer extranjeros, ¡Sin Dios y sin patria!\r\n¡Hoy somos de Cristo! Según la promesa: ¡Linaje de Abraham!', 'CORO(final)\r\n¡Fueron señalados! Porque eran primicias\r\nDel glorioso pacto y depositarios del culto y la Ley,\r\nPero había unas ramas que fueron cortadas;\r\n¡“A lo suyo vino”! ¡No le recibieron como Hijo de Dios!', '', '', '', '', '', '', 6),
(172, '172\r\nEN LAS MANOS DEL SEÑOR', '1\r\nPon tus penas en las manos del Señor,\r\nCuando gime tu sufrido corazón.\r\nSi tu andar ha sido lleno de dolor,\r\nTocará tu ser con santo y dulce amor.', 'CORO\r\nEn el Padre deposita, hoy, tu ser.\r\n¡Ábrele tu corazón, debes confiar!\r\nSin dudar de su poder: ¡Pide con fe!,\r\nY respuesta a tu clamor encontrarás.', '2\r\nPon tus cargas en las manos del Señor,\r\nCuando la aflicción tocare tu salud;\r\nReconfortará tu ser con la unción;\r\nSanidad, Él, te dará con su virtud.', 'CORO\r\nEn el Padre deposita, hoy, tu ser.\r\n¡Ábrele tu corazón, debes confiar!\r\nSin dudar de su poder: ¡Pide con fe!,\r\nY respuesta a tu clamor encontrarás.', '3\r\nPon tu casa en las manos del Señor:\r\nAbundancia, siempre habrá, de bendición;\r\n¡La protegerá y la sustentará!\r\nBajo su sombra divina habitarás.', 'CORO\r\nEn el Padre deposita, hoy, tu ser.\r\n¡Ábrele tu corazón, debes confiar!\r\nSin dudar de su poder: ¡Pide con fe!,\r\nY respuesta a tu clamor encontrarás.', '4\r\nPon tu vida en las manos del Señor\r\nY tus lágrimas las cambiará por paz.\r\n¡Su Presencia siempre te acompañará!\r\nAl amparo de su amor siempre estarás.', 'CORO(final)\r\nEn el Padre deposita, hoy, tu ser.\r\n¡Ábrele tu corazón, debes confiar!\r\nSin dudar de su poder: ¡Pide con fe!,\r\nY respuesta a tu clamor encontrarás.', '', '', '', '', 8),
(173, '173\r\nENTRE NOSOTROS UNÁNIMES', '1\r\nSabemos que hay un mundo\r\nDe maldad y aflicción;\r\nSangre se ve derramar,\r\nEn todo el derredor.', 'CORO\r\nEntre nosotros, unánimes,\r\nPor la fe del Evangelio.\r\n¡Todos con fe combatir!\r\nMensaje de salvación,\r\n//Tenemos que anunciar//', '2\r\nUnidos contra el mal,\r\nHay que luchar y ganar.\r\n¡Dios nos da la fortaleza!\r\n¡No lo pensemos ya más!', 'CORO\r\nEntre nosotros, unánimes,\r\nPor la fe del Evangelio.\r\n¡Todos con fe combatir!\r\nMensaje de salvación,\r\n//Tenemos que anunciar//', '3\r\nQue el mundo, por nuestras obras,\r\nNuestra fe, en Dios, pueda ver;\r\nY galardón obtendremos,\r\nSi es que cumplimos con Él.', 'CORO(final)\r\nEntre nosotros, unánimes,\r\nPor la fe del Evangelio.\r\n¡Todos con fe combatir!\r\nMensaje de salvación,\r\n//Tenemos que anunciar//', '', '', '', '', '', '', 6),
(174, '174\r\nERAN CIEN OVEJAS', '1\r\nEran cien ovejas que el pastor cuidaba,\r\nEntre los vallados del frio invernal.\r\nEran cien hermosas cargas que guiaba,\r\nEntre pastos verdes por el manantial.', '1\r\nEran cien ovejas allá en los apriscos,\r\nPreciosas reliquias benditas de Dios.\r\nY el fiero enemigo tenerlas quería,\r\nY de noche y día velaba el pastor.', 'Coro 1\r\nY la más pequeña extravió el camino,\r\nEl Pastor muy triste a buscarla fue.\r\nLa buscó en los valles, en los pastizales,\r\nSubió a la montaña y no la encontró.', 'Coro 1\r\nY allá en el abismo la vio malherida,\r\nBajó con cuidado, la tomó en sus brazos,\r\nCon ella lloró.', '2\r\nYo fui aquella oveja, que extravió el camino,\r\nMuchos desengaños y en prisión me vi.\r\nHasta que un buen día, Cristo, caminando,\r\nCon sus pies sangrando, corrió y me abrazó.', '2\r\nSi vagas sin rumbo allá entre la gente,\r\nVuelve alma perdida, regresa al hogar.\r\nTe llama: ¡Hijo mío! Él quiere estrecharte,\r\nTomarte en sus brazos, sanar tu dolor.', 'Coro 2\r\nY la más pequeña extravió el camino,\r\nEl pastor muy triste a buscarla fue.\r\nLa buscó en los valles en los pastizales,\r\nSubió a la montaña y no la encontró.', 'Coro 2(final)\r\nY allá en el abismo la vio malherida,\r\nBajó con cuidado, me tomó en sus brazos,\r\nConmigo lloró.', '', '', '', '', 8),
(175, '175\r\nESCUCHA A DIOS', '1\r\nAl Señor, ¡Sea toda gloria!\r\nSólo en Él hay redención.\r\nY te habla con amor; no le niegues, dile hoy:\r\n“Habla Dios, que escuchará mi corazón”.', 'CORO\r\nEs momento, ¡Vamos prestos!\r\nA escuchar la voz de Dios;\r\nEs mensaje que nos da la salvación.\r\nCompartiendo la alegría de vivir en comunión,\r\nDisfrutemos el momento con amor.', '2\r\nEn el mundo hay aflicciones,\r\nSin Dios... ¡No podrás seguir!\r\nDeja que la tierna voz del Señor, ¡Al decidir!\r\nEsperanza y fuerza, den a tu vivir.', 'CORO\r\nEs momento, ¡Vamos prestos!\r\nA escuchar la voz de Dios;\r\nEs mensaje que nos da la salvación.\r\nCompartiendo la alegría de vivir en comunión,\r\nDisfrutemos el momento con amor.', '3\r\n¿Dejarás pasar más tiempo?\r\n¡Con amor te habla Dios!\r\n¡Ven! Renueva tu vivir, imitando desde hoy,\r\nEl ejemplo que el Maestro nos marcó.', 'CORO(final)\r\nEs momento, ¡Vamos prestos!\r\nA escuchar la voz de Dios;\r\nEs mensaje que nos da la salvación.\r\nCompartiendo la alegría de vivir en comunión,\r\nDisfrutemos el momento con amor.', '', '', '', '', '', '', 6),
(176, '176\r\nESPERA EN EL SEÑOR', '1\r\nEn los días de angustia, que no asoma la solución...\r\nPaz no tiene y no encuentra el corazón.\r\nClama a Dios y en Él confiado, en verdad, Él contestará.\r\nPeticiones y anhelos, te dará.', 'CORO\r\n¡Espera en el Señor!\r\nContestación, tú, tendrás.\r\nNo desmayes en la espera; mantén viva tu firme fe;\r\nQue el Señor responderá y no tardará.', '2\r\nDeposita tu confianza en Dios; ¡Que todo puede hacer!\r\n¡Imposibles no existen!, para Él.\r\nHaz el bien, no te desvíes. No hagas mal, y tu oración\r\nSea estorbada y no llegue ante el Señor.', 'CORO\r\n¡Espera en el Señor!\r\nContestación, tú, tendrás.\r\nNo desmayes en la espera; mantén viva tu firme fe;\r\nQue el Señor responderá y no tardará.', '3\r\nY cuando hayas recibido los deseos del corazón,\r\nNo te olvides... ¡Tienes que agradecer!\r\nDale gloría, alabanzas, al que ha obrado en tu favor.\r\nY recuerda, que sin Él, no pueden ser.', 'CORO(final)\r\n¡Espera en el Señor!\r\nContestación, tú, tendrás.\r\nNo desmayes en la espera; mantén viva tu firme fe;\r\nQue el Señor responderá y no tardará.', '', '', '', '', '', '', 6),
(177, '177\r\nLA FAMILIA DE DIOS', '1\r\nEl hogar que Dios, en el huerto formó:\r\n¡Gran bendición! ¡Gloria es en su honor!\r\n¡Multiplicarse! Mandato de amor;\r\nCon felicidad y alegría al Creador.', 'CORO\r\nEl hogar que, a su gloria, creó,\r\n“La familia de Dios”, le llamó.\r\nFue deleite de su grande amor.\r\nPor su felicidad su Hijo murió.', '2\r\nAl matrimonio, que en el huerto formó,\r\nPlan de armonía, les dio el Señor.\r\nPor su favor, con amor les colmó;\r\nAsí, bien unidos, den gloria en su honor.', 'CORO\r\nEl hogar que, a su gloria, creó,\r\n“La familia de Dios”, le llamó.\r\nFue deleite de su grande amor.\r\nPor su felicidad su Hijo murió.', '3\r\nEl enemigo, ¡Con astucia engañó!,\r\nDe cardos y espinas, él los rodeó\r\nCada quién su caminar prefirió...\r\nLa Ley, olvidando: el hombre pecó.', 'CORO(final)\r\nEl hogar que, a su gloria, creó,\r\n“La familia de Dios”, le llamó.\r\nFue deleite de su grande amor.\r\nPor su felicidad su Hijo murió.', '', '', '', '', '', '', 6),
(178, '178\r\nLA IGLESIA DE DIOS', '1\r\nCada uno de vosotros\r\nDé un “paz a voz” leal;\r\nSiendo una Iglesia unida\r\nEn el Nombre del Señor.', 'CORO\r\nTodos ante su Presencia,\r\nVuestros brazos uniréis.\r\nJuntos somos una fuerza,\r\nQue todo, en Dios, alcanzareis.\r\nNo hay semejante dicha\r\nQue ser la Iglesia de Dios.', '2\r\nCon amor, unos a otros,\r\nUn abrazo os daréis.\r\nComo una Iglesia unida,\r\nSiempre os comportareis.', 'CORO\r\nTodos ante su Presencia,\r\nVuestros brazos uniréis.\r\nJuntos somos una fuerza,\r\nQue todo, en Dios, alcanzareis.\r\nNo hay semejante dicha\r\nQue ser la Iglesia de Dios.', '3\r\nCon amor, unos por otros,\r\nSiempre al Padre, pediréis.\r\nEn el gozo y la tristeza,\r\nUnidos siempre estaréis.', 'CORO(final)\r\nTodos ante su Presencia,\r\nVuestros brazos uniréis.\r\nJuntos somos una fuerza,\r\nQue todo, en Dios, alcanzareis.\r\nNo hay semejante dicha\r\nQue ser la Iglesia de Dios.', '', '', '', '', '', '', 6),
(179, '179\r\nLA MUERTE FUE VENCIDA', '1\r\nLa sombra de la muerte fue vencida,\r\nLa luz del Salvador la disipó;\r\nMuriendo en una cruz por darnos vida,\r\nCumpliendo el mandamiento de mi Dios.', 'Coro\r\nNo dejes que sea en vano el sacrificio,\r\nDe Aquel que en el Calvario padeció;\r\n//Pagando los pecados por nosotros,\r\nQue hasta la última sangre derramó//', '2\r\nLa muerte fue vencida al tercer día,\r\nJesús con gran poder la derrotó.\r\nHoy ruega por nosotros ante el Padre,\r\nRecuerda que a los cielos ascendió.', 'Coro\r\nNo dejes que sea en vano el sacrificio,\r\nDe Aquel que en el Calvario padeció;\r\n//Pagando los pecados por nosotros,\r\nQue hasta la última sangre derramó//', '3\r\nAlzamos nuestras voces jubilosas,\r\nRendimos todo honor y gloria a Dios;\r\nQue reina con el Hijo, el fiel Cordero,\r\nAquel que nos mostró lo que es amor.', 'Coro(final)\r\nNo dejes que sea en vano el sacrificio,\r\nDe Aquel que en el Calvario padeció;\r\n//Pagando los pecados por nosotros,\r\nQue hasta la última sangre derramó//', '', '', '', '', '', '', 6),
(180, '180\r\nLA VIDA ES FUGAZ', '1\r\nLa vida es fugaz... ¡Veloz pasará!\r\nHaciendo obras buenas, se debe llenar.\r\nEn mal, ya no pienses; debemos actuar,\r\nPor la vida eterna, que Cristo dará.', 'CORO\r\nEl Autor de la vida, su voz deja oír;\r\nDesea que vivamos, y ha dicho así:\r\n“No ames al mundo, que va a sucumbir;\r\nYo os doy la vida que dura sin fin”.', '2\r\nLa vida del hombre es como la flor:\r\nNace en la mañana y en la tarde cae.\r\nEs como la sombra delante del sol:\r\nEstá un momento y luego se va.', 'CORO\r\nEl Autor de la vida, su voz deja oír;\r\nDesea que vivamos, y ha dicho así:\r\n“No ames al mundo, que va a sucumbir;\r\nYo os doy la vida que dura sin fin”.', '3\r\nLa vida es fugaz y se pasará;\r\n“¿Qué has hecho de ella?\": ¡Nos va a preguntar!\r\nEs hoy el momento y así valorar...\r\nA Dios, demos gracias por su gran bondad.', 'CORO(final)\r\nEl Autor de la vida, su voz deja oír;\r\nDesea que vivamos, y ha dicho así:\r\n“No ames al mundo, que va a sucumbir;\r\nYo os doy la vida que dura sin fin”.', '', '', '', '', '', '', 6),
(181, '181\r\nMARCHA HACIA ADELANTE', '1\r\nJesucristo quiere que le sigas,\r\nDeja todo, no lo dudes más;\r\nSi lo aceptas bendición tendrás,\r\nSu bondad te colmará.', 'Coro\r\n¡Adelante! marcha hacia adelante,\r\nLas pisadas de Jesús seguid.\r\nCon firmeza y decisión hay que luchar\r\nEn sus filas, hasta el fin.', '2\r\nSal del mundo, entrégale a Cristo\r\nTu confianza y tu necesidad,\r\nCon poder te librará del mal\r\nY tu senda alumbrará.', 'Coro\r\n¡Adelante! marcha hacia adelante,\r\nLas pisadas de Jesús seguid.\r\nCon firmeza y decisión hay que luchar\r\nEn sus filas, hasta el fin.', '3\r\nCuando a Cristo hayas aceptado\r\nNo te olvides de perseverar,\r\nSi eres valiente y fiel, tendrás\r\nPremio noble, al final.', 'Coro(final)\r\n¡Adelante! marcha hacia adelante,\r\nLas pisadas de Jesús seguid.\r\nCon firmeza y decisión hay que luchar\r\nEn sus filas, hasta el fin.', '', '', '', '', '', '', 6),
(182, '182\r\nMEJORANDO NUESTROS CAMINOS', '1\r\nSi la vida anhelamos salvar\r\nY el reino poder contemplar...\r\n¡Nuestros hechos debemos cambiar!\r\nSiendo fieles, ¡Sí! ¡Hasta el final!', 'CORO\r\nMejorando nuestros caminos,\r\nObtendremos el reino de Dios;\r\nAcatando el mandato de Cristo\r\nY el ejemplo que, Él, nos mostró.', '2\r\nSi hay un cambio en nuestro vivir,\r\nSi luchamos por la perfección...\r\nDel Señor podremos recibir,\r\nLa corona de la salvación.', 'CORO\r\nMejorando nuestros caminos,\r\nObtendremos el reino de Dios;\r\nAcatando el mandato de Cristo\r\nY el ejemplo que, Él, nos mostró.', '3\r\nY al final hemos de contemplar,\r\nAl Señor en su gran majestad;\r\nPero firmes debemos estar\r\nY esforzarnos por avanzar.', 'CORO(final)\r\nMejorando nuestros caminos,\r\nObtendremos el reino de Dios;\r\nAcatando el mandato de Cristo\r\nY el ejemplo que, Él, nos mostró.', '', '', '', '', '', '', 6),
(183, '183\r\nMENSAJERO FIEL', '1\r\n¡Oh, bella melodía! Mi Señor me has concedido\r\nMi alma enferma estaba; postrada de dolor.\r\nCon una suave palabra, vertida de tu amor;\r\nMi ser y mi alma, con tu espíritu, sanó.', 'Coro\r\nMensajero fiel, medicina es tu palabra;\r\nQue al alma cansada, consuelo le das.\r\nMensajero fiel, medicina es tu palabra;\r\nUngüento divino, que del cielo Él nos derrama.', '2\r\nAlégrense los montes, con los valles y el abismo;\r\nEntonen alabanzas, gloriosa su canción.\r\nLo que se mueve en la tierra, las aves con su voz,\r\n¡Hosanas, proclamar! Al Dios de la creación.', 'Coro\r\nMensajero fiel, medicina es tu palabra;\r\nQue al alma cansada, consuelo le das.\r\nMensajero fiel, medicina es tu palabra;\r\nUngüento divino, que del cielo Él nos derrama.', '3\r\nFeliz hoy te alabo, Padre eterno, Dios bendito.\r\nRey justo y amoroso, supremo en majestad.\r\nMi corazón ¡Oh Dios santo!, llenaste de tu paz;\r\nMe das tu bendición, me guías en verdad.', 'Coro(final)\r\nMensajero fiel, medicina es tu palabra;\r\nQue al alma cansada, consuelo le das.\r\nMensajero fiel, medicina es tu palabra;\r\nUngüento divino, que del cielo Él nos derrama.', '', '', '', '', '', '', 6),
(184, '184\r\nMI VIDA EN TI', '1\r\nHazme comprender, mi Señor, el fin...\r\nEl final de mis días: ¿Cuándo llegará?\r\n¿Qué habrá de ser, de mí existir?\r\nComo nube se consume, que viene y pasará.', 'CORO\r\nAhora Señor, ¿Qué esperaré?\r\nDe la sombra de muerte, no quiero temer.\r\n¡Sólo Tú me protegerás!\r\nMi existir en Ti, escondido está.', '2\r\nY cuando al morir: ¿Qué provecho habrá?\r\n¡Tu verdad en la tumba no podré anunciar!\r\nEn tu mano está todo el poder,\r\nPara darnos la vida o hacernos fenecer.', 'CORO(final)\r\nAhora Señor, ¿Qué esperaré?\r\nDe la sombra de muerte, no quiero temer.\r\n¡Sólo Tú me protegerás!\r\nMi existir en Ti, escondido está.', '', '', '', '', '', '', '', '', 4),
(185, '185\r\nMIRAD A LAS COSAS DE ARRIBA', '1\r\nIglesia de Dios: ¡Aviva tu fe!\r\nÚltimos tiempos, de apostasía, son.\r\nNo mires atrás, ni cosas terrenas;\r\nAmando este siglo, ¡Te perderás!', 'CORO\r\n¡Poned la mira en las cosas de arriba!\r\nNo vayas a ser fácilmente engañado...\r\nY así, tu sentimiento movido,\r\nSea en deslealtad.', '2\r\nSi alguno dijere: ¡Aquí el Cristo está!,\r\nNo veas atrás, como la mujer de Lot.\r\nSi eres leal, tu vida y de otros,\r\nSirviendo a Cristo, tú salvarás.', 'CORO\r\n¡Poned la mira en las cosas de arriba!\r\nNo vayas a ser fácilmente engañado...\r\nY así, tu sentimiento movido,\r\nSea en deslealtad.', '3\r\nMuchos escogidos burlados serán;\r\nDoctrinas falsas, ¡Con atención oirán!,\r\nNegando su fe y la sana doctrina...\r\n¡Cual vírgenes fatuas perecerán!', 'CORO\r\n¡Poned la mira en las cosas de arriba!\r\nNo vayas a ser fácilmente engañado...\r\nY así, tu sentimiento movido,\r\nSea en deslealtad.', '4\r\nLos falsos profetas se levantarán;\r\nFilosofías de hombre, enseñarán.\r\nY el día del juicio, el Cristo bendito,\r\nA su mano izquierda, Él apartará.', 'CORO(final)\r\n¡Poned la mira en las cosas de arriba!\r\nNo vayas a ser fácilmente engañado...\r\nY así, tu sentimiento movido,\r\nSea en deslealtad.', '', '', '', '', 8),
(186, '186\r\nNO TENGAS MIEDO', '1\r\n“No tengas miedo,\r\nYo soy contigo,\r\nSiente mis manos,\r\nQue son tu abrigo.”', 'CORO\r\n“¡Pues yo te amo,\r\nYo soy tu Padre!\r\nCuenta conmigo,\r\nSoy tu amigo.”', '2\r\n“Eres mi hijo,\r\nYo te he creado,\r\nY tus pecados,\r\nHe perdonado.”', 'CORO\r\n“¡Pues yo te amo,\r\nYo soy tu Padre!\r\nCuenta conmigo,\r\nSoy tu amigo.”', '3\r\n“No temas nada,\r\nYo soy tu escudo,\r\nSoy tu esperanza,\r\nY quien te ayudo.”', 'CORO\r\n“¡Pues yo te amo,\r\nYo soy tu Padre!\r\nCuenta conmigo,\r\nSoy tu amigo.”', '4\r\n“No habrá maldades\r\nQue a ti te alcancen\r\nNi enfermedades\r\nQue en Mí no sanen”.', 'CORO(final)\r\n“¡Pues yo te amo,\r\nYo soy tu Padre!\r\nCuenta conmigo,\r\nSoy tu amigo.”', '', '', '', '', 8),
(187, '187\r\nPONED LA MIRA EN LAS COSAS DE ARRIBA', '1\r\nQueremos, todos, servir al Salvador.\r\nMas no olvidemos, ¡Mirar hacia adelante!', '1\r\nPorque en Jesús, ¡Está la salvación!\r\nPero este mundo, de pecado y de placeres,\r\nYa nos quiere apartar del Salvador.', 'CORO\r\n//¡Poned la mira en las cosas de arriba\r\nY no en las de la tierra!//', '2\r\nIr con cuidado... ¡Buscando no fallar!;\r\n¡Porque este mundo nos quiere atrapar!', '2\r\nHay que pedir a Dios en oración,\r\nNos cuide siempre y nos aleje del pecado,\r\nY permitirnos alcanzar la salvación', 'CORO\r\n//¡Poned la mira en las cosas de arriba\r\nY no en las de la tierra!//', '3\r\n¡Como soldados debemos de luchar!\r\nY alejados del mal y del pecado.', '3\r\nHay que dejar el mundo terrenal,\r\nPorque las cosas de la carne y el pecado\r\nNo nos permiten vivir en santidad.', 'CORO(final)\r\n//¡Poned la mira en las cosas de arriba\r\nY no en las de la tierra!//', '', '', '', 9),
(188, '188\r\nQUE SE CONVIERTAN ELLOS A TI', '1\r\nDijo el necio en su corazón:\r\n“¡No existe! ¡No hay Dios!”\r\nMuchos creen, que sólo su poder,\r\nLos hará prevalecer.\r\nPero tú, joven de Dios,\r\nSabes del buen Salvador.', 'CORO\r\n¡Que se conviertan ellos a ti!\r\nSabiduría de Dios hay en ti.\r\nSi los conviertes,\r\nrescatarás almas que están en error.\r\n¡Confía en Dios!', '2\r\nPon tus ojos en el Salvador;\r\nNo hay fábulas de él.\r\nEl Señor, con todo su poder,\r\nPronto habrá de aparecer.\r\nPero tú, joven de Dios,\r\n¡Fiel has de permanecer!', 'CORO(final)\r\n¡Que se conviertan ellos a ti!\r\nSabiduría de Dios hay en ti.\r\nSi los conviertes,\r\nrescatarás almas que están en error.\r\n¡Confía en Dios!', '', '', '', '', '', '', '', '', 4),
(189, '189\r\nQUISIERA CANTAR', '1\r\nQuisiera cantar, quisiera llorar...\r\nMis penas me obligan, ¡No puedo callar!\r\nSi hay tribulación, pobreza o dolor...\r\nCreyendo y orando, consuela el Señor.', '2\r\n¿Para qué reír y hacer ilusión?...\r\n¡Qué! ¿Se fue el sufrir?... Si llora el corazón;\r\nEs mejor llorar, si hoy, Dios quiere así.\r\nMas puedes gozar... ¡Con Cristo ir a vivir!', '3\r\nNo es tiempo aun de reír. No es tiempo de gozar;\r\nTampoco de dormir. ¡Es tiempo de luchar!\r\nMuy pronto, ya, volverá Cristo Jesús, ¡Mi Rey!\r\n¡Galardón traerá!, al que guardó su ley.', '4\r\n¡Al mundo no amar!: Ordena el Señor\r\n¡El mundo y sus cosas hay que desechar!\r\nSi amamos a Dios, pero de verdad,\r\nDebemos servirle y no andar en maldad.', '5\r\n¡Cristo no vivió en vana ilusión!\r\nSino que sufrió por darnos salvación.\r\nEscogió el dolor. Prefirió morir.\r\n¡Era el buen Pastor! Nos vino a redimir.', '6(final)\r\n¡Nuestro Señor viene! así como advirtió;\r\nCon Él todo será tiempo de gozar.\r\nLágrimas no habrá, ni más tribulación.\r\nLa risa será para los hijos de Dios.', '', '', '', '', '', '', 6),
(190, '190\r\nSALID DE EGIPTO YA', '1\r\n¡Salid de Egipto ya!\r\nManda el Señor Jehová;\r\nAl Pueblo de Israel,\r\nQue subyugado está.', 'CORO\r\n¡Ven! ¡Oh Señor libertador!\r\nLibra a tu pueblo de Faraón;\r\nTú nos guiarás y llevarás,\r\nA esa tierra... ¡Bendita Sión!', '2\r\nMira la aflicción,\r\nDe tu pueblo Israel;\r\nTú eres compasión...\r\n¡Tendrás piedad de él!', 'CORO\r\n¡Ven! ¡Oh Señor libertador!\r\nLibra a tu pueblo de Faraón;\r\nTú nos guiarás y llevarás,\r\nA esa tierra... ¡Bendita Sión!', '3\r\nMe voy de Egipto, ya;\r\n¡No quiero en ti vivir!\r\nEl castigo de Dios,\r\nYa viene sobre ti.', 'CORO\r\n¡Ven! ¡Oh Señor libertador!\r\nLibra a tu pueblo de Faraón;\r\nTú nos guiarás y llevarás,\r\nA esa tierra... ¡Bendita Sión!', '4\r\nNo temas Israel,\r\nQue el viento sopla ya;\r\nPara partir la mar,\r\nY tú puedas pasar.', 'CORO\r\n¡Ven! ¡Oh Señor libertador!\r\nLibra a tu pueblo de Faraón;\r\nTú nos guiarás y llevarás,\r\nA esa tierra... ¡Bendita Sión!', '5\r\nCuando allí estés,\r\nNo habrá ya más dolor;\r\nDescansarán tus pies,\r\nDespués de la labor.', 'CORO(final)\r\n¡Ven! ¡Oh Señor libertador!\r\nLibra a tu pueblo de Faraón;\r\nTú nos guiarás y llevarás,\r\nA esa tierra... ¡Bendita Sión!', '', '', 10),
(191, '191\r\nSED COMO NIÑOS', '1\r\nYo quiero seguir a Cristo,\r\nMeditando en su palabra;\r\nQue le dice a todo el mundo:\r\n¡”Dejad, los niños, venir a Mí”!', 'CORO\r\n“Y no se los impidáis”,\r\nDijo el Hijo de Dios;\r\nPorque el reino de su Padre,\r\nNiños, lo heredarán.', '2\r\nSi no fuereis como niños\r\nY os volviereis como ellos,\r\nNo podréis tener el reino.\r\n¡Siempre condúcelos al Señor!', 'CORO\r\n“Y no se los impidáis”,\r\nDijo el Hijo de Dios;\r\nPorque el reino de su Padre,\r\nNiños, lo heredarán.', '3\r\nHumilde, sé, como un niño.\r\nCon un corazón sencillo;\r\nLimpio siempre en pensamiento.\r\nSu ejemplo hoy te invita a seguir.', 'CORO\r\n“Y no se los impidáis”,\r\nDijo el Hijo de Dios;\r\nPorque el reino de su Padre,\r\nNiños, lo heredarán.', '4\r\nRecibe a Dios como niño\r\nY tendrás corona de ello.\r\nJesucristo te lo pide;\r\nEscucha, ya, su voz de amor.', 'CORO(final)\r\n“Y no se los impidáis”,\r\nDijo el Hijo de Dios;\r\nPorque el reino de su Padre,\r\nNiños, lo heredarán.', '', '', '', '', 8),
(192, '192\r\nSI BUSCAS EL AMOR', '1\r\nSi buscas el amor,\r\nEn Dios lo encontrarás.\r\nTú lo puedes mirar,\r\nPues vida hoy te da.', 'CORO\r\n¿No has visto grandes cosas? ¿Cosas maravillosas?\r\nMás nunca encontrarás, este amor sin igual:\r\nVer a Dios a su Hijo por ti entregar.', '2\r\nSi en la tribulación\r\nTe sientes desmayar,\r\nVe a la “piedra angular”\r\nY Él te sostendrá.', 'CORO\r\n¿No has visto grandes cosas? ¿Cosas maravillosas?\r\nMás nunca encontrarás, este amor sin igual:\r\nVer a Dios a su Hijo por ti entregar.', '3\r\nEn este tiempo cruel\r\nHay que perseverar,\r\nY aquel que venza al mal,\r\n¡Corona obtendrá!', 'CORO(final)\r\n¿No has visto grandes cosas? ¿Cosas maravillosas?\r\nMás nunca encontrarás, este amor sin igual:\r\nVer a Dios a su Hijo por ti entregar.', '', '', '', '', '', '', 6),
(193, '193\r\nSI TÚ CONFIAS EN CRISTO', '1\r\nCuando tengas una pena, un mal...\r\n¿Te agobian las tristezas?\r\n¿Paz no logras encontrar?\r\nSi tu carga es difícil llevar...\r\nEspera un momento,\r\nQue en Jesús podrás confiar.', 'CORO\r\nPorque si tú confías en Cristo,\r\nTu clamor, Dios lo oirá;\r\nY atenderá tus oraciones,\r\nSi su temor, en ti está.', 'CORO\r\nY al final de tu camino,\r\n¡Su luz te iluminará!\r\nPorque la gracia de Cristo,\r\nEn ti reposará.', '2\r\nSi la prueba es un yugo que, hoy,\r\nAl llanto te encadena\r\nY te quieres liberar...\r\n¡No lo pienses! ¡Y desecha el temor!\r\nConfía sólo en Cristo,\r\nLa salida, Él, ¡Te dará!', 'CORO\r\nPorque si tú confías en Cristo,\r\nTu clamor, Dios lo oirá;\r\nY atenderá tus oraciones,\r\nSi su temor, en ti está.', 'CORO(final)\r\nY al final de tu camino,\r\n¡Su luz te iluminará!\r\nPorque la gracia de Cristo,\r\nEn ti reposará.', '', '', '', '', '', '', 6),
(194, '194\r\nSUFRID CON GOZO', '1\r\nA mis hermanos Muy amados en Jesús,\r\nMucho cuidado Con el mundo engañador.\r\nNada ni nadie Mueva vuestro corazón,\r\nSufrid con gozo En la Presencia del Señor.', 'CORO\r\n¡Regocijaos en oración!\r\nY dando gracias al Señor,\r\nCantando salmos y alabanzas\r\nEn su honor.\r\n¡Regocijaos con gratitud!\r\nHasta la gloria conquistar,\r\nDios es más grande\r\nQue cualquier adversidad.', '2\r\nCon gozo vamos En la lucha sin cesar,\r\nEn el camino De la luz y la verdad.\r\nToma el escudo De la fe y de salvación,\r\nProsigue al blanco Hasta tu encuentro con Jesús.', 'CORO(final)\r\n¡Regocijaos en oración!\r\nY dando gracias al Señor,\r\nCantando salmos y alabanzas\r\nEn su honor.\r\n¡Regocijaos con gratitud!\r\nHasta la gloria conquistar,\r\nDios es más grande\r\nQue cualquier adversidad.', '', '', '', '', '', '', '', '', 4),
(195, '195\r\nTIERNO CONSUELO', '1\r\n“Solo he encontrado”, dijo el sabio,\r\n“Penas y angustias, debajo del sol”.\r\nEl hombre corre tras vanidades,\r\nY en su camino se afana por paz.', 'CORO\r\nY si con fuerza, azotan los vientos...\r\nDesesperanza nos quiere vencer.\r\nTan sólo el Padre, en el nombre de Cristo,\r\nLa cruel tormenta desvanecerá.', '2\r\nPruebas y luchas, siempre, enfrentamos.\r\nCardos y espinas, laceran la fe.\r\nPero encontramos tierno consuelo\r\nEn Jesucristo; ¡Nuestro amigo fiel!', 'CORO\r\nY si con fuerza, azotan los vientos...\r\nDesesperanza nos quiere vencer.\r\nTan sólo el Padre, en el nombre de Cristo,\r\nLa cruel tormenta desvanecerá.', '3\r\n“Ya no te angusties”, dice el Maestro;\r\n“Que en Mí, tú puedes, tus cargas dejar”.\r\n“¡Sólo confía!, pues he vencido.\r\nY en Mí, tú alma, descanso hallará”.', 'CORO(final)\r\nY si con fuerza, azotan los vientos...\r\nDesesperanza nos quiere vencer.\r\nTan sólo el Padre, en el nombre de Cristo,\r\nLa cruel tormenta desvanecerá.', '', '', '', '', '', '', 6),
(196, '196\r\n¿TÚ AMAS A CRISTO?', '1\r\nSi tú en verdad amas a Cristo:\r\n¡Manifiéstalo, manifiéstalo!\r\nSi tú en verdad amas a Cristo:\r\n¡Manifiéstalo!, con obra y acción.', 'CORO\r\nEl que dice que ama, lo hace sin esperar,\r\nQue reciba lo mismo o más, de lo que dio.\r\nSi tu amor es sincero, ¡Bendición llegará!;', 'CORO\r\nSuave llama producirá...\r\nTierno aliento te envolverá...\r\nY en el seno del Padre, estarás.', '2\r\nSi tú en verdad amas al Padre:\r\n¡Demuéstralo! ¡Demuéstralo!\r\nSi tú en verdad amas al Padre:\r\n¡Demuéstralo!, andando en su verdad.', 'CORO\r\nEl que dice que ama, lo hace sin esperar,\r\nQue reciba lo mismo o más, de lo que dio.\r\nSi tu amor es sincero, ¡Bendición llegará!;', 'CORO(final)\r\nSuave llama producirá...\r\nTierno aliento te envolverá...\r\nY en el seno del Padre, estarás.', '', '', '', '', '', '', 6),
(197, '197\r\nUN SEÑOR, UNA FE', '1\r\nOs ruego que andéis, como es digno, en el Señor;\r\nCon humildad, paciencia y soportando en amor.\r\nSolícitos guardar, la unidad, la paz.\r\nRogando unos por otros, en ferviente oración.', 'CORO\r\nUn Señor, una fe.\r\nUn bautismo; un Dios y Padre,\r\nQue su amor, en todos, es.\r\nUn Señor, una fe.\r\nUna mente y un mismo parecer.', '2\r\nEsta gran verdad la debemos conservar;\r\nY así que habite Cristo en nuestra mente y corazón.\r\nLos dones que nos dio, son para perfección,\r\nDe su amada Iglesia, que es el cuerpo del Señor.', 'CORO(final)\r\nUn Señor, una fe.\r\nUn bautismo; un Dios y Padre,\r\nQue su amor, en todos, es.\r\nUn Señor, una fe.\r\nUna mente y un mismo parecer.', '', '', '', '', '', '', '', '', 4),
(198, '198 \r\nVANIDAD DE VANIDADES', '1\r\nVanidad de vanidades,\r\n¡Todo!, ¡Todo vanidad!\r\nEl mal reina y falsedades...\r\n¡Predomina la impiedad!', 'CORO\r\nSi oro, plata y tesoro preciado,\r\nAlgún día lograres tener;\r\nVanidad te habrá rodeado,\r\n¡Ten cuidado!, no te vayas a perder.', '2\r\nNo trabajes por ser rico,\r\nNi famoso anheles ser.\r\nSi esto buscas con ahínco...\r\n¡Inocente no has de ser!', 'CORO\r\nSi oro, plata y tesoro preciado,\r\nAlgún día lograres tener;\r\nVanidad te habrá rodeado,\r\n¡Ten cuidado!, no te vayas a perder.', '3\r\n¡Este mundo es engañoso!\r\nNada eterno encontrarás.\r\nLo fragante y muy hermoso,\r\n¡Pronto!, ya no lo hallarás.', 'CORO\r\nSi oro, plata y tesoro preciado,\r\nAlgún día lograres tener;\r\nVanidad te habrá rodeado,\r\n¡Ten cuidado!, no te vayas a perder.', '4\r\nDijo al hombre, el Poderoso;\r\nDijo Dios, con rectitud:\r\n“Si eres vano y orgulloso,\r\nNo has de ser eterno, tú”.', 'CORO\r\nSi oro, plata y tesoro preciado,\r\nAlgún día lograres tener;\r\nVanidad te habrá rodeado,\r\n¡Ten cuidado!, no te vayas a perder.', '5\r\nNo te engañes a ti mismo,\r\nNi confíes en vanidad.\r\nQuita de ti, el egoísmo\r\nY del pobre, ten piedad.', 'CORO\r\nSi oro, plata y tesoro preciado,\r\nAlgún día lograres tener;\r\nVanidad te habrá rodeado,\r\n¡Ten cuidado!, no te vayas a perder.', '6\r\n¡Pronto!, el Rey de reyes viene.\r\n“El Camino y la Verdad”;\r\nQuien la vida, sólo Él tiene,\r\nPor toda la eternidad .', 'CORO(final)\r\nSi oro, plata y tesoro preciado,\r\nAlgún día lograres tener;\r\nVanidad te habrá rodeado,\r\n¡Ten cuidado!, no te vayas a perder.', 12),
(199, '199\r\nVESTIDO BLANCO, EL ATUENDO', '1\r\nEn este mundo, “las ropas”,\r\nFácil podemos manchar.\r\nY al adversario le agrada,\r\nDe nuestro Dios, renegar.', 'CORO\r\n// “Vestido blanco”, el atuendo;\r\n¡Que nunca vaya a mancharse!\r\n¡Camina limpio! Limpia tu alma,\r\nCon sangre pura, de ese Cordero. //', '2\r\nFirmes y fieles, ¡En lucha!\r\nDios nos desea, hallar.\r\nSu paz y misericordia,\r\nNuestra bandera, serán.', 'CORO(final)\r\n// “Vestido blanco”, el atuendo;\r\n¡Que nunca vaya a mancharse!\r\n¡Camina limpio! Limpia tu alma,\r\nCon sangre pura, de ese Cordero. //', '', '', '', '', '', '', '', '', 4),
(200, '200\r\nVESTÍOS DE TODA ARMADURA DE DIOS', '1\r\nSomos soldados de nuestro Dios\r\nYelmo, espada y escudo nos dio;\r\nArmas perfectas para ganar\r\nCada batalla contra Satán.', 'CORO\r\n¡Vestíos, de Dios, armadura!\r\nY en su Nombre venceréis.\r\n¡Vestíos de toda armadura!\r\nY al maligno resistiréis.', '2\r\nEl adversario, ofreciendo está\r\nMil tentaciones: ¡Nos quiere tener!\r\nPor eso firmes y sin dudar,\r\n¡Fortalezcamos siempre la fe!', 'CORO\r\n¡Vestíos, de Dios, armadura!\r\nY en su Nombre venceréis.\r\n¡Vestíos de toda armadura!\r\nY al maligno resistiréis.', '3\r\nHasta la muerte hay que pelear\r\nPorque corona al final nos dará,\r\nSi conservamos nuestra lealtad\r\nY nuestras vidas en santidad', 'CORO(final)\r\n¡Vestíos, de Dios, armadura!\r\nY en su Nombre venceréis.\r\n¡Vestíos de toda armadura!\r\nY al maligno resistiréis.', '', '', '', '', '', '', 6),
(201, '201\r\n¿Y QUIÉN HA CREÍDO?', '1\r\nHace muchos años,\r\nUn Hombre llegó a esta tierra,\r\nPara salvar al mundo', 'CORO\r\n//¿Y quién ha creído a nuestro anuncio?\r\nLo dijo el profeta:\r\n¿Y quién ha creído?//', '2\r\nTodo hombre estaba en pecado,\r\nSin Dios, sin esperanza\r\nY sin su Amor Divino.', 'CORO\r\n//¿Y quién ha creído a nuestro anuncio?\r\nLo dijo el profeta:\r\n¿Y quién ha creído?//', '3\r\nNuestro Dios y Padre nos llama,\r\nA hacer obras dignas,\r\nDe arrepentimiento.', 'CORO(final)\r\n//¿Y quién ha creído a nuestro anuncio?\r\nLo dijo el profeta:\r\n¿Y quién ha creído?//', '', '', '', '', '', '', 6),
(202, '202\r\nALLÁ EN LOS CIELOS ESTÁ TU TRONO', '1\r\n¿Dónde me iré, que tú no estés?\r\n¿Dónde me esconderé? Si todo, Tú, lo ves.\r\nEn esto Señor, hazme comprender;\r\nPorque soy nada, Señor, sin tu poder.', 'CORO\r\n¡Allá en los cielos está tu trono!\r\nLa tierra es estrado de tus pies.\r\nEn todas partes te manifiestas.\r\nDe tu Presencia, esconderme no podré.', '2\r\nTu habitación divina es.\r\nTus ojos, con amor, al justo mirarán.\r\nPara Ti, oh Dios, nada oculto hay;\r\nEn todas partes, Tú ves mi proceder.', 'CORO\r\n¡Allá en los cielos está tu trono!\r\nLa tierra es estrado de tus pies.\r\nEn todas partes te manifiestas.\r\nDe tu Presencia, esconderme no podré.', '3\r\nDesde las alturas puedes ver;\r\nLo mismo te son las tinieblas que la luz.\r\nSi es mi habitación la profundidad,\r\nAllí tu rostro, Señor, me encontrará.', 'CORO(final)\r\n¡Allá en los cielos está tu trono!\r\nLa tierra es estrado de tus pies.\r\nEn todas partes te manifiestas.\r\nDe tu Presencia, esconderme no podré.', '', '', '', '', '', '', 6),
(203, '203\r\nANDANDO EL CAMINO', '1\r\nAndando el camino...\r\n¡El camino de la verdad!\r\n//Mi guía ha sido Cristo\r\nPara poder llegar. //', 'CORO\r\nCuando Él venga en su reino,\r\nMi nombre quiero escuchar.\r\nY poder recibirle con gozo: ¡Qué dicha habrá!\r\nCuando venga en su reino,\r\nCorona, Él, me dará.', '2\r\n“Espinas y cardos”\r\nHe encontrado, en mi vivir.\r\n// ¡Su mano me ha sustentado!\r\nPara poder seguir. //', 'CORO\r\nCuando Él venga en su reino,\r\nMi nombre quiero escuchar.\r\nY poder recibirle con gozo: ¡Qué dicha habrá!\r\nCuando venga en su reino,\r\nCorona, Él, me dará.', '3\r\nY si en mi camino,\r\nÉl me llame a descansar...\r\n// Sin miedo y agradecido,\r\nMi ser reposará. //', 'CORO(final)\r\nCuando Él venga en su reino,\r\nMi nombre quiero escuchar.\r\nY poder recibirle con gozo: ¡Qué dicha habrá!\r\nCuando venga en su reino,\r\nCorona, Él, me dará.', '', '', '', '', '', '', 6),
(204, '204\r\nANDAR JUNTO A TI', '1\r\nMi Señor me salvó del peligro;\r\nEs por siempre mi Amigo, es mi intercesor.\r\nÉl borró todas mis transgresiones;\r\nMe libró de la muerte, y encontré en Él solaz.', 'CORO\r\nHas llenado mi vida de amor,\r\nLas gracias te doy, pues siempre estás aquí.\r\nTu camino, hoy quiero seguir;\r\nAndar junto a Ti. ¡No te separes de mí!', '2\r\nRoca firme, es, refugio y abrigo;\r\nEsperanza me ha sido; Mora en mi corazón.\r\nÉl disipa de mí los temores.\r\nPara Él, son mis loores, pues me ha dado su paz.', 'CORO\r\nHas llenado mi vida de amor,\r\nLas gracias te doy, pues siempre estás aquí.\r\nTu camino, hoy quiero seguir;\r\nAndar junto a Ti. ¡No te separes de mí!', '3\r\nEn las penas ha sido mi alivio.\r\nGuía Él, mi destino, a mi lado Él va.\r\nMis dolores, consuela y conforta;\r\nBondadoso me cura, ¡Nuevas fuerzas me da!', 'CORO(final)\r\nHas llenado mi vida de amor,\r\nLas gracias te doy, pues siempre estás aquí.\r\nTu camino, hoy quiero seguir;\r\nAndar junto a Ti. ¡No te separes de mí!', '', '', '', '', '', '', 6),
(205, '205\r\nAYER QUE EN EL MUNDO ESTABA', '1\r\nAyer que en el mundo estaba,\r\nVivía sin Dios y sin fe;\r\nCreía en cosas tan vanas...\r\nPerdido, iba a perecer.', 'CORO\r\nMas yo vine a Cristo, ¡Mi vida cambió!\r\nCon fuerza, Él, me rescató.\r\nSoy nueva criatura: un hijo de Dios.\r\nPor gracia, hoy salvo ya soy.', '2\r\nAyer que en el mundo estaba,\r\nY no conocía al Señor,\r\nMi carne anhelaba placeres;\r\n¡Vivía en total confusión!', 'CORO\r\nMas yo vine a Cristo, ¡Mi vida cambió!\r\nCon fuerza, Él, me rescató.\r\nSoy nueva criatura: un hijo de Dios.\r\nPor gracia, hoy salvo ya soy.', '3\r\nAyer que en el mundo estaba...\r\nCansado de tanto pecar.\r\nLa gracia de Dios me esperaba;\r\nMis faltas, ¡Llegó a perdonar!', 'CORO(final)\r\nMas yo vine a Cristo, ¡Mi vida cambió!\r\nCon fuerza, Él, me rescató.\r\nSoy nueva criatura: un hijo de Dios.\r\nPor gracia, hoy salvo ya soy.', '', '', '', '', '', '', 6),
(206, '206\r\nASÍ LO HA DICHO EL SEÑOR', '1\r\n¡Nunca me dejes Señor!\r\n¡Nunca, te ruego, mi Dios!\r\nPorque, en momentos, el mal,\r\nCon mi fe, busca acabar.', 'CORO\r\nAsí lo ha dicho el Señor:\r\n“Contigo siempre estaré”;\r\n“Prométeme, siempre ser fiel”;\r\n“Seguir mi camino hasta el fin”.', 'CORO\r\n“Diez mil a tu diestra caerán,\r\nY mil, a tu izquierda también”.\r\n“Contigo, mi espíritu irá...\r\nPara acompañarte”.', '2\r\nPor eso, ¡Nunca! Señor,\r\nDe Ti, me quiero alejar;\r\nPorque eres amigo fiel.\r\nSi cumplo, me ayudarás.', 'CORO\r\nAsí lo ha dicho el Señor:\r\n“Contigo siempre estaré”;\r\n“Prométeme, siempre ser fiel”;\r\n“Seguir mi camino hasta el fin”.', 'CORO(final)\r\n“Diez mil a tu diestra caerán,\r\nY mil, a tu izquierda también”.\r\n“Contigo, mi espíritu irá...\r\nPara acompañarte”.', '', '', '', '', '', '', 6),
(207, '207\r\nAUMENTA MI FE', '1\r\nEn tu sendero mi Dios, estoy caminando.\r\nA cada paso que doy, voy reconociendo:\r\nTu infinito amor, tu inmenso poder,\r\nTu bendito perdón, para el pecador.', '2\r\nEn este andar, mi Señor, también he encontrado,\r\nQue el enemigo también camina a mi lado;\r\nY me causa dolor, desaliento y pesar.\r\nMás confiado en Ti, lo podré alejar.', '3(final)\r\n¿Qué más me espera aquí? Señor: ¡Tú lo sabes!\r\nTe ruego, ¡Aumenta mi fe!, ...y pueda estar firme.\r\nNo permitas Señor, sea probado de más;\r\nNo me quiero perder, por tener débil fe.', '', '', '', '', '', '', '', '', '', 3),
(208, '208\r\nBUSCA A DIOS', '1\r\n¡Cuánto amor me ha dado el Padre!\r\nDe ser llamado: ¡hijo de Dios!\r\n¡Pues no hay bendición más grande!\r\nQue ser hijo de mi Dios.', 'CORO\r\n¡Él me compró con la sangre de su Hijo!\r\nBondad más grande, nadie puede contar.\r\nHoy busco a Dios mientras puede ser hallado,\r\n...Tal vez mañana muy tarde será...', '2\r\nLa maldad siempre me rodeaba;\r\nMi corazón iba presto al mal\r\nY el amor de Dios me buscaba...\r\n¡Mi alma, así, logró salvar!', 'CORO\r\n¡Él me compró con la sangre de su Hijo!\r\nBondad más grande, nadie puede contar.\r\nHoy busco a Dios mientras puede ser hallado,\r\n...Tal vez mañana muy tarde será...', '3\r\nAhora mi corazón es salvo;\r\nCon nada yo lo podré pagar.\r\nCon mi voz, Padre, ¡Yo te alabo!\r\n¡Quiero tu Nombre ensalzar!', 'CORO(final)\r\n¡Él me compró con la sangre de su Hijo!\r\nBondad más grande, nadie puede contar.\r\nHoy busco a Dios mientras puede ser hallado,\r\n...Tal vez mañana muy tarde será...', '', '', '', '', '', '', 6),
(209, '209\r\nBUSCANDO AL SALVADOR', '1\r\nTransitando en el mundo yo vivía,\r\nSin alivio, sin ayuda y sin amor;\r\n//Con el corazón muy triste en agonía,\r\nY buscando a Jesús el Salvador. //', '2\r\nHace tiempo que yo te andaba buscando,\r\nPero nadie pudo hablarme bien de Ti,\r\n//Hasta que un siervo de Dios me vio llorando\r\nY me dijo: ¿Tú por qué lloras así? //', '3(final)\r\nNo te tardes, ¡Ven a Cristo!, hoy te invito.\r\nNo derroches más el tiempo en vanidad;\r\n//Ya se acerca la venida del bendito\r\nY su reino con sus santos de verdad. //', '', '', '', '', '', '', '', '', '', 3),
(210, '210\r\nCAMINO QUE LLEVA A DIOS', '1\r\nEn el correr del tiempo cruel,\r\nVeo pasar tanta maldad.\r\nEs tan difícil vivir, sin tenerte...;\r\nVivir sin Ti, ¡Mejor morir!\r\nTemo a la muerte, pero sin tenerte.\r\n¡Hoy soy feliz! Porque en mí estás.', '2\r\nQue bendición es conocer\r\nLa ruta que lleva hasta Ti;\r\n¡Aquel sendero!... Tu hermoso evangelio;\r\nNuevas de paz, ¡Qué gran solaz!\r\nEn él camino, cual un peregrino...\r\nY así llegar, ¡Hasta el final!', '3(final)\r\nEl recordar, el tiempo atrás,\r\nEs naufragar..., es añorar...\r\n¡Aquella vida!, viviendo sin Cristo.\r\n¡Qué triste fue vivir sin fe!...\r\nHoy sigo a Cristo, ¡Mi gran esperanza!\r\n¡No volveré la vista atrás!', '', '', '', '', '', '', '', '', '', 3),
(211, '211\r\nCONCÉDEME SEÑOR SERVIRTE', '1\r\nConcédeme, Señor, servirte desde la edad de mi juventud.\r\nConcédeme, Señor, morar dentro de tus “atrios”, ¡Dios!', '1\r\nQue pueda yo, Señor, vivir para tu gloria y honor.\r\n¡Ayúdame!, que pueda ser útil en tu obra, Señor.', 'CORO\r\nAntes que mi fuerza termine y mis pies pierdan vigor;\r\nAntes que mis ojos se cierren, concédeme servirte, ¡Oh Dios!', 'CORO\r\nQue jamás mi fe desmaye, aunque triste sea mi andar.\r\nPermite poder escuchar, a tiempo, tu precioso llamar.', '2\r\nConcede que pueda mostrarte, el anhelo que ha nacido en mí,\r\nDe hacer cumplir Tu Voluntad y entregarme sólo a Ti.', '2\r\nConcede que mi brazo esté fortalecido, Señor;\r\nQue no le tema al porvenir; ser por Ti, más que vencedor.', 'CORO\r\nAntes que mi fuerza termine y mis pies pierdan vigor;\r\nAntes que mis ojos se cierren, concédeme servirte, ¡Oh Dios!', 'CORO(final)\r\nQue jamás mi fe desmaye, aunque triste sea mi andar.\r\nPermite poder escuchar, a tiempo, tu precioso llamar.', '', '', '', '', 8),
(212, '212\r\nCONMIGO ESTÁS', '1\r\nYo confiaré en Ti ¡Oh, Dios!\r\nMi fe firme en Ti está.\r\nEn tempestad no temeré,\r\nTu brazo me apoyará.', 'CORO\r\nEl dolor se irá de mí,\r\nPues Tú lo quitarás.\r\nEn lucha cruel no habrá temor, pues Tú conmigo estás.\r\nEn lucha cruel no habrá temor, pues Tú a mi lado vas.', '2\r\nCon gran amor, con gran poder,\r\nTú siempre me ayudarás.\r\nMi senda siempre alumbrarás,\r\nMi pie no tropezará.', 'CORO\r\nEl dolor se irá de mí,\r\nPues Tú lo quitarás.\r\nEn lucha cruel no habrá temor, pues Tú conmigo estás.\r\nEn lucha cruel no habrá temor, pues Tú a mi lado vas.', '3\r\nMis cargas ya ligeras son:\r\nApoyo eres para mí.\r\nConfiado estoy, no temo ya,\r\nMi vida en tu mano está.', 'CORO(final)\r\nEl dolor se irá de mí,\r\nPues Tú lo quitarás.\r\nEn lucha cruel no habrá temor, pues Tú conmigo estás.\r\nEn lucha cruel no habrá temor, pues Tú a mi lado vas.', '', '', '', '', '', '', 6),
(213, '213\r\nCONSERVANDO NUESTRA FE', '1\r\nEn dura prueba, ¡El amor siempre estará!\r\nEn la contienda, ¡La paciencia reinará!\r\nEn sufrimiento, ¡La oración florecerá!\r\nY nuestro Padre, ¡Siempre nos protegerá!', 'CORO\r\n¡Vamos hermanos!, ¡Siempre unidos con fervor!\r\n¡Y venceremos en el Nombre del Señor!\r\n¡Seremos fuertes contra la tentación!\r\nNuestro Creador, nos concederá el valor.', '2\r\nConservaremos nuestra fe con mucho amor.\r\nCaminaremos el sendero de aflicción...\r\nNos tomaremos de la mano en la oración\r\n¡Y triunfaremos sobre la tribulación!', 'CORO(final)\r\n¡Vamos hermanos!, ¡Siempre unidos con fervor!\r\n¡Y venceremos en el Nombre del Señor!\r\n¡Seremos fuertes contra la tentación!\r\nNuestro Creador, nos concederá el valor.', '', '', '', '', '', '', '', '', 4),
(214, '214\r\nCUANDO LLEGASTE', '1\r\nA Ti que moras en el cielo; a Ti que guías mis pasos;\r\nA Ti que conoces mi sendero; te rindo, hoy, adoración.', '1\r\nRecuerdo aquellos momentos, en que mi vida estaba sin Ti.\r\nTú fuiste quien me dio consuelo; pusiste de tu paz en mí.', 'CORO\r\nCuando llegaste a mí,\r\nSacaste del mal a mi ser.\r\nHoy te vengo a servir,\r\nPorque sé que eres Tú, mi Dios de amor.', '2\r\nEl hombre sin tu dirección, por sendas del mal camina,\r\nY Tú, que eres grande y bondadoso, esperas, brindando perdón.', '2\r\nAgradezco y te digo: Que a tu Iglesia le basta ser fiel.\r\nNo importa que vengan las pruebas, ¡Tu mano nos apoyará!', 'CORO(final)\r\nCuando llegaste a mí,\r\nSacaste del mal a mi ser.\r\nHoy te vengo a servir,\r\nPorque sé que eres Tú, mi Dios de amor.', '', '', '', '', '', '', 6),
(215, '215\r\n¡CUÁNTO TE AMO MI JESÚS!', '1\r\nBendición das a mi vida,\r\nCariñoso Salvador.\r\n¡Quiero estar en tu Presencia!\r\n¡Necesito de tu amor!', 'CORO\r\nQuiero estar, siempre en tus brazos\r\nY sentir tu protección.\r\n¿Noche oscura? ¡No le temo!\r\n¡Eres Luz al corazón!\r\n¡Cuánto te amo mi Jesús!', '2\r\nTus pisadas, cual camino\r\nQue has dejado con amor,\r\nSeguiré mi fiel Amigo.\r\n¡Siempre en pos de Ti, Señor!', 'CORO\r\nQuiero estar, siempre en tus brazos\r\nY sentir tu protección.\r\n¿Noche oscura? ¡No le temo!\r\n¡Eres Luz al corazón!\r\n¡Cuánto te amo mi Jesús!', '3\r\nEn invierno, en verano,\r\nEn vejez y en mocedad:\r\nTú me extenderás la mano,\r\nMensajero de bondad.', 'CORO(final)\r\nQuiero estar, siempre en tus brazos\r\nY sentir tu protección.\r\n¿Noche oscura? ¡No le temo!\r\n¡Eres Luz al corazón!\r\n¡Cuánto te amo mi Jesús!', '', '', '', '', '', '', 6),
(216, '216\r\nDAME TU MANO', '1\r\nEn los momentos de dura prueba,\r\nDame tu mano, mi Redentor.\r\nMantenme firme en ésta vida.\r\n¡Firme y confiado en Ti! Señor.', 'CORO\r\nDame tu mano, que es mi Consuelo;\r\nEs mi apoyo para seguir.\r\n¡Sostén y ayuda en mi sendero!\r\nSé, que de ella, llegaré al fin.', '2\r\nVencer al mundo y sus placeres,\r\nNo es algo fácil, si uno no está,\r\nDe Ti, asido y convencido;\r\nCon el peligro de claudicar.', 'CORO\r\nDame tu mano, que es mi Consuelo;\r\nEs mi apoyo para seguir.\r\n¡Sostén y ayuda en mi sendero!\r\nSé, que de ella, llegaré al fin.', '3\r\nTierno, amoroso, tiendes tu mano.\r\nQuieres que el hombre se allegue a Ti.\r\nYo he decidido tomar tu mano.\r\n¡Toma la mía y llévame, así!', 'CORO(final)\r\nDame tu mano, que es mi Consuelo;\r\nEs mi apoyo para seguir.\r\n¡Sostén y ayuda en mi sendero!\r\nSé, que de ella, llegaré al fin.', '', '', '', '', '', '', 6),
(217, '217\r\nDE SU MANO YO CAMINARÉ', '1\r\nEl camino de la vida es difícil,\r\nPues sufrimos acechanzas de Satán.\r\nSi confiamos plenamente en Jesucristo,\r\n¡Vencedores llegaremos al final!', 'CORO\r\n¡Jehová es escudo! Y gran fortaleza,\r\n¡Con mi cántico le alabaré!\r\n¡Jehová es escudo! ¡Mi fortaleza!\r\nDe su mano yo caminaré.', '2\r\nNo importa cuán difícil sea la senda,\r\n¡Voy camino a la eterna salvación!\r\n¡Lucharé y venceré todas las pruebas!\r\n¡Seguiré hasta obtener mi galardón!', 'CORO\r\n¡Jehová es escudo! Y gran fortaleza,\r\n¡Con mi cántico le alabaré!\r\n¡Jehová es escudo! ¡Mi fortaleza!\r\nDe su mano yo caminaré.', '3\r\nGloria es del joven, sin duda, su fuerza;\r\nEs virtud que proviene del Señor.\r\n¡Heme aquí!: yo iré, por donde Él quiera.\r\nDo me guíe la presencia de mi Dios.', 'Coro Final\r\n¡Jehová es escudo! Y gran fortaleza,\r\n¡Con mi cántico le alabaré!\r\n¡Jehová es escudo! ¡Mi fortaleza!\r\n/// De su mano yo caminaré. ///', '', '', '', '', '', '', 6);
INSERT INTO `himnos` (`id_himno`, `pagina_0`, `pagina_1`, `pagina_2`, `pagina_3`, `pagina_4`, `pagina_5`, `pagina_6`, `pagina_7`, `pagina_8`, `pagina_9`, `pagina_10`, `pagina_11`, `pagina_12`, `num`) VALUES
(218, '218 \r\nEL AMOR DE DIOS', '1\r\nEn esto consiste el amor de Dios:\r\nNo que yo haya amado a mi Señor,\r\nSino primero que Él a mí me amó,\r\nEnviando a su Hijo, el Redentor.', 'CORO\r\nPero jamás habrá un amor tan grande,\r\nComo el de Dios, cuando entregó a su Hijo;\r\n// Y todo aquel que crea, no se pierda;\r\nMas tenga vida eterna con el Salvador. //', '2\r\nInexplicable es el amor de Dios...;\r\nMe dio esperanza, vida y salvación.\r\nEn medio de tan vil maldad me vio...;\r\n¡Más tengo ya su bendición!', 'CORO\r\nPero jamás habrá un amor tan grande,\r\nComo el de Dios, cuando entregó a su Hijo;\r\n// Y todo aquel que crea, no se pierda;\r\nMas tenga vida eterna con el Salvador. //', '3\r\nA mi Señor, mi canto hoy le doy.\r\n¡Para alabanza suya me formó!\r\nEn su Hijo, mis pecados, perdonó,\r\nY así mi vida transformó.', 'CORO\r\nPero jamás habrá un amor tan grande,\r\nComo el de Dios, cuando entregó a su Hijo;\r\n// Y todo aquel que crea, no se pierda;\r\nMas tenga vida eterna con el Salvador. //', '4\r\nJamás terminaré de agradecer,\r\nSu fiel bondad y tanta compasión.\r\nPor gracia y don me encuentro hoy en fe.\r\n¡Doy gracias por mi redención!', 'CORO(final)\r\nPero jamás habrá un amor tan grande,\r\nComo el de Dios, cuando entregó a su Hijo;\r\n// Y todo aquel que crea, no se pierda;\r\nMas tenga vida eterna con el Salvador. //', '', '', '', '', 8),
(219, '219\r\nEL AMOR DE DIOS NO ESTÁ OCULTO', '1\r\nMuchas veces nos olvidamos\r\nDel amor que nos tiene Dios,\r\nPues en nuestro poder confiamos\r\nY olvidamos quién nos creó.', 'CORO\r\nEl amor de Dios no está oculto\r\nY en muchas formas lo podemos sentir;\r\nPues Él nos da una vida llena\r\nDe dicha y paz, para ser feliz.', 'CORO\r\nNo desperdicies hoy el momento,\r\nNi tardes más en decirle a Dios:\r\nQue, sin su gracia y bondad infinita,\r\n¿Quién eres tú?', '2\r\nHoy y siempre ensalzaremos\r\nSu amor, que por siempre es fiel;\r\nPues Él nos cuida y nos protege;\r\n¡Jamás nos dejará caer!', 'CORO\r\nEl amor de Dios no está oculto\r\nY en muchas formas lo podemos sentir;\r\nPues Él nos da una vida llena\r\nDe dicha y paz, para ser feliz.', 'CORO\r\nNo desperdicies hoy el momento,\r\nNi tardes más en decirle a Dios:\r\nQue, sin su gracia y bondad infinita,\r\n¿Quién eres tú?', '3\r\n¡Tributemos honor y gloria!\r\n¡Alabemos con gran fervor!\r\nNo olvidemos que Él da la vida.\r\n¡A su Nombre todo loor!', 'CORO\r\nEl amor de Dios no está oculto\r\nY en muchas formas lo podemos sentir;\r\nPues Él nos da una vida llena\r\nDe dicha y paz, para ser feliz.', 'CORO(final)\r\nNo desperdicies hoy el momento,\r\nNi tardes más en decirle a Dios:\r\nQue, sin su gracia y bondad infinita,\r\n¿Quién eres tú?', '', '', '', 9),
(220, '220 \r\nEL PODER DE TU MIRAR', '1\r\nEn la distancia puedo ver,\r\nLa tenue luz de tu mirar,\r\nQue me invita a caminar\r\nA donde Tú estás.\r\n', '1\r\nPor lo cual, me esfuerzo más,\r\nPor alcanzar tu voluntad\r\nY gozar de un reino de luz y amor,\r\nQue Tú prometes para mí.', '2\r\nCierto es que yo, muy sucio estoy...;\r\nPero por eso vine a Ti;\r\nPara limpiar mi corazón\r\nCon tu mirar, Señor.', '2\r\nEl poder de tu mirar,\r\nMe ayudará a transformar,\r\nMi camino de maldad,\r\nPor un camino de paz y amor.', '3\r\n¡Un gran terror sentía en mí!\r\n¡Todo era sólo oscuridad!\r\nNo conocía la bondad\r\nDe tu mirar, Señor.', '3(final)\r\n¡Esa luz iluminó!\r\nLa senda que debía seguir;\r\nNunca más me extraviaré,\r\nPorque Tú, iluminas mi andar.', '', '', '', '', '', '', 6),
(221, '221\r\nÉL PROVEERÁ', '1\r\nTodo lo que hoy pueda ocurrir\r\nY todo lo que podrá acontecer,\r\nTiene un “por qué”, también su lugar;\r\n¡Difícil saber lo que el día traerá!', 'CORO\r\nEn Dios, confía mi alma, mi ser;\r\n¡Y no temeré!\r\nDe lo que el día traerá con su afán,\r\nPues sé bien, que Él proveerá.', '2\r\nSiempre está Dios, que quita el dolor.\r\nA veces en mí, existe el dudar.\r\nCon tierno amor, mi mal curará;\r\n¡Y la fortaleza, otorgará!', 'CORO\r\nEn Dios, confía mi alma, mi ser;\r\n¡Y no temeré!\r\nDe lo que el día traerá con su afán,\r\nPues sé bien, que Él proveerá.', '3\r\n¡Misericordia y gracia sin par!\r\nDispuestas están, a todo aquel\r\nQue cree que Dios le puede salvar;\r\nNo tiene más que seguir siempre fiel.', 'CORO(final)\r\nEn Dios, confía mi alma, mi ser;\r\n¡Y no temeré!\r\nDe lo que el día traerá con su afán,\r\nPues sé bien, que Él proveerá.', '', '', '', '', '', '', 6),
(222, '222\r\nEL SEÑOR ES MI PASTOR', '1\r\nPor amor de tu Nombre,\r\nQue yo pueda andar,\r\nPor las sendas de justicia,\r\nPor caminos de bondad.', '1\r\nMuéstrame tus senderos,\r\nTe lo ruego, Señor,\r\nQue hacia todo lo bueno\r\nPueda ir mi corazón.', 'CORO\r\n¡Escúchame Dios mío!\r\nTodo el día espero en Ti,\r\nMi alma de Ti dice:\r\n“El Señor es mi Pastor”.\r\nPon tu voluntad sobre mí,\r\nMi camino ilumina, Señor.', '2\r\n¡Oh Señor! Que confiado\r\nPueda mis pasos dar,\r\nQue no tema en el valle,\r\nNi en la fría oscuridad.', '2\r\nY cuando el sol decline,\r\nY en mi dulce vejez,\r\nMiraré complacido,\r\nQue jamás me aparté.', 'CORO(final)\r\n¡Escúchame Dios mío!\r\nTodo el día espero en Ti,\r\nMi alma de Ti dice:\r\n“El Señor es mi Pastor”.\r\nPon tu voluntad sobre mí,\r\nMi camino ilumina, Señor.', '', '', '', '', '', '', 6),
(223, '223\r\nEN ÉL CONFIARÉ', '1\r\nDivino Pastor es mi Cristo...\r\nMis penas yo dejo en Él;\r\nSu ayuda me da, en dura aflicción;\r\nYa no temo, ¡En Él confiaré!', 'CORO\r\nMis cargas, alivia Jesús.\r\nDisfruto la paz que me da.\r\nVendrán tempestades que azoten mi fe:\r\n¡En Cristo Jesús confiaré!', '2\r\nPerdido, Él me dio su mano...\r\n¡La noche tornóse en luz!\r\nHoy puedo cantar, que Cristo el Señor,\r\nFortalece mi espíritu.', 'CORO\r\nMis cargas, alivia Jesús.\r\nDisfruto la paz que me da.\r\nVendrán tempestades que azoten mi fe:\r\n¡En Cristo Jesús confiaré!', '3\r\nEn Cristo está mi esperanza;\r\nQue cuando me alcance el final,\r\nMe levantará para contemplar\r\nSu rostro; ¡Él viene a reinar!', 'CORO(final)\r\nMis cargas, alivia Jesús.\r\nDisfruto la paz que me da.\r\nVendrán tempestades que azoten mi fe:\r\n¡En Cristo Jesús confiaré!', '', '', '', '', '', '', 6),
(224, '224\r\n¿EN DÓNDE ESTÁS?', '1\r\nTras el ocaso...cuando cae la noche;\r\n¡El sol no brilla!... las sombras cubren ya...\r\nMi alma te busca, emite un lamento;\r\nSuspira triste... ¡Anhela ver tu faz!', 'CORO\r\nY me pregunto: “¿En dónde estás Maestro?”\r\nQue ni en el mismo huerto, te puedo encontrar...\r\nTu voz divina, no se oye en el viento...\r\nMe acusa el cruel silencio: de que te abandoné.', '2\r\nY cuando todo ha quedado en calma...\r\nVeo a lo lejos, un frío amanecer...\r\n¿Cómo les digo, divino Maestro,\r\nQue soy culpable?... ¡Que Tú no has hecho mal!', 'CORO(final)\r\nY me pregunto: “¿En dónde estás Maestro?”\r\nQue ni en el mismo huerto, te puedo encontrar...\r\nTu voz divina, no se oye en el viento...\r\nMe acusa el cruel silencio: de que te abandoné.', '', '', '', '', '', '', '', '', 4),
(225, '225\r\nEN MALDAD VIVÍ', '1\r\nMucho tiempo en maldad viví,\r\nLleno de tristeza y de sufrir.\r\n//Arrastrando mi pecado vine a Ti,\r\nA decirte todo lo que fui//', '2\r\nHoy te digo, como el siervo aquel:\r\n“Ya no soy digno de Ti, Señor,\r\n//He pecado contra el cielo y contra Ti.\r\nYa no soy digno de Ti, Señor” //', '3\r\nYo vagaba en este mundo infiel;\r\nAgobiado del dolor tan cruel;\r\n//Cuando oí una voz que me decía así:\r\n“Ven y lava tu pecado vil” //', '4(final)\r\nHoy te doy las gracias, mi Señor,\r\nPor cambiar mi vida del error.\r\n//Ya no soy el mismo ser que antes fui.\r\nYa mi vida pertenece a Ti//', '', '', '', '', '', '', '', '', 4),
(226, '226\r\nEN MI VIDA PERMANECES', '1\r\nAl recordar momentos de dolor en mi vida...\r\nAl recordar el tiempo sumergido en la angustia...', '1\r\nSon instantes que jamás olvidaré, ¡Mi Señor!\r\nPorque Tú me ayudaste... junto a mí estuviste...\r\n¡Oh, Gran Dios!', 'CORO\r\n¡Tu grande amor hacia mí! ¡Oh Señor!\r\nMe sostiene y me guía.\r\nHoy, ya no hay dolor; ¡Permaneces en mí!\r\nTu poder me protege, donde quiera, que voy.', '2\r\nSólo a Ti ofrezco lo mejor de mi esfuerzo,\r\nPorque me has dado todo; ¡Mi alma está reposando!', '2\r\n¡Hasta el fin, junto a Ti!... ¡Yo quiero seguirte fiel!\r\nPorque me levantaste; tu bondad me otorgaste,\r\n¡Oh, Gran Dios!', 'CORO\r\n¡Tu grande amor hacia mí! ¡Oh Señor!\r\nMe sostiene y me guía.\r\nHoy, ya no hay dolor; ¡Permaneces en mí!\r\nTu poder me protege, donde quiera, que voy.', '', '', '', '', '', '', 6),
(227, '227\r\nEN PAZ DORMIRÉ', '1\r\nEsta noche en paz dormiré\r\nY así mismo descansaré,\r\nPorque Tú, solo Tú, ¡Oh Señor!\r\nMe haces vivir confiado.', 'CORO\r\nEn la angustia, Tú me confortarás;\r\nCon tu mano, mi vida sostendrás;\r\nComo escudo me protegerás\r\nY aunque tema, en paz me guardarás.', '2\r\nCuando el sol decline otra vez,\r\nMis rodillas a Ti doblaré,\r\nPues a Ti, llevo mi oración...\r\nEsperando en Ti, reposo.', 'CORO\r\nEn la angustia, Tú me confortarás;\r\nCon tu mano, mi vida sostendrás;\r\nComo escudo me protegerás\r\nY aunque tema, en paz me guardarás.', '3\r\nSi el día llega a su fin\r\nY el mañana ya no pueda ver;\r\nAún deshecho este cuerpo vil:\r\nCuando vuelvas, ¡Viviré!', 'CORO(final)\r\nEn la angustia, Tú me confortarás;\r\nCon tu mano, mi vida sostendrás;\r\nComo escudo me protegerás\r\nY aunque tema, en paz me guardarás.', '', '', '', '', '', '', 6),
(228, '228\r\nENSÉÑAME', '1\r\nEstando en humilde oración;\r\nPidiendo de noble corazón;\r\nSintiendo un deseo en mi interior:\r\nTe ruego, oigas Tú, mi clamor.', 'CORO\r\nEnséñame, por dónde debo andar.\r\n¡Quiero vivir siempre en santidad!\r\nPadre afirma hoy mi corazón\r\nY a tu Nombre daré loor.', '2\r\nHay cosas que no puedo entender,\r\n. Mas con tu gran poder lo haré\r\nCreyendo fielmente en Ti,\r\nMe alejo de mi lamento vil.', 'CORO\r\nEnséñame, por dónde debo andar.\r\n¡Quiero vivir siempre en santidad!\r\nPadre afirma hoy mi corazón\r\nY a tu Nombre daré loor.', '3\r\n¡Oh Padre! Tú que eres bondad,\r\nPermite cumpla tu voluntad.\r\nConfianza eres, Tú, para mí.\r\n¡Contigo seguiré hasta el fin!', 'CORO(final)\r\nEnséñame, por dónde debo andar.\r\n¡Quiero vivir siempre en santidad!\r\nPadre afirma hoy mi corazón\r\nY a tu Nombre daré loor.', '', '', '', '', '', '', 6),
(229, '229\r\nENTIENDO HOY EL SACRIFICIO', '1\r\nCaminar ¡A tu lado quiero ir!\r\nSiguiendo paso a paso, tu andar.\r\nComo Tú, quiero ser mi Señor,\r\n// Teniendo, en mi vida, humildad. //', 'CORO\r\nEntiendo hoy el sacrificio\r\nQue hiciste Tú en la cruz.\r\nEntiendo hoy el sacrificio,\r\nQué hiciste mi Jesús.', '2\r\n¡Trabajar! ¡Muchos frutos quiero dar!\r\n¡No sólo con crecer me bastará!\r\nImitar tu ejemplo, ¡Oh Señor!\r\n//Mostrando en mi vida caridad. //', 'CORO(final)\r\nEntiendo hoy el sacrificio\r\nQue hiciste Tú en la cruz.\r\nEntiendo hoy el sacrificio,\r\nQué hiciste mi Jesús.', '', '', '', '', '', '', '', '', 4),
(230, '230\r\nENTRA HOY A MI VIDA', '1\r\nPerdido caminaba en el mundo...\r\n¡Viviendo sin una razón!\r\nPero por tu gracia sublime,\r\nLlegaste a mi corazón.', 'CORO\r\nCristo, ¡Entra hoy a mi vida!\r\n¡Ansío que tus ojos me miren!\r\nDerrama una lluvia de amor\r\nQue, renueve mi ser interior.', '2\r\nConfiado, yo me siento contigo;\r\nNo hay dudas, tampoco temor...;\r\nPorque siento en mí, tu Presencia.\r\n¡Tu luz brilla en mi corazón!', 'CORO\r\nCristo, ¡Entra hoy a mi vida!\r\n¡Ansío que tus ojos me miren!\r\nDerrama una lluvia de amor\r\nQue, renueve mi ser interior.', '3\r\nMaestro: ¡Tómame de la mano!\r\n¡No quiero mirar hacia atrás!\r\nContigo: ¡Andar tu camino!\r\nTus pasos sigo hasta el final.', 'CORO(final)\r\nCristo, ¡Entra hoy a mi vida!\r\n¡Ansío que tus ojos me miren!\r\nDerrama una lluvia de amor\r\nQue, renueve mi ser interior.', '', '', '', '', '', '', 6),
(231, '231\r\nJEHOVÁ, MI GRAN ESCUDO', '1\r\nEn tiempo de mi angustia y dolor,\r\nMi paciencia y mi fe se desmayó;\r\nMás tu misericordia se mostró.\r\n¡Oh bendito Jehová!, que mi ruego escuchó.', 'CORO\r\n// ¡Jehová mi fortaleza!\r\nConfía en Él, mi corazón.\r\n¡Jehová mi gran escudo!\r\nTe daré por siempre mi cantar.//', '2\r\nMi juventud, brillando como el sol,\r\nCon la duda y el error, se oscureció.\r\nMas con tu luz, mi vida iluminó.\r\n¡Oh Bendito Jehová! Que mi ruego escuchó.', 'CORO(final)\r\n// ¡Jehová mi fortaleza!\r\nConfía en Él, mi corazón.\r\n¡Jehová mi gran escudo!\r\nTe daré por siempre mi cantar.//', '', '', '', '', '', '', '', '', 4),
(232, '232\r\nJESUCRISTO ME HA CAMBIADO', '1\r\n¡El Señor me ha cambiado!...\r\nCristo, es un amigo fiel.\r\nY si estás necesitado,\r\nTe cambiará, a ti también.', 'CORO\r\n¡Jesucristo me ha cambiado!\r\nÉl también trasformará,\r\nCorazones angustiados,\r\nQue a Él rendidos, vengan ya.', '2\r\nEl vivir no es suficiente,\r\nNi creer, que hay un Dios;\r\nSino que sinceramente,\r\nSe le entregue el corazón.', 'CORO\r\n¡Jesucristo me ha cambiado!\r\nÉl también trasformará,\r\nCorazones angustiados,\r\nQue a Él rendidos, vengan ya.', '3\r\n¡Ven! Desde hoy, a Jesucristo;\r\n¡Y ya no lo pienses más!\r\n¡Ven! Te dice... ¡Ven conmigo!\r\nY de pecar, descansarás.', 'CORO(final)\r\n¡Jesucristo me ha cambiado!\r\nÉl también trasformará,\r\nCorazones angustiados,\r\nQue a Él rendidos, vengan ya.', '', '', '', '', '', '', 6),
(233, '233\r\nLA BÚSQUEDA DE LO SANTO', '1\r\nEl buscar con temor lo santo aún no termina,\r\nMientras siga en este mundo, preso en las tinieblas.\r\n¡Prosigo a mi blanco! pues Dios conmigo va;\r\n¡El mal no me amedrenta! ¡Su luz me guiará!', '2\r\nDistinguir lo que es santo y profano, es mi tarea.\r\nDecidir entre la muerte y la vida, ¡Debo ahora!\r\n¡Satán está acechando! ¡Tropiezos me pondrá!\r\n¡Jesús me fortalece! ¡Él me ayudará!', '3\r\nEl ayuno y la oración con fe me fortalecen\r\nAunque muy cruel sea el combate ¡Sigo en santidad!\r\nMuy firme es mi objetivo, ¡Mi meta he de lograr!\r\nLas pruebas de esta vida, ¡Ya no me detendrán!', '4(final)\r\nMi existencia dedico a Dios, ¡Su espíritu me guía!\r\nApartarme de la maldad; de Dios, es voluntad.\r\nMil penas y angustias, tal vez, vaya a sufrir;\r\n¡Más seguiré adelante! ¡No temo ya morir!', '', '', '', '', '', '', '', '', 4),
(234, '234\r\nLA FE', '1\r\nEs pues la fe\r\nLa certeza de lo que se espera,\r\nLa convicción de lo que no se ve;\r\n¡Es la que vence al mundo, haciéndonos vivir!', 'CORO\r\n// Y es por la fe\r\nQue vences las tribulaciones;\r\nSi fías en Cristo en tempestades.\r\nUna fe viva que a Dios agradará. //', '2\r\nY es la fe,\r\nLa que siempre nos da fortaleza,\r\nPues que la vida es de nuestro Dios;\r\nLa que nos justifica delante de Él.', 'CORO\r\n// Y es por la fe\r\nQue vences las tribulaciones;\r\nSi fías en Cristo en tempestades.\r\nUna fe viva que a Dios agradará. //', '3\r\nY por la fe,\r\nEs que recibimos bendiciones\r\nY que haya amor en nuestro corazón;\r\nY por la cual, creemos que Cristo vendrá.', 'CORO(final)\r\n// Y es por la fe\r\nQue vences las tribulaciones;\r\nSi fías en Cristo en tempestades.\r\nUna fe viva que a Dios agradará. //', '', '', '', '', '', '', 6),
(235, '235\r\nLA LUZ DE CRISTO', '1\r\nEs la Luz de Cristo que brilla en mi camino,\r\nY la senda oscura, su amor alumbrará.\r\nSiento que a mi lado está mi fiel amigo;\r\nCon su brazo fuerte, ¡Jesús me sostendrá!', '2\r\nValles hay de sombra, de muerte y desconsuelo;\r\nFácil es perderse... extraviarse más y más...\r\nPresto el adversario, con fuerza arremete;\r\nLa esperanza... oculta, con dudas y temor.', '3(final)\r\nBello y gran consuelo, el de mi buen Maestro,\r\nQue, con rayos suaves, el mal desvaneció.\r\n¡Ahora miro al frente! ¡Distingo ya la meta!\r\nCon la Luz de Cristo, ¡El reino alcanzaré!', '', '', '', '', '', '', '', '', '', 3),
(236, '236\r\nLA MISERICORDIA DE DIOS', '1\r\nSi estoy en angustia, Señor, pienso en Ti;\r\nAl flaquear la fe, mi esperanza pongo en Ti\r\nY clamo a Ti, supremo Dios.', 'CORO\r\nDesde tu habitación, Señor,\r\nMe escuchaste, me colmaste de bendición;\r\nMe envolviste con tu amor, ¡Oh Dios!\r\nY fue tu misericordia, más dulce que la miel.', '2\r\nA Ti, Dios eterno, entrego mi ser;\r\nMe consagro a Ti y dedico mi vivir:\r\n¡A tu servicio siempre estar!', 'CORO\r\nDesde tu habitación, Señor,\r\nMe escuchaste, me colmaste de bendición;\r\nMe envolviste con tu amor, ¡Oh Dios!\r\nY fue tu misericordia, más dulce que la miel.', '3\r\nEl Señor ayuda al que espera en Él.\r\nBueno es Jehová para el que le buscó.\r\nMi salvación está en Jehová.', 'CORO(final)\r\nDesde tu habitación, Señor,\r\nMe escuchaste, me colmaste de bendición;\r\nMe envolviste con tu amor, ¡Oh Dios!\r\nY fue tu misericordia, más dulce que la miel.', '', '', '', '', '', '', 6),
(237, '237\r\nLEJOS DEL MUNDO', '1\r\nEstaba en el mundo, vivía entre sombras;\r\nTan lejos de mi Dios; estaba infeliz.\r\nSi uno ama al mundo, en él no está el Padre...\r\nTe alejas, sin pensar que te perderás.', 'CORO\r\n// Por eso no améis al mundo,\r\n¡Apártate! Y sigue a Dios.\r\nEl mundo y sus deseos pasan pronto\r\n¿Y el amor de Dios? //\r\n¡Permanecerá!', '2\r\nHastiado del mundo, llegué a este momento,\r\nCansado y abatido, en cruel tempestad.\r\nAhora comprendo: ¡Cuán lejos estaba!\r\nViviendo en la maldad y sin su perdón.', 'CORO\r\n// Por eso no améis al mundo,\r\n¡Apártate! Y sigue a Dios.\r\nEl mundo y sus deseos pasan pronto\r\n¿Y el amor de Dios? //\r\n¡Permanecerá!', '3\r\n¡El tiempo se agota! Transforma el camino;\r\nNo te arrepentirás de vivir en luz.\r\nEs tiempo de un cambio, que acerque tu vida,\r\nA la verdad divina. ¡Es tu salvación!', 'CORO(final)\r\n// Por eso no améis al mundo,\r\n¡Apártate! Y sigue a Dios.\r\nEl mundo y sus deseos pasan pronto\r\n¿Y el amor de Dios? //\r\n¡Permanecerá!', '', '', '', '', '', '', 6),
(238, '238\r\nLLAMAMIENTO DIVINO', '1\r\nFue tu gran compasión la que obró en mi favor;\r\nY por eso estoy aquí... ¡Arrepentido!\r\nMe envolviste en tu piedad y con “cuerdas de amor”.\r\n¡Ya no pude resistir al llamado divino!', 'CORO\r\nCerca de Ti\r\nMantén mi corazón, ¡Así!\r\nY en camino, mis pies,\r\nDe rectitud, no se aparten, ¡Nunca! Otra vez.', '2\r\nConvertiste a Ti, mi extraviado corazón.\r\nMis heridas con tu amor fueron sanadas.\r\nHoy te entrego ¡Oh Señor! Mi empeño y voluntad,\r\nQue otros puedan escuchar, el llamado divino.', 'CORO(final)\r\nCerca de Ti\r\nMantén mi corazón, ¡Así!\r\nY en camino, mis pies,\r\nDe rectitud, no se aparten, ¡Nunca! Otra vez.', '', '', '', '', '', '', '', '', 4),
(239, '239\r\nMI ALMA CLAMA', '1\r\nA Ti, ¡Oh Dios! clama mi alma\r\nEn medio de la tribulación,\r\nConstante angustia de la vida;\r\nSedienta busca en Ti, el agua viva.', 'CORO\r\nMi alma clama, mi alma te alaba,\r\n¡Oh Dios de las luces! Mi alma ilumina.\r\nCon espíritu radiante, mi alma canta:\r\n¡Glorias a tu Nombre! en toda mi vida.', '2\r\nEl gran poder de tu Presencia,\r\nMi ser defienda de la maldad;\r\n¡Persecución!,... Quizá me atente.\r\nConduce mi alma a feliz bonanza.', 'CORO\r\nMi alma clama, mi alma te alaba,\r\n¡Oh Dios de las luces! Mi alma ilumina.\r\nCon espíritu radiante, mi alma canta:\r\n¡Glorias a tu Nombre! en toda mi vida.', '3\r\nEn el desierto de ésta vida,\r\nDame la fuerza para vencer;\r\nRefresca mi alma en tu palabra\r\nY así podré obtener la vida eterna.', 'CORO(final)\r\nMi alma clama, mi alma te alaba,\r\n¡Oh Dios de las luces! Mi alma ilumina.\r\nCon espíritu radiante, mi alma canta:\r\n¡Glorias a tu Nombre! en toda mi vida.', '', '', '', '', '', '', 6),
(240, '240\r\nMI AMIGO ES ÉL', 'CORO\r\n|| Yo voy con Él, ¡Mi amigo es!\r\nY su amistad será eternal.\r\nY junto a Él estaré allá en Sión,\r\n¡Sí! ¡Allá en Sión! ||', '1\r\n|| Tomando su mano, Él me mostró\r\nLa luz verdadera de su creación\r\nY yo le dije: ¡Oh Señor!\r\n¡Cuán grande es tu amor!||', 'CORO\r\n|| Yo voy con Él, ¡Mi amigo es!\r\nY su amistad será eternal.\r\nY junto a Él estaré allá en Sión,\r\n¡Sí! ¡Allá en Sión! ||', '2\r\n|| Teniendo su gozo en mi ser,\r\nMi alma feliz siempre estará.\r\nCantando exclamo: ¡Oh Señor!\r\n¡Bendito seas Tú!||', 'CORO FINAL\r\nYo voy con Él, ¡Mi amigo es!\r\nY su amistad será eternal.\r\nY junto a Él estaré allá en Sión,\r\n¡Sí! ¡Allá en Sión!\r\nYo voy con Él, ¡Mi amigo es!\r\nY su amistad será eternal.\r\nY junto a Él, estaré allá en Sión.', '', '', '', '', '', '', '', 5),
(241, '241\r\nMI AMIGO Y PROTECTOR', 'CORO\r\n¡Oh, Señor! Eres mi amigo, mi Rey y mi protector.\r\nPor doquiera que yo ando, Tú, me cuidas con amor.', '1\r\nY a delicados pastos, siempre me conducirás;\r\nJunto a aguas de reposo, Cristo, me pastorearás.', 'CORO\r\n¡Oh, Señor! Eres mi amigo, mi Rey y mi protector.\r\nPor doquiera que yo ando, Tú, me cuidas con amor.', '2\r\nAunque en valle de sombra y de muerte llegue a andar;\r\nNo tendré temor alguno, porque Tú, conmigo estás.', 'CORO\r\n¡Oh, Señor! Eres mi amigo, mi Rey y mi protector.\r\nPor doquiera que yo ando, Tú, me cuidas con amor.', '3\r\nTu callado y tu vara... ¡Aliento me infundirán!\r\nAnte mis angustiadores, mesa me aderezarás.', 'CORO\r\n¡Oh, Señor! Eres mi amigo, mi Rey y mi protector.\r\nPor doquiera que yo ando, Tú, me cuidas con amor.', '4\r\nMi cabeza has ungido con aceite, en bendición;\r\nRebosando está mi copa. ¡Te alabo ¡Oh, gran Dios!', 'CORO\r\n¡Oh, Señor! Eres mi amigo, mi Rey y mi protector.\r\nPor doquiera que yo ando, Tú, me cuidas con amor.', '5\r\nQue el bien y misericordia, en mi vida, siempre estén.\r\nY en tu templo, alabanzas, con gran voz exclamaré.', 'CORO(final)\r\n¡Oh, Señor! Eres mi amigo, mi Rey y mi protector.\r\nPor doquiera que yo ando, Tú, me cuidas con amor.', '', 11),
(242, '242\r\nMI ESCUDO', '1\r\nMi escudo Tú, alrededor de mí.\r\n¡Mi gloria! el que levanta mi cabeza,\r\n//Yo me acosté, dormí y desperté,\r\nPorque Jehová me sustentaba.//', 'CORO\r\nEn tus manos están la fuerza y el poder.\r\n¡Es tuya la victoria y el honor!\r\nLas cosas, tuyas son, aquí y en la expansión.\r\nExcelso es tu Nombre, ¡Oh Señor!', '2\r\nLa salvación viene de nuestro Dios.\r\nSobre tu pueblo sea tu bendición.\r\n//No importa que de mí se diga así:\r\n“Para él no hay salvación de Dios”.//', 'CORO\r\nEn tus manos están la fuerza y el poder.\r\n¡Es tuya la victoria y el honor!\r\nLas cosas, tuyas son, aquí y en la expansión.\r\nExcelso es tu Nombre, ¡Oh Señor!', '3\r\nNo temeré lo que me pueda hacer\r\nEl hombre que no teme a Jehová;\r\n//Mi Dios me guardará y me apartará,\r\nSobre la peña me pondrá.//', 'Final\r\nMi escudo Tú, alrededor de mí.\r\n¡Mi gloria!, el que levanta mi cabeza.\r\n//Yo me acosté, dormí y desperté,\r\nPorque Jehová me sustentaba.//', '', '', '', '', '', '', 6),
(243, '243\r\nNUEVA CRIATURA', '1\r\nYa conocí a Cristo, mi vida Él cambió.\r\nMis vicios ha quitado, un nuevo ser soy yo.\r\nLos errores pasados, ya todos los borró\r\nY una nueva criatura ya por su gracia, soy.', 'CORO\r\nYa de mi vida pasada,\r\nOlvídala, Señor.\r\nQue el viejo hombre con vicios\r\nNo vive ya en mi interior.', '2\r\nMi vida, del pecado, con su amor rescató;\r\nY me dio vida, cuando Cristo resucitó;\r\nSu sangre me ha lavado, al mal no sirvo más.\r\nMi espíritu Él renueva, ¡Por Él al reino voy!', 'CORO\r\nYa de mi vida pasada,\r\nOlvídala, Señor.\r\nQue el viejo hombre con vicios\r\nNo vive ya en mi interior.', '3\r\n¡Acércate a Cristo! No le rechaces más;\r\nTe ofrece vida eterna si aceptas su llamar.\r\nDe antaño los pecados: Él disimulará;\r\nOfrece, presto tu vida, y Él te coronará.', 'CORO(final)\r\nYa de mi vida pasada,\r\nOlvídala, Señor.\r\nQue el viejo hombre con vicios\r\nNo vive ya en mi interior.', '', '', '', '', '', '', 6),
(244, '244 \r\nNUEVA LUZ', '1\r\nNueva luz ven nuestros ojos:\r\nOtra oportunidad para cambiar.\r\nEsa luz renueva el alma,\r\nInundando de paz nuestro ser.', '1\r\n¡Es ahora tiempo de empezar!\r\n¡Construyamos junto a Cristo, el sueño de libertad!', 'CORO\r\n¡Ven Señor Jesús! Límpianos más.\r\nTodos queremos, tu fiel ejemplo imitar.\r\nY aunque las sombras nos cubran por doquier,\r\nSabemos, que siempre en Ti podemos confiar.', '2\r\nSantidad, es nuestra meta,\r\nPara poder morar con el Señor.\r\n¡Sale el sol! ¡Un nuevo día!\r\n¡Que felicidad vivir con Él!', '2\r\n¡Es ahora tiempo de empezar!\r\n¡Construyamos junto a Cristo, el sueño de libertad!', 'CORO(final)\r\n¡Ven Señor Jesús! Límpianos más.\r\nTodos queremos, tu fiel ejemplo imitar.\r\nY aunque las sombras nos cubran por doquier,\r\nSabemos, que siempre en Ti podemos confiar.', '', '', '', '', '', '', 6),
(245, '245\r\nNUNCA DESMAYES', '1\r\nEn mis momentos de angustia tenaz,\r\nOro a mi Dios y confianza, Él, me da.\r\nEn los pesares del vil mundo cruel,\r\nSigo a mi Cristo y camino con Él.', 'CORO\r\n“Nunca desmayes”, le oigo decir;\r\n“Sólo el valiente podrá a Mí venir”.\r\n“Nunca desmayes, que prueba te di:\r\nPor darte vida, en madero morí”.', '2\r\nCuando mi alma va a desfallecer,\r\nDios me promete mi fe sostener;\r\nPorque su oído, atento estará\r\nPara sus hijos; ¡Auxilio dará!', 'CORO\r\n“Nunca desmayes”, le oigo decir;\r\n“Sólo el valiente podrá a Mí venir”.\r\n“Nunca desmayes, que prueba te di:\r\nPor darte vida, en madero morí”.', '3\r\nY caminando voy, siempre, con fe.\r\nYa no hay temor, pues confianza tendré.\r\nSi estoy con Dios, ¿Quién habrá contra mí?\r\nVoy con mi Cristo: ¡Él vive, ya, en mí!', 'CORO(final)\r\n“Nunca desmayes”, le oigo decir;\r\n“Sólo el valiente podrá a Mí venir”.\r\n“Nunca desmayes, que prueba te di:\r\nPor darte vida, en madero morí”.', '', '', '', '', '', '', 6),
(246, '246 \r\n¡OH CUÁN GRANDE GRATITUD!', '1\r\nDios pagó con la sangre de su Hijo,\r\nEse precio que yo debía pagar;\r\n¡Cuánto amor nos mostró!, que a su Hijo entregó,\r\nY en la cruz del Calvario, murió.', 'CORO\r\n¡Oh cuán grande gratitud!: Él me vino a salvar.\r\nEse gesto de amor, ¿Con qué, yo, podré pagar?\r\nNunca más olvidaré, su tristeza y su dolor;\r\nPlena muestra de su amor, ¿Con qué yo, podré pagar?', '2\r\nDe Israel era solo la promesa:\r\nDescendientes del patriarca Abraham.\r\nPero gracias a Dios y a su Hijo Jesús,\r\nGozo hoy de una oportunidad.', 'CORO\r\n¡Oh cuán grande gratitud!: Él me vino a salvar.\r\nEse gesto de amor, ¿Con qué, yo, podré pagar?\r\nNunca más olvidaré, su tristeza y su dolor;\r\nPlena muestra de su amor, ¿Con qué yo, podré pagar?', '3\r\nSi tu vida está llena de pecado\r\ny tu alma sedienta de Él está;\r\n¡No te tardes ya más! ¡Él te puede salvar!\r\n¡Ven a Cristo!, no lo pienses más.', 'CORO(final)\r\n¡Oh cuán grande gratitud!: Él me vino a salvar.\r\nEse gesto de amor, ¿Con qué, yo, podré pagar?\r\nNunca más olvidaré, su tristeza y su dolor;\r\nPlena muestra de su amor, ¿Con qué yo, podré pagar?', '', '', '', '', '', '', 6),
(247, '247\r\n¡QUÉ HERMOSO ES SER NIÑO!', '1\r\n¡Qué hermoso es ser niño! ¡Qué hermoso es cantar!\r\nDe las maravillas de un Dios sin igual.\r\nQue guía mis pasos doquiera que voy,\r\nQue extiende sus brazos con inmenso amor.', 'CORO\r\n¡Qué bello es cantar, cantarle al Señor!\r\nPoder disfrutar de su amor.\r\nQuiero expresar mi gratitud de todo corazón\r\n¡Quiero cantar, cantarle al Señor!', '2\r\n¡Qué hermoso es ser niño! Ser niño de Dios,\r\nCantando en su templo, con mucho fervor.\r\nY aunque soy pequeño le puedo sentir,\r\nY estoy tan seguro que Dios está aquí.', 'CORO(final)\r\n¡Qué bello es cantar, cantarle al Señor!\r\nPoder disfrutar de su amor.\r\nQuiero expresar mi gratitud de todo corazón\r\n¡Quiero cantar, cantarle al Señor!', '', '', '', '', '', '', '', '', 4),
(248, '248\r\n¿QUIÉN COMO TU SEÑOR?', '1\r\nSea tu voluntad, Señor en mí;\r\nSea tu voluntad. Confío en Ti.\r\n|| ¿Quién como Tú, Señor? Que al ciego vista das\r\nY haces andar al cojo por tu bondad ||', '2\r\nQuita de mí, Señor, toda maldad,\r\nY haz que tu luz, Señor, pueda alcanzar.\r\n|| ¿Quién como Tú, Señor, que puedes transformar\r\nY haces también al mudo poder hablar? ||', '3(final)\r\nAyúdame, Señor, a serte fiel;\r\nAyúdame, Señor, con tu poder.\r\n|| ¿Quién como Tú, Señor, que lo puedes hacer?\r\nY haces al pecador rendirse a tus pies.||', '', '', '', '', '', '', '', '', '', 3),
(249, '249\r\nRECONFORTA MI EXISTIR', '1\r\nTú que miras desde arriba,\r\nNada, de Ti, se esconde.\r\nAnalizas lo profundo...\r\nLo secreto que hay en mí.\r\nPuedes mirar mi intención\r\nY mi sentir... ¡Mi voluntad!', 'CORO\r\n¡Sostenme con tu gracia!;\r\nNo me dejes caer.\r\nQue en la cruel tormenta,\r\n¡Tu diestra esté en mí!\r\nTu amor, ¡Que no me falte!\r\nReconforta mi existir.', '2\r\n¡Excelentes son tus obras!,\r\nSoberano Creador.\r\nSimplemente, yo me rindo.\r\n¡A tu Nombre doy loor!\r\nTu bendición me das, sin par.\r\nMi vida, en tu mano está.', 'CORO(final)\r\n¡Sostenme con tu gracia!;\r\nNo me dejes caer.\r\nQue en la cruel tormenta,\r\n¡Tu diestra esté en mí!\r\nTu amor, ¡Que no me falte!\r\nReconforta mi existir.', '', '', '', '', '', '', '', '', 4),
(250, '250\r\nSI JEHOVÁ NO EDIFICARE LA CASA', '1\r\nFortalece nuestra obra Señor\r\nY bendice a cada uno que edifica;\r\nPues su esfuerzo vano es, si Tú, no eres con él.\r\n¿Quién, sin Ti, prosperará?', 'CORO(final)\r\n//Es en vano trabajar, al que edifica,\r\n“Si Jehová no edificare la casa.”\r\nPor demás es levantarse de mañana,\r\nSi tu mano con nosotros no está//\r\nSi tu mano con nosotros no está.', '', '', '', '', '', '', '', '', '', '', 2),
(251, '251\r\nSI ORAS CON FE', '1\r\nSi oras con fe y con gran devoción...\r\nLo que pidieres: ¡Lo da el Señor!\r\nComo Elías que, con su oración,\r\nDel cielo, fuego pidió del gran Dios.', 'CORO\r\nSi oras con fe y con seguridad...\r\nSi es cosa justa, el Señor la dará.\r\nMira al Maestro, que cuando oró,\r\nAl mismo mar, con poder, reprendió.', '2\r\nSi oras con fe, en medio de lucha cruel...\r\nSi suplicante acudes a Él...\r\nSiempre muy firme, en Dios, estarás,\r\nY una montaña a la mar lanzarás.', 'CORO\r\nSi oras con fe y con seguridad...\r\nSi es cosa justa, el Señor la dará.\r\nMira al Maestro, que cuando oró,\r\nAl mismo mar, con poder, reprendió.', '3\r\nSi oras con fe y ferviente amor...\r\nTu petición oirá el Creador.\r\nPiensa en Josué que, venciendo el temor,\r\nEl sol detuvo con grande valor.', 'CORO\r\nSi oras con fe y con seguridad...\r\nSi es cosa justa, el Señor la dará.\r\nMira al Maestro, que cuando oró,\r\nAl mismo mar, con poder, reprendió.', '4\r\nOra con fe, que confianza, Dios da;\r\nAunque el maligno te quiera tentar.\r\nPide al Padre y alivio dará.\r\n¡Todas tus pruebas podrás soportar!', 'CORO(final)\r\nSi oras con fe y con seguridad...\r\nSi es cosa justa, el Señor la dará.\r\nMira al Maestro, que cuando oró,\r\nAl mismo mar, con poder, reprendió.', '', '', '', '', 8),
(252, '252\r\nSOLDADOS DE CRISTO', '1\r\nSomos un gran pueblo de humildes soldados\r\nY Cristo va al frente; ¡Nuestro General!\r\nSomos combatientes de muchas hazañas,\r\nQue hemos logrado, con fuerza y valor.', 'CORO\r\n¡Somos la vanguardia! ¡En la lucha muy diestros!\r\nCombatiendo siempre contra el invasor.\r\nCon filosa espada, haremos hoy libres\r\nA los que cautivos, están en maldad.', '2\r\nMuchos de los nuestros, ya vienen diezmados,\r\nLidiando batallas contra el malhechor.\r\nY aunque muy cansados, nos dan el ejemplo:\r\n“Por nuestros ideales, ¡Luchad con valor!”', 'CORO\r\n¡Somos la vanguardia! ¡En la lucha muy diestros!\r\nCombatiendo siempre contra el invasor.\r\nCon filosa espada, haremos hoy libres\r\nA los que cautivos, están en maldad.', '3\r\nSe invita a todos; ¡Cristo está llamando!\r\nAl que quiera sus filas venir a engrosar.\r\nUrgen voluntarios que quieran ceñirse,\r\nLas armas de Cristo, de la libertad.', 'CORO\r\n¡Somos la vanguardia! ¡En la lucha muy diestros!\r\nCombatiendo siempre contra el invasor.\r\nCon filosa espada, haremos hoy libres\r\nA los que cautivos, están en maldad.', '4\r\nHoy se necesitan de nuevos refuerzos\r\nY más estrategas que venzan el mal.\r\nFirmes y adelante, contra el enemigo,\r\nMarchemos unidos: ¡Vamos a ganar!', 'CORO(final)\r\n¡Somos la vanguardia! ¡En la lucha muy diestros!\r\nCombatiendo siempre contra el invasor.\r\nCon filosa espada, haremos hoy libres\r\nA los que cautivos, están en maldad.', '', '', '', '', 8),
(253, '253\r\nSOY NADA SIN TI', '1\r\nMiro el cielo,\r\nNubes negras a mi alrededor.\r\nLuego comprendo\r\nQue requiero de tu protección.', 'CORO\r\nMe sustentas cada instante, Señor;\r\nPues soy nada, si contigo no estoy.\r\nMe proteges y me das de tu amor.\r\nIluminas mi camino, ¡Oh Dios!', '2\r\nSombras me acechan,\r\nMás tu amor, todas las disipó.\r\nSé bien que puedo\r\nAlejarme de todo error.', 'CORO\r\nMe sustentas cada instante, Señor;\r\nPues soy nada, si contigo no estoy.\r\nMe proteges y me das de tu amor.\r\nIluminas mi camino, ¡Oh Dios!', '3\r\n¡Todo he dejado!\r\nA servirte, entregado estoy.\r\n¡Úsame ahora!\r\nHazme útil, en tu obra, mi Dios.', 'CORO(final)\r\nMe sustentas cada instante, Señor;\r\nPues soy nada, si contigo no estoy.\r\nMe proteges y me das de tu amor.\r\nIluminas mi camino, ¡Oh Dios!', '', '', '', '', '', '', 6),
(254, '254\r\nSOY UN SOLDADO', '1\r\nSoy un soldado que, en la lucha,\r\nNo tiene miedo a combatir;\r\nPorque mi adalid es Cristo,\r\nY es mi razón para seguir.', 'CORO\r\nMe he preparado, y lucho presto\r\nCon la armadura de la fe.\r\nSoy un soldado\r\nY con mi Cristo venceré.', '2\r\nMi corazón nunca desmaya,\r\nPorque el Señor me da valor.\r\nTomado de su mano, siempre,\r\nCombato fiel y sin temor.', 'CORO\r\nMe he preparado, y lucho presto\r\nCon la armadura de la fe.\r\nSoy un soldado\r\nY con mi Cristo venceré.', '3\r\nLa tempestad no me doblega,\r\nPues a mi lado va el Señor.\r\nY aunque muy cruenta sea la lucha\r\nFuerza le infunde, al corazón.', 'CORO(final)\r\nMe he preparado, y lucho presto\r\nCon la armadura de la fe.\r\nSoy un soldado\r\nY con mi Cristo venceré.', '', '', '', '', '', '', 6),
(255, '255\r\nSUSTENTA MIS PASOS', '1\r\nAnhelo, Señor, siempre servirte;\r\nY por siempre, tu Ley obedecer.\r\nPor eso necesito pedirte,\r\nQue me ayudes, y así seguirte fiel.', 'CORO\r\nSustenta mis pasos en tus sendas.\r\n¡Que mi vida, la viva en santidad!\r\nPermite que tu luz me guíe,\r\nEn caminos de rectitud y paz.', '2\r\n¿Podré, Señor, hacerle frente\r\nA este mundo, yo solo, sin caer?\r\nPor eso te ruego me acompañes;\r\nQue tu diestra me pueda sostener.', 'CORO\r\nSustenta mis pasos en tus sendas.\r\n¡Que mi vida, la viva en santidad!\r\nPermite que tu luz me guíe,\r\nEn caminos de rectitud y paz.', '3\r\nEn Ti, las cargas son ligeras;\r\nLos problemas, ¡Si tienen solución!\r\nLos miedos, tu amor los desvanece.\r\nEres roca y escudo protector.', 'CORO(final)\r\nSustenta mis pasos en tus sendas.\r\n¡Que mi vida, la viva en santidad!\r\nPermite que tu luz me guíe,\r\nEn caminos de rectitud y paz.', '', '', '', '', '', '', 6),
(256, '256\r\nTESTIFICANDO LA VERDADERA GRACIA DE DIOS', '1\r\nAyer reconocí que todo mi ser\r\nNecesitaba más de Ti,\r\nY me propuse, cada día, serte fiel;\r\nY hoy me encuentro viviendo en Ti.', 'CORO\r\nTestificando la verdadera gracia de Dios,\r\nPersevero sostenido por la fe,\r\nTe alabo y bendigo, por tu amor,\r\n¡Que hoy disfruto ya!', '2\r\nLo que me diste Tú, lo quiero ofrecer;\r\nMi ofrenda es solo tuya, Dios.\r\nHablando tu doctrina de amor y fe,\r\nQue hoy me ha dado la salvación.', 'CORO\r\nTestificando la verdadera gracia de Dios,\r\nPersevero sostenido por la fe,\r\nTe alabo y bendigo, por tu amor,\r\n¡Que hoy disfruto ya!', '3\r\nEn cada suspirar, mi corazón en fe,\r\nRecuerda tu Gracia y perdón.\r\nA este inmenso mundo que muere sin fe,\r\nLe hablaré de tu grande amor.', 'CORO\r\nTestificando la verdadera gracia de Dios,\r\nPersevero sostenido por la fe,\r\nTe alabo y bendigo, por tu amor,\r\n¡Que hoy disfruto ya!', '4\r\nResuelto yo estoy, a testificar;\r\nSeguro de servirte Dios\r\nEmprendo con valor, con fe y humildad\r\nLa senda que Cristo nos dejó.', 'CORO(final)\r\nTestificando la verdadera gracia de Dios,\r\nPersevero sostenido por la fe,\r\nTe alabo y bendigo, por tu amor,\r\n¡Que hoy disfruto ya!', '', '', '', '', 8),
(257, '257\r\nTOMA MI CORAZÓN', '1\r\nToma hoy mi corazón,\r\nLlénalo de tu amor.\r\n¡Cambia mi ser!, ¡Transfórmame!\r\nLibre de mal seré.', '1\r\nQuita hoy mi iniquidad\r\nY haz que sienta tu perdón;\r\n¡Oh Dios! ¡Oh Dios!\r\nSólo tu perdón.', 'CORO\r\n¡Oh mi gran Dios! Escucha mi voz.\r\n¡Oh mi Jesús! Intercede por mí;\r\nQuiero estar,\r\nCuando vengas junto a Ti.', '2\r\nQue mis labios al hablar,\r\nHablen solo de tu amor.\r\nTe cantaré, te alabaré,\r\nCon gran fervor lo haré.', '2\r\nToma hoy mi voluntad\r\nY hazla tuya nada más;\r\n¡Oh Dios! ¡Oh Dios!\r\nTuya nada más.', 'CORO\r\n¡Oh mi gran Dios! Escucha mi voz.\r\n¡Oh mi Jesús! Intercede por mí;\r\nQuiero estar,\r\nCuando vengas junto a Ti.', '3\r\n¿Qué más te puedo pedir?\r\n¡Tengo todo junto a Ti!\r\nTu compasión, tu bendición,\r\nTu infinito amor.', '3\r\nTodo ha cambiado en mí:\r\nMi vida es llena de tu amor.\r\n¡Oh Dios! ¡Oh Dios!\r\nLlena de tu amor.', 'CORO(final)\r\n¡Oh mi gran Dios! Escucha mi voz.\r\n¡Oh mi Jesús! Intercede por mí;\r\nQuiero estar,\r\nCuando vengas junto a Ti.', '', '', '', 9),
(258, '258\r\nTOMA MI MANO', '1\r\n¡Toma mi mano! Señor.\r\n¡Guíame! No quiero yo caer.\r\nSostenme con tu poder.\r\n¡No me deseches!\r\nEn Ti encontraré perdón.\r\nHa dicho mi corazón:\r\n“Búscalo”.', '2\r\nSé bien que no hallaré,\r\n¡No! Jamás, alguien así de fiel;\r\nQue cuide mi caminar.\r\nComo árbol seco,\r\nMi vida, así, siempre fue.\r\n¡Cual nube errante!...\r\n¡No quiero, más, ser!', '3(final)\r\n¡Toma mi mano! Señor.\r\nGuarda mi alma, siempre, junto a Ti.\r\nEn la peña, escóndeme.\r\nCon manos limpias\r\nMe presentaré ante Ti.\r\n¡Alabaré! ¡Exaltaré!\r\nTu Nombre, Tu Nombre.', '', '', '', '', '', '', '', '', '', 3),
(259, '259\r\nTU AMOR POR MÍ', '1\r\nEs más alto que los cielos;\r\nMás profundo que la mar.\r\nEs tu amor inmensurable,\r\n¡Oh, divina majestad!', 'CORO\r\n// ¡Es tan grande tu amor por mí!\r\nNo lo puedo comprender.\r\n¡Es tan grande tu amor por mí,\r\nQue tu Hijo, diste a morir. //', '2\r\nTu amor ha rescatado,\r\nMi existir de la maldad.\r\nAlumbraste, Tú, mis pasos\r\nY el Camino pude hallar.', 'CORO\r\n// ¡Es tan grande tu amor por mí!\r\nNo lo puedo comprender.\r\n¡Es tan grande tu amor por mí,\r\nQue tu Hijo, diste a morir. //', '3\r\nAnte tu presencia vengo,\r\nEn contrito corazón.\r\nPor tu gran misericordia,\r\nRecibe mi gratitud.', 'CORO(final)\r\n// ¡Es tan grande tu amor por mí!\r\nNo lo puedo comprender.\r\n¡Es tan grande tu amor por mí,\r\nQue tu Hijo, diste a morir. //', '', '', '', '', '', '', 6),
(260, '260\r\nTU GRACIA DIVINA', '1\r\n¡Señor, mi Dios! Tu gracia divina me reconforta,\r\nCuando mi alma está herida.\r\nNo tengo más que pensar en ella, ¡La calma vuelve!\r\nMis pensamientos, por Ti, se ordenan.', 'CORO\r\n¡Es por tu gracia que aún sigo aquí!\r\n¡Asido de Ti!\r\nGracia divina, que me atrajo hasta Ti.', '2\r\n¡Señor, mi Dios! Tu gracia divina me fortalece,\r\nCuando el maligno, quiere vencerme.\r\nComprendo, hoy, que entre tus manos quieres moldearme;\r\nToma mi vida, ¡Te pertenece!', 'CORO(final)\r\n¡Es por tu gracia que aún sigo aquí!\r\n¡Asido de Ti!\r\nGracia divina, que me atrajo hasta Ti.', '', '', '', '', '', '', '', '', 4),
(261, '261\r\nTÚ JUZGAS CON VERDAD', '1\r\n¡Oh Señor! Tú que ves,\r\nSólo Tú podrás juzgar\r\nAl perdido en su maldad;\r\nSin errar y sin dudar.', 'CORO\r\nCon justicia y con verdad\r\nTú corriges el pecar.\r\n¡Oh bondad incomparable!\r\n¡Oh, Divina Majestad!', '2\r\n¡Oh Señor! Tú vendrás\r\nA tus santos a salvar.\r\nCon poder transformarás\r\nAl que mereció tu amor.', 'CORO\r\nCon justicia y con verdad\r\nTú corriges el pecar.\r\n¡Oh bondad incomparable!\r\n¡Oh, Divina Majestad!', '3\r\nEl mal tiempo, Señor,\r\nTú dijiste: acortarás;\r\nPor amor fiel a tus santos,\r\nEl dolor aliviarás.', 'CORO(final)\r\nCon justicia y con verdad\r\nTú corriges el pecar.\r\n¡Oh bondad incomparable!\r\n¡Oh, Divina Majestad!', '', '', '', '', '', '', 6),
(262, '262 \r\nTU PRESENCIA EN MI, SEÑOR', '1\r\nTu Presencia, ¡Oh Dios! En mi ser,\r\nSiento en cada respiro.\r\nEs por tu gracia que puedo vivir.\r\nLo que soy, lo debo a Ti.', 'CORO\r\nTu bendición me acaricia.\r\nSiempre estás cerca de mí.\r\nTu voz escucho y te oigo decir:\r\n“Eres mi hijo y vivo en ti.”', '2\r\nTe percibo a mi alrededor:\r\n¡Todo es tu creación!\r\nEstás presente en cada rincón.\r\nTe oigo en mi interior.', 'CORO\r\nTu bendición me acaricia.\r\nSiempre estás cerca de mí.\r\nTu voz escucho y te oigo decir:\r\n“Eres mi hijo y vivo en ti.”', '3\r\nLa paz que Tú me haces sentir,\r\nTu grande amor me declara.\r\nIndigno yo, ¡Me ensalzas Señor!\r\nTe manifiestas en mí.', 'CORO\r\nTu bendición me acaricia.\r\nSiempre estás cerca de mí.\r\nTu voz escucho y te oigo decir:\r\n“Eres mi hijo y vivo en ti.”', '4\r\nTu Presencia me inspira valor,\r\nMe ha motivado a vencer.\r\nSiempre me ayudas en la dura lid.\r\n¡Gran fortaleza eres Tú!', 'CORO(final)\r\nTu bendición me acaricia.\r\nSiempre estás cerca de mí.\r\nTu voz escucho y te oigo decir:\r\n“Eres mi hijo y vivo en ti.”', '', '', '', '', 8),
(263, '263\r\nUN FAVOR A MI CRISTO YO IMPLORO', '1\r\nCuando el fin de mi vida termine\r\n“Y que a cuentas me llame el Señor”\r\nUn favor yo quisiera pedirle\r\nA mi Cristo, mi intercesor.', 'CORO\r\n//Me ayude a morir sin temor y con fe;\r\nAlgún día su gloria veré//', '2\r\nUn favor a mi Cristo yo imploro,\r\nEn su reino yo quiero estar;\r\nSolo espero que en la última prueba,\r\nDe Él, no vaya yo a renegar.', 'CORO\r\n//Me ayude a morir sin temor y con fe;\r\nAlgún día su gloria veré//', '3\r\n¡Oh Señor! En mi ruego te imploro,\r\nNo me dejes por siempre morir,\r\nCuando al mundo, Tú vuelvas en gloria,\r\nA tu encuentro yo quisiera ir.', 'CORO(final)\r\n//Me ayude a morir sin temor y con fe;\r\nAlgún día su gloria veré//', '', '', '', '', '', '', 6),
(264, '264\r\nVIVES EN MÍ', '1\r\nHe oído a las aves cantar,\r\nA las olas, gritar tu Nombre;\r\nLos cielos y la lluvia que cae,\r\nNo han podido negar que me amas.\r\nMuy dentro de mí, estás Tú, Señor,\r\nY así, todo lo puedo en Ti.', 'CORO\r\n//Vives en mí, ¡Oh Dios! Vives en mí//\r\nNi prueba ni tentación,\r\nNi angustia ni dolor,\r\nPodrán apartarte de mi corazón.', '2\r\nMis pasos son espuma de mar,\r\nQue no pueden andar sin Ti.\r\nVendaste mis heridas de ayer,\r\nLevantaste mi ser del polvo.\r\n¡Soy libre al fin! En mí no hay temor,\r\nPorque vives dentro de mí.', 'CORO(final)\r\n//Vives en mí, ¡Oh Dios! Vives en mí//\r\nNi prueba ni tentación,\r\nNi angustia ni dolor,\r\nPodrán apartarte de mi corazón.', '', '', '', '', '', '', '', '', 4),
(265, '265\r\n¿Y CÓMO YO PODRÉ PAGAR?', '1\r\nJesús, el Señor, está pendiente, de mi vivir.\r\nMe cuida de todo mal. ¡Conduce mi pie!Me muestra su grande amor. Me ha dado de su verdad;\r\nSu gran compasión y paz, ...su fidelidad.', 'CORO\r\n¿Y cómo yo podré pagar,\r\nPor tanto, bien, que Cristo me da?\r\n¡Servirle quiero sólo a Él!\r\nAnhelo siempre serle fiel.', '2\r\nY cuando, en dolor fatal, me mira sufrir Jesús,\r\nDerrama bondad sin par, consuelo me da.\r\nPoniendo en Él, mi fe, yo clamo al Salvador;\r\nMe escucha con tierno amor. ¡Me da sanación!', 'CORO\r\n¿Y cómo yo podré pagar,\r\nPor tanto, bien, que Cristo me da?\r\n¡Servirle quiero sólo a Él!\r\nAnhelo siempre serle fiel.', '3\r\nCorrige mi mal de ayer; orienta mi mente al bien.\r\nMe libra del tentador; ¡Me llena de paz!\r\nEscudo, Él es, para mí; la fuente de mi saber;\r\nTesoro de bendición. ¡Amigo más Fiel!', 'CORO(final)\r\n¿Y cómo yo podré pagar,\r\nPor tanto, bien, que Cristo me da?\r\n¡Servirle quiero sólo a Él!\r\nAnhelo siempre serle fiel.', '', '', '', '', '', '', 6),
(266, '266 \r\nYA NO SEGUÍ PECANDO', '1\r\nYo caminaba en senda del mal,\r\nCuando fui hallado por el Señor;\r\nY Él me dijo: ¡No peques más!\r\nPara que obtengas, vida eternal.', 'CORO\r\n|| Ya no seguí pecando,\r\nPorque al fin descubrí,\r\nQue Él no me engañaba,\r\nQue era la verdad. ||', '2\r\nMi vida era un caos total,\r\nSin esperanza, sin ilusión.\r\n¿Cómo olvidarlo? Cuando llegó,\r\nTodo en mi entorno, Él transformó.', 'CORO\r\n|| Ya no seguí pecando,\r\nPorque al fin descubrí,\r\nQue Él no me engañaba,\r\nQue era la verdad. ||', '3\r\nNo lo merezco, lo sé muy bien.\r\nEs invaluable su amor por mí.\r\nMe comprometo a serle fiel.\r\nMi vida entera entrego a Él.', 'CORO(final)\r\n|| Ya no seguí pecando,\r\nPorque al fin descubrí,\r\nQue Él no me engañaba,\r\nQue era la verdad. ||', '', '', '', '', '', '', 6),
(267, '267\r\nYO DESEO SERVIRLE', '1\r\n¡Yo confío en mi Dios!\r\nSé que Él, es amoroso y fiel,\r\nPorque en mi debilidad, me ha levantado con poder.\r\nSoy testigo de su salvación y amor.', 'CORO\r\n¡Yo deseo servirle porque grande es Él!\r\n¡Deseo con mi vida su Nombre ensalzar!\r\n//¡En sombras no quiero andar!//\r\nMas, sí en su amor, que vida, al alma da.', '2\r\nHe andado en error.\r\nCon anhelo busco una luz:\r\nQue el camino en que yo voy, con luz, se alumbre desde hoy...\r\n¡La gloriosa luz que emite mi Jesús!', 'CORO\r\n¡Yo deseo servirle porque grande es Él!\r\n¡Deseo con mi vida su Nombre ensalzar!\r\n//¡En sombras no quiero andar!//\r\nMas, sí en su amor, que vida, al alma da.', '3\r\n¡Creo en Dios mi Salvador!,\r\nPues su paz ha inundado mi ser.\r\nMi deseo es estar en su bondad y su amor,\r\nY en su espíritu que hace florecer.', 'CORO(final)\r\n¡Yo deseo servirle porque grande es Él!\r\n¡Deseo con mi vida su Nombre ensalzar!\r\n//¡En sombras no quiero andar!//\r\nMas, sí en su amor, que vida, al alma da.', '', '', '', '', '', '', 6),
(268, '268\r\nYO Y MI CASA SERVIREMOS AL SEÑOR', '1\r\n¡Yo y mi casa, serviremos al Señor!\r\nCuidaré de cada miembro de mi hogar.\r\nHe de conducirlos a la santidad;\r\nCon firmeza y valor lo he de lograr.', 'CORO\r\n¡Sí! Con prudencia y oración.\r\nY así todos, vida eterna ganarán.\r\nSi mi casa edifico en su temor,\r\nAnte Dios, un ser sabio, yo seré.', '2\r\nSin escatimar esfuerzo en mi labor,\r\nHoy mi casa fiel a Ti, te servirá;\r\nY con ánimo perfecto, sostendrán\r\nFe y amor. Hasta tu reino: ¡Alcanzar!', 'CORO(final)\r\n¡Sí! Con prudencia y oración.\r\nY así todos, vida eterna ganarán.\r\nSi mi casa edifico en su temor,\r\nAnte Dios, un ser sabio, yo seré.', '', '', '', '', '', '', '', '', 4),
(269, '269\r\nCERCA ESTÁ EL DÍA DEL SEÑOR', '1\r\nEl Señor no tarda su promesa,\r\nComo algunos la tienen por tardanza.\r\nEs paciente para con nosotros,\r\nY así le podamos aceptar.', 'CORO\r\n// ¡Pronto vendrá!\r\n¡No tardará!\r\nCerca está\r\nEl día del Señor. //', '2\r\n¡Y vendrá como ladrón de noche!\r\n¡Cual relámpago en el horizonte!\r\nQue velando nos encuentre, hermanos;\r\n¡Nuestra lámpara encendida esté!', 'CORO\r\n// ¡Pronto vendrá!\r\n¡No tardará!\r\nCerca está\r\nEl día del Señor. //', '3\r\nCuando al fin se escuche la trompeta;\r\n¡Con voz fuerte! ¡Con voz de arcángel!,\r\nEl Maestro se hará presente\r\nY los justos se levantarán.', 'CORO(final)\r\n// ¡Pronto vendrá!\r\n¡No tardará!\r\nCerca está\r\nEl día del Señor. //', '', '', '', '', '', '', 6),
(270, '270\r\nCRISTO REY DE REYES', '1\r\nCristo inefable,\r\nDe amor infinito,\r\nHazme de tu sangre,\r\n“Tomar un poquito”.', 'CORO\r\nManantial de vida,\r\nCristo, así te llamas,\r\n|| Haz que en tu venida\r\nSean las almas. ||', '2\r\nCristo, Rey y Santo,\r\nDe Dios el enviado,\r\nRecibe mi canto,\r\nQue por Ti he formado.', 'CORO\r\nManantial de vida,\r\nCristo, así te llamas,\r\n|| Haz que en tu venida\r\nSean las almas. ||', '3\r\nCristo, fiel Cordero,\r\nPor quien vivo yo;\r\nMe mostró primero\r\nLa resurrección.', 'CORO\r\nManantial de vida,\r\nCristo, así te llamas,\r\n|| Haz que en tu venida\r\nSean las almas. ||', '4\r\nCristo, ¡Rey de reyes!\r\nPronta es tu venida;\r\nY glorioso vienes,\r\nPara darme vida.', 'CORO(final)\r\nManantial de vida,\r\nCristo, así te llamas,\r\n|| Haz que en tu venida\r\nSean las almas. ||', '', '', '', '', 8),
(271, '271\r\nEL BANQUETE DEL SEÑOR', '1\r\nQue alegría hermano, el Señor pronto vendrá\r\nY del justo su llanto enjugará.\r\nEstá atento hermano, su venida así será:\r\n“Cuál relámpago aparecerá”.', '1\r\nQue tu lámpara encendida siempre esté,\r\nQue no falte aceite para ver.\r\nA mitad de la noche el clamor será:\r\n“Entrad al banquete del Señor”.', '2\r\nSu Palabra es santa y Jesús no tardará;\r\nLas promesas de Dios se cumplirán.\r\nAl sonar la trompeta, solo el fiel escuchará\r\nLa voz de quien lo puede levantar.', '2\r\nTransformado será, el que con vida esté;\r\nAquel que mantenga fiel su fe.\r\nCon un cuerpo inmortal, de Cristo escuchará:\r\n“Entrad al banquete del Señor”', '3\r\n“El Cordero y sus bodas, a las puertas ya están”;\r\nA los hombres se anuncia sin cesar.\r\nAún es tiempo, hermano, de aceptar la invitación;\r\nPues la puerta, al final se cerrará.', '3(final)\r\n“¡No os conozco! ¡Apartaos, siervos de maldad!,”\r\nSerán las palabras del Señor.\r\nMas al justo que, en Dios, vivió en santidad:\r\n“Entrad al banquete del Señor”.', '', '', '', '', '', '', 6),
(272, '272\r\nÉL VOLVERÁ', '1\r\n¡Cristo Jesús volverá!;\r\nEntre las nubes vendrá.\r\n// Su pueblo irá y le recibirá.\r\nLa tierra, Él gobernará. //', '2\r\nComo se fue, así vendrá,\r\nY todo ser le verá;\r\n// Y cuando Él venga, tu premio traerá;\r\nSi le amas, la vida tendrás. //', '3\r\nLlanto y dolor, ya no habrá;\r\nÉl, todo lo cambiará.\r\n//Jamás, aflicción en su reino habrá,\r\nPues lleno de paz estará. //', '4(final)\r\n¡Cristo Jesús viene ya!,\r\nVelando debes estar.\r\n// Acepta ya, presto, hoy su llamar;\r\nLuchando y sin desmayar. //', '', '', '', '', '', '', '', '', 4);
INSERT INTO `himnos` (`id_himno`, `pagina_0`, `pagina_1`, `pagina_2`, `pagina_3`, `pagina_4`, `pagina_5`, `pagina_6`, `pagina_7`, `pagina_8`, `pagina_9`, `pagina_10`, `pagina_11`, `pagina_12`, `num`) VALUES
(273, '273\r\nEN EL REINO DEL SEÑOR', '1\r\nHay un lugar do quiero estar...\r\n¡Lugar de dicha sin igual!\r\nEn donde Cristo estará\r\nReinando con paz y solaz.', 'CORO\r\n¡Ahí estarán los redimidos!\r\n¡Los escogidos del Señor!\r\nResucitados y transformados,\r\nCuando aparezca el Redentor.', '2\r\nNo se hallará otro lugar,\r\nComo el reino del Señor.\r\nSu gloria quiero contemplar\r\nY disfrutar de eternidad.', 'CORO\r\n¡Ahí estarán los redimidos!\r\n¡Los escogidos del Señor!\r\nResucitados y transformados,\r\nCuando aparezca el Redentor.', '3\r\n¡Allí no habrá, ya más dolor!\r\nLlanto, tristeza ni temor.\r\nSe encontrará felicidad,\r\nSólo en el reino del Señor.', 'CORO(final)\r\n¡Ahí estarán los redimidos!\r\n¡Los escogidos del Señor!\r\nResucitados y transformados,\r\nCuando aparezca el Redentor.', '', '', '', '', '', '', 6),
(274, '274\r\nEN TU PRESENCIA PUEDA ESTAR', '1\r\n¿Por qué será, oh, buen Señor,\r\nQue existe tanta aflicción?\r\nHombres que piensan sólo en mal;\r\nSu vida lejos, de Ti, está.', 'CORO\r\n¡Ayúdame! Señor Jesús,\r\nPara vivir en santidad.\r\nY cuando vuelvas, otra vez,\r\nEn tu presencia pueda estar.', '2\r\n¡Dolor!... Temor hay por doquier.\r\n¿La caridad dejó de ser?\r\nHacer el bien: ¡No importa ya!\r\n¿El enemigo triunfará?', 'CORO\r\n¡Ayúdame! Señor Jesús,\r\nPara vivir en santidad.\r\nY cuando vuelvas, otra vez,\r\nEn tu presencia pueda estar.', '3\r\n¡Regresa pronto! ¡Ven, Señor!\r\nA establecer, Reino de amor.\r\nLlanto y tristeza acabarán;\r\nPaz y tranquilidad, darás.', 'CORO(final)\r\n¡Ayúdame! Señor Jesús,\r\nPara vivir en santidad.\r\nY cuando vuelvas, otra vez,\r\nEn tu presencia pueda estar.', '', '', '', '', '', '', 6),
(275, '275\r\nESPERANZA TENGO', '1\r\nEsperanza tengo en Dios...\r\nQue, a mi Jesús, algún día le veré.\r\n¿Cuánto tiempo faltará?...\r\n¡Pero aquí esperando estoy!', '1\r\nNo merezco, ¡Oh Gran Dios! Tu inmenso amor;\r\nQue corona mi existir. Aunque yo te defraudé,\r\nLevantaste, Tú, mi pie.', '2\r\nA tu encuentro, mi Jesús,\r\n¡Rey y Señor!, todos los fieles irán;\r\n“Muertos”: ¡Resucitarán! “Vivos”: ¡Se han de trasformar!', '2\r\n¡Oh gran dicha! que será, reunido allí...\r\nEscuchar su voz decir: \"Entra al gozo del Señor\",\r\n\"Tú eres bienvenido aquí\".', '3\r\nEste mundo y su maldad,\r\n¡Terminará! Trasformado ha de ser,\r\nTodo lo creado, aquí, por la acción de tu poder.', '3\r\n¡Nueva y santa la ciudad!: Jerusalén;\r\nTabernáculo de Dios. Premio has de ser en mí:\r\n¡Por toda la eternidad!', '4\r\nYa no más recordaré,\r\nLo que sufrí. ¡No habrá llanto ni dolor!\r\nY entonces gozaré del amor en plenitud.', '4(final)\r\nSi tú quieres disfrutar con el Señor,\r\nAsegúrate de estar, limpio de tu corazón,\r\nY con obras de amor.\r\n///¡Esperanza tengo en Dios!///', '', '', '', '', 8),
(276, '276\r\nGOZOSOS EN LA ESPERANZA', '1\r\nA Ti que habitas en los cielos,\r\nAlzamos nuestra voz;\r\nPidiendo seas nuestro refugio.\r\n¡En Ti confiaremos!', 'CORO\r\nGozosos en la esperanza,\r\nContinuemos hacia la vida eterna.\r\nTomados de la mano: ¡Luchemos! ¡Luchemos!\r\n¡En Dios haremos proezas!', '2\r\nNo importa si hay tribulación,\r\nMi Dios, nos sostendrá.\r\nY constantes en la oración,\r\nAl fin llegaremos.', 'CORO\r\nGozosos en la esperanza,\r\nContinuemos hacia la vida eterna.\r\nTomados de la mano: ¡Luchemos! ¡Luchemos!\r\n¡En Dios haremos proezas!', '3\r\nNo seamos vencidos de lo malo,\r\nMás con el bien, ¡Triunfar!\r\nLa batalla que tenemos diario,\r\nPodremos ganar.', 'CORO(final)\r\nGozosos en la esperanza,\r\nContinuemos hacia la vida eterna.\r\nTomados de la mano: ¡Luchemos! ¡Luchemos!\r\n¡En Dios haremos proezas!', '', '', '', '', '', '', 6),
(277, '277\r\nLA GRAN JERUSALÉN', '1\r\n¡Grande el Nombre del Señor!\r\n¡Digno es de adoración!\r\nCanta a nuestro Rey, ¡Cantad!\r\n¡Pronto viene a reinar!\r\nCon poder retornará,\r\nA juzgar toda maldad.', 'CORO\r\nEn la gran Jerusalén,\r\nLa ciudad de nuestro Rey,\r\n¡Cristo siempre estará!;\r\nSolo al justo heredará:\r\n¡Por sus puertas entrará!\r\n¡En la gran Jerusalén!', '2\r\nEl mensaje ya salió;\r\nPor doquier ya se escuchó:\r\nQue el Señor no tardará.\r\nDía a día hay que luchar,\r\nY poder así alcanzar,\r\nLa corona eternal.', 'CORO\r\nEn la gran Jerusalén,\r\nLa ciudad de nuestro Rey,\r\n¡Cristo siempre estará!;\r\nSolo al justo heredará:\r\n¡Por sus puertas entrará!\r\n¡En la gran Jerusalén!', '3\r\nY en la santa ciudad,\r\nDios el llanto enjugará.\r\nNo habrá muerte, sólo paz.\r\nTodo aquel que pueda entrar,\r\nLuz del sol ya no verá:\r\n¡El Cordero alumbrará!', 'CORO(final)\r\nEn la gran Jerusalén,\r\nLa ciudad de nuestro Rey,\r\n¡Cristo siempre estará!;\r\nSolo al justo heredará:\r\n¡Por sus puertas entrará!\r\n¡En la gran Jerusalén!', '', '', '', '', '', '', 6),
(278, '278\r\nLA PROMESA DEL REY', '1\r\nCon presteza, hoy, busca la salvación,\r\nQue, a la puerta, Él, ¡Está!\r\n¡Sigue fiel el camino del Señor!\r\nCon firmeza, Jesús te sostendrá.', 'CORO\r\n¡A luchar con valor! ¡Sin desmayar!\r\nLa promesa del Rey se cumplirá.\r\nAl palacio real tu entrarás\r\nY con Cristo, por fin, tú morarás.', '2\r\nQué alegría será, para Él, cantar\r\nY su rostro contemplar.\r\nLa maldad y la muerte no serán.\r\n¡Aún hay tiempo para poder entrar!', 'CORO\r\n¡A luchar con valor! ¡Sin desmayar!\r\nLa promesa del Rey se cumplirá.\r\nAl palacio real tu entrarás\r\nY con Cristo, por fin, tú morarás.', '3\r\nLas señales anuncian que cerca está\r\nLa venida del Señor.\r\nHay que orar y velar sin desmayar,\r\n¡Con esfuerzo tendrás tu galardón!', 'CORO\r\n¡A luchar con valor! ¡Sin desmayar!\r\nLa promesa del Rey se cumplirá.\r\nAl palacio real tu entrarás\r\nY con Cristo, por fin, tú morarás.', '4\r\nNo te rindas si sientes desfallecer;\r\n¡A la meta llegarás!\r\nFortaleza, el Señor, dará a tu ser\r\nY al final del camino, ¡Vencerás!', 'CORO(final)\r\n¡A luchar con valor! ¡Sin desmayar!\r\nLa promesa del Rey se cumplirá.\r\nAl palacio real tu entrarás\r\nY con Cristo, por fin, tú morarás.', '', '', '', '', 8),
(279, '279\r\nLA VENIDA DE CRISTO', '1\r\n¡Jubilosas las voces!\r\nAl Señor alabemos.\r\nSu Iglesia esperando está,\r\nA Jesús el Salvador.', 'CORO\r\n¡Con las nubes vendrá!\r\nTodo ser, le verá,\r\nCuando vuelva con esplendor\r\nEl bendito Redentor.', '2\r\n¡Aleluya! ¡Qué gozo!,\r\n¡El Señor viene pronto!;\r\nA este mundo retornará\r\nCon el premio inmortal.', 'CORO\r\n¡Con las nubes vendrá!\r\nTodo ser, le verá,\r\nCuando vuelva con esplendor\r\nEl bendito Redentor.', '3\r\nCada cual a su tiempo\r\nSe verá transformado\r\nY al estar con nosotros, Él,\r\nPor mil años reinará.', 'CORO(final)\r\n¡Con las nubes vendrá!\r\nTodo ser, le verá,\r\nCuando vuelva con esplendor\r\nEl bendito Redentor.', '', '', '', '', '', '', 6),
(280, '280\r\nNECESARIO ES QUE PROFETICES OTRA VEZ', '1\r\n¡Ya pronto Cristo volverá!\r\n¡Vendrá con gloria y majestad!\r\nLos reinos de temor caerán,\r\nY en su pueblo gozo habrá.', 'CORO\r\n//Necesario es//\r\nQue profetices otra vez.\r\nNaciones, pueblos aclamar\r\nQue el Rey, ya pronto volverá.', '2\r\nMinistros de la fiel verdad:\r\n¡No cejen de profetizar!\r\n¡Final trompeta sonará!\r\nSalir a recibirle, ¡Ya!', 'CORO\r\n//Necesario es//\r\nQue profetices otra vez.\r\nNaciones, pueblos aclamar\r\nQue el Rey, ya pronto volverá.', '3\r\nLos cielos luz han de emitir,\r\nPues con las nubes Él vendrá.\r\n¡Al Rey de reyes recibir!\r\nLa vida eterna, Él, traerá.', 'CORO(final)\r\n//Necesario es//\r\nQue profetices otra vez.\r\nNaciones, pueblos aclamar\r\nQue el Rey, ya pronto volverá.', '', '', '', '', '', '', 6),
(281, '281\r\nNO ES EL FIN', '1\r\nRecordemos las palabras del Señor,\r\n¡Que en su venida nos dará la Salvación!\r\n“Cielo, mar y tierra, pronto pasarán”;\r\nMas su palabra, que es fiel, ¡Perdurará!', '1\r\nHambre y sed, ¡Oh sí!, nos llegarán.\r\nTerremotos: ¡Vidas quitarán!\r\n¡No es el fin! ¡No es el fin!', '1\r\nY unos a otros se entregarán;\r\nDe no estar con ellos, ¡Nos matarán!;\r\n¡No es el fin! ¡No es el fin!', '2\r\nEl que persevera en el amor de Dios,\r\nTendrá siempre su bendición; su protección;\r\nEl camino es largo, pero junto a Él,\r\nLa vida eterna alcanzará, si cumple fiel.\r\nEl confiar en Él, nos librará', '2(final)\r\nDel castigo que pronto vendrá.\r\n¡Ven a Él, ven a Él!\r\nHoy es tiempo hermano, de aceptarle ya,\r\nY en sus brazos tiernos, descansarás;\r\n¡Ven a Él, ven a Él!', '', '', '', '', '', '', '', 5),
(282, '282\r\nPOR LA FE', '1\r\nLa vida pasa pronto\r\nY mi tiempo ha de acabar.\r\n¡Como la flor del campo!\r\nQue en la tarde no será.', 'CORO\r\n¡Pues yo sé!, por la fe,\r\n¡Que Jesús volverá!\r\n¡Él ha de levantarme!\r\n¡Él mi vida guardará!', '2\r\nElevo hoy mi canto.\r\nSus promesas, ¡Cumplirá!\r\nY los que en Él durmieron,\r\n¡Vida eterna, obtendrán!', 'CORO\r\n¡Pues yo sé!, por la fe,\r\n¡Que Jesús volverá!\r\n¡Él ha de levantarme!\r\n¡Él mi vida guardará!', '3\r\nDe todo el sufrimiento,\r\nMás memoria, no habrá.\r\nY cuando Cristo venga,\r\n¡Todo mal terminará!', 'CORO\r\n¡Pues yo sé!, por la fe,\r\n¡Que Jesús volverá!\r\n¡Él ha de levantarme!\r\n¡Él mi vida guardará!', '4\r\nAsí espero en Cristo,\r\nY fiel le aguardaré.\r\nAunque mis días terminen,\r\nA mi Redentor veré.', 'CORO(final)\r\n¡Pues yo sé!, por la fe,\r\n¡Que Jesús volverá!\r\n¡Él ha de levantarme!\r\n¡Él mi vida guardará!', '', '', '', '', 8),
(283, '283\r\nPRONTÓ VOLVERA', '1\r\n¡Cristo pronto volverá!\r\n¡Con poder retornará!\r\nY dará a cada cual,\r\nRecompensa a su obrar.', 'CORO\r\nDe los cielos descenderá.\r\n¡Con las nubes, Él, vendrá!\r\n¡La trompeta sonará!\r\nY los santos la oirán.', '2\r\nMuertos: ¡Resucitarán!\r\nVivos: ¡Se transformarán!\r\nTodo esto ocurrirá\r\nY lo malo acabará.', 'CORO\r\nDe los cielos descenderá.\r\n¡Con las nubes, Él, vendrá!\r\n¡La trompeta sonará!\r\nY los santos la oirán.', '3\r\n¿Quieres, tú, participar\r\nDe la vida eternal?\r\n¡Deja el pecado atrás!\r\n¡Pronto! pues no tardará.', 'CORO(final)\r\nDe los cielos descenderá.\r\n¡Con las nubes, Él, vendrá!\r\n¡La trompeta sonará!\r\nY los santos la oirán.', '', '', '', '', '', '', 6),
(284, '284\r\nREINO DE PAZ', '1\r\nEl día llegará que todo se acabará.\r\nLo que Dios prometió, ¡Será!\r\nTristezas y angustias: ¡Todo mal terminará!\r\nNi llanto, ni dolor habrá.', 'CORO\r\nEl día en que el justo, del Señor recibirá,\r\nLa corona de vida, ¡Al fin!\r\nSerá cuando Jesús con gloria volverá,\r\nA establecer: reino de paz.', '2\r\nAquellos vencedores solamente entrarán;\r\nLo impío no tendrá lugar.\r\nCumplidos los mil años, Cristo al Padre entregará\r\nEl reino, por la eternidad.', 'CORO\r\nEl día en que el justo, del Señor recibirá,\r\nLa corona de vida, ¡Al fin!\r\nSerá cuando Jesús con gloria volverá,\r\nA establecer: reino de paz.', '3\r\nLa tierra y cielos nuevos; y lo que es, se cambiará,\r\nEn la nueva Jerusalén.\r\nNo habrá necesidad de noche, ni tampoco luz;\r\nPorque Dios iluminará.', 'CORO(final)\r\nEl día en que el justo, del Señor recibirá,\r\nLa corona de vida, ¡Al fin!\r\nSerá cuando Jesús con gloria volverá,\r\nA establecer: reino de paz.', '', '', '', '', '', '', 6),
(285, '285\r\nSÉ QUE VENDRÁ', '1\r\nEn éste mundo de maldad,\r\nDonde sólo hay aflicción,\r\nPor doquier, el hombre va,\r\nA eterna perdición.', '2\r\nCuando el Maestro murió,\r\nNuestras culpas, borró.\r\nY por eso viviré,\r\nSirviendo al Salvador.', '3\r\nEn las pruebas o dolor;\r\nLo que haya que pasar;\r\nSiempre a Cristo clamaré.\r\n¡Me gozaré en su amor!', 'CORO\r\n//Pues yo sé que vendrá\r\nCon nuestro galardón.\r\nLa victoria, alcanzarán\r\nLos santos de Dios.\r\n¡Ven, no tardes! ¡Ven Señor!\r\nCon gloria y majestad. //', '', '', '', '', '', '', '', '', 4),
(286, '286\r\nSEÑOR, CENA CONMIGO', '1\r\nJesucristo,\r\nHe oído tu voz.\r\nMi corazón espera en Ti,\r\n¡Entra en él!, y quédate allí.', 'CORO\r\nSeñor, ¡Quiero mi galardón!\r\n¡Venceré! Mi Jesús, quiero ser tu amigo.\r\nReinaré por mil años contigo,\r\nPero ahora Señor, ven y cena conmigo.', '2\r\nTe suplico:\r\nNo te escondas de mí;\r\nRespóndeme, atento está.\r\nMi clamor elevo a Ti.', 'CORO\r\nSeñor, ¡Quiero mi galardón!\r\n¡Venceré! Mi Jesús, quiero ser tu amigo.\r\nReinaré por mil años contigo,\r\nPero ahora Señor, ven y cena conmigo.', '3\r\nCastigado\r\nPor mí, fuiste, Jesús\r\nSin merecer la culpa cruel...\r\nY morir...¡Salvándome así!', 'CORO(final)\r\nSeñor, ¡Quiero mi galardón!\r\n¡Venceré! Mi Jesús, quiero ser tu amigo.\r\nReinaré por mil años contigo,\r\nPero ahora Señor, ven y cena conmigo.', '', '', '', '', '', '', 6),
(287, '287 \r\nTE ESPERARÉ', '1\r\nHace mucho tiempo que viniste a hablar\r\nDe la paz y amor para la humanidad.\r\nMuchos hombres de valor han venido a predicar\r\nEl mensaje que nos da la sanidad.', 'CORO\r\n¿Cuándo volverás? Me pregunto, mi Señor;\r\nY que juzgues a este mundo pecador\r\nCristo ¿cuándo volverás, a la tierra, otra vez?\r\nCon paciencia y amor te esperaré', '2\r\nEste mundo ¿Cambiará su proceder?\r\nSu sentir, su corazón en mal está,\r\nCreando guerras sin razón, actuando sin conocer,\r\nEl amor y paz que tu gracia nos da.', 'CORO(final)\r\n¿Cuándo volverás? Me pregunto, mi Señor;\r\nY que juzgues a este mundo pecador\r\nCristo ¿cuándo volverás, a la tierra, otra vez?\r\nCon paciencia y amor te esperaré', '', '', '', '', '', '', '', '', 4),
(288, '288\r\nTU FIDELIDAD SIEMPRE SERÁ', '1\r\nCreo en Ti, Señor, mi Salvador.\r\n¡Como luz llegaste a mí!\r\nMe miraste con gran compasión.\r\nMe llamaste a tu redil.', 'CORO\r\nTu fidelidad siempre será.\r\n¡Cuidarás muy bien de mí!\r\nCuando mi aliento cese al fin,\r\n¡Sé que no me olvidarás!', 'CORO\r\nTu promesa, sé que cumplirás.\r\nSeré fiel, hasta el final.\r\nSi dormido en un sepulcro estoy,\r\n¡Sé que me levantarás!', '2\r\nAlgún día, sé que no estaré;\r\nQue mi tiempo acabará.\r\nPero mi confianza, en Ti, pondré.\r\n¡Fiel, Señor, te esperaré!', 'CORO\r\nTu fidelidad siempre será.\r\n¡Cuidarás muy bien de mí!\r\nCuando mi aliento cese al fin,\r\n¡Sé que no me olvidarás!', 'CORO\r\nTu promesa, sé que cumplirás.\r\nSeré fiel, hasta el final.\r\nSi dormido en un sepulcro estoy,\r\n¡Sé que me levantarás!', '3\r\nA su tiempo, sé que Tú vendrás.\r\n¡Quiero ver, Señor, tu faz!\r\nY si convertido en polvo estoy,\r\nCon amor, ¡Me llamarás!', 'CORO\r\nTu fidelidad siempre será.\r\n¡Cuidarás muy bien de mí!\r\nCuando mi aliento cese al fin,\r\n¡Sé que no me olvidarás!', 'CORO(final)\r\nTu promesa, sé que cumplirás.\r\nSeré fiel, hasta el final.\r\nSi dormido en un sepulcro estoy,\r\n¡Sé que me levantarás!', '', '', '', 9),
(289, '289\r\nTU REGRESO ESTÁ CERCA', '1\r\nCuando la higuera bella, florezca;\r\nSeñal de tu venida, que está ya muy cerca.\r\nCuando tu pueblo esté reunido,\r\nEn tierra santa... que Tú has provisto.', 'CORO\r\n¡Yo quiero estar en aquel lugar!\r\n¡Yo quiero estar con tu pueblo!\r\n¡Yo quiero ser transformado!\r\n¡Y quiero estar a tu lado!', '2\r\nLa tierra tiembla, ¡Se estremece!\r\nEscucha a lo lejos..., que el Rey, ya está cerca.\r\nEl mar se agita en reverencias;\r\nEscucha la voz del Rey, que ya viene.', 'CORO\r\n¡Yo quiero estar en aquel lugar!\r\n¡Yo quiero estar con tu pueblo!\r\n¡Yo quiero ser transformado!\r\n¡Y quiero estar a tu lado!', '3\r\n¡Oh, Rey de reyes! Tú lo dijiste:\r\n“Mirad, velad y orad”. ¡No hay que dormir!\r\nCerca, muy cerca, está el verano\r\nY con él, el tiempo que da a tu regreso.', 'CORO(final)\r\n¡Yo quiero estar en aquel lugar!\r\n¡Yo quiero estar con tu pueblo!\r\n¡Yo quiero ser transformado!\r\n¡Y quiero estar a tu lado!', '', '', '', '', '', '', 6),
(290, '290 \r\nÚLTIMOS TIEMPOS', '1\r\nÚltimos tiempos, ¡Estos son!...\r\nHijos de Dios: ¡Sabedlo ya!\r\nGuerras vendrán, desolación...\r\nSe acerca ¡Ya! El día de Jehová.', 'CORO\r\n¡Dame más fe, dame valor!\r\n¡Oh Gran Señor! Dios de Israel.\r\nQue con la fe huirá el dolor\r\nY ya nada temeré.', '2\r\n¿El día?... ¡Cercano ya está!\r\nEn que vendrá el Salvador.\r\nPersecución, antes, vendrá,\r\nPara tus hijos, ¡Oh, Señor!', 'CORO\r\n¡Dame más fe, dame valor!\r\n¡Oh Gran Señor! Dios de Israel.\r\nQue con la fe huirá el dolor\r\nY ya nada temeré.', '3\r\nEl enemigo grande es\r\nY nos acecha por doquier;\r\nPone tropiezo a nuestros pies\r\nPara hacernos caer.', 'CORO\r\n¡Dame más fe, dame valor!\r\n¡Oh Gran Señor! Dios de Israel.\r\nQue con la fe huirá el dolor\r\nY ya nada temeré.', '4\r\n¡Qué triste es volver atrás!\r\nDel camino de la salud;\r\nEstando tan cercano ya,\r\nEl regreso de mi Jesús.', 'CORO(final)\r\n¡Dame más fe, dame valor!\r\n¡Oh Gran Señor! Dios de Israel.\r\nQue con la fe huirá el dolor\r\nY ya nada temeré.', '', '', '', '', 8),
(291, '291 \r\nUN DÍA VENDRÁ', '1\r\nUn día vendrá el Rey celestial,\r\nCon grande poder, dispuesto a juzgar.\r\nUn día vendrá, al mundo a reinar,\r\nCon gloria eternal. ¡Qué gozo habrá!', '2\r\nSi quieres venir, no lo dudes más...\r\n¡Ven pronto a sus pies!... ¡Y en Él gozarás!\r\nQué dicha será, estar junto a Él;\r\nOírle su voz, diciéndote: ¡Ven!', '3(final)\r\nGlorioso será, gozar su bondad;\r\nSer libre del mal; llenarte de paz.\r\n¡Tristeza no habrá! ni llanto y dolor.\r\nSeguro estarás, ¡Gozando su amor!', '', '', '', '', '', '', '', '', '', 3),
(292, '292\r\nUN REINO FELIZ', '1\r\nTodo en el reino será felicidad.\r\nNo habrá más llanto y el dolor se irá.\r\nSi amas a Cristo, vivirás con Él\r\nY todos juntos: cantaremos a Dios.', 'CORO\r\nHabrá mil cosas tan hermosas,\r\nComo ver el cielo azul.\r\nY los collados, verdes estarán.\r\n¡Qué maravilla es contemplarlas!\r\n¡Qué total transformación!\r\nSeñor Jesús, ¡Ven a mí! No tardes más.', '2\r\nEn aquel día, tu voz se unirá,\r\nEn la alabanza que los santos darán,\r\nY yo a tu lado, mi arpa llevaré\r\nY todos juntos cantaremos a Dios.', 'CORO\r\nHabrá mil cosas tan hermosas,\r\nComo ver el cielo azul.\r\nY los collados, verdes estarán.\r\n¡Qué maravilla es contemplarlas!\r\n¡Qué total transformación!\r\nSeñor Jesús, ¡Ven a mí! No tardes más.', '3\r\n¡Sigue adelante! No mires hacia atrás.\r\nDeja el pasado, no te aferres a él.\r\nRopas muy blancas, Dios te ceñirá\r\nY todos juntos cantaremos a Dios.', 'CORO(final)\r\nHabrá mil cosas tan hermosas,\r\nComo ver el cielo azul.\r\nY los collados, verdes estarán.\r\n¡Qué maravilla es contemplarlas!\r\n¡Qué total transformación!\r\nSeñor Jesús, ¡Ven a mí! No tardes más.', '', '', '', '', '', '', 6),
(293, '293\r\nVELAD Y ORAD', '1\r\nEl mandato del Señor,\r\nEs estar velando en oración,\r\nPara andar siempre en luz,\r\nY así, jamás tropezar.', 'CORO\r\n¡Velad y orad!\r\nPara que no entréis en tentación.\r\n¡Velad y orad!\r\nPara ser fuertes en la aflicción\r\nY la fe conservar.', '2\r\nNi sabemos ni el día\r\nNi la hora en que Él retornará;\r\nPues vendrá cual ladrón\r\nY velando nos debe hallar.', 'CORO\r\n¡Velad y orad!\r\nPara que no entréis en tentación.\r\n¡Velad y orad!\r\nPara ser fuertes en la aflicción\r\nY la fe conservar.', '3\r\nLa Iglesia del Señor,\r\nPreparada y lista debe estar.\r\nNo dormir, ¡Ya no más!\r\nY a las bodas pueda entrar.', 'CORO(final)\r\n¡Velad y orad!\r\nPara que no entréis en tentación.\r\n¡Velad y orad!\r\nPara ser fuertes en la aflicción\r\nY la fe conservar.', '', '', '', '', '', '', 6),
(294, '294 \r\nVEN SEÑOR JESÚS', '1\r\nSeñor Jesucristo, tu Iglesia te espera.\r\nTú ves sus angustias, penar y temor.\r\nNo dejes que mueran en el cruel dolor;\r\nRedime sus almas, te ruego Señor.', 'CORO\r\nVen Señor y destruye al pecado.\r\nVen Señor y reprende al tentador,\r\nVe Señor, tu pueblo atribulado\r\nY así en tu camino, confírmanos Señor.', '2\r\nNaciones y pueblos, en mil aflicciones,\r\nPor causa del hombre y su gran error.\r\nProtege a tus santos, divino Señor.\r\nHoy te damos gracias: a Ti sea el loor.', 'CORO\r\nVen Señor y destruye al pecado.\r\nVen Señor y reprende al tentador,\r\nVe Señor, tu pueblo atribulado\r\nY así en tu camino, confírmanos Señor.', '3\r\nTu Iglesia esparcida, está en todo el mundo;\r\nEs aborrecida, y es por tu verdad.\r\nAlumbra el camino, hasta a Ti llegar,\r\nY ver tu Presencia, por la eternidad.', 'CORO(final)\r\nVen Señor y destruye al pecado.\r\nVen Señor y reprende al tentador,\r\nVe Señor, tu pueblo atribulado\r\nY así en tu camino, confírmanos Señor.', '', '', '', '', '', '', 6),
(295, '295\r\nANUNCIANDO EL EVANGELIO', '1\r\nVen hermano, ¡Te invito a trabajar!\r\nNos es impuesta, de Dios, necesidad.\r\nY al mundo, anunciar debemos ya,\r\nEl evangelio que le ha de salvar.', 'CORO\r\n¡Anuncia el evangelio del Señor!\r\n¡Muchas almas que hay por rescatar!\r\n¡Que viven erradas y engañadas van!\r\n¡Sólo en Él! Hallarán la libertad.', '2\r\n¡Mira! Blancos, los campos ya están.\r\nSólo esperando a quién los ha de segar.\r\n¡Vamos hermano! La mies lista está\r\nY los graneros, en Dios rebosarán.', 'CORO\r\n¡Anuncia el evangelio del Señor!\r\n¡Muchas almas que hay por rescatar!\r\n¡Que viven erradas y engañadas van!\r\n¡Sólo en Él! Hallarán la libertad.', '3\r\nAún hay tiempo, hermano, ¡Hay que ir!\r\n¿No ves que el sol está por declinar?\r\nY si queda una hora de labor,\r\nDe Cristo recibiremos galardón.', 'CORO(final)\r\n¡Anuncia el evangelio del Señor!\r\n¡Muchas almas que hay por rescatar!\r\n¡Que viven erradas y engañadas van!\r\n¡Sólo en Él! Hallarán la libertad.', '', '', '', '', '', '', 6),
(296, '296\r\nBUSCA A JEHOVÁ', '1\r\n¡Ven joven! Hoy te invito a trabajar.\r\n¡Por ti espera el Señor!\r\n¡Hermano, no lo pienses más!\r\nQue el primer paso tenemos que dar.', 'CORO\r\nBusca a Jehová y Él te guardará.\r\n¡Llamadle! Que cercano está.\r\nNo sea que, perdida la oportunidad,\r\nAñores su bondad...', '2\r\n¡Hay que luchar contra la vanidad!\r\n¡Que Cristo pronto vendrá!\r\n¿Qué has hecho tú por mejorar?...\r\n¿Examinaste ya tu santidad?', 'CORO\r\nBusca a Jehová y Él te guardará.\r\n¡Llamadle! Que cercano está.\r\nNo sea que, perdida la oportunidad,\r\nAñores su bondad...', '3\r\nNo anheles la riqueza terrenal,\r\nPues ésta perecerá.\r\nOra, por tu fe y humildad,\r\n¡Que ya es el tiempo para despertar!', 'CORO\r\nBusca a Jehová y Él te guardará.\r\n¡Llamadle! Que cercano está.\r\nNo sea que, perdida la oportunidad,\r\nAñores su bondad...', '4\r\nArduo el trabajo a desempeñar\r\nY duras pruebas vendrán...\r\nSi hay confianza en Jehová,\r\nDe bendiciones, Él, nos llenará.', 'CORO(final)\r\nBusca a Jehová y Él te guardará.\r\n¡Llamadle! Que cercano está.\r\nNo sea que, perdida la oportunidad,\r\nAñores su bondad...', '', '', '', '', 8),
(297, '297\r\n¡CUAN HERMOSOS SON LOS PIES!', '1\r\nLevanta tus ojos y contempla:\r\n¡Los campos preparados ya están!\r\nToma el arado entre tus manos, ¡No lo pienses más!\r\n¡Y empieza a trabajar!', 'CORO\r\n¡Cuán hermosos son los pies!\r\nDe aquel, que alegres nuevas trae.\r\nAnunciará a los pueblos paz; dará nuevas del bien;\r\nDe Cristo, su Nombre, anunciará.', '2\r\nLlorando va el que lleva simiente;\r\nEn cardos y espinas sembrará.\r\nErrante irá buscando tierra, que buen fruto da.\r\n¡Con gozo regresará!', 'CORO\r\n¡Cuán hermosos son los pies!\r\nDe aquel, que alegres nuevas trae.\r\nAnunciará a los pueblos paz; dará nuevas del bien;\r\nDe Cristo, su Nombre, anunciará.', '3\r\nSembrado ha, y con justicia,\r\nPues sabe que a su tiempo segará.\r\nRegando la semilla con paciencia y caridad;\r\nEl fruto, en Dios, ¡Dará!', 'CORO(final)\r\n¡Cuán hermosos son los pies!\r\nDe aquel, que alegres nuevas trae.\r\nAnunciará a los pueblos paz; dará nuevas del bien;\r\nDe Cristo, su Nombre, anunciará.', '', '', '', '', '', '', 6),
(298, '298\r\nDA DE CORAZÓN', '1\r\n¡Traed los diezmos al alfolí!\r\nEs un mandato que dio el Señor;\r\nY las ofrendas, también así\r\nSean entregadas a nuestro Dios.', 'CORO\r\nDa de corazón,\r\nTu ofrenda, a Dios.\r\nDa de corazón,\r\nTu diezmo, con fe y amor.', '2\r\nNo con tristeza ni con pesar,\r\nPues ama Dios, al que alegre da.\r\nSus beneficios, otorgará,\r\nAl que si cumple en fidelidad.', 'CORO\r\nDa de corazón,\r\nTu ofrenda, a Dios.\r\nDa de corazón,\r\nTu diezmo, con fe y amor.', '3\r\nY por lo mucho que Él nos da,\r\nDe nuestro esfuerzo hay que entregar;\r\n¡Solo una parte! En integridad.\r\nDios, bendiciones dará, sin par.', 'CORO(final)\r\nDa de corazón,\r\nTu ofrenda, a Dios.\r\nDa de corazón,\r\nTu diezmo, con fe y amor.', '', '', '', '', '', '', 6),
(299, '299\r\nDESPIERTA HERMANO', '1\r\n¡Somos el pueblo del Dios verdadero!\r\n¡Somos la Iglesia de Dios!\r\nEn medio de pueblos y del mundo entero,\r\n¡Somos un gran batallón!', 'CORO\r\n//¡Despierta hermano! ¡Hay que trabajar!\r\nTus manos, la Iglesia, necesita ya.\r\nLa obra del Padre, hay que engrandecer.\r\n¡Unamos esfuerzo por Él!//', '2\r\nTodos unidos, pongamos las manos,\r\nPara la obra de Dios.\r\nNinguna acechanza de nuestro adversario,\r\nPodrá empañar la labor.', 'CORO\r\n//¡Despierta hermano! ¡Hay que trabajar!\r\nTus manos, la Iglesia, necesita ya.\r\nLa obra del Padre, hay que engrandecer.\r\n¡Unamos esfuerzo por Él!//', '3\r\nHoy iniciamos un nuevo día,\r\nPara la Iglesia de Dios.\r\nObreros muy fieles que, con alegría,\r\nHoy sirven, siguiendo al Señor.', 'CORO(final)\r\n//¡Despierta hermano! ¡Hay que trabajar!\r\nTus manos, la Iglesia, necesita ya.\r\nLa obra del Padre, hay que engrandecer.\r\n¡Unamos esfuerzo por Él!//', '', '', '', '', '', '', 6),
(300, '300\r\nECHA TU PAN SOBRE LAS AGUAS', '1\r\nLa gran comisión de predicar,\r\nTarea es de la Iglesia de Dios.\r\nEl día saludable es hoy.\r\nTocar con su amor un corazón.', 'CORO\r\n¡Tu pan hoy echa en las aguas!\r\nDespués de muchos días lo hallarás.\r\nMandato excelente a cumplir,\r\nEn el nombre de Cristo Jesús.', '2\r\nSi duermes hoy, tú no sembrarás,\r\n¡Despierta!, que así no segarás.\r\n¡Arando fiel, lo lograrás!\r\nLa tierra dispuesta ha sido ya.', 'CORO\r\n¡Tu pan hoy echa en las aguas!\r\nDespués de muchos días lo hallarás.\r\nMandato excelente a cumplir,\r\nEn el nombre de Cristo Jesús.', '3\r\n¡Las nuevas de Cristo predicar!;\r\nLa gloria del reino del Señor.\r\n¡Anuncia ya su salvación!\r\nDios, siempre tu obrar prosperará.', 'CORO\r\n¡Tu pan hoy echa en las aguas!\r\nDespués de muchos días lo hallarás.\r\nMandato excelente a cumplir,\r\nEn el nombre de Cristo Jesús.', '4\r\nNo tarda, cercano el día está.\r\nEl fruto de tu obra pedirá.\r\n¡Esfuérzate por convertir!\r\n¡A quien va perdido en su pecar!', 'CORO(final)\r\n¡Tu pan hoy echa en las aguas!\r\nDespués de muchos días lo hallarás.\r\nMandato excelente a cumplir,\r\nEn el nombre de Cristo Jesús.', '', '', '', '', 8),
(301, '301\r\nES MOMENTO DE LUCHAR', '1\r\nEs el momento de luchar con Dios.\r\nTodos juntos, ¡Formando un batallón!\r\nDios espera, hoy, de ti: valor y convicción;\r\nDespertando cada uno, su gran don.', 'CORO\r\nVe con esta fortaleza, ¡Ya!\r\nVamos, sin parar, a trabajar.\r\nSi juntos luchamos, la gloria será:\r\nHaber podido derribar al mal.', '2\r\n¡Valiente joven! Dios ha puesto en ti\r\nMuchos dones; ¡Que hoy despertarás!\r\n¡Créelo hay varios en ti! ¡Tendrás que trabajar!\r\nNuestro Dios te recompensará, al fin.', 'CORO\r\nVe con esta fortaleza, ¡Ya!\r\nVamos, sin parar, a trabajar.\r\nSi juntos luchamos, la gloria será:\r\nHaber podido derribar al mal.', '3\r\nEn la batalla, ¡Obstáculos habrá!\r\n¡Tu armadura debes de llevar!\r\nNo te alejes del Señor. Entrégate con fe;\r\nY verás que Dios te guardará en Él.', 'CORO(final)\r\nVe con esta fortaleza, ¡Ya!\r\nVamos, sin parar, a trabajar.\r\nSi juntos luchamos, la gloria será:\r\nHaber podido derribar al mal.', '', '', '', '', '', '', 6),
(302, '302\r\nJEHOVÁ ES MI FORTALEZA', '1\r\nLevantemos nuestras voces, ¡Oh cristiana juventud!\r\nPredicando a todo el mundo, el sublime amor de Dios.\r\nÉl murió, salvándonos a ti y a mí del pecado y su dolor;', '1\r\nSolo ofrece buenas obras; sólo cumple tu misión;\r\n¡Dignifícale en tu vida!\r\nsé un ejemplo de valor;\r\nGlorifícale en tu vida, en espíritu y virtud.', 'CORO\r\n¡Jehová es mi fortaleza y mi escudo, mi guiador!\r\nCon mi cántico alabo, al Señor, Dios de amor.', '2\r\nCaminemos con pie firme, en las huestes que hoy están;\r\nDirigidas por mi Cristo, ¡Mi piloto y capitán!\r\n¡Levantemos nuestras voces al Creador! Que se escuche retumbar,', '2\r\nSu palabra, justa y digna, en la gran humanidad.\r\nTodos juntos, con ahínco: ¡Buenas nuevas anunciad!\r\nAunque nos cueste la vida, su amor siempre vivirá.', 'CORO\r\n¡Jehová es mi fortaleza y mi escudo, mi guiador!\r\nCon mi cántico alabo, al Señor, Dios de amor.', '', '', '', '', '', '', 6),
(303, '303\r\nJOVEN SALID A LUCHAR', '1\r\nJoven valiente, ¡Salid a luchar!\r\nPenas y pruebas hay que soportar.\r\nDía con día, hay que anunciar\r\nLa palabra del Padre celestial.', 'CORO\r\n¡Ve con esta fortaleza, hoy!,\r\nLa cual te ha dado Jehová, a ti.\r\n¡Sigue siempre hacia adelante!\r\n¡En el mundo habrás de triunfar!', '2\r\nY con la fuerza de nuestro Señor,\r\nvencer podremos así la maldad;\r\nMaldad que solo nos quiere apartar,\r\nDel camino que nos ha de salvar.', 'CORO\r\n¡Ve con esta fortaleza, hoy!,\r\nLa cual te ha dado Jehová, a ti.\r\n¡Sigue siempre hacia adelante!\r\n¡En el mundo habrás de triunfar!', '3\r\nNuestra potencia está en Jehová\r\n¡Nunca temamos enfrentar al mal!\r\nPues nuestro Dios, ¡Jamás nos dejará!\r\nCon confianza debemos de marchar.', 'CORO(final)\r\n¡Ve con esta fortaleza, hoy!,\r\nLa cual te ha dado Jehová, a ti.\r\n¡Sigue siempre hacia adelante!\r\n¡En el mundo habrás de triunfar!', '', '', '', '', '', '', 6),
(304, '304\r\nLA ESCUELA SABÁTICA', '1\r\nUna semana ha pasado ya.\r\nEl sol se oculta, ¡Sábado es ya!\r\nNos reuniremos todos a estudiar.\r\nAprenderemos, de Dios, su Voluntad.', 'CORO\r\n¡La Escuela Sabática vamos a estudiar!\r\nUn siervo de Cristo nos enseñará;\r\nLa lectura base nos explicará.\r\nY el “texto gema”, ¡Te debes grabar!', '2\r\nCada pregunta hay que analizar;\r\nUna respuesta le debemos dar.\r\n“Si yo no entiendo, he de preguntar”,\r\nPara que pueda a otros enseñar.', 'CORO\r\n¡La Escuela Sabática vamos a estudiar!\r\nUn siervo de Cristo nos enseñará;\r\nLa lectura base nos explicará.\r\nY el “texto gema”, ¡Te debes grabar!', '3\r\nHoy es momento de participar\r\nEn la lectura, para edificar;\r\nPues de otra cosa no se debe hablar;\r\nConforme al tema se debe comentar.', 'CORO\r\n¡La Escuela Sabática vamos a estudiar!\r\nUn siervo de Cristo nos enseñará;\r\nLa lectura base nos explicará.\r\nY el “texto gema”, ¡Te debes grabar!', '4\r\nLo que hoy aprenda, lo voy a enseñar\r\nPor todo el mundo, al evangelizar;\r\nPues es la orden que Jesús nos da:\r\n“Id por el mundo y a todos doctrinad”.', 'CORO(final)\r\n¡La Escuela Sabática vamos a estudiar!\r\nUn siervo de Cristo nos enseñará;\r\nLa lectura base nos explicará.\r\nY el “texto gema”, ¡Te debes grabar!', '', '', '', '', 8),
(305, '305\r\nLA JUVENTUD', '1\r\nPon tus ojos en el Señor,\r\nMirando hacia el horizonte;\r\n¡Se puede ver una nueva luz!\r\n¡El día llega ya!', '1\r\nRenovadas ya las fuerzas,\r\nHay que seguir el camino;\r\nLlevando paz, predicando amor:\r\n¡Esa es nuestra misión!', 'CORO\r\nEs la juventud,\r\nFortaleza del hombre.\r\nEn ti ha puesto, Dios, una luz,\r\n¡Hazla brillar!', 'CORO\r\nEs la juventud,\r\nFortaleza del hombre.\r\n¡Jamás caerás!, ¡Ni tendrás temor!\r\n¡Solo sigue al Salvador!', '2\r\nTen confianza en Jesucristo,\r\nPor duras que sean las pruebas;\r\nVerás que, al fin, la paz reinará;\r\n¡La tormenta se irá!', '2\r\n¡Ven conmigo! caminemos,\r\nÉl cuida de nuestros pasos\r\nY antes que se oculte el sol,\r\nLlegaremos al final.', 'CORO\r\nEs la juventud,\r\nFortaleza del hombre.\r\nEn ti ha puesto, Dios, una luz,\r\n¡Hazla brillar!', 'CORO(final)\r\nEs la juventud,\r\nFortaleza del hombre.\r\n// ¡Jamás caerás!, ¡Ni tendrás temor!\r\n¡Sólo sigue al Salvador //', '', '', '', '', 8),
(306, '306\r\nLA GLORIA DEL JOVEN ES SU FORTALEZA', '1\r\nHoy es tiempo de trabajar\r\nEn la obra de Jehová\r\nY decirle: “Heme aquí”,\r\n“Quiero consagrarme a Ti”.', 'CORO\r\n// Porque la gloria del joven es\r\nsu fortaleza y confianza en Dios.\r\nSi te dedicas en cuerpo y alma,\r\nBendición tendrás de Dios //', '2\r\nJoven, busca tu salvación:\r\nTu andar lleva al Señor;\r\nPon la fe en tu Creador\r\nY tendrás su bendición.', 'CORO\r\n// Porque la gloria del joven es\r\nsu fortaleza y confianza en Dios.\r\nSi te dedicas en cuerpo y alma,\r\nBendición tendrás de Dios //', '3\r\nJesucristo muy cerca está,\r\n¡Ya no pierdas tiempo, más!\r\nPues te quiere coronar.\r\n¡Vida eterna ganarás!', 'CORO(final)\r\n// Porque la gloria del joven es\r\nsu fortaleza y confianza en Dios.\r\nSi te dedicas en cuerpo y alma,\r\nBendición tendrás de Dios //', '', '', '', '', '', '', 6),
(307, '307\r\nLA LECCIÓN DE HOY', '1\r\nLa hora de la clase va a iniciar,\r\nMi cuaderno y Biblia llevo ya.\r\nY la lección de hoy repasaré,\r\nPues en la semana la estudie.', 'Coro\r\nEl verso aprendido voy a recitar,\r\nLa lectura base es primordial.\r\nY el objetivo me orientará,\r\nPara las preguntas contestar.', '2\r\nCon gozo y en silencio vamos ya,\r\nCada grupo se debe integrar.\r\nLos instructores preparados van,\r\nY el tiempo hay que aprovechar.', 'Coro\r\nEl verso aprendido voy a recitar,\r\nLa lectura base es primordial.\r\nY el objetivo me orientará,\r\nPara las preguntas contestar.', '3\r\nAl terminar la clase, listos ya,\r\nHacia el templo, prestos hay que ir.\r\nRecitaremos, como ofrenda a Dios,\r\nLa lección que hoy se estudió.', 'Coro(final)\r\nEl verso aprendido voy a recitar,\r\nLa lectura base es primordial.\r\nY el objetivo me orientará,\r\nPara las preguntas contestar.', '', '', '', '', '', '', 6),
(308, '308\r\nLOS DIEZMOS VOY A DAR', '1\r\nDe lo que me has dado, yo el diezmo doy.\r\nSiempre que yo diezmo, me bendecirás;\r\nYa lo he comprobado, no lo dudo más.\r\nTodo lo que tengo, te lo debo ¡Oh Dios!', 'CORO\r\nEs un mandamiento, ¡no lo olvidaré!\r\nSi lo doy con gozo, bendición tendré.\r\nLo hicieron tus siervos en la antigüedad,\r\nYo como hijo tuyo, haré tu voluntad.', '2\r\nCuantas veces veo que se va a agotar,\r\nEl recurso que gané por trabajar;\r\nY al momento, Tú ves mi necesidad\r\nY de alguna forma me lo haces llegar.', 'CORO\r\nEs un mandamiento, ¡no lo olvidaré!\r\nSi lo doy con gozo, bendición tendré.\r\nLo hicieron tus siervos en la antigüedad,\r\nYo como hijo tuyo, haré tu voluntad.', '3\r\nQue en tus alfolíes no llegue a faltar,\r\nPara que tu obra pueda continuar;\r\nPor eso Señor, los diezmos voy a dar,\r\nPara que tu Iglesia pueda mejorar.', 'CORO(final)\r\nEs un mandamiento, ¡no lo olvidaré!\r\nSi lo doy con gozo, bendición tendré.\r\nLo hicieron tus siervos en la antigüedad,\r\nYo como hijo tuyo, haré tu voluntad.', '', '', '', '', '', '', 6),
(309, '309\r\nMARCHAD JUVENTUD', '1\r\n¡Marchad juventud con valor!\r\nConsagra tu vida al Señor.\r\nPoderoso es nuestro Dios Jehová,\r\n¡Vencedor en Él, serás!', 'CORO\r\n// ¡Jehová es mi escudo\r\nY mi fortaleza!\r\nPor siempre le alabaré //', '2\r\nY cuando en tu vida, al andar,\r\nEl mundo te aceche en maldad,\r\n¡No desmayes! ¡Dios te ayudará!,\r\nFortaleza, Él, te dará.', 'CORO\r\n// ¡Jehová es mi escudo\r\nY mi fortaleza!\r\nPor siempre le alabaré //', '3\r\n¡Marchad juventud sin temor!\r\n¡Id en pos de tu Salvador!\r\nAnunciad lo inmenso de su Amor,\r\nRescatando al pecador.', 'Coro Final\r\n// ¡Jehová es mi escudo\r\nY mi fortaleza!\r\nPor siempre le alabaré. //\r\n¡Por siempre le alabaré!', '', '', '', '', '', '', 6),
(310, '310\r\nMENSAJEROS DE SU PALABRA', '1\r\nPredicando la Verdad de salvación.\r\nCorrigiendo el pecado con amor.\r\nSomos siervos del Señor, y bendición,\r\nEsperamos día a día, con fervor.', 'CORO\r\n¡Mensajeros de su palabra!:\r\nTestificamos con gran verdad,\r\nDel Dios eterno, la plena gracia.\r\n¡Qué siempre abunde su gran piedad!', '2\r\nYa se acerca la venida de Jesús,\r\n¡Nuestra lámpara hoy brille a plena luz!\r\nEsperemos en ayuno y oración,\r\nEl regreso de Jesús el Redentor.', 'CORO\r\n¡Mensajeros de su palabra!:\r\nTestificamos con gran verdad,\r\nDel Dios eterno, la plena gracia.\r\n¡Qué siempre abunde su gran piedad!', '3\r\nNuevo reino de justicia y de amor,\r\nPreparó, ya, para el justo, nuestro Dios.\r\nTodo llanto en gozo, Él transformará.\r\nReino eterno su Iglesia gozará.', 'CORO(final)\r\n¡Mensajeros de su palabra!:\r\nTestificamos con gran verdad,\r\nDel Dios eterno, la plena gracia.\r\n¡Qué siempre abunde su gran piedad!', '', '', '', '', '', '', 6),
(311, '311\r\nMI DIEZMO PARA TI', '1\r\nPor ese sustento que siempre me das...\r\nPor tus bendiciones y tu gran bondad...\r\nMe acerco, Dios mío, con mi gratitud,\r\nRecibe la ofrenda que, hoy, voy a dar.', '2\r\nTrabajo y comida, Tú siempre me das;\r\nUn techo y cobijo, también disfruté;\r\nTú amor siempre suple mi necesidad;\r\nTú das bendiciones de acuerdo a mi fe.', '3\r\nNo me des de menos, tampoco de más...\r\nY así no blasfeme o me olvide de Ti...\r\nY por el sustento que siempre me das,\r\nGozoso entrego mi diezmo a Ti.', '', '', '', '', '', '', '', '', '', 3),
(312, '312\r\n¡OH JUVENTUD: ACUÉRDATE DE ALABAR A DIOS!', '1\r\n¡Joven!, debes de acordarte de Jehová;\r\nSabes que sólo a Él, toda honra debes dar.', 'CORO\r\n¡Oh Juventud!... ¡Acuérdate de alabar a Dios!\r\n¡Entrégale tu ser, tu amor!\r\n¡Oh juventud!\r\nRecuerda que por ti murió Jesús.\r\nAcuérdate de engrandecer su labor.', '2\r\n¡Joven!, debes de acordarte del Creador.\r\nTeme y sigue fiel, el mandato del Señor.', 'CORO\r\n¡Oh Juventud!... ¡Acuérdate de alabar a Dios!\r\n¡Entrégale tu ser, tu amor!\r\n¡Oh juventud!\r\nRecuerda que por ti murió Jesús.\r\nAcuérdate de engrandecer su labor.', '3\r\n¡Joven!, ¡Cuida bien!, de que el mal no entre en ti:\r\n¡Fama es vanidad!... ¡Tu existencia entrega a Dios!', 'CORO(final)\r\n¡Oh Juventud!... ¡Acuérdate de alabar a Dios!\r\n¡Entrégale tu ser, tu amor!\r\n¡Oh juventud!\r\nRecuerda que por ti murió Jesús.\r\nAcuérdate de engrandecer su labor.', '', '', '', '', '', '', 6),
(313, '313\r\nUNA MENTE, UN MISMO PARECER', '1\r\n¡Oh juventud!, tienes que estar unida.\r\nTu fortaleza es importante para Dios.\r\nCon entusiasmo y una fe viva,\r\nMarchemos pues, en el camino del Señor.', 'CORO\r\n// Con una mente y un mismo parecer,\r\nSigue adelante, en pos de perfección.\r\nEstá muy cerca el nuevo amanecer,\r\nDonde estaremos con Cristo, allá en Sión //', '2\r\n¡Oh juventud!: hablad la misma lengua,\r\nPues almas hay, que están perdidas y sin Dios.\r\nHaz que tu luz destelle en las tinieblas,\r\nPara llevar a este mundo salvación.', 'CORO(final)\r\n// Con una mente y un mismo parecer,\r\nSigue adelante, en pos de perfección.\r\nEstá muy cerca el nuevo amanecer,\r\nDonde estaremos con Cristo, allá en Sión //', '', '', '', '', '', '', '', '', 4),
(314, '314\r\nVALIOSA JUVENTUD', '1\r\nJuventud entusiasta que, a Cristo nuestro Rey,\r\nVamos con esperanza, la esperanza de crecer\r\nEn su amor, por el cual es nuestro amigo fiel.', 'CORO\r\nCrece en tu fe, lejos de la maldad.\r\nTomados de Cristo, podemos vencer.\r\n¡Démosle!, ¡Démosle! nuestro tesoro al Rey.\r\nPues valiosa juventud: para Cristo es.', '2\r\nNo durmamos ahora, tenemos que velar.\r\nNuestro Rey va adelante: ¡Vamos todos a luchar!\r\nLa maldad pasará, y un nuevo día será.', 'CORO\r\nCrece en tu fe, lejos de la maldad.\r\nTomados de Cristo, podemos vencer.\r\n¡Démosle!, ¡Démosle! nuestro tesoro al Rey.\r\nPues valiosa juventud: para Cristo es.', '3\r\nCristo te está llamando a compartir con Él,\r\nSu bondad infinita, en su reino de amor.\r\nNuestro Rey, Él será y todo cambiará.', 'CORO(final)\r\nCrece en tu fe, lejos de la maldad.\r\nTomados de Cristo, podemos vencer.\r\n¡Démosle!, ¡Démosle! nuestro tesoro al Rey.\r\nPues valiosa juventud: para Cristo es.', '', '', '', '', '', '', 6),
(315, '315 \r\nVENID EN POS DE MÍ', '1\r\n¡Venid en pos de mí!, clama el Maestro;\r\n“Busco pescadores de preciosas almas”.', 'CORO\r\n// ¿Quién dejará su red? //\r\nPara acompañarlo.\r\n// ¿Quién dejará su red? //\r\nY así seguir su ejemplo.', '2\r\nLo que pide de ti, es tu compasión\r\nA los extraviados. Del Maestro, ¡Habladles!', 'CORO\r\n// ¿Quién dejará su red? //\r\nPara acompañarlo.\r\n// ¿Quién dejará su red? //\r\nY así seguir su ejemplo.', '3\r\nCumple su voluntad con amor fraterno.\r\nTú, ¡Ve y anuncia el Santo Evangelio!', 'CORO(final)\r\n// ¿Quién dejará su red? //\r\nPara acompañarlo.\r\n// ¿Quién dejará su red? //\r\nY así seguir su ejemplo.', '', '', '', '', '', '', 6),
(316, '316 \r\nVE, Y ANUNCIA EL REINO DE DIOS', '1\r\n¡Oh Ministros! de Cristo sois.\r\nNuestro lema: “Id a predicar”\r\nMensajeros, que en dura lid,\r\nLa bandera de victoria levantad.', 'CORO\r\nVe y anuncia el reino de Dios.\r\nEl mensaje de amor predicad.\r\nId por el mundo y anunciad:\r\nQue Cristo pronto volverá.', '2\r\nMarchen firmes, hacia el Señor,\r\nLos ministros con el gran Pastor.\r\nSean firmes, ceñidos, ya,\r\nNuestros lomos de confianza y de valor.', 'CORO\r\nVe y anuncia el reino de Dios.\r\nEl mensaje de amor predicad.\r\nId por el mundo y anunciad:\r\nQue Cristo pronto volverá.', '3\r\nTrabajemos con mucho amor.\r\nLa unidad por siempre conservad.\r\nPor victoria y por la fe,\r\nLa corona eterna, Cristo ceñirá.', 'CORO(final)\r\nVe y anuncia el reino de Dios.\r\nEl mensaje de amor predicad.\r\nId por el mundo y anunciad:\r\nQue Cristo pronto volverá.', '', '', '', '', '', '', 6);

