  `pagina_10` text COLLATE utf8mb4_spanish2_ci NOT NULL,
  `pagina_11` text COLLATE utf8mb4_spanish2_ci NOT NULL,
  `pagina_12` text COLLATE utf8mb4_spanish2_ci NOT NULL,
  `num` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_spanish2_ci;

--
-- Volcado de datos para la tabla `himnos_juveniles`
--

INSERT INTO `himnos_juveniles` (`id_himno`, `pagina_0`, `pagina_1`, `pagina_2`, `pagina_3`, `pagina_4`, `pagina_5`, `pagina_6`, `pagina_7`, `pagina_8`, `pagina_9`, `pagina_10`, `pagina_11`, `pagina_12`, `num`) VALUES
(1, '1\r\nNINGUNO TENGA EN POCO TU JUVENTUD', '//Ninguno tenga en poco tu juventud//\r\nPero se ejemplo de los fieles\r\nEn palabras, en conversación\r\n//En caridad, en espíritu\r\nEn fe, limpieza amen//', '', '', '', '', '', '', '', '', '', '', '', 1),
(2, '2\r\nACUÉRDATE DE TU CREADOR', 'I\r\nCon fuerza como la mar\r\nY la que lleva el viento\r\nNo dejes que la gente\r\nSe lleve tus pensamientos', 'Coro\r\nAcuérdate de tu Creador\r\nEn los días de tu Juventud\r\nAntes que pasen los años y digas\r\nNo tengo en ellos contentamiento', 'II\r\nSi hoy le sirves a Dios\r\nUnido a tu hermano\r\nLos días aprovecha\r\nAsí como buen cristiano', 'Coro\r\nAcuérdate de tu Creador\r\nEn los días de tu Juventud\r\nAntes que pasen los años y digas\r\nNo tengo en ellos contentamiento', 'III\r\nCon la palabra de Dios \r\nLas manos en el arado\r\nIras sembrando amor\r\nCantando con entusiasmo', 'Coro(final)\r\nAcuérdate de tu Creador\r\nEn los días de tu Juventud\r\nAntes que pasen los años y digas\r\nNo tengo en ellos contentamiento', '', '', '', '', '', '', 6),
(3, '3\r\nESFUÉRZATE Y SE VALIENTE', 'I\r\nLevántese la juventud\r\nDe todo lugar de mar a mar \r\nSera el caminar de día\r\nY de noche tú lo meditaras', 'Coro\r\nMira que te mando que te esfuerces \r\nY seas valiente y no desmayes\r\nTu Dios será contigo\r\nY no desmayes tu Dios será contigo (Final)', 'II\r\nTú puedes ser como Josué \r\nY como Caleb despierta el don\r\nQue está en ti que esta\r\nEscondido en algún lugar de ti', 'Coro\r\nMira que te mando que te esfuerces \r\nY seas valiente y no desmayes\r\nTu Dios será contigo\r\nY no desmayes tu Dios será contigo (Final)', 'III\r\nEl mundo allá quiere quitar\r\nEl poder de Dios no dejes más \r\nSu voluntad sigue el camino\r\nY Jehová tu ayudara ', 'Coro(final)\r\nMira que te mando que te esfuerces \r\nY seas valiente y no desmayes\r\nTu Dios será contigo\r\nY no desmayes tu Dios será contigo (Final)', '', '', '', '', '', '', 6),
(4, '4\r\nTENGO A CRISTO EN MI ALMA', 'I \r\n// Tengo a Cristo en mi alma\r\nA Cristo en mi alma\r\nTengo a Cristo en mi alma\r\nY en mi ser //', 'II\r\n// Hay un cambio en mi vida\r\nUn cambio en mi vida\r\nHay un cambio en mi vida\r\nDesde hoy //', 'III\r\n// Estoy dispuesto a servirle \r\nListo a servirle \r\nEstoy dispuesto a servirle \r\nCon amor //', '', '', '', '', '', '', '', '', '', 3),
(5, '5\r\nFORTALEZA TIENES TU', 'I\r\nFortaleza tienes tu Reconoce tu momento\r\nPorque la vida marcha fugaz y en Cristo debes fiar\r\nTu existir en flor ya esta y un mensaje has de llevar\r\nEl evangelio puro llevaras por doquier a predicar', 'Coro\r\nTú tu eres tú juventud\r\nLevántate y recibe fortaleza más\r\nTú tu eres tú juventud\r\nY gloriosa por siempre serás', 'II\r\nY tus alas de paloma Potentes blancas serán\r\nEl Evangelio puro llevaras por doquier a predicar\r\nY tus lomos se ceñirán y tu cuerpo se transformará\r\nY tus ropas blancas serás cuando Cristo venga ya', 'Coro II (final)\r\nTú tú eres tú juventud\r\nLevántate y recibe fortaleza más\r\nTú tú eres tú juventud\r\nEres tú juventud \r\nDe la iglesia de Dios', '', '', '', '', '', '', '', '', 4),
(6, '6\r\nECHAD LA HOZ', 'I\r\nNaciendo el sol Juventud debes tomar la hoz\r\nEn la labor tus manos necesitan Dios\r\nFraternidad es hora ya de laborar\r\nVayamos todos por Dios a trabajar', 'Coro\r\nEchad la hoz por que la mies madura esta\r\nLos frutos ya tenéis que levantar\r\nFraternidad es hora ya de laborar\r\nVayamos juntos por Dios a trabajar', 'II\r\nOh juventud el tiempo de la siega es hoy\r\nEn plenitud te encuentras oh fraternidad\r\nCon gran amor tendremos que tomar la hoz\r\nCual labrador trabaja para Dios', 'Coro (final)\r\nEchad la hoz por que la mies madura esta\r\nLos frutos ya tenéis que levantar\r\nFraternidad es hora ya de laborar\r\nVayamos juntos por Dios a trabajar', '', '', '', '', '', '', '', '', 4),
(7, '7\r\nVAMOS ADELANTE A LA PERFECCIÓN', 'I\r\n// Hasta cuando // despertaras\r\n// Hasta cuando // trabajaras\r\nDice mi Dios la hora es \r\nVamos valiente no hay que temer\r\nCon gran valor oh juventud\r\nEl dar amor es tu virtud', 'II\r\n// Adelante // a la perfección\r\n// Adelante // contigo es Dios\r\nDice el Señor tiempo ya es \r\nVictoria habrá contigo seré\r\nVamos valiente oh Juventud\r\nSer excelentes es tu virtud', 'Coro\r\n // Adelante Adelante\r\nA la perfección //', '', '', '', '', '', '', '', '', '', 3),
(8, '8\r\nDA SU FRUTO A SU TIEMPO', 'I\r\nSerás como árbol plantado\r\nJunto arroyos de agua\r\nComo su hijo amado\r\nDios te ha cuidado', 'Coro\r\nDa su fruto en su tiempo\r\n// Así eres tú //\r\nQue da su fruto en su tiempo\r\nAsí como tú', 'II\r\nSiempre su tronco esta firme \r\nAlto como las palmas\r\nJoven tu eres grandeza\r\nY fortaleza', 'Coro\r\nDa su fruto en su tiempo\r\n// Así eres tú //\r\nQue da su fruto en su tiempo\r\nAsí como tú', 'III\r\nRacimos de bendiciones \r\nSi trabajas por el \r\nVamos marchando adelante\r\nEn pos ya de Él', 'Coro(final)\r\nDa su fruto en su tiempo\r\n// Así eres tú //\r\nQue da su fruto en su tiempo\r\nAsí como tú', '', '', '', '', '', '', 6),
(9, '9\r\nID Y PREDICAD', ' I\r\nCon ánimo hermanos id y predicad\r\nLlevad la doctrina y sus vidas salvad\r\nSi entregas tu vida con fe y valor\r\nPredica por siempre a nuestro Salvador', 'Coro\r\n// Id y predicad //\r\nEl santo Evangelio a la humanidad\r\n// Id y predicad //\r\nEl santo Evangelio al mundo llevad', 'Coro (final)\r\n// Id y predicad //\r\nEl santo Evangelio a la humanidad\r\n// Id y predicad //\r\nEl santo Evangelio al mundo llevad', '', '', '', '', '', '', '', '', '', 3),
(10, '10\r\nRENOVAROS EN EL ESPÍRITU', 'I\r\nLa vida llena de prueba esta\r\nDesastre hay que pasar escrito está\r\nEn el camino muchos quedaran\r\nDejando el amor que Cristo nos da', 'Coro\r\nA renovaros en el Espíritu\r\nDe vuestra mente dice el Señor\r\nA renovaros en el Espíritu \r\nDe vuestra mente y tendrás salvación', 'II\r\nTambién las obras hay que mostrar\r\nNo solo aparentar que somos de Ël \r\nTambién Importa toda nuestra fe\r\nPara poder lograr una vida en Él', 'Coro (final)\r\nA renovaros en el Espíritu\r\nDe vuestra mente dice el Señor\r\nA renovaros en el Espíritu \r\nDe vuestra mente y tendrás salvación', '', '', '', '', '', '', '', '', 4),
(11, '11\r\nTOCAD TROMPETA', 'Coro\r\nTocad trompeta pero ya\r\n¡ Oh juventud de la iglesia de Dios ¡\r\nTocad trompeta sin vacilar\r\nY apercibir al pueblo del Señor', 'I\r\nEl que escuchare se preparara\r\nPara la guerra que se sostendrá\r\nY el que creyere la fe lo salvara\r\nY entrara al reino milenial', 'Coro\r\nTocad trompeta pero ya\r\n¡ Oh juventud de la iglesia de Dios ¡\r\nTocad trompeta sin vacilar\r\nY apercibir al pueblo del Señor', 'II\r\nEl que escucho y no se preparó \r\nTendrá sobre él la ira de Jehová\r\n// Porque él trompeta toco //\r\nY aquel no más se burlo', 'Coro\r\nTocad trompeta pero ya\r\n¡ Oh juventud de la iglesia de Dios ¡\r\nTocad trompeta sin vacilar\r\nY apercibir al pueblo del Señor', 'III\r\nEntre la entrada y frente al altar \r\nLloren pastores ministros del Señor\r\nBuscando paz buscando su perdón\r\nMisericordia también su bendición', 'Coro (final)\r\nTocad trompeta pero ya\r\n¡ Oh juventud de la iglesia de Dios ¡\r\nTocad trompeta sin vacilar\r\nY apercibir al pueblo del Señor', '', '', '', '', '', 7),
(12, '12\r\nPERMANEZCA EN AMOR FRATERNAL', 'I\r\nSi el amor fraternal se busca\r\nY despierta en tu corazón \r\nGrandes cosas con el haremos\r\nCon tu hermano serás mejor', 'Coro\r\nJonathan y David lo sintieron \r\nEse amor en el corazón \r\nY en la prueba que ellos vivieron \r\nEse amor sobresalió', 'II\r\nY si Dios ya te ha mostrado \r\nEn Jesús su grande amor\r\nFraternal amor busquemos\r\nEn ayuno y oración', 'Coro\r\nJonathan y David lo sintieron \r\nEse amor en el corazón \r\nY en la prueba que ellos vivieron \r\nEse amor sobresalió', 'III\r\nQue en la iglesia de Dios se sienta \r\nLa presencia de ese amor\r\nY los jóvenes de esta iglesia \r\nDen su voto en oración', 'Coro (final)\r\nJonathan y David lo sintieron \r\nEse amor en el corazón \r\nY en la prueba que ellos vivieron \r\nEse amor sobresalió', '', '', '', '', '', '', 6),
(13, '13\r\nVIVE CRISTO EN MI', 'I\r\nEl corazón arde en mi pecho\r\nAl escuchar la voz de Jesús\r\nDeclarándome en la biblia\r\nLa salvación de mi vida', 'Coro\r\nY ya no soy quien vive más\r\nVive Cristo en mi\r\nVivo en la fe de mi señor\r\nEl cual me amo y murió por mi', 'II\r\nDiscípulo soy de Jesús\r\nSiempre debo cargar mi cruz\r\nDel mundo vivo apartado\r\nDando gloria en mi cuerpo a Dios', 'Coro\r\nY ya no soy quien vive más\r\nVive Cristo en mi\r\nVivo en la fe de mi señor\r\nEl cual me amo y murió por mi', 'III\r\nJoven llamado a ser santo\r\nAnuncia el Evangelio con virtud\r\nCon hambre sed, desnudez\r\nPero ayudados por Dios', 'Coro\r\nY ya no soy quien vive más\r\nVive Cristo en mi\r\nVivo en la fe de mi señor\r\nEl cual me amo y murió por mi', 'IV\r\nJuventud consagra tu vida\r\nLleva las marcas de Cristo Jesús\r\nMuriendo en la carne al mundo\r\nViviendo en espíritu a Dios', 'Coro (final)\r\nY ya no soy quien vive más\r\nVive Cristo en mi\r\nVivo en la fe de mi señor\r\nEl cual me amo y murió por mi', '', '', '', '', 8),
(14, '14\r\nPERFECCIONANDO LA SANTIFICACIÓN', 'I\r\nTú que puede ver hasta lo más profundo\r\nY conoces antes lo que podemos pensar \r\nTienes el poder de transformar al mundo\r\nPero solo quiere al que por fe le servirá \r\n', 'I\r\nSeñor yo te pido por medio de tu hijo \r\nPues el bien que quiero aun no lo puedo alcanzar\r\nQuita mi dureza que ha sido la barrera\r\nPara que te sirva mi corazón sin vacilar', 'Coro\r\nHay un vacío que no he podido llenar \r\nEstoy sediento de tu rio espiritual \r\nHoy quiero volverlo a intentar ', 'Coro\r\nDejando el hombre viejo atrás \r\nPuedo ofrecerte más si yo me lo propongo \r\nPude entender que no basta solo en decir ', 'Coro\r\nPorque mi fe sin obras no te sirve a ti\r\nMi vida quiero mejorar perfeccionar mi santidad\r\nCon hechos te voy a mostrar cuanto he cambiado', 'II\r\nDios nuestro señor no puede ser burlado \r\nY lo que sembremos eso vamos a segar\r\nEscucha hay darle lo mejor que tenemos\r\nPorque solo Él sabe cuánto tiempo nos durara', 'II\r\nNo esperes a que te encuentres afligido \r\nPara darte cuenta que necesitas a Dios\r\nSigue en el camino reconociendo a Cristo\r\nY el día de mañana Él también te recordara ', 'Coro\r\nHay un vacío que no he podido llenar \r\nEstoy sediento de tu rio espiritual \r\nHoy quiero volverlo a intentar ', 'Coro\r\nDejando el hombre viejo atrás \r\nPuedo ofrecerte más si yo me lo propongo \r\nPude entender que no basta solo en decir ', 'Coro\r\nPorque mi fe sin obras no te sirve a ti\r\nMi vida quiero mejorar perfeccionar mi santidad\r\nCon hechos te voy a mostrar cuanto he cambiado', '', '', 10),
(15, '15\r\nDIOS ES AMOR', 'I\r\nEl amor de Dios es grande en el mundo\r\nCualquiera que ama nace en amor', 'Coro\r\n// Dios es amor y el que vive en amor\r\nVive en Dios y Dios en Él //', 'II\r\nHa llegado el tiempo de saber amar\r\nDios está en nosotros y su amor dará', 'Coro\r\n// Dios es amor y el que vive en amor\r\nVive en Dios y Dios en Él //', 'III\r\nNo lo pienses únete a nosotros hoy\r\nTodos juntos vamos a vencer al mal ', 'Coro(final)\r\n// Dios es amor y el que vive en amor\r\nVive en Dios y Dios en Él //', '', '', '', '', '', '', 6),
(16, '16\r\nTODO LO PUEDO EN CRISTO QUE ME FORTALECE ', 'I\r\nEs el momento de servir a Dios\r\nY crecer en comunión \r\nPorque si todos confiamos en Cristo\r\nObtendremos salvación', 'Coro\r\nLa potencia de Cristo habita en mi\r\nPuedes decirlo a una voz\r\nPorque todo lo puedo en Cristo \r\nQue me fortalece hoy\r\nPorque todo lo puedo en Cristo \r\nQue me fortalece hoy', 'II\r\nAhora Cristo te dice joven \r\nBástate en mi gracia ya\r\nPara estar firme ante las pruebas \r\nY también en la tentación', 'Coro\r\nLa potencia de Cristo habita en mi\r\nPuedes decirlo a una voz\r\nPorque todo lo puedo en Cristo \r\nQue me fortalece hoy\r\nPorque todo lo puedo en Cristo \r\nQue me fortalece hoy', 'III\r\nConfortaos siempre en Jesucristo\r\nFortaleza tuya es Él \r\nTodos vestidos de su armadura\r\nPara poder vencer al mal ', 'Coro(final)\r\nLa potencia de Cristo habita en mi\r\nPuedes decirlo a una voz\r\nPorque todo lo puedo en Cristo \r\nQue me fortalece hoy\r\nPorque todo lo puedo en Cristo \r\nQue me fortalece hoy', '', '', '', '', '', '', 6),
(17, '17\r\nEL VIVIR ES CRISTO', 'I\r\nEn este camino en el cual yo voy\r\nEl enemigo me quiere apartar \r\nPero no estoy solo Cristo va conmigo \r\nY mi pensamiento este será', 'Coro\r\n// (H)Porque para mí (M)Porque para mí\r\n(H)El vivir es Cristo (M) El vivir es Cristo\r\n(T) Y el morir es ganancia //', 'II\r\nSi te sientes solo no lo dudes más\r\nEntrégate a Cristo y Él contigo\r\nSiempre estará Pablo mismo lo dijo \r\nQue el vivir en Cristo es mejor que vivir sin Él', 'Coro\r\n// (H)Porque para mí (M)Porque para mí\r\n(H)El vivir es Cristo (M) El vivir es Cristo\r\n(T) Y el morir es ganancia //', 'III\r\nNo no no te engañes pues en este mundo\r\nTe ofrecen cosas que pronto perecerán \r\nAhora es el tiempo de tener el cambio\r\nSantifica tu vida y tu pensar siempre será ', 'Coro(final)\r\n// (H)Porque para mí (M)Porque para mí\r\n(H)El vivir es Cristo (M) El vivir es Cristo\r\n(T) Y el morir es ganancia //', '', '', '', '', '', '', 6),
(18, '18\r\nDESPIÉRTATE TU QUE DUERMES ', 'I\r\nDebes de pensar bien las cosas desde hoy\r\nTu vida arriesgaras si no sigues al señor\r\nDebes decidir desde hoy que vas hacer\r\nEscucha el consejo de tu hermano', 'Coro\r\nDespiértate tú que duermes\r\nDe los muertos levántate hoy\r\nDespiértate tú que duermes\r\nQue Cristo te alumbrara\r\nQue Cristo te alumbrara', 'II\r\nDios está contigo nunca te dejara \r\nSolo confía en Él y no temas de satán \r\nAsí dardos mil y tentaciones ya \r\nNo te dañaran Él es contigo', 'Coro\r\nDespiértate tú que duermes\r\nDe los muertos levántate hoy\r\nDespiértate tú que duermes\r\nQue Cristo te alumbrara\r\nQue Cristo te alumbrara', 'III\r\nProblemas tendrás tu no debes desmayar \r\nUnidos en amor hasta el éxito alcanzar \r\nCon toda la iglesia en el reino estarás\r\nSi sigues fiel en el camino ', 'Coro(final)\r\nDespiértate tú que duermes\r\nDe los muertos levántate hoy\r\nDespiértate tú que duermes\r\nQue Cristo te alumbrara\r\nQue Cristo te alumbrara', '', '', '', '', '', '', 6),
(19, '19\r\nHEME AQUÍ ENVÍAME A MI ', 'I\r\nMirando el cielo directo al horizonte \r\nDestella una luz que veo brillar \r\nEs Jesús quien pide ayuda\r\nQue al mundo ha de salvar', 'Coro\r\n// Heme aquí envíame a mí\r\nQue mis manos aquí están\r\nPreparadas a trabajar\r\nEn el nombre del señor //', 'II\r\nSi juntos luchamos tendremos que vencer \r\nEl mal que en el mundo ha de florecer\r\nCaminemos con mira al Creador\r\nY digamos al Señor', 'Coro\r\n// Heme aquí envíame a mí\r\nQue mis manos aquí están\r\nPreparadas a trabajar\r\nEn el nombre del señor //', 'III\r\nVamos adelante que es el tiempo final \r\nUnamos las fuerzas no hay que descansar \r\nSi tú quieres seguir al Señor\r\nSolo di en tu Corazón ', 'Coro(final)\r\n// Heme aquí envíame a mí\r\nQue mis manos aquí están\r\nPreparadas a trabajar\r\nEn el nombre del señor //', '', '', '', '', '', '', 6),
(20, '20\r\nLA GLORIA DEL JOVEN ES SU FORTALEZA', 'I\r\nPuedes desarrollar tu virtud\r\nEn los días de tu juventud\r\nDe ti depende demostrar\r\nLos dones que Dios te da', 'Coro\r\nPorque la gloria de los jóvenes su fortaleza es \r\nNada habrá que detenga su poder\r\nPara lograr proezas en tu nombre Oh Dios\r\nY manifiesta su gloria se verá\r\n', 'II\r\nJoven sigue y no mires atrás \r\nSi en verdad tú te quieres salvar \r\nConstruye obras para Dios\r\nY sigue la perfección', 'Coro\r\nPorque la gloria de los jóvenes su fortaleza es \r\nNada habrá que detenga su poder\r\nPara lograr proezas en tu nombre Oh Dios\r\nY manifiesta su gloria se verá', 'III\r\nNo olvides tu fortaleza es Dios\r\nY no interrumpas tu misión \r\nRecuerda que hay un Galardón \r\nA lado del Salvador ', 'Coro (final)\r\n//Porque la gloria de los jóvenes su fortaleza es \r\nNada habrá que detenga su poder\r\nPara lograr proezas en tu nombre Oh Dios\r\nY manifiesta su gloria se verá//(Final)\r\nY manifiesta su gloria se verá (Final)', '', '', '', '', '', '', 6),
(21, '21\r\nJEHOVÁ ES MI FORTALEZA', 'I\r\nLevantemos nuestras voces oh cristiana juventud \r\nPredicando a todo el mundo en más sublime amor\r\nEl murió para salvarte a ti y a mí del pecado y el dolor', 'I\r\nSolo ofrece buenas obras solo cumple tu misión \r\nDignifícale en tu vida se un ejemplo de valor\r\nGlorifícale en vida en cuerpo y espíritu', 'Coro\r\nJehová es mi fortaleza \r\nY mi escudo mi guiador \r\nQue mi cantico le alabe\r\nAl señor Dios de amor', 'II\r\nCaminemos con pie firme en las filas que hoy están \r\nDirigidas por mi Cristo, mi piloto y capitán \r\nLevantemos nuestras voces al Creador que se escuche retumbar ', 'II\r\nSu palabra justa y digna a toda la humanidad \r\nDemostrando con ahincó buenas nuevas anunciad\r\nAunque nos cueste la vida su amor siempre vivirá', 'Coro\r\nJehová es mi fortaleza \r\nY mi escudo mi guiador \r\nQue mi cantico le alabe\r\nAl señor Dios de amor', '', '', '', '', '', '', 6),
(22, '22\r\nACUÉRDATE DE ENGRANDECER SU OBRA ', 'I\r\nTodos unidos somos la Iglesia de Dios \r\nSalvos por su gran amor\r\nY como uno solo la Iglesia de Dios\r\nCanta con grande fervor', 'Coro\r\n(H)Acuérdate de engrandecer la obra del Señor\r\n(M)Unidos triunfantes hasta llegar a la presencia de Dios\r\n(T)//Acuérdate de engrandecer la obra de nuestro Dios//', 'II\r\nTodos con alegría haciendo labor \r\nTodos conforme a su don\r\nY en armonía tal cual sinfonía\r\nEleva la dulce canción', 'Coro\r\n(H)Acuérdate de engrandecer la obra del Señor\r\n(M)Unidos triunfantes hasta llegar a la presencia de Dios\r\n(T)//Acuérdate de engrandecer la obra de nuestro Dios//', 'III\r\nSomos un solo cuerpo en fraternidad \r\nLibres de la vanidad\r\nTodos nos ayudamos con sinceridad \r\nEn el amor de Jehová', 'Coro(final)\r\n(H)Acuérdate de engrandecer la obra del Señor\r\n(M)Unidos triunfantes hasta llegar a la presencia de Dios\r\n(T)//Acuérdate de engrandecer la obra de nuestro Dios//', '', '', '', '', '', '', 6),
(23, '23\r\nVE CON ESTA TU FORTALEZA', 'I\r\nJuventud que a Dios has venido\r\nHoy en esta reunión en que estamos\r\nFelices todos hermanos\r\nA Dios cantemos unidos', 'I\r\nAcude al llamado que Dios \r\nHa puesto en tu vida hermano\r\nCon ese poder que te ha dado\r\nTrabaja no tardes hermano', 'Coro\r\n// (M) Ve con esta\r\n(H) Tu fortaleza\r\n(M) El tiempo cerca esta\r\n(T) Siembra semilla que Dios te da //', 'II\r\nSigue siempre con valor al frente \r\nCaminando con Dios nuestro fuerte\r\nVenciendo peligro muy grande\r\nJuventud camina valiente ', 'Coro (final)\r\n// (M) Ve con esta\r\n(H) Tu fortaleza\r\n(M) El tiempo cerca esta\r\n(T) Siembra semilla que Dios te da //', '', '', '', '', '', '', '', 5),
(24, '24\r\nEL PRINCIPIO DE LA SABIDURÍA ES EL TEMOR DE JEHOVÁ', 'I\r\nJuventud es tiempo de buscar a Dios\r\nEn su palabra tú meditaras\r\nY seguro conocimiento hallaras \r\nNo te dejes llevar por teorías de falsedad', 'Coro\r\n(H) El principio de la sabiduría\r\nEs el temor de Jehová \r\n(M) La clave que te llevara\r\n(T) Al camino de la santidad', 'II\r\nJuventud el mundo corrompido esta\r\nPor su ignorancia sumergido va\r\nAntes lucha contra el mal y vencerás \r\nSi caminas con Dios en tu vida\r\nSu paz tendrás', 'Coro\r\n(H) El principio de la sabiduría\r\nEs el temor de Jehová \r\n(M) La clave que te llevara\r\n(T) Al camino de la santidad', 'III\r\nJuventud sigue el ejemplo de Jesús \r\nSabiduría en su Ley encontraras \r\nMil bendiciones a tu vida llegaran \r\nHoy escucha su voz oh hermano \r\nNo tardes mas ', 'Coro (final)\r\n(H) El principio de la sabiduría\r\nEs el temor de Jehová \r\n(M) La clave que te llevara\r\n(T) Al camino de la santidad', '', '', '', '', '', '', 6),
(25, '25\r\nTODO LO QUE HAGÁIS HACEDLO DE ANIMO', 'I\r\n(H) Joven guarda la doctrina aprendida\r\n(H) Que firme es el fundamento de Dios\r\n(M) En la instrucción del evangelio participa \r\n(T) Según el don que Él señor otorgo', 'Coro\r\nTodo lo que hagáis hacedlo de ánimo\r\nConforme a la ordenanza de Jehová \r\nReconócelo en tus caminos\r\nQue es muestra de amor y lealtad', 'II\r\n(H) Joven santifica tu vida al padre \r\n(H) No inclines tu corazón a iniquidad \r\n(M) Poned los ojos en el autor de los dones \r\n(T) Que rectos son los caminos de Dios', 'Coro\r\nTodo lo que hagáis hacedlo de ánimo\r\nConforme a la ordenanza de Jehová \r\nReconócelo en tus caminos\r\nQue es muestra de amor y lealtad', 'III\r\n(H) Traza con justicia palabra de verdad \r\n(H) Y confirma tu corazón a Jehová \r\n(M) No des honor y trabajo en casa de extraño\r\n(T) Y sigue el camino de santidad ', 'Coro(final)\r\n//Todo lo que hagáis hacedlo de ánimo\r\nConforme a la ordenanza de Jehová \r\nReconócelo en tus caminos\r\nQue es muestra de amor y lealtad//', '', '', '', '', '', '', 6),
(26, '26\r\nEL DIOS DE PAZ SERÁ CON VOSOTROS ', 'I\r\nAl eterno Dios que con poder te formo \r\nCongregado alza la voz y alabemos \r\nCon la oración aparta la aflicción\r\nY da gracias a Dios por su amor', 'Coro\r\nEl Dios de paz será con vosotros \r\nDale hoy tu corazón \r\n// Bendición paz y amor\r\nTe dará el creador //', 'II\r\nSigue practicando lo que el padre enseño \r\nY las instrucciones que con amor te dio\r\nDe buenas obras muéstrate como ejemplo \r\nPues del joven esta es su virtud', 'Coro\r\nEl Dios de paz será con vosotros \r\nDale hoy tu corazón \r\n// Bendición paz y amor\r\nTe dará el creador //', 'III\r\nQue ninguno tenga en poco tu juventud\r\nA este pobre mundo debes llenar de luz\r\nPalabra de verdad a todos predicar \r\nY almas del pecado liberar ', 'Coro\r\nEl Dios de paz será con vosotros \r\nDale hoy tu corazón \r\n// Bendición paz y amor\r\nTe dará el creador // (Final)', '', '', '', '', '', '', 6),
(27, '27\r\nUNIDOS COMO UN SOLO HOMBRE', 'I\r\n! Oh ¡ joven de la iglesia de Dios\r\nAlza tu espada con valor \r\nY se digno soldado de nuestro Dios\r\nY con tu ejemplo predica', 'Coro\r\n(T) Unidos como un solo hombre\r\n(M) Hasta alcanzar (H) Quiero alcanzar\r\n(T) La santidad\r\n(H) Sabiendo que (M) Yo se muy bien \r\n(T) Que en su venida mi galardón me entregara', 'II\r\nEs tiempo de empezar la labor\r\nDeja tu unicuo corazón\r\nPeleando la buena batalla de la fe\r\nY al final la victoria tendrás', 'Coro\r\n(T) Unidos como un solo hombre\r\n(M) Hasta alcanzar (H) Quiero alcanzar\r\n(T) La santidad\r\n(H) Sabiendo que (M) Yo se muy bien \r\n(T) Que en su venida mi galardón me entregara', 'III\r\nUnidos en una misma fe\r\nMente espíritu y verdad \r\nNunca solo en el camino te hallaras \r\nPorque Cristo contigo esta', 'Coro\r\n//(T) Unidos como un solo hombre\r\n(M) Hasta alcanzar (H) Quiero alcanzar\r\n(T) La santidad\r\n(H) Sabiendo que (M) Yo se muy bien \r\n(T) Que en su venida mi galardón me entregara//', '', '', '', '', '', '', 6),
(28, '28\r\nPROSIGO AL BLANCO ', 'I\r\nSomos ejércitos del Señor\r\nLuchamos juntos con valor\r\nAl mal que acecha a nuestro ser\r\nVenceremos con gran poder', 'Coro\r\n(H) Prosigo al blanco (M) Sin mirar atrás\r\n(H) Con fortaleza hasta el final\r\n(M) Uniendo dones para triunfar ', 'Coro\r\n(H) Prosigo al blanco (M) Sin dudar jamás\r\n(T) El viene pronto y nos dará \r\nLa corona eternal', 'II\r\nSomos los siervos del señor\r\nDispuesto para trabajar\r\nUn pueblo santo lleno de fe\r\nUnido en mente y en parecer', 'Coro\r\n(H) Prosigo al blanco (M) Sin mirar atrás\r\n(H) Con fortaleza hasta el final\r\n(M) Uniendo dones para triunfar ', 'Coro\r\n(H) Prosigo al blanco (M) Sin dudar jamás\r\n(T) El viene pronto y nos dará \r\nLa corona eternal', 'III\r\nEsta carrera hay que seguir \r\nSi premio quieres recibir\r\nPerseverantes hasta lograr\r\nLa vida eterna alcanzar ', 'Coro\r\n(H) Prosigo al blanco (M) Sin mirar atrás\r\n(H) Con fortaleza hasta el final\r\n(M) Uniendo dones para triunfar ', 'Coro (final)\r\n(H) Prosigo al blanco (M) Sin dudar jamás\r\n(T) El viene pronto y nos dará \r\nLa corona eternal', '', '', '', 9),
(29, '29\r\nTOMAD TODA LA ARMADURA DE DIOS ', 'I\r\nSal a luchar oh juventud \r\nTu fortaleza busca Dios\r\nVen y demuestra la virtud\r\nQue posa en todo hijo de Dios', 'Coro\r\nTomad toda la armadura de Dios\r\nY revistámonos del bien\r\nLucha (Lucha) Con fe (Con fe)\r\nVictoria dar a nuestro Rey', 'II\r\nVamos unidos combatid \r\nEl mundo lleno esta del más\r\nFieles soldados acudid\r\nQue es Cristo nuestro capitán', 'Coro\r\nTomad toda la armadura de Dios\r\nY revistámonos del bien\r\nLucha (Lucha) Con fe (Con fe)\r\nVictoria dar a nuestro Rey', 'III\r\nVistámonos de santidad\r\nY prediquemos la verdad\r\nVenzamos con el bien al mal\r\nCorona eterna Dios dará', 'Coro (final)\r\n//Tomad toda la armadura de Dios\r\nY revistámonos del bien\r\nLucha (Lucha) Con fe (Con fe)\r\nVictoria dar a nuestro Rey//', '', '', '', '', '', '', 6),
(30, '30\r\nMOSTRÁNDOTE EN TODO POR EJEMPLO', 'I\r\nLa obra sublime del Señor\r\nTe encomienda resplandezca con fulgor \r\nEjemplo de doctrina con fervor\r\nTu vida en lealtad dedica al Señor', 'Coro\r\n(T) Mostrándote en todo por ejemplo\r\n(M) La doctrina de tu Dios adornaras \r\n(H) Recuerda de su espíritu eres templo\r\n(T) Con tu vida al Señor exaltaras', 'II\r\nLa fidelidad a nuestro Dios\r\nDigno estandarte con vigor a sostener \r\nNo habrá dura batalla en que tu Dios\r\nTe desampare pues victoria has de tener', 'Coro\r\n(T) Mostrándote en todo por ejemplo\r\n(M) La doctrina de tu Dios adornaras \r\n(H) Recuerda de su espíritu eres templo\r\n(T) Con tu vida al Señor exaltaras', 'III\r\nJusticia el ejemplo de Jesús\r\nLlevamos como lema ¡Es por convicción ¡\r\nSiguiendo las pisadas de Jesús\r\nSeguros vamos a la Eterna Salvación', 'Coro (final)\r\n//(T) Mostrándote en todo por ejemplo\r\n(M) La doctrina de tu Dios adornaras \r\n(H) Recuerda de su espíritu eres templo\r\n(T) Con tu vida al Señor exaltaras//', '', '', '', '', '', '', 6),
(31, '31\r\nHAME ENVIADO A PREDICAR BUENAS NUEVAS', 'I\r\nHermano no perdamos la oportunidad\r\nDe anunciar el evangelio de bondad\r\nSembrando juntos la semilla con amor', 'I\r\nDaremos frutos al señor \r\nCon fuerza, valor y el Espíritu de Dios\r\nDel Padre hare su buena y santa voluntad ', 'I\r\nRescatando de este mundo engañador \r\n(M) Al más vil pecador\r\n“Hame enviado a predicar las buenas nuevas de salvación”', 'II\r\nHermano no perdamos la oportunidad \r\n¿Qué esperas para ponerte a trabajar?\r\nMuchas almas perdidas hay rescatar ', 'II\r\nCon la palabra de Jehová\r\nCon alegría, gozo, fe y caridad \r\nEsta alabanza a nuestro Dios hay que elevar ', 'II\r\nNo olvidamos su grandeza y majestad\r\n(M) En este día especial\r\n“Hame enviado a predicar las buenas nuevas de salvación”', '', '', '', '', '', '', 6),
(32, '32\r\nGOTEARA COMO LA LLUVIA MI DOCTRINA', 'I\r\nEntre el humo incesante de la confusión \r\nEstando al calor fatigante del desierto abrasador', 'I\r\nAlmas marchitas que mueren sedientas de Dios\r\nPor ello mi voz se levanta con inspiración:', 'Coro\r\nGoteara como la lluvia mi doctrina \r\nDestilara como el roció mi razón\r\nComo llovizna nutrirá', 'Coro\r\nComo las gotas sobre la hierba ya casi muerta\r\nVerdor pondrás en el palo seco\r\nY agua en secadal', 'II\r\nSana doctrina al mundo yo quiero anunciar\r\nQue oigan la tierra y los cielos la palabra de Jehová ', 'II\r\nPues es mandato divino llevar su verdad\r\nDios es la Roca y su obra sabre proclamar ', 'Coro\r\nGoteara como la lluvia mi doctrina \r\nDestilara como el roció mi razón\r\nComo llovizna nutrirá', 'Coro (final)\r\nComo las gotas sobre la hierba ya casi muerta\r\nVerdor pondrás en el palo seco\r\nY agua en secadal', '', '', '', '', 8),
(33, '33\r\nTE ENSEÑARE COSAS GRANDES Y DIFICULTOSAS', 'I\r\nExamíname Dios mío \r\nMira tú mi corazón \r\nY Enséñame a contar mis días\r\nY traer sabiduría:\r\nSeré fiel a tus caminos ¡Mi Señor ¡', 'Coro\r\n(H) Clama a mi\r\n(M) Clamare\r\n(T) Te enseñare cosas grandes y dificultosas\r\n(H) Clama a mi\r\n(M) Clamare\r\n(T) Te enseñare cosas grandes que aún no puedes entender', 'II\r\n¡Oh mi Dios¡, tus pensamientos\r\nInefables siempre son\r\nComprender tu voluntad divina \r\nMuy difícil me seria \r\nSi mi vida no consagro ¡A ti Señor ¡', 'Coro\r\n(H) Clama a mi\r\n(M) Clamare\r\n(T) Te enseñare cosas grandes y dificultosas \r\n(H) Clama a mi\r\n(M) Clamare\r\n(T) Te enseñare cosas grandes que aún no puedes entender', 'III\r\nPadre Dios en ti espero\r\nSe propicio a mi clamor\r\nDe hinojos vengo a ti Mi Roca\r\nA suplicar misericordia\r\n Y sea en mi en quien reveles tu razón ', 'Coro(final)\r\n//(H) Clama a mi\r\n(M) Clamare\r\n(T) Te enseñare cosas grandes y dificultosas \r\n(H) Clama a mi\r\n(M) Clamare\r\n(T) Te enseñare cosas grandes que aún no puedes entender//', '', '', '', '', '', '', 6),
(34, '34\r\nPrimer amor', 'I\r\nHoy quiero preguntarte hijo mío \r\nSi mi presencia ya no te hace feliz\r\nPocas veces buscas mi rostro ¿No es así? (Si)', 'I\r\nHe visto también que ya no haces mi labor con amor\r\nSi no solo como una rutina nada más\r\n¿Ya no recuerdas cuando juntos caminábamos los dos? (Noo)\r\nUna Sonrisa se dibujaba en tu rostro con amor\r\n', 'Coro      \r\nVuélveme a Amar, como lo hiciste la primera vez \r\nCuando del lodo yo te saque\r\n(Como a hijo de té trate)\r\nVuelve a sentir aquel amor que puse dentro de ti\r\nQue aun recuerdas que Prometí \r\n(Estar Siempre Junto a Ti)', 'II\r\n¿Ya no recuerdas cuando juntos caminábamos los dos? (Noo)\r\nUna Sonrisa se dibujaba en tu rostro con amor', 'Coro (final)\r\nVuélveme a Amar, como lo hiciste la primera vez\r\nCuando del lodo yo te saque\r\n(Como a hijo de té trate)\r\nVuelve a sentir aquel amor que puse dentro de ti\r\nQue aun recuerdas que Prometí\r\n(Estar Siempre Junto a Ti)', '', '', '', '', '', '', '', 5),
(35, '35 CONSOLIDA MI CORAZÓN PARA QUE TEMA TU NOMBRE', '1\r\nInclina, oh Jehová, tu oído a mi clamor,\r\nEl camino de las gentes cautivo mi corazón,\r\nMe encuentro solo y triste rogando por mi error,\r\nMi alma esta afligida y busca tu perdón.', 'Coro\r\nConsolida mi corazón para que tema tu nombre,\r\nTen misericordia de tu siervo que en ti confía,\r\nHas de mí una luz para alumbrar en la oscuridad,\r\nY así poder guiar a aquel que perdido esta.', '2\r\nDifícil es lograr, cumplir tu voluntad,\r\nCon espíritu de gracia, tu mi alma guiaras,\r\nSostienes fiel mi mano y no me dejaras,\r\nMe alientas y consuelas pues sabes mi pesar.', 'Coro\r\nConsolida mi corazón para que tema tu nombre,\r\nTen misericordia de tu siervo que en ti confía,\r\nHas de mí una luz para alumbrar en la oscuridad,\r\nY así poder guiar a aquel que perdido esta.', '3\r\nMe diste con amor ayuda en la aflicción,\r\nTu palabra me sustenta y me da consolación,\r\nLoores a tu nombre, daré con devoción,\r\nPor tu misericordia y grata bendición.', 'Coro(Final)\r\n//Consolida mi corazón para que tema tu nombre,\r\nTen misericordia de tu siervo que en ti confía,\r\nHas de mí una luz para alumbrar en la oscuridad,\r\nY así poder guiar a aquel que perdido esta.//', '', '', '', '', '', '', 6);

