  `pagina_6` text COLLATE utf8mb4_spanish2_ci NOT NULL,
  `pagina_7` text COLLATE utf8mb4_spanish2_ci NOT NULL,
  `pagina_8` text COLLATE utf8mb4_spanish2_ci NOT NULL,
