--
-- Volcado de datos para la tabla `himnos_juveniles_nuevos`
--

INSERT INTO `himnos_juveniles_nuevos` (`id_himno`, `pagina_0`, `pagina_1`, `pagina_2`, `pagina_3`, `pagina_4`, `pagina_5`, `pagina_6`, `pagina_7`, `pagina_8`, `pagina_9`, `pagina_10`, `pagina_11`, `pagina_12`, `num`) VALUES
(1, 'Salmo 3', '1\r\nOh Jehová muchos son mis adversarios, \r\nMuchos son los que vienen contra mí,\r\nMuchos son los que dicen de mí:\r\n“No hay para él la salvación de Dios”', 'CORO\r\n//Mas Jehová es escudo alrededor de mí,\r\nMi gloria y el que levanta mi cabeza//', '2\r\n// Con mi voz clame a Jehová\r\nMe respondió desde su monte Santo//', 'CORO\r\n//Mas Jehová es escudo alrededor de mí,\r\nMi gloria y el que levanta mi cabeza//', '3\r\n//No temeré a diez millares de gentes\r\nQue pusieren sitio contra mi//', 'CORO\r\n//Mas Jehová es escudo alrededor de mí,\r\nMi gloria y el que levanta mi cabeza//', '', '', '', '', '', '', 6),
(2, 'Salmo 13 \r\n¿Hasta Cuándo?', '1\r\n//Porque estas lejos Oh Jehová\r\nY te escondes en el tiempo de la tribulación//', 'Coro\r\nHasta cuando Jehová me olvidaras para siempre \r\nHasta cuando esconderás tu rostro de mi', '2\r\n//A ti clamare Oh Jehová\r\nRoca mía y Dios mío\r\nNo te apartes de mi//', 'Coro\r\nHasta cuando Jehová me olvidaras para siempre \r\nHasta cuando esconderás tu rostro de mi', '', '', '', '', '', '', '', '', 4),
(3, 'Salmo 27\r\nMi luz y Mi Salvación\r\n', '1\r\n//Es Jehová mi Luz y mi Salvación//\r\nDe quien temeré, Oh Señor\r\nSi tú estás conmigo, de quien tendré temor\r\nEres tú la fortaleza de mi vida\r\nDe quien tendré temor \r\n//Si tú estás conmigo, de quien tendré temor//', 'CORO                       \r\nMi Corazón ha dicho tú rostro buscare \r\nY en la adversidad en tú nombre confiare \r\nPorque tú eres mi refugio \r\nMi barco en altamar', '2\r\n//En ti oh Jehová he esperado//\r\n//Eres tú mi roca y mi castillo oh Dios//\r\nEres tú la fortaleza de mi vida\r\nDe quien tendré temor \r\n//Si tú estás conmigo, de quien tendré temor//', 'CORO                       \r\nMi Corazón ha dicho tú rostro buscare \r\nY en la adversidad en tú nombre confiare \r\nPorque tú eres mi refugio \r\nMi barco en altamar', '', '', '', '', '', '', '', '', 4),
(4, 'Salmo 43\r\nEnvía tu Luz y tu Verdad', '1\r\nCuando en mi vida creí no faltarme nada \r\nIgnorante imagine que era feliz \r\nÉl va mi caminar y distinto es\r\nPadre confiado en ti lo podre hacer', '1\r\nHe buscado la manera de acercarme a ti Señor\r\nTu camino voy buscando sin desfallecer \r\nSe que angosto es el camino \r\nMas en ti Señor yo venceré \r\n', 'CORO\r\n(H) Envía tú luz y tú Verdad (M) Estas me guiaran en tú sendero de bondad\r\n(H) Que me lleven a encontrar el amor y la paz (T) Que solo Tú nos puedes otorgar', '2\r\nNo sabía que vivía en un error hoy me he dado cuenta \r\nQue sin ti Señor no existe la felicidad \r\nY por eso vengo a ti agradecidamente ', '2\r\nQue en los planes tuyos me escogiste a mí\r\nSe que angosto es el camino \r\nmás en ti Señor yo venceré', 'CORO(Final)\r\n//(H) Envía tú luz y tú Verdad (M) Estas me guiaran en tú sendero de bondad\r\n(H) Que me lleven a encontrar el amor y la paz (T) Que solo Tú nos puedes otorgar//', '', '', '', '', '', '', 6),
(5, 'Salmo 51', '1\r\nOh Dios ten piedad de mí, conforme a tu misericordia \r\nOh Dios ten piedad de mí, conforme a la multitud de tus piedades', 'Coro\r\n//(H)Lávame más y más de mi maldad (M) Dame Paz \r\n(H)Quita de mí el Pecado tú eres Piedad, (M) Tu Bondad\r\nQue grande eres Dios\r\nTú eres Salvación\r\nTe alabare Oh Dios\r\nGracias te doy//', '', '', '', '', '', '', '', '', '', '', 2),
(6, 'Salmo 91', '1\r\n(M) El que habita al abrigo de Dios (H) Al abrigo de Dios\r\n(M) Morara bajo su sombra\r\n(H) El que habita al abrigo de Dios (H) Al abrigo de Dios\r\n(M) gozara de larga vida', '1\r\nDiré yo a Jehová castillo mío y esperanza mía \r\nDiré yo a Jehová tú eres mi Dios en quien yo confiaré\r\nÉl te cubrirá bajo sus alas estarás seguro\r\nY Él te librará por siempre del lazo del cazador\r\n', '2\r\n(M)Fuerte escudo es su verdad, (H) Es su verdad\r\n(M) Fuerte adarga protectora\r\n(M)Terror nocturno nunca temerás (H) Nunca temerás\r\n(M) Ni flecha que vuele de día', '2\r\nÁngeles habrá siempre a tu lado para que te guarden\r\nY te guiara para que nunca tropiece tu pie\r\nTú le invocaras y contigo estará en la angustia \r\nÉl te saciara y mostrara su salvación', '', '', '', '', '', '', '', '', 4),
(7, 'Salmo 113', '1\r\nAlabad servidores de Jehová\r\nAlabad el nombre de mi Dios\r\nBendito sea el nombre del Señor', '1\r\nDesde ahora y para siempre\r\nDesde la salida del Sol hasta su ocaso\r\nSea alabado su nombre', 'CORO\r\nGrande sobre todas las naciones es Jehová\r\nPor encima de los Cielos, \r\nPor encima de los cielos ; su Gloria\r\nQuien como el Señor nuestro Dios\r\nQué se sienta en las alturas', 'CORO\r\nY baja para ver los Cielos y la Tierra\r\nY baja para ver los Cielos y la Tierra\r\nAleluya\r\n// (M) Los cielos y la Tierra Aleluya//\r\n(H) Aleluya', '2\r\nÉl levanta del polvo al desvalido\r\nDe lo vil hace subir al pobre\r\nPara sentarlo con los Príncipes', '2\r\nCon los Príncipes de su Pueblo\r\nÉl asienta a la estéril como madre\r\nDe hijos Jubilosa\r\nSea alabado su nombre', 'CORO\r\nGrande sobre todas las naciones es Jehová\r\nPor encima de los Cielos, \r\nPor encima de los cielos ; su Gloria\r\nQuien como el Señor nuestro Dios\r\nQué se sienta en las alturas', 'CORO\r\nY baja para ver los Cielos y la Tierra\r\nY baja para ver los Cielos y la Tierra\r\nAleluya\r\n// (M) Los cielos y la Tierra Aleluya//\r\n(H) Aleluya', '', '', '', '', 8),
(8, 'Salmo 114', 'Alma Mia, recobra tu calma,\r\nQue el Señor fue bueno contigo;\r\nAlma Mia, recobra tu calma,\r\nQue el Señor escucha tu voz.', 'Arrancó mi alma de la muerte,\r\nMis ojos de las lágrimas,\r\nMis pies de la caída.', 'Caminaré en presencia del Señor,\r\nEn el país de la vida.', 'Alma Mia, recobra tu calma,\r\nQue el Señor fue bueno contigo;\r\nAlma Mia, recobra tu calma,\r\nQue el Señor escucha tu voz.', 'Arrancó mi alma de la muerte,\r\nMis ojos de las lágrimas,\r\nMis pies de la caída.', 'Caminaré en presencia del Señor,\r\nEn el país de la vida.', '', '', '', '', '', '', 6),
(9, 'Salmo 138', 'Alabarte he con todo mi corazón\r\nNo hay otro Dios como tú \r\nMe Encorvare en tu templo oh Señor \r\nTu nombre alabare por tu verdad, por tu verdad', 'Porque has hecho tu nombre magnifico   \r\nTu dicho sobre todas las cosas es \r\nEl día que clame, me respondiste;\r\nEsforzasteme mi corazón, mi corazón', 'Porque alto es Jehová, que atiende al humilde\r\nSi yo anduviere en tribulación, tú me vivificaras\r\nTú me vivificaras', '', '', '', '', '', '', '', '', '', 3),
(10, 'Salmo 145', '1\r\nTe exaltare mi Dios mi Rey \r\nY bendeciré tu nombre\r\nEternamente y para siempre\r\nCada día te bendeciré', 'CORO\r\nY alabare tu nombre \r\nEternamente y para siempre \r\nGrande es el Señor y digno de suprema alabanza\r\nY su grandeza Inescrutable \r\nCada día te bendeciré', '2\r\nGeneración a generación \r\nCelebraran tus obras\r\nY anunciaran tus poderosos hechos \r\nCada día te bendeciré', 'CORO\r\nY alabare tu nombre \r\nEternamente y para siempre \r\nGrande es el Señor y digno de suprema alabanza\r\nY su grandeza Inescrutable \r\nCada día te bendeciré', '', '', '', '', '', '', '', '', 4),
(11, 'Salmo 150', 'Alabad a nuestro Dios en su Santuario\r\nPor la hermosura de su cielo\r\nPor sus proezas alabadle a Él', 'Alabadle por la grandeza de su nombre\r\nCon el sonido de bocinas\r\nSalterio y arpa a la vez cantad', 'Alabadle con el alegre pandero \r\nCon el canto de Alabanza  \r\nCantar con Gozo al Señor', 'Alabadle con cuerdas, flautas y címbalos\r\nDe júbilos resonantes\r\nEn su lenguaje todo \r\n', 'CORO\r\n//Lo que respira alabe al Señor//\r\n(Final)\r\n//Lo que respira alabe al Señor//\r\nAl Señor\r\nAl Señor\r\n', '', '', '', '', '', '', '', 5),
(12, 'Evenushalom Popurrí', '///Evenushalom Alehem///\r\nEvenushalom, shalom, shalom Alehem\r\n///La paz de Dios sea con vosotros///\r\nQue con vosotros siempre, siempre \r\nSiempre sea la Paz', 'Pedimos paz para el mundo\r\nCantamos paz para el mundo\r\nQue nuestra vida sea Gloriosa\r\nYo te saludo la paz, la paz sea con vos', '//Grande es el Señor Creador del universo\r\nCantan y alaba al Rey que viene pronto\r\nQue felicidad el corazón nos llena\r\nGrande es el Señor//', '//Quien, quien, quien como el Señor\r\nQue con su poder el mar abrió//\r\nEcho a la mar los carros de faraón\r\nlara-la-la-la-la\r\n', '(H) //Ha echado a la mar quien los perseguía \r\nJinete y caballo ha echado a la mar// \r\nEcho a la mar los carros de faraón\r\nlara-la-la-la-la', '// Digno, digno, digno, digno\r\nDigno, digno, digno eres tú Jehová//\r\n//Jehová es la fortaleza de mi vida\r\n¿De quién he de temer? //', '(H) ///Cantad a Jehová \r\n(M) Un cantico nuevo///\r\n(T) Alabad a Jehová, Alabad a Jehová', '(H) /// ¿Y qué hace Dios?\r\n(M) Haces maravillas///\r\n(T) Alabad a Jehová, Alabad a Jehová', '(H) ///El mar les abrió\r\n(M) Pasaron en seco///\r\n(T) Alabad a Jehová, Alabad a Jehová', '// Digno, digno, digno, digno\r\nDigno, digno, digno eres tú Jehová//\r\n//Jehová es la fortaleza de mi vida\r\n¿De quién he de temer? //', '', '', 10),
(13, 'Unidad de la Fe', '1\r\nEs digno de cada joven \r\nCumplir con el llamado \r\nCon que Dios nos selló\r\nPues a cada uno de nosotros es dada\r\nLa gracia por amor de Dios', 'CORO\r\nHasta alcanzar la Unidad de la Fe \r\nY alejarme de toda maldad \r\nY así como miembros en Cristo \r\nBuscar constantemente \r\nCrecer en Santidad', '2\r\nAunque el cuerpo tiene miembros \r\nQue son muy diferentes \r\nEn Cristo son igual \r\nSi trabajamos unidos por la Iglesia \r\nPodremos un buen fruto dar', 'CORO\r\nHasta alcanzar la Unidad de la Fe \r\nY alejarme de toda maldad \r\nY así como miembros en Cristo \r\nBuscar constantemente \r\nCrecer en Santidad', '', '', '', '', '', '', '', '', 4),
(14, 'El señor es mi fuerza', '//El Señor es mi fuerza, mi roca y salvación//\r\nTú me guías por sendas de justicia, me enseñas la verdad.\r\nTú me das la esperanza para amarte, y no me detendré.', '//El Señor es mi fuerza, mi roca y salvación//\r\nEl Señor es la fuerza de mi pueblo, mi gran libertador,\r\n\r\n\r\n', 'tú me das el valor para la lucha, sin miedo avanzare.\r\n//El Señor es mi fuerza, mi roca y salvación//', '', '', '', '', '', '', '', '', '', 3),
(15, 'Gracias Señor', '1\r\nGracias Señor por la oportunidad\r\nDe estar en tu Iglesia Alabándote\r\nGracias Señor porque una vez más\r\nPor tu palabra yo puedo escucharte', 'Coro\r\nY me deleito en cada sonido \r\nEn cada frase de tu lindo decir\r\nGracias Señor, Gracias Señor\r\nPorque estás conmigo', 'Coro\r\nBendice las almas que deban seguirte\r\nGracias Señor porque puedo seguirte\r\nGracias Señor, Gracias Señor\r\nPorque me escogiste', '2\r\nGracias Señor porque con tu poder\r\nCambiaste mi vida y puedo seguirte\r\nTú me confortas en todo momento\r\nNunca me dejas caer de tu mano', 'Coro\r\nY me deleito en cada sonido \r\nEn cada frase de tu lindo decir\r\nGracias Señor, Gracias Señor\r\nPorque estás conmigo', 'Coro\r\nBendice las almas que deban seguirte\r\nGracias Señor porque puedo seguirte\r\nGracias Señor, Gracias Señor\r\nPorque me escogiste', '3\r\nGracias Señor porque tocas las fibras\r\nMas intimas del alma\r\nY las transformas y son más sensibles\r\nCorazones de piedra Tú puedes cambiarlas', 'Coro\r\nY me deleito en cada sonido \r\nEn cada frase de tu lindo decir\r\nGracias Señor, Gracias Señor\r\nPorque estás conmigo', 'Coro\r\nBendice las almas que deban seguirte\r\nGracias Señor porque puedo seguirte\r\nGracias Señor, Gracias Señor\r\nPorque me escogiste', '', '', '', 9),
(16, 'Dios de amor', 'Bendito Santísimo Dios\r\nDigno de Gloria eres Tú\r\nTus obras son bellas \r\nMis ojos los ven ', '1\r\nNo puedo imaginar \r\nEste gran poder\r\nQue tienes Señor\r\nDios de amor', 'Coro\r\nY tú presencia siento en mi ser\r\nEse amor que llena el corazón \r\nYo quiero más\r\nYo quiero mas\r\n', '2\r\nGracias por tu Bendición \r\nPor siempre, \r\nYo te cantare\r\nTus obras son bellas ', '2\r\nMis ojos los ven \r\nNo puedo imaginar \r\nEste gran poder\r\nQue tienes Señor\r\nDios de amor', 'Coro\r\nY tú presencia siento en mi ser\r\nEse amor que llena el corazón \r\nYo quiero más\r\nYo quiero mas\r\n(Final) De ti Señor ', '', '', '', '', '', '', 6),
(17, 'Mi Orgullo', '1\r\nCada día me levanto a contemplar\r\nLa belleza de tu Obra Celestial\r\nY doy gracias por la vida que hoy me das\r\nY la nueva Oportunidad para cambiar', '1\r\n(M)Tú me formaste, Me regalaste la vida o Dios\r\n(H)En tu camino, Me sostienes con tu Amor\r\n', '//(H)Mi Orgullo, Es ser Hijo de Dios en este mundo\r\nMi Camino y Mi Vida en Él Refugio\r\n(M)Amare, Adorare\r\n(H)Yo lo siento, Tú Bendición conmigo a cada paso\r\nGuíame por tu Sendero Padre Santo\r\n(M)Guíame, Levántame//', '2\r\nBendición a Mi Vida Tú traes con Amor\r\nAun sin ser yo tal merecedor \r\nCada día, cada instante a tu honra yo doy', '2\r\nSabiendo que Tu mereces todo Honor\r\n(M)Todos los días, Tú Amor se refleja en Mi\r\n(H)Una sonrisa, En mi mente sale para Decir', 'Coro\r\n//(H)Mi Orgullo, Es ser Hijo de Dios en este mundo\r\nMi Camino y Mi Vida en Él Refugio\r\n(M)Amare, Adorare\r\n(H)Yo lo siento, Tú Bendición conmigo a cada paso\r\nGuíame por tu Sendero Padre Santo\r\n(M)Guíame, Levántame// \r\n', 'Final\r\nMi Camino y Mi Vida en Ti Esta, Mi Camino y Mi Vida en Ti Esta', '', '', '', '', '', 7);

