  `id_question1` int(11) NOT NULL,
  `question1` text COLLATE utf8mb4_spanish2_ci NOT NULL,
  `name_lesson` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_spanish2_ci;

--
-- Volcado de datos para la tabla `questions1`
--

INSERT INTO `questions1` (`id_question1`, `question1`, `name_lesson`) VALUES
(7, '1.- ¿Cuáles deben ser nuestras actitudes al comprometernos al hábito  de orar? 2° Crónicas 7:14; Santiago 1:5-6; Mateo 6:6; Lucas 11:2.', 17),
(8, '1.- ¿Qué enseñanzas encontramos en la Palabra de Dios, en lo  referente a la importancia de su estudio? 1ª Timoteo 4:13,15-16 y  Josué 1:8.', 18),
(9, '1.- ¿De qué manera podemos obtener las promesas que Dios ha establecido por medio de su Hijo? Juan 5:24; 17:3; Mateo 7:21 y 1ª Pedro 1:14-16.', 19),
(10, '1.- Todos los hijos de Dios tenemos adversidades: 2ª Timoteo  3:12 y 1ª Pedro 5:9. ¿Qué importante enseñanza encontramos  en las Sagradas Escrituras para hacer frente a la adversidad? 1ª  Pedro 4:7; 5:8-9; Santiago 4:7 y Romanos 12:12.', 20),
(11, '1. Aporte un ejemplo de las Sagradas Escrituras, de cómo la oración,  el ayuno y la meditación en la Palabra de Dios, fortalecen el espíritu  del hombre. Al respecto, mencione un momento similar en que a  usted le fue dada fortaleza.', 21),
(12, '1. ¿Cuál es la voluntad de Dios para sus hijos? 1ª Tesalonicenses  4:3. ¿Por qué debemos ser santos? Levítico 11:44-45 y 1ª Pedro  1:14-16; Mateo 5:48. Analice Josué 24:19.', 22),
(13, '1.- Con base en las citas biblicas de Colosenses 3:5-7 y Efesios 2:11- 12, ¿de dónde hemos sido sacados y por qué? Considere a su vez  Romanos 6:17-18.', 23),
(14, '1.- Con base a 1ª Corintios 3:1-3; Proverbios 12:1 y Hebreos 13:9,  determine algunos de los factores que nos impiden progresar en la  verdadera comprensión de la Palabra de Dios.', 24),
(15, '1.- ¿Cuál es la Voluntad de Dios? 1ª Tesalonicenses 4:3; 1ª Corintios 1:2.  ¿Cuál es el propósito de Dios para su Iglesia? Efesios 5:25-27.', 25),
(16, '1.- ¿De qué forma fue el llamado de Saulo? Hechos 22:6-8, 10 y 26:13- 16. ¿Cuál fue su primera reacción? Hechos 22:10 y 26:19. ¿Cómo fue  su conducta después de haber recibido al Señor en su corazón? Hechos  22:12-16 y 9:18-22. Reflexione: ¿De qué manera usted recibió su llamado  y para qué?', 26),
(17, '1.- ¿Qué efectos tiene el bautismo, en el creyente? 1ª Pedro 3:21. ¿Cuál es  la consecuencia de Bautizarse? Marcos 16:16.', 27),
(18, '1.- ¿Qué es un tropiezo moral? Proverbios 24:16. Vea el caso del  Apóstol Pedro en Mateo 26:69-75. ¿Fue un fallo o error involuntario?  Argumente', 28),
(19, '1.- ¿Qué es la disciplina? Ver comentario. Relacione la respuesta  con los siguientes ejemplos: Jerusalem, en Sofonías 3:1-2 y el niño  Jesús en Lucas 2:43, 46 y 51.', 29),
(20, '1.- ¿Cuál es el proceso para obtener el poder de Dios? Hechos 2:38  y 5:32.', 30),
(21, '1.- ¿Qué implica ser seguidor de Jesús? Juan 15:8 y 13:35; Lucas 14:26-27;  Mateo 16:24. Explique qué significan las frases: “tome su cruz”; “aborrece  su vida”; “niéguese a sí mismo“.', 31);

