-- Estructura de tabla para la tabla `questions3`
--

CREATE TABLE `questions3` (
  `id_question3` int(11) NOT NULL,
  `question3` text COLLATE utf8mb4_spanish2_ci NOT NULL,
  `name_lesson` int(11) NOT NULL
